        // Generate lines to prevent bugs
        private async void GenerateLines()
        {
            string path = (Environment.CurrentDirectory + "\\APBGame\\Localization\\GER\\TaskObjectives.GER");
            if (File.Exists(path))
            {
                using (StreamWriter sw = File.CreateText(path))
                {
                    int x = 0;

                    while (true)
                    {
                        sw.WriteLine(System.Environment.NewLine);
                        x++;

                        if (x == 3000)
                            break;
                    }
                }
            }
        }



        private void Default_Btn_MouseLeftButtonUp(object sender, MouseButtonEventArgs e)
        {
            this.preview.Source = new BitmapImage(new Uri("/Configs/Default.png", UriKind.Relative));
            GenerateLines();
            // ================================================== Default ==================================================


            //Read
            string[] contentsSimple = File.ReadAllLines(Environment.CurrentDirectory + "\\APBGame\\Localization\\GER\\TaskObjectives.GER");


            #region DefaultCode
            contentsSimple[0] = "[TaskObjectives]";
            contentsSimple[1] = "TaskObjectives_AE_BCL4_BoB4_Stage01_OwnerBrief=Dr. Borious needs your help moving prescription drugs to our brothers in prison. <Col: StageText>Break into</Col> a <Col: StageText>security truck</Col> - our SPPD contact will use it to drive the drugs past prison security.";
            contentsSimple[2] = "TaskObjectives_AE_BCL4_BoB4_Stage01_DispatchBrief=The SPPD has been investigating a gang planning to smuggle drugs into prison with an armored car. <Col: StageText>Prevent</Col> the theft of this <Col: StageText>vehicle</Col> by Criminals.";
            contentsSimple[3] = "TaskObjectives_AE_BCL4_BoB4_Stage02_OwnerBrief=Next step: get the drugs. The pharmacy we'll hit is pretty big on security - the windows are reinforced. <Col: StageText>Bomb</Col> the <Col: StageText>pharmacy</Col> to get access to the drugs inside.";
            contentsSimple[4] = "TaskObjectives_AE_BCL4_BoB4_Stage02_DispatchBrief=Now they have the armored car, the gang is planning to bomb a pharmacy and steal prescription drugs. <Col: StageText>Protect</Col> this <Col: StageText>drug store</Col> from the Criminals.";
            contentsSimple[5] = "TaskObjectives_AE_BCL4_BoB4_Stage03_OwnerBrief=Dr. Borious found where the pharmacy keeps the specialty drugs requested by our imprisoned gang members. <Col: StageText>Break into </Col> the pharmacy's secure storage area and <Col: StageText>pick up</Col> the medication.";
            contentsSimple[6] = "TaskObjectives_AE_BCL4_BoB4_Stage03_DispatchBrief=The gang is searching for more drugs in the pharmacy's secure storage area. <Col: StageText>Protect</Col> the storage area's <Col: StageText>door</Col> from the Criminals.";
            contentsSimple[7] = "TaskObjectives_AE_BCL4_BoB4_Stage04_OwnerBrief=It won't be easy dropping that truck in to the SPPD without getting noticed, but that's why we pay you the big bucks. <Col: StageText>Deliver</Col> the drugs and the security truck to the SPPD's <Col: StageText>lockup</Col>.";
            contentsSimple[8] = "TaskObjectives_AE_BCL4_BoB4_Stage04_DispatchBrief=A dirty cop plans to deliver the drugs by driving the van into the prison. <Col: StageText>Prevent</Col> the Criminals from <Col: StageText>dropping off</Col> the van and prescription drugs to their driver. He's at this SPPD <Col: StageText>lockup</Col>.";
            contentsSimple[9] = "TaskObjectives_AE_BCL4_BoB4_Stage05_OwnerBrief=The medication is on its way to the prison, but an Enforcer ID'd your leader as an associate of Dr. Borious. <Col: StageText>Protect your leader</Col> and kill the Enforcers, or they'll uncover the drug transfer!";
            contentsSimple[10] = "TaskObjectives_AE_BCL4_BoB4_Stage05_DispatchBrief=The gang dropped the drugs at the police lockup, but the SPPD driver was a no-show - he saw us coming. <Col: StageText>Take down</Col> the gang's <Col: StageText>leader</Col> - we want to get the name of that dirty officer.";
            contentsSimple[11] = "TaskObjectives_AE_BCS0_Ter1_B_Stage01_OwnerBrief=Jeung just totally annihilated a G-King group. He walked in, calm and cool, and walked out again, leaving nothing but bodies. Get into our newly claimed turf and <Col: StageText>spray </Col>our colors up!";
            contentsSimple[12] = "TaskObjectives_AE_BCS0_Ter1_B_Stage01_DispatchBrief=The Blood Roses have made a move to expand their territory. <Col: StageText>Prevent </Col>those emo assholes from <Col: StageText>spraying </Col>up their colors.";
            contentsSimple[13] = "TaskObjectives_AE_BCS0_Ter1_B_Stage02_OwnerBrief=The bodies of those G-King scum aren't even cold yet and we're already hearing rumblings of revolt. <Col: StageText>Take control </Col>of this troublesome <Col: StageText>area</Col>. Show these welfare state fucks who's in charge.";
            contentsSimple[14] = "TaskObjectives_AE_BCS0_Ter1_B_Stage02_DispatchBrief=Blood Roses are intimidating the locals to establish control of their new-found turf. <Col: StageText>Defend </Col>the <Col: StageText>area</Col>. Without their guns I doubt they could intimidate an old lady, never mind a neighborhood.";
            contentsSimple[15] = "TaskObjectives_AE_BCS0_Ter1_B_Stage03_OwnerBrief=God, these plebs don't give up. We've got some G-King sympathizers being vocal now. <Col: StageText>Take control </Col>of the <Col: StageText>area</Col>; flash your guns and let them know you're there. They'll get the message.";
            contentsSimple[16] = "TaskObjectives_AE_BCS0_Ter1_B_Stage03_DispatchBrief=The Blood Roses are moving to put down the more vocal members of our community. <Col: StageText>Defend </Col>this <Col: StageText>area </Col>from the Roses. If you manage to take any Roses alive, let me know: I could use the target practice.";
            contentsSimple[17] = "TaskObjectives_AE_BCS0_Ter1_B_Stage04_OwnerBrief=Well, well - looks like these crybabies went one step further and called the Enforcers. If it's not the Gs, it's these badge-waving pricks. Don't let them take the block. <Col: StageText>Capture the area</Col>.";
            contentsSimple[18] = "TaskObjectives_AE_BCS0_Ter1_B_Stage04_DispatchBrief=The Blood Roses have secured a stranglehold over the people in this block. Show these skeletal goth wannabes that we ain't impressed - <Col: StageText>capture the area</Col> out from under them.";
            contentsSimple[19] = "TaskObjectives_AE_BCS0_Ter1_G_Stage01_OwnerBrief=Orders from on high: we gotta take control of a new block. Get in there and show the locals who's in charge. <Col: StageText>Spray </Col>our colors loud and proud.";
            contentsSimple[20] = "TaskObjectives_AE_BCS0_Ter1_G_Stage01_DispatchBrief=Some G-King dirtbags just ran another gang out of their turf. <Col: StageText>Prevent </Col>the Gs from <Col: StageText>spraying </Col>up their colors and solidifying their position.";
            contentsSimple[21] = "TaskObjectives_AE_BCS0_Ter1_G_Stage02_OwnerBrief=Good. The block should know what the G-King colors mean now, but there are still pockets of resistance. <Col: StageText>Take control </Col>of this <Col: StageText>area</col>. Scare the shit out of anyone who ain't a G.";
            contentsSimple[22] = "TaskObjectives_AE_BCS0_Ter1_G_Stage02_DispatchBrief=The G-Kings are rolling on the locals, trying to establish control. <Col: StageText>Defend </Col>this <Col: StageText>area </Col>and keep the Gs out. We've got enough dumbass haircuts and bad clothing round here as it is.";
            contentsSimple[23] = "TaskObjectives_AE_BCS0_Ter1_G_Stage03_OwnerBrief=Looks kinda quiet! But sure as shit, the locals are asking for Enforcer protection right about now... We better consolidate. <Col: StageText>Take control </Col>of this <Col: StageText>area</Col>.";
            contentsSimple[24] = "TaskObjectives_AE_BCS0_Ter1_G_Stage03_DispatchBrief=The G-Kings are moving to put down any resistance in the block. <Col: StageText>Defend </Col>this <Col: StageText>area </Col>from the Criminals and show them what authority really means.";
            contentsSimple[25] = "TaskObjectives_AE_BCS0_Ter1_G_Stage04_OwnerBrief=New turf; same old call to the Enforcers... Don't let them take back our territory. <Col: StageText>Capture the area</Col>.";
            contentsSimple[26] = "TaskObjectives_AE_BCS0_Ter1_G_Stage04_DispatchBrief=According to high command, this ain't going so well. At this rate, we'll never get the Gs out! Throw away the textbook on civic-minded Enforcement: <Col: StageText>capture the area</col>.";
            contentsSimple[27] = "TaskObjectives_AE_BEL0_Inv3_Stage01_OwnerBrief=We've tracked down a couple of major drug dealers. <Col: StageText>Raid </Col>these <Col: StageText>drug dens </Col>and pick up any substances you find.";
            contentsSimple[28] = "TaskObjectives_AE_BEL0_Inv3_Stage01_DispatchBrief=A vice squad caught dealers with the Glow we smuggled into the city. We don't want anyone tracking the drug back to us. <Col: StageText>Prevent </Col>the Enforcers from <Col: StageText>raiding </Col>the dealer's hideouts.";
            contentsSimple[29] = "TaskObjectives_AE_BEL0_Inv3_Stage02_OwnerBrief=Good work, we had a tip-off that these dealers were pushing some unusual Class A merchandise. <Col: StageText>Deliver </Col>the <Col: StageText>powder </Col>to our <Col: StageText>lab </Col>for analysis.";
            contentsSimple[30] = "TaskObjectives_AE_BEL0_Inv3_Stage02_DispatchBrief=The Enforcers have the drugs! If they realize it's Glow, they'll look under every rock in the city till they find the shipment. <Col: StageText>Prevent the delivery </Col>of the drug to the Enforcer's lab.";
            contentsSimple[31] = "TaskObjectives_AE_BEL0_Inv3_Stage03_OwnerBrief=The powder is Glow, and it's a killer! With those dealers gone, the supplier decided to get out fast - now quick, <Col: StageText>defuse the bombs </Col>on these <Col: StageText>boxes </Col>of Glow before the supplier blows them up!";
            contentsSimple[32] = "TaskObjectives_AE_BEL0_Inv3_Stage03_DispatchBrief=We can't let the Enforcers find the raw Glow, or they'll work out how we've been smuggling it in. We've set explosives to destroy our remaining stock. <Col: StageText>Protect </Col>the <Col: StageText>boxes </Col>until the bombs go off.";
            contentsSimple[33] = "TaskObjectives_AE_BEL0_Inv3_Stage04_OwnerBrief=Those suppliers didn't move quickly enough - we located the lab equipment they used to refine the Glow. <Col: StageText>Keep </Col>the <Col: StageText>box</Col>.";
            contentsSimple[34] = "TaskObjectives_AE_BEL0_Inv3_Stage04_DispatchBrief=Now the Enforcers know we're importing raw Glow, we're moving the refining lab before the Enforcers can find it. <Col: StageText>Keep </Col>the <Col: StageText>lab equipment </Col>safe from Enforcers <Col: StageText>or take out </Col>the <Col: StageText>investigators</Col>.";
            contentsSimple[35] = "TaskObjectives_AE_BEM0_Inv7_Stage01_OwnerBrief=Promoting their noxious Four-Wheeled Doggi comic, the Gs have hidden a golden Doggi somewhere in the city. Hacked vending machines give out clue tickets. <Col: StageText>Investigate </Col>these <Col: StageText>ticket machines</col>.";
            contentsSimple[36] = "TaskObjectives_AE_BEM0_Inv7_Stage01_DispatchBrief=You heard? Effigy just released a golden ticket for a front cover promo - your style, they take the shots. Problem is, the 'Forcers want it too. <Col: StageText>Keep </Col>Enforcement away from this <Col: StageText>ticket machine</col>.";
            contentsSimple[37] = "TaskObjectives_AE_BEM0_Inv7_Stage02_OwnerBrief=We've been after the Four-Wheeled Doggi artist for years. Whoever that seditious mofo is, they just sent out more Gs to destroy the evidence. <Col: StageText>Disarm </Col>the bombs on these <Col: StageText>ticket machines</Col>.";
            contentsSimple[38] = "TaskObjectives_AE_BEM0_Inv7_Stage02_DispatchBrief=That machine was a bust. But we'll search every other machine in the city 'till we find that winning ticket! Boobytraps'll slow down the competition. <Col: StageText>Defend </Col>the <Col: StageText>bombs </Col>on these ticket machines.";
            contentsSimple[39] = "TaskObjectives_AE_BEM0_Inv7_Stage03_OwnerBrief=Want a golden Doggi? Our analysts located the treasure by buying over 2,000,000 cans of XSChill. <Col: StageText>Confiscate </Col>the <Col: StageText>suitcase </Col>. God, this stuff tastes like ass...";
            contentsSimple[40] = "TaskObjectives_AE_BEM0_Inv7_Stage03_DispatchBrief=Lucky break! We tortured an exec from the ticket company until they told us where the golden ticket was. <Col: StageText>Keep </Col>the <Col: StageText>suitcase </Col>away from the Enforcers!";
            contentsSimple[41] = "TaskObjectives_AE_BES2_Dis1_P_Stage01_OwnerBrief=A chop shop gang posted a recording of their plans to eliminate a rival garage on Lie-Net. Eva wants you to <Col: StageText>pick up</Col> the <Col: StageText>camcorder</Col> with the unedited footage.";
            contentsSimple[42] = "TaskObjectives_AE_BES2_Dis1_P_Stage01_DispatchBrief=Some kids recorded our gang setting bombs designed to put a rival chop shop out of business. <Col: StageText>Prevent</Col> the Enforcers from <Col: StageText>picking up</Col> the <Col: StageText>camcorder</Col> with the recording.";
            contentsSimple[43] = "TaskObjectives_AE_BES2_Dis1_P_Stage02_OwnerBrief=Good work. <Col: StageText>Drop off</Col> the <Col: StageText>camcorder</Col> at our tech lab. Eva and the interns will find what we can use for evidence against the chop shop gang.";
            contentsSimple[44] = "TaskObjectives_AE_BES2_Dis1_P_Stage02_DispatchBrief=A Praetorian squad collected the camcorder evidence against our chop shop. <Col: StageText>Prevent</Col> the Enforcers from reaching their <Col: StageText>drop off</Col>.";
            contentsSimple[45] = "TaskObjectives_AE_BES2_Dis1_P_Stage03_OwnerBrief=The last five minutes of recorded footage shows bombs planted at the rival gang's chop shops! <Col: StageText>Disarm</Col> the bombs on these <Col: StageText>garage</Col> doors.";
            contentsSimple[46] = "TaskObjectives_AE_BES2_Dis1_P_Stage03_DispatchBrief=The camera's recording will lead the 'Torians to the bombs we placed on the rival chop shop garages. <Col: StageText>Prevent</Col> the Enforcers from <Col: StageText>disarming</Col> the bombs.";
            contentsSimple[47] = "TaskObjectives_AE_BES2_Dis1_P_Stage04_OwnerBrief=The recording also shows the chop shop gang attaching bombs to two of the rival gang's cars. <Col: StageText>Disarm</Col> these <Col: StageText>bombs</Col> before they hurt any civilians.";
            contentsSimple[48] = "TaskObjectives_AE_BES2_Dis1_P_Stage04_DispatchBrief=The recording those 'Forcers captured also shows the bombs we attached to the rival chop shop's cars. <Col: StageText>Prevent</Col> the Enforcers from <Col: StageText>disarming</Col> the bombs.";
            contentsSimple[49] = "TaskObjectives_AE_BES2_Dis1_P_Stage05_OwnerBrief=The chop shop gang is hunting down the rival gang's members at their hangout. Eva thinks you should <Col: StageText>capture the area </Col>.";
            contentsSimple[50] = "TaskObjectives_AE_BES2_Dis1_P_Stage05_DispatchBrief=Those Praetorians are a real pain in the ass! <Col: StageText>Capture</Col> this <Col: StageText>area</Col> so we can move our operation.";
            contentsSimple[51] = "TaskObjectives_AE_BES2_Dis1_T_Stage01_OwnerBrief=Chiza wants justice served on a chop shop gang that tried to blackmail her. <Col: StageText>Pick up </Col>this <Col: StageText>camcorder</Col> - it has a recording of the gang and their stolen cars.";
            contentsSimple[52] = "TaskObjectives_AE_BES2_Dis1_T_Stage01_DispatchBrief=You know Chiza? Annoying Prentiss Tiger? We tried to bribe her, and now the bitch is after our chop shop. <Col: StageText>Prevent </Col>the Enforcers from <Col: StageText>picking up </Col>a camcorder with a recording of our operations.";
            contentsSimple[53] = "TaskObjectives_AE_BES2_Dis1_T_Stage02_OwnerBrief=Good work; <Col: StageText>drop off </Col>the <Col: StageText>camcorder </Col>at our tech lab. They'll analyze the footage and find anything we can use for evidence against the chop shop gang.";
            contentsSimple[54] = "TaskObjectives_AE_BES2_Dis1_T_Stage02_DispatchBrief=Chiza's got the Tigers running off with camcorder evidence that features our chop shop. <Col: StageText>Prevent </Col>the Enforcers from reaching their <Col: StageText>drop-off </Col>with the camera.";
            contentsSimple[55] = "TaskObjectives_AE_BES2_Dis1_T_Stage03_OwnerBrief=The gang knows we're after them - a parolee let slip we took their camcorder. <Col: StageText>Disarm </Col>the bombs on these <Col: StageText>garage </Col>doors before the gang can blow everything sky-high!";
            contentsSimple[56] = "TaskObjectives_AE_BES2_Dis1_T_Stage03_DispatchBrief=Fuck Chiza! I know her squad wants to ransack our chop shop for themselves. If we can't have it, no one can! <Col: StageText>Prevent </Col>the Enforcers from <Col: StageText>disarming </Col>the bombs on our garages.";
            contentsSimple[57] = "TaskObjectives_AE_BES2_Dis1_T_Stage04_OwnerBrief=The gang is destroying any stolen cars featured in the recording. <Col: StageText>Disarm </Col>these <Col: StageText>bombs </Col>- or the recording will be useless as evidence. Chiza will personally drive the cars back to our lockup.";
            contentsSimple[58] = "TaskObjectives_AE_BES2_Dis1_T_Stage04_DispatchBrief=Now the Tigers are headed for the stolen cars we fixed up. <Col: StageText>Prevent </Col>the Enforcers from <Col: StageText>disarming </Col>the bombs on our cars. Leave nothing for Chiza and her squad!";
            contentsSimple[59] = "TaskObjectives_AE_BES2_Dis1_T_Stage05_OwnerBrief=Now that chop shop operation is toast, the gang's trying to claim another area and set up again. Chiza thinks you should <Col: StageText>capture the area</Col>.";
            contentsSimple[60] = "TaskObjectives_AE_BES2_Dis1_T_Stage05_DispatchBrief=It's Chiza's fault we had to target our own chop shops and cars - we'll take our revenge later! Meanwhile, <Col: StageText>capture </Col>this new <Col: StageText>area </Col>so we can start fresh.";
            contentsSimple[61] = "TaskObjectives_AE_BES4_Inv5_Stage01_OwnerBrief=A producer working at Moki 808 has been under SPPD investigation for embezzlement. Now he's gone missing. <Col: StageText>Pick up</Col> his <Col: StageText>briefcase</Col> - it could help us locate him.";
            contentsSimple[62] = "TaskObjectives_AE_BES4_Inv5_Stage01_DispatchBrief=A producer at Moki 808 was under SPPD investigation for embezzlement until we put him six feet under. The Enforcers found his briefcase - <Col: StageText>prevent</Col> them from <Col: StageText>picking it up</Col>.";
            contentsSimple[63] = "TaskObjectives_AE_BES4_Inv5_Stage02_OwnerBrief=Hopefully this briefcase can help us find the Moki 808 producer, and clear up some mysteries in the embezzlement case against him. <Col: StageText>Drop off</Col> the briefcase at our <Col: StageText>lockup</Col>.";
            contentsSimple[64] = "TaskObjectives_AE_BES4_Inv5_Stage02_DispatchBrief=The briefcase contains a ledger proving the producer was embezzling money from Moki 808 with our help. <Col: StageText>Prevent </Col>the briefcase being <Col: StageText>dropped off</Col>, or Enforcers will start looking for accomplices.";
            contentsSimple[65] = "TaskObjectives_AE_BES4_Inv5_Stage03_OwnerBrief=The briefcase contained a bail bond receipt for the producer's son, and receipts for the funding of a fake TV show in Korea. <Col: StageText>Search </Col>the producer's <Col: StageText>portacabins</Col> for further evidence.";
            contentsSimple[66] = "TaskObjectives_AE_BES4_Inv5_Stage03_DispatchBrief=The ledger details meetings we held at the producer's portacabins. <Col: StageText>Prevent</Col> the Enforcers from <Col: StageText>searching</Col> the cabins for further evidence that could connect us with the producer's embezzlement.";
            contentsSimple[67] = "TaskObjectives_AE_BES4_Inv5_Stage04_OwnerBrief=The producer's been murdered for his embezzled money. His safe's contents implicate the killers, but they're trying to disrupt the case. Either <Col: StageText>drop off</Col> the safe at our <Col: StageText>lockup.";
            contentsSimple[68] = "TaskObjectives_AE_BES4_Inv5_Stage04_DispatchBrief=The Enforcers have the producer's safe! With that, even Helen Keller could follow the embezzlement paper trail directly to us. <Col: StageText>Capture and drop off</Col> the safe.";
            contentsSimple[69] = "TaskObjectives_AE_BES4_Inv5_Stage04_Opp_OwnerBrief=";
            contentsSimple[70] = "TaskObjectives_AE_BES4_Inv5_Stage04_Opp_DispatchBrief=The Enforcers have the producer's safe! With that, even Helen Keller could follow the embezzlement paper trail directly to us. <Col: StageText>Capture and drop off</Col> the safe.";
            contentsSimple[71] = "TaskObjectives_DB_BCL0_Gra1_B_Stage01_OwnerBrief=Imitation is the sincerest form of flattery, I suppose, but we're drowning in a sea of Blood Rose-style gangs. We need to ensure everyone knows we are the original and best. <Col: StageText>Spray the area </Col>in your own beautiful way.";
            contentsSimple[72] = "TaskObjectives_DB_BCL0_Gra1_B_Stage01_DispatchBrief=The Blood Roses have been spotted in the area carrying spray cans; it's guaranteed they aren't planning to freshen up the playground. <Col: StageText>Stop them </Col>from <Col: StageText>tagging </Col>the area up.";
            contentsSimple[73] = "TaskObjectives_DB_BCL0_Gra1_B_Stage02_OwnerBrief=Elegant. This block is all yours. <Col: StageText>Spray </col>our designs on every spot you can find. But save some paint - you've got more to do.";
            contentsSimple[74] = "TaskObjectives_DB_BCL0_Gra1_B_Stage02_DispatchBrief=The Blood Roses must think this block is their own private canvas. <Col: StageText>Stop them</Col> from <Col: StageText>spraying</Col> up any more of their crap.";
            contentsSimple[75] = "TaskObjectives_DB_BCL0_Gra1_B_Stage03_OwnerBrief=A wonderful sight. That block is a beacon of Blood Rose style. <Col: StageText>Spray </Col>another block and our dominance will be seen by all.";
            contentsSimple[76] = "TaskObjectives_DB_BCL0_Gra1_B_Stage03_DispatchBrief=I'm going to shake those Blood Roses up and down like a spray can until I hear something rattle inside. <Col: StageText>Stop them </Col>from <Col: StageText>spraying</Col> their colors here.";
            contentsSimple[77] = "TaskObjectives_DB_BCL0_Gra1_B_Stage04_OwnerBrief=Your creative work has attracted some attention - not from the art critics, but from the Enforcers. <Col: StageText>Bomb this area </Col>and they will be too busy to cover up your tags.";
            contentsSimple[78] = "TaskObjectives_DB_BCL0_Gra1_B_Stage04_DispatchBrief=The Blood Roses don't like us covering up their tags, and have thrown a tantrum. <Col: StageText>Stop the Roses </Col>from planting a <Col: StageText>bomb </Col>in this public area.";
            contentsSimple[79] = "TaskObjectives_DB_BCL0_Gra1_G_Stage01_OwnerBrief=The city has been getting way too crowded with other crews and Enforcers. We need to be sure everyone remembers that it's the G-Kings who rule San Paro. <Col: StageText>Spray the area</Col>.";
            contentsSimple[80] = "TaskObjectives_DB_BCL0_Gra1_G_Stage01_DispatchBrief=The G-Kings have been spotted in the area carrying spray cans. You know what they're up to. <Col: StageText>Stop them </Col>from <Col: StageText>spraying </Col>the area.";
            contentsSimple[81] = "TaskObjectives_DB_BCL0_Gra1_G_Stage02_OwnerBrief=Cool, hit the rest of the block and <Col: StageText>spray our tags </Col>on every spot you can find. G-King b-ling, baby.";
            contentsSimple[82] = "TaskObjectives_DB_BCL0_Gra1_G_Stage02_DispatchBrief=I didn't think the G-Kings knew which way to point a spray can - that's how come they look like painted clowns. Guess I was wrong, as they're <Col: StageText>spraying </Col>their crap up everywhere. <Col: StageText>Stop them</Col>.";
            contentsSimple[83] = "TaskObjectives_DB_BCL0_Gra1_G_Stage03_OwnerBrief=Bonita Benjamin saw your work and loved it. She thinks it needs to be more widely seen. <Col: StageText>Spray </Col>another block so everyone knows we totally own this area.";
            contentsSimple[84] = "TaskObjectives_DB_BCL0_Gra1_G_Stage03_DispatchBrief=It's so cute to see the G-Kings learning to read and write. But I wish they wouldn't practice by <Col: StageText>spray painting </Col>on the walls. <Col: StageText>Stop them</Col>.";
            contentsSimple[85] = "TaskObjectives_DB_BCL0_Gra1_G_Stage04_OwnerBrief=Let's get some extra publicity for your work. <Col: StageText>Set off a bomb </Col>in this public place to draw in the news crews.";
            contentsSimple[86] = "TaskObjectives_DB_BCL0_Gra1_G_Stage04_DispatchBrief=The G-Kings have finished tagging the area and are hell-bent on ensuring everyone notices it. <Col: StageText>Stop them </Col>from <Col: StageText>setting off a bomb </Col>in this public area.";
            contentsSimple[87] = "TaskObjectives_DB_BCL0_Gra2_Stage01_OwnerBrief=Enforcers have cleaned off a bunch of tags as part of an anti-graffiti drive by Mayor Derren. This fresh canvas is an opportunity for us to take graffiti in a new direction. <Col: StageText>Tag these areas. </Col>";
            contentsSimple[88] = "TaskObjectives_DB_BCL0_Gra2_Stage01_DispatchBrief=We've put in a lot of man-hours to clean up San Paro's graffiti, and now we're hearing rumors of a crew looking to reclaim walls all over the city. <Col: StageText>Prevent the crew </Col>from <Col: StageText>tagging </Col>these areas.";
            contentsSimple[89] = "TaskObjectives_DB_BCL0_Gra2_Stage02_OwnerBrief=Despite Derren's occasional crackdowns, the people of San Paro know our art well. That can only happen through dedicated tagging. The art in these areas has been cleaned off; <Col: StageText>tag them up </Col>again.";
            contentsSimple[90] = "TaskObjectives_DB_BCL0_Gra2_Stage02_DispatchBrief=There used to be more tags on this city than tattoos on a G-King. We can't let San Paro fall back into that sorry state. <Col: StageText>Stop </Col>the graffiti crew from <Col: StageText>tagging </Col>these spots.";
            contentsSimple[91] = "TaskObjectives_DB_BCL0_Gra2_Stage03_OwnerBrief=The Enforcers are realizing we're not just a bunch of easily intimidated street kids. Show them and show Derren that our artistic expression can't be silenced! <Col: StageText>Tag these spots </Col>up.";
            contentsSimple[92] = "TaskObjectives_DB_BCL0_Gra2_Stage03_DispatchBrief=I don't care how artistic that graffiti crew think these tags are, it's still defacing city property. <Col: StageText>Prevent them </Col>from <Col: StageText>tagging </Col>these locations.";
            contentsSimple[93] = "TaskObjectives_DB_BCL0_VTh1_B_Stage01_OwnerBrief=Hey, Jeung wants to enter the Nantego 520 street race, but his car runs about as smoothly as San Paro. If we can find him the parts he needs, he'll owe us a favor. <Col: StageText>Steal this vehicle </Col>for spares.";
            contentsSimple[94] = "TaskObjectives_DB_BCL0_VTh1_B_Stage01_DispatchBrief=We shot up Jeung Bloodrose's car yesterday and he'll want to enter the Nantego 520 street race tomorrow. <Col: StageText>Stop the Roses </Col>from <Col: StageText>stealing this car</Col>, it has the replacement parts he'll need.";
            contentsSimple[95] = "TaskObjectives_DB_BCL0_VTh1_B_Stage02_OwnerBrief=Making off with the whole car is easier than stealing the parts. Plus, carrying a monkey wrench all day is emphatically uncool. <Col: StageText>Drop off the car </Col>and we can chop it up for parts later.";
            contentsSimple[96] = "TaskObjectives_DB_BCL0_VTh1_B_Stage02_DispatchBrief=The Blood Roses are currently transporting the stolen vehicle in order to strip it for parts. Man, they're like a swarm of piranhas. <Col: StageText>Stop them </Col>from <Col: StageText>delivering that car</Col>.";
            contentsSimple[97] = "TaskObjectives_DB_BCL0_VTh1_B_Stage03_OwnerBrief=Jeung says he'll need some souped-up parts to give him an advantage in the race. <Col: StageText>Steal, then deliver this car </Col>to our hideout. It has the suspension needed to withstand a nitro boost.";
            contentsSimple[98] = "TaskObjectives_DB_BCL0_VTh1_B_Stage03_DispatchBrief=The Blood Roses will need more spare parts than they got from that last vehicle. <Col: StageText>Stop them </Col>from <Col: StageText>stealing the car </Col>and taking a can opener to it at their lockup.";
            contentsSimple[99] = "TaskObjectives_DB_BCL0_VTh1_B_Stage04_OwnerBrief=";
            contentsSimple[100] = "TaskObjectives_DB_BCL0_VTh1_B_Stage04_DispatchBrief=The Blood Roses will need more spare parts than they got from that last vehicle. <Col: StageText>Stop them </Col>from <Col: StageText>stealing the car </Col>and taking a can opener to it at their lockup.";
            contentsSimple[101] = "TaskObjectives_DB_BCL0_VTh1_B_Stage05_OwnerBrief=<Col: StageText>Pick up the car parts</Col> we've stolen and <Col: StageText>keep them</Col> out of the hands of our opponents. If Jeung doesn't get what he wants, he tends to sic the Furies on people...";
            contentsSimple[102] = "TaskObjectives_DB_BCL0_VTh1_B_Stage05_DispatchBrief=<Col: StageText>Recover the car parts</Col> the Roses stole, and <Col: StageText>keep them</Col> out of their hands. Last thing we need is Jeung attracting more recruits with a souped-up ride...";
            contentsSimple[103] = "TaskObjectives_DB_BCL0_VTh1_G_Stage01_OwnerBrief=We've seen your sly grand theft auto skills. Most of the G-Kings just huck a brick through the window. <Col: StageText>Steal</Col> this vehicle to order, windows intact.";
            contentsSimple[104] = "TaskObjectives_DB_BCL0_VTh1_G_Stage01_DispatchBrief=We arrested a G-King lowlife with a list of cars in her pocket. We can't read most of her childish scrawl, but check out this car and <Col: StageText>stop the Gs </Col>from <Col: StageText>stealing it </Col>to order.";
            contentsSimple[105] = "TaskObjectives_DB_BCL0_VTh1_G_Stage02_OwnerBrief=Sweet ride. <Col: StageText>Deliver</Col> it to the drop off point we've been given. A friend of Zombie's named Deadbolt will pick it up later. It's for his Auntie - ain't he sweet?";
            contentsSimple[106] = "TaskObjectives_DB_BCL0_VTh1_G_Stage02_DispatchBrief=The G-Kings are currently transporting a 'stolen to order' vehicle. <Col: StageText>Stop them </Col>from <Col: StageText>delivering that car</Col>. Don't worry too much about its condition, as long as those dirty skid marks don't get to keep it.";
            contentsSimple[107] = "TaskObjectives_DB_BCL0_VTh1_G_Stage03_OwnerBrief=You can't do anyone a good deed around here! Now my aunt is giving me an earful: she wants a pimped-out ride too. She's picked it out. Go and <Col: StageText>steal it, then deliver it </Col>to the G-King hideout.";
            contentsSimple[108] = "TaskObjectives_DB_BCL0_VTh1_G_Stage03_DispatchBrief=The list of cars to be stolen by the G-Kings seems accurate. All the owners've been told to move their cars, except for one we can't reach. <Col: StageText>Stop the G-Kings </Col>from <Col: StageText>stealing that car</Col>.";
            contentsSimple[109] = "TaskObjectives_DB_BCL0_VTh1_G_Stage04_OwnerBrief=";
            contentsSimple[110] = "TaskObjectives_DB_BCL0_VTh1_G_Stage04_DispatchBrief=The list of cars to be stolen by the G-Kings seems accurate. All the owners've been told to move their cars, except for one we can't reach. <Col: StageText>Stop the G-Kings </Col>from <Col: StageText>stealing that car</Col>.";
            contentsSimple[111] = "TaskObjectives_DB_BCL0_VTh1_G_Stage05_OwnerBrief=<Col: StageText>Pick up the car parts</Col> we've stolen and <Col: StageText>keep them</Col> out of the hands of our opponents. If Deadbolt doesn't get what he wants, he tends to go looking for the people who let him down...";
            contentsSimple[112] = "TaskObjectives_DB_BCL0_VTh1_G_Stage05_DispatchBrief=<Col: StageText>Recover the car parts</Col> the G-Kings stole, and <Col: StageText>keep them</Col> out of their hands. Last thing we need is those punks driving souped-up rides...";
            contentsSimple[113] = "TaskObjectives_DB_BCL0_Van1_B_Stage01_OwnerBrief=A local shop has thrown out some Blood Rose members. Show them what happens to places that don't value our custom. <Col: StageText>Vandalize their shop front.</Col>";
            contentsSimple[114] = "TaskObjectives_DB_BCL0_Van1_B_Stage01_DispatchBrief=The Blood Roses are vandalizing a shop that pays us for professional protection services. <Col: StageText>Protect </Col>the <Col: StageText>shop </col>from these dandified assholes.";
            contentsSimple[115] = "TaskObjectives_DB_BCL0_Van1_B_Stage02_OwnerBrief=I think we're entitled to some compensation. We've found out where the shop owner keeps their accounts. <Col: StageText>Break in</Col> through the <Col: StageText>back door </Col>and get all their customers' credit card details.";
            contentsSimple[116] = "TaskObjectives_DB_BCL0_Van1_B_Stage02_DispatchBrief=Seems like vandalizing the shop under our protection wasn't enough. Now Blood Roses are breaking into their accountant's office. <Col: StageText>Protect </Col>the <Col: StageText>back office</col>.";
            contentsSimple[117] = "TaskObjectives_DB_BCL0_Van1_B_Stage03_OwnerBrief=Okay, take the credit card details and <Col: StageText>deliver </Col>'em to one of the Blood Rose <Col: StageText>safehouses</Col>. And watch your ass - we can't have you getting caught with them.";
            contentsSimple[118] = "TaskObjectives_DB_BCL0_Van1_B_Stage03_DispatchBrief=Blood Roses have stolen hundreds of credit card details from a shop we are supposed to be protecting. <Col: StageText>Prevent </col>the Roses from <Col: StageText>delivering</col> the details back to their hangout.";
            contentsSimple[119] = "TaskObjectives_DB_BCL0_Van1_B_Stage04_OwnerBrief=<Col: StageText>Spray</Col> up the <Col: StageText>area</Col> to discourage anyone else from having rebellious thoughts.";
            contentsSimple[120] = "TaskObjectives_DB_BCL0_Van1_B_Stage04_DispatchBrief=Now the Roses are laughing at us - tagging up the neighborhood to intimidate other paying clients. <Col: StageText>Stop them </Col>from <Col: StageText>spraying </col>their crap everywhere.";
            contentsSimple[121] = "TaskObjectives_DB_BCL0_Van1_G_Stage01_OwnerBrief=A shop owner thinks her place is too good to serve the G-Kings. Teach her a lesson in your own special style. <Col: StageText>Vandalize the shop front.</Col>";
            contentsSimple[122] = "TaskObjectives_DB_BCL0_Van1_G_Stage01_DispatchBrief=The G-Kings are vandalizing a shop that pays us for security. <Col: StageText>Protect </Col>the <Col: StageText>shop </col>from these skater morons.";
            contentsSimple[123] = "TaskObjectives_DB_BCL0_Van1_G_Stage02_OwnerBrief=Make sure you get something for your time. We've found out where the owner hides the cash from the checkout. <Col: StageText>Break in </col>through the <Col: StageText>back door </Col>and get what you can.";
            contentsSimple[124] = "TaskObjectives_DB_BCL0_Van1_G_Stage02_DispatchBrief=Seems like vandalizing the shops under our protection wasn't enough, now the G-Kings are trying to rob them as well. <Col: StageText>Protect </Col>the <Col: StageText>shop</col>.";
            contentsSimple[125] = "TaskObjectives_DB_BCL0_Van1_G_Stage03_OwnerBrief=Get the cash and <Col: StageText>drop it off </Col>at the nearest G-King <Col: StageText>safehouse</Col>. Once it's there I can start to launder it.";
            contentsSimple[126] = "TaskObjectives_DB_BCL0_Van1_G_Stage03_DispatchBrief=The G-Kings have stolen the cash. This shop is supposed to be under our protection. <Col: StageText>Prevent </col>the G-Kings from <Col: StageText>delivering </Col>the money to their hangout.";
            contentsSimple[127] = "TaskObjectives_DB_BCL0_Van1_G_Stage04_OwnerBrief=<Col: StageText>Spray</col> our tags up to send a clear message to anyone else thinking of dissing the G-Kings.";
            contentsSimple[128] = "TaskObjectives_DB_BCL0_Van1_G_Stage04_DispatchBrief=Now the G-Kings are tagging up the neighborhood, laughing at your security detail. <Col: StageText>Stop them </Col>from <Col: StageText>spraying </col>up their crap. That'll wipe the smile off their faces!";
            contentsSimple[129] = "TaskObjectives_DB_BCL3_Pic1_Stage01_OwnerBrief=We got a call from someone at DMZ Realtors. They want us to quietly dump some hazardous material they found in one of their refurbished properties. <Col: StageText>Pick up these vans </Col>loaded with the materials.";
            contentsSimple[130] = "TaskObjectives_DB_BCL3_Pic1_Stage01_DispatchBrief=We just received a tip-off from the realtors over at Altered Realty - there's illegal dumping occurring. <Col: StageText>Stop anyone </Col>from <Col: StageText>picking up </Col>these suspicious <Col: StageText>vans </Col>until we can look into this.";
            contentsSimple[131] = "TaskObjectives_DB_BCL3_Pic1_Stage02_OwnerBrief=<Col: StageText>Drop off the vans </Col>outside this hideout. We know some people who dump hazardous material outside the city. Don't let Enforcers get hold of it - they'll run straight to a 3N reporter.";
            contentsSimple[132] = "TaskObjectives_DB_BCL3_Pic1_Stage02_DispatchBrief=Criminals have picked up the vans full of dangerous materials and are likely to just dump them anywhere. <Col: StageText>Stop them </Col>from <Col: StageText>dropping off </Col>those <Col: StageText>vans</Col>.";
            contentsSimple[133] = "TaskObjectives_DB_BCL3_Pic1_Stage03_OwnerBrief=DMZ Realty must be buying up some shady properties - they've got more of what they like to call 'unsavory materials' for us to pick up. <Col: StageText>Pick up these SUVs </Col>loaded with the materials.";
            contentsSimple[134] = "TaskObjectives_DB_BCL3_Pic1_Stage03_DispatchBrief=We haven't been able to discover what's in these SUVs, but we have it on good authority that it's hazardous waste. <Col: StageText>Stop anyone </Col>from <Col: StageText>moving </Col>these <Col: StageText>vans </Col>until we can find out more.";
            contentsSimple[135] = "TaskObjectives_DB_BCL3_Pic1_Stage04_OwnerBrief=I swear, if the money weren't so good, we'd never be dealing with this shit. <Col: StageText>Drop off the SUVs </Col>loaded with hazardous materials outside this hideout.";
            contentsSimple[136] = "TaskObjectives_DB_BCL3_Pic1_Stage04_DispatchBrief=The materials these Criminals are messing with are extremely dangerous and shouldn't be moved without a full HazMat suit on! <Col: StageText>Stop </Col>the Crims from <Col: StageText>dumping </Col>that stuff.";
            contentsSimple[137] = "TaskObjectives_DB_BCL3_Pic1_Stage05_OwnerBrief=Looks like someone cottoned onto what we've been doing. They're heading to the DMZ Realtor offices to raid them for evidence of our involvement. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>raiding </Col>the office.";
            contentsSimple[138] = "TaskObjectives_DB_BCL3_Pic1_Stage05_DispatchBrief=";
            contentsSimple[139] = "TaskObjectives_DB_BCL3_Pic1_Stage05_Opp_OwnerBrief=";
            contentsSimple[140] = "TaskObjectives_DB_BCL3_Pic1_Stage05_Opp_DispatchBrief=The Criminals were able to stash those hazardous materials but we still need to know where they came from. <Col: StageText>Raid </Col>these realtors' <Col: StageText>offices </Col>to find evidence of the Criminal's involvement.";
            contentsSimple[141] = "TaskObjectives_DB_BCL3_Pic1_Stage06_OwnerBrief=The computers containing our emails have been seized from the DMZ Realty office. Stop the 'Forcers from dropping off those PCs. <Col: StageText>Get them to our safehouse.</Col>";
            contentsSimple[142] = "TaskObjectives_DB_BCL3_Pic1_Stage06_DispatchBrief=";
            contentsSimple[143] = "TaskObjectives_DB_BCL3_Pic1_Stage06_Opp_OwnerBrief=";
            contentsSimple[144] = "TaskObjectives_DB_BCL3_Pic1_Stage06_Opp_DispatchBrief=Emails sent from the realty offices were used to contact and hire the Criminals. <Col: StageText>Drop off </Col>those computers at our <Col: StageText>lockup</Col>; we'll extract the evidence we need to end this illegal dumping.";
            contentsSimple[145] = "TaskObjectives_DB_BCL3_VTh1_Stage01_OwnerBrief=After a showdown with the Pit Vipers, most of our street racers got chased down and arrested, but their cars have yet to be impounded. <Col: StageText>Steal this car </Col>before it's picked up by the Enforcers.";
            contentsSimple[146] = "TaskObjectives_DB_BCL3_VTh1_Stage01_DispatchBrief=The SPPD picked up most of the White Blades street racing crew, but are waiting on paperwork before collecting the gang's vehicles. <Col: StageText>Protect this car </Col>till it can be impounded.";
            contentsSimple[147] = "TaskObjectives_DB_BCL3_VTh1_Stage02_OwnerBrief=Those Pit Vipers sure fight dirty - pulling in the Enforcers like that. Wish we'd thought of it. <Col: StageText>Steal </Col>and <Col: StageText>deliver </Col>these street racing <Col: StageText>vehicles </Col>before they are sent to the impound lot.";
            contentsSimple[148] = "TaskObjectives_DB_BCL3_VTh1_Stage02_DispatchBrief=The White Blades desperately want to recover their street racing vehicles. <Col: StageText>Protect and keep these cars </Col>till they can be taken to the SPPD secure impound lot. Don't let the gang take them.";
            contentsSimple[149] = "TaskObjectives_DB_BCL3_VTh1_Stage03_OwnerBrief=Those Pit Vipers sure fight dirty - pulling in the Enforcers like that. Wish we'd thought of it. <Col: StageText>Steal </Col>and <Col: StageText>deliver </Col>these street racing <Col: StageText>vehicles </Col>before they are sent to the impound lot.";
            contentsSimple[150] = "TaskObjectives_DB_BCL3_VTh1_Stage03_DispatchBrief=The White Blades desperately want to recover their street racing vehicles. <Col: StageText>Protect and keep these cars </Col>till they can be taken to the SPPD secure impound lot. Don't let the gang take them.";
            contentsSimple[151] = "TaskObjectives_DB_BCL3_VTh1_Stage04_OwnerBrief=With most of our street racing crew busted, we're going to have to re-crew if we want to beat the Pit Vipers. New drivers need new cars! <Col: StageText>Steal this car </Col>before it's picked up.";
            contentsSimple[152] = "TaskObjectives_DB_BCL3_VTh1_Stage04_DispatchBrief=The SPPD did a good job bringing in the White Blades - they spent five years racing on the San Paro asphalt. They need their star car back - <Col: StageText>protect the car </Col>until it can be impounded.";
            contentsSimple[153] = "TaskObjectives_DB_BCL3_VTh1_Stage05_OwnerBrief=<Col: StageText>Steal </Col>and <Col: StageText>deliver all the cars </Col>before they are picked up by Enforcement. We're gonna recruit some new drivers - y'know, crew with more on the clock than five hours playing Drift Turbo II.";
            contentsSimple[154] = "TaskObjectives_DB_BCL3_VTh1_Stage05_DispatchBrief=There can't be many White Blades left, but they're still pissing me off - <Col: StageText>stop them </Col>from <Col: StageText>stealing </Col>and <Col: StageText>delivering </Col>these <Col: StageText>vehicles </Col>which have yet to be taken to the impound.";
            contentsSimple[155] = "TaskObjectives_DB_BCL3_VTh1_Stage06_OwnerBrief=<Col: StageText>Steal </Col>and <Col: StageText>deliver all the cars </Col>before they are picked up by Enforcement. We're gonna recruit some new drivers - y'know, crew with more on the clock than five hours playing Drift Turbo II.";
            contentsSimple[156] = "TaskObjectives_DB_BCL3_VTh1_Stage06_DispatchBrief=There can't be many White Blades left, but they're still pissing me off - <Col: StageText>stop them </Col>from <Col: StageText>stealing </Col>and <Col: StageText>delivering </Col>these <Col: StageText>vehicles </Col>which have yet to be taken to the impound.";
            contentsSimple[157] = "TaskObjectives_DB_BCL3_VTh1_Stage07_OwnerBrief=Okay, time to scare up some new talent.  <Col:StageText>Take and Hold the Block</Col> to show these city boys that it's worth being on our side.";
            contentsSimple[158] = "TaskObjectives_DB_BCL3_VTh1_Stage07_DispatchBrief=Okay, we need to get ahead of this street racing crew - you've been cleared to personally <Col: StageText>Take </Col>and <Col: StageText>Hold</Col> these three <Col: StageText>areas </Col>from the White Blades.";
            contentsSimple[159] = "TaskObjectives_DB_BCM0_Ars1_Stage01_OwnerBrief=We need cash for a new venture capital scam, so we insured some used cars for more than they're worth. <Col: StageText>Blow those cars up </Col>so we can collect on the policies.";
            contentsSimple[160] = "TaskObjectives_DB_BCM0_Ars1_Stage01_DispatchBrief=Gang members are planning on destroying some cars in the area. See to it that it doesn't happen. <Col: StageText>Guard </Col>those <Col: StageText>vehicles</Col>.";
            contentsSimple[161] = "TaskObjectives_DB_BCM0_Ars1_Stage02_OwnerBrief=Lookin' good, but we still need more cash to entice those venture capitalists. <Col: StageText>Burn </Col>these <Col: StageText>cars </Col>so we get a bigger insurance payout.";
            contentsSimple[162] = "TaskObjectives_DB_BCM0_Ars1_Stage02_DispatchBrief=Criminals are targeting more vehicles. It looks like they're collecting the insurance to fund some other job. <Col: StageText>Guard </Col>those <Col: StageText>cars</Col>.";
            contentsSimple[163] = "TaskObjectives_DB_BCM0_Ars1_Stage03_OwnerBrief=Awesome, find somewhere close by and throw another gang's <Col: StageText>graffiti tag </Col>up, so no one thinks this was an inside job.";
            contentsSimple[164] = "TaskObjectives_DB_BCM0_Ars1_Stage03_DispatchBrief=The gang members are spraying up their graffiti. After destroying those vehicles, that's just adding insult to injury… <Col: StageText>Stop them </Col>from <Col: StageText>spray painting </Col>the neighborhood.";
            contentsSimple[165] = "TaskObjectives_DB_BCM0_Ars1_Stage04_OwnerBrief=We're tryin' to collect on car insurance, not life insurance! <Col: StageText>Hold the area</Col> from them before they get a chance to ruin the plan.";
            contentsSimple[166] = "TaskObjectives_DB_BCM0_Ars1_Stage04_DispatchBrief=I hate insurance scammers! <Col: StageText>Capture their HQ</Col> so that the insurance money can go to sick people paying hospital bills, or whatever.";
            contentsSimple[167] = "TaskObjectives_DB_BCM0_Van1_B_Stage01_OwnerBrief=A couple of local shop owners are refusing to serve the Blood Roses. Show them what happens to places that don't value our custom. <Col: StageText>Vandalize their shop fronts.</Col>";
            contentsSimple[168] = "TaskObjectives_DB_BCM0_Van1_B_Stage01_DispatchBrief=Serious breaches of the peace are being called in from stores near you. <Col: StageText>Protect </Col>the <Col: StageText>stores </col>from Blood Rose <Col: StageText>vandals</col>.";
            contentsSimple[169] = "TaskObjectives_DB_BCM0_Van1_B_Stage02_OwnerBrief=A couple of local shop owners are refusing to serve the Blood Roses. Show them what happens to places that don't value our custom. <Col: StageText>Vandalize their shop fronts.</Col>";
            contentsSimple[170] = "TaskObjectives_DB_BCM0_Van1_B_Stage02_DispatchBrief=Serious breaches of the peace are being called in from stores near you. <Col: StageText>Protect </Col>the <Col: StageText>stores </col>from Blood Rose <Col: StageText>vandals</col>.";
            contentsSimple[171] = "TaskObjectives_DB_BCM0_Van1_B_Stage03_OwnerBrief=One store owner has asked the G-Kings for help. <Col: StageText>Vandalize </col>a few of his places to show that disloyalty to the Blood Roses isn't tolerated. <Col: StageText>Don't get killed or arrested </Col>while you're out there.";
            contentsSimple[172] = "TaskObjectives_DB_BCM0_Van1_B_Stage03_DispatchBrief=A store owner is caught in the middle of gang war. <Col: StageText>Protect </col>his <Col: StageText>stores </col>from being <Col: StageText>vandalized </Col>and <Col: StageText>arrest or kill </Col>anyone involved.";
            contentsSimple[173] = "TaskObjectives_DB_BCM0_Van1_B_Stage04_OwnerBrief=One store owner has asked the G-Kings for help. <Col: StageText>Vandalize </col>a few of his places to show that disloyalty to the Blood Roses isn't tolerated. <Col: StageText>Don't get killed or arrested </Col>while you're out there.";
            contentsSimple[174] = "TaskObjectives_DB_BCM0_Van1_B_Stage04_DispatchBrief=A store owner is caught in the middle of gang war. <Col: StageText>Protect </col>his <Col: StageText>stores </col>from being <Col: StageText>vandalized </Col>and <Col: StageText>arrest or kill </Col>anyone involved.";
            contentsSimple[175] = "TaskObjectives_DB_BCM0_Van1_B_Stage05_OwnerBrief=Seems we need to remind the locals of the economic hierarchy - and their place at the bottom of it. <Col: StageText>Spray </col>some fresh <Col: StageText>tags </Col>in the area; make sure no one else decides to refuse us service.";
            contentsSimple[176] = "TaskObjectives_DB_BCM0_Van1_B_Stage05_DispatchBrief=The Blood Roses have started spraying up their slogans in the surrounding area, to intimidate local businesses. <Col: StageText>Prevent </Col>them from <Col: StageText>spraying </col>up their tags.";
            contentsSimple[177] = "TaskObjectives_DB_BCM0_Van1_G_Stage01_OwnerBrief=Some local store owners are refusing to serve the G-Kings. Get over there and make sure we're treated with respect from now on. <Col: StageText>Vandalizing their shops </Col>should work.";
            contentsSimple[178] = "TaskObjectives_DB_BCM0_Van1_G_Stage01_DispatchBrief=The G-Kings are pissed at a store owner for refusing to serve them. We can serve them instead - some nice cold justice! <Col: StageText>Protect </Col>the <Col: StageText>stores </col>from being <Col: StageText>vandalized</col>.";
            contentsSimple[179] = "TaskObjectives_DB_BCM0_Van1_G_Stage02_OwnerBrief=Some local store owners are refusing to serve the G-Kings. Get over there and make sure we're treated with respect from now on. <Col: StageText>Vandalizing their shops </Col>should work.";
            contentsSimple[180] = "TaskObjectives_DB_BCM0_Van1_G_Stage02_DispatchBrief=The G-Kings are pissed at a store owner for refusing to serve them. We can serve them instead - some nice cold justice! <Col: StageText>Protect </Col>the <Col: StageText>stores </col>from being <Col: StageText>vandalized</col>.";
            contentsSimple[181] = "TaskObjectives_DB_BCM0_Van1_G_Stage03_OwnerBrief=One store owner has put up some 'No G-Kings' signs. Go <Col: StageText>vandalize </col>a couple of his places. There may be heat on us, so try <Col: StageText>not </Col>to get <Col: StageText>killed or arrested</Col>.";
            contentsSimple[182] = "TaskObjectives_DB_BCM0_Van1_G_Stage03_DispatchBrief=One of the store owners has made a stand. <Col: StageText>Protect </col>his <Col: StageText>stores </col>from being <Col: StageText>vandalized</Col>, and <Col: StageText>arrest or kill </Col>the G-Kings who've been attacking him.";
            contentsSimple[183] = "TaskObjectives_DB_BCM0_Van1_G_Stage04_OwnerBrief=One store owner has put up some 'No G-Kings' signs. Go <Col: StageText>vandalize </col>a couple of his places. There may be heat on us, so try <Col: StageText>not </Col>to get <Col: StageText>killed or arrested</Col>.";
            contentsSimple[184] = "TaskObjectives_DB_BCM0_Van1_G_Stage04_DispatchBrief=One of the store owners has made a stand. <Col: StageText>Protect </col>his <Col: StageText>stores </col>from being <Col: StageText>vandalized</Col>, and <Col: StageText>arrest or kill </Col>the G-Kings who've been attacking him.";
            contentsSimple[185] = "TaskObjectives_DB_BCM0_Van1_G_Stage05_OwnerBrief=Maybe people have forgotten that the G-Kings rule around here? <Col: StageText>Spray </col>fresh <Col: StageText>tags </Col>in the area to make sure no one else tries to kick us out of their shop.";
            contentsSimple[186] = "TaskObjectives_DB_BCM0_Van1_G_Stage05_DispatchBrief=Now the G-Kings have started spraying up their slogans on walls in the surrounding area. Cocky little bastards. <Col: StageText>Prevent </Col>them from <Col: StageText>spraying </col>up their tags.";
            contentsSimple[187] = "TaskObjectives_DB_BCM1_Del1_B_Stage01_OwnerBrief=TOSHIO contracted us to steal technology from a tech research company, for their 'Gameport 4' console. <Col: StageText>Break into </Col>this researcher's <Col: StageText>vehicle </Col>and steal the motion controller prototypes.";
            contentsSimple[188] = "TaskObjectives_DB_BCM1_Del1_B_Stage01_DispatchBrief=Since the Cexis company showed off their tech at the Games Industry Expo, they've had problems with industrial spies. <Col: StageText>Protect </Col>this <Col: StageText>vehicle </Col>containing their motion controller prototypes.";
            contentsSimple[189] = "TaskObjectives_DB_BCM1_Del1_B_Stage02_OwnerBrief=The research company's office has blueprints: <Col: StageText>Break in </Col>and retrieve the <Col: StageText>files </Col>there. I guess we're doing this because TOSHIO couldn't buy the company out. That's business for you.";
            contentsSimple[190] = "TaskObjectives_DB_BCM1_Del1_B_Stage02_DispatchBrief=Industrial spies are targeting the Cexis company's offices. <Col: StageText>Protect </Col>the <Col: StageText>building </Col>and prevent theft of the research files for their motion controllers being stored there.";
            contentsSimple[191] = "TaskObjectives_DB_BCM1_Del1_B_Stage03_OwnerBrief=Good work. Now <Col: StageText>drop off </Col>the <Col: StageText>files and prototypes </Col>at one of our hideouts and we'll do the rest. Who'd have thought the games industry was so cutthroat? Oh well, hopefully it pays well!";
            contentsSimple[192] = "TaskObjectives_DB_BCM1_Del1_B_Stage03_DispatchBrief=Several files, including blueprints of the Cexis company's prototype motion controller technology, were stolen. <Col: StageText>Prevent delivery </Col>of those files at all costs.";
            contentsSimple[193] = "TaskObjectives_DB_BCM1_Del1_B_Stage04_OwnerBrief=Looks like some Enforcers picked up your trail while you were fulfilling our contract. <Col: StageText>Take them out</Col> and <Col: StageText>protect your leader </Col>- they're out for blood...";
            contentsSimple[194] = "TaskObjectives_DB_BCM1_Del1_B_Stage04_DispatchBrief=We've lost track of the files; they're a lost cause now. <Col: StageText>Kill or arrest the leader </Col>of the industrial spies instead. Use any means necessary.";
            contentsSimple[195] = "TaskObjectives_DB_BCM1_Del1_G_Stage01_OwnerBrief=Orders from Big Daddy Arlon: Green Planet is due in court for supplying us with guns and the law has a strong case against them. <Col: StageText>Break in </Col>to the <Col: StageText>prosecutor's car </Col>and steal evidence.";
            contentsSimple[196] = "TaskObjectives_DB_BCM1_Del1_G_Stage01_DispatchBrief=An SPPD cop went missing while working undercover in the Gs. Carter has asked us to investigate. We found the cop's car: <Col: StageText>protect </Col>the <Col: StageText>vehicle </Col>until the SPPD can come for it.";
            contentsSimple[197] = "TaskObjectives_DB_BCM1_Del1_G_Stage02_OwnerBrief=Files are also kept at the prosecutor's lockup. <Col: StageText>Break in </Col>and <Col: StageText>retrieve the files</Col>. You know they call him the demon prosecutor? Until now he's never failed to get a 'guilty' verdict!";
            contentsSimple[198] = "TaskObjectives_DB_BCM1_Del1_G_Stage02_DispatchBrief=The G-Kings ran off with the contents of the missing cop's car. We traced their movements to this lockup. <Col: StageText>Protect the building </Col>and prevent theft of further clues.";
            contentsSimple[199] = "TaskObjectives_DB_BCM1_Del1_G_Stage03_OwnerBrief=Nice. Now <Col: StageText>drop off </Col>the <Col: StageText>files </Col>at our hideout to dispose of them. With that done, Green Planet's attorney can easily rip apart what's left of the prosecution's case.";
            contentsSimple[200] = "TaskObjectives_DB_BCM1_Del1_G_Stage03_DispatchBrief=The G-Kings have evidence we can use to locate the missing SPPD officer. <Col: StageText>Prevent delivery </Col>of those items. We can't afford to look bad in front of the SPPD!";
            contentsSimple[201] = "TaskObjectives_DB_BCM1_Del1_G_Stage04_OwnerBrief=The demon prosecutor has some dim-witted detectives out, trying to locate the files you stole. Make sure the only thing they find is a messy death: <Col: StageText>kill them</Col> and <Col: StageText>protect your leader.</Col>";
            contentsSimple[202] = "TaskObjectives_DB_BCM1_Del1_G_Stage04_DispatchBrief=Those G-Kings destroyed the evidence leading to the missing officer. They likely saw through his cover and killed him; they're notoriously hard to infiltrate. <Col: StageText>Kill or arrest the crew's leader.</Col>";
            contentsSimple[203] = "TaskObjectives_DB_BCM1_Vlo1_B_Stage01_OwnerBrief=Twizzy, a wealthy but paranoid blogger, has been slamming Charlotte's fashion sense. Big mistake. <Col: StageText>Break in </Col>to this <Col: StageText>van </Col>and grab the kit needed to nudge her mansion's electro-mechanical locks.";
            contentsSimple[204] = "TaskObjectives_DB_BCM1_Vlo1_B_Stage01_DispatchBrief=Our attempt to ambush Jeung Bloodrose ended in complete disaster. Find our <Col: StageText>surveillance van </Col>and <Col: StageText>prevent Blood Roses </Col>from <Col: StageText>breaking in</Col>; we need the data from the van to know what happened.";
            contentsSimple[205] = "TaskObjectives_DB_BCM1_Vlo1_B_Stage02_OwnerBrief=We need security tags to bypass Twizzy's door security. Luckily a friend got us some. <Col: StageText>Pick up </Col>the <Col: StageText>tags</Col>. Too bad Twizzy doesn't have pets or we could've mailed them to her, in bits, instead.";
            contentsSimple[206] = "TaskObjectives_DB_BCM1_Vlo1_B_Stage02_DispatchBrief=The Blood Roses are taking shit from the strike teams they wipe out. That's prime equipment; we can't let them have it. Find the equipment and <Col: StageText>prevent the Roses </Col>from <Col: StageText>picking it up</col>.";
            contentsSimple[207] = "TaskObjectives_DB_BCM1_Vlo1_B_Stage03_OwnerBrief=The last bit of gear we need is an <Col: StageText>image-neutralizing system </Col>to remotely disable Twizzy's security cameras. Go <Col: StageText>pick up </Col>the system and bring it back here, with everything else.";
            contentsSimple[208] = "TaskObjectives_DB_BCM1_Vlo1_B_Stage03_DispatchBrief=Of the strike teams we sent to ambush Jeung, only one survivor was seen... leaving with a briefcase. A bribe? He's a corpse now, but the case is missing. <Col: StageText>Prevent </Col>the Roses from <Col: StageText>picking it up</col>.";
            contentsSimple[209] = "TaskObjectives_DB_BCM1_Vlo1_B_Stage04_OwnerBrief=Good, good. <Col: StageText>Drop off the whole lot </Col>at the hideout and we'll be all set for 'Operation Kiss Charlotte's Ass' later tonight. That silly Twizzy bitch won't even see us coming.";
            contentsSimple[210] = "TaskObjectives_DB_BCM1_Vlo1_B_Stage04_DispatchBrief=The Blood Roses hold all the answers to the mysteries behind our failed ambush on Jeung. <Col: StageText>Don't let the Roses </Col>get those items to their <Col: StageText>drop-off</Col>. We can't let this go unsolved.";
            contentsSimple[211] = "TaskObjectives_DB_BCM1_Vlo1_B_Stage05_OwnerBrief=Twizzy's fan club seems to have got wind of our little game. <Col: StageText>Protect your leader </Col> and <Col: StageText>kill </Col>any fans who come after you: they'll just be trouble later, if we let them live.";
            contentsSimple[212] = "TaskObjectives_DB_BCM1_Vlo1_B_Stage05_DispatchBrief=All the evidence we could've used to piece this event together is beyond our reach now. All we can do is get revenge for our fallen. <Col: StageText>Kill or arrest the leader</Col>.";
            contentsSimple[213] = "TaskObjectives_DB_BCM1_Vlo1_G_Stage01_OwnerBrief=Veronika just gave me a call saying her crew spotted Prentiss Tigers moving weapons through here: real top-grade stuff like OCAs and Somatics. <Col: StageText>Break in </Col>to this <Col: StageText>van </Col>and grab the guns.";
            contentsSimple[214] = "TaskObjectives_DB_BCM1_Vlo1_G_Stage01_DispatchBrief=A smuggling operation we've been tracking took a big delivery, but before we could raid them, the G-Kings turned up. Find one of the <Col: StageText>smuggler's vans </Col>and <Col: StageText>prevent the Gs </Col>from <Col: StageText>breaking in </Col>to it.";
            contentsSimple[215] = "TaskObjectives_DB_BCM1_Vlo1_G_Stage02_OwnerBrief=The Tigers high-tailed it with the rest. They were too wounded to carry the guns far, so they'll have them stashed somewhere. Find and <Col: StageText>pick up </Col>the <Col: StageText>boxes </Col>those shiny guns are in.";
            contentsSimple[216] = "TaskObjectives_DB_BCM1_Vlo1_G_Stage02_DispatchBrief=The G-Kings have sent the smugglers running. We need some of the smuggled merchandise to give us a lead on their organization. Find the merch and <Col: StageText>prevent the Gs </Col>from <Col: StageText>picking it up</col>.";
            contentsSimple[217] = "TaskObjectives_DB_BCM1_Vlo1_G_Stage03_OwnerBrief=Hey, whaddya know: found me some corpses with paperwork. Says here the last part of the Tiger shipment was a custom sniper rifle. Go <Col: StageText>pick up </Col>the rifle and bring it back with everything else.";
            contentsSimple[218] = "TaskObjectives_DB_BCM1_Vlo1_G_Stage03_DispatchBrief=Looks like we weren't the only ones tracking this smuggling deal; the G-Kings are trying to steal the money that was to be used as payment. <Col: StageText>Prevent </Col>the G-Kings from <Col: StageText>picking it up</col>.";
            contentsSimple[219] = "TaskObjectives_DB_BCM1_Vlo1_G_Stage04_OwnerBrief=Nice one! We've got ourselves some new toys and a whole bunch of Tigers are now fertilizer. <Col: StageText>Drop off </Col>those bullet-spitters at the hideout and watch out for trouble.";
            contentsSimple[220] = "TaskObjectives_DB_BCM1_Vlo1_G_Stage04_DispatchBrief=All the evidence we need to pursue our smuggling case is being whisked away by those G-Kings! <Col: StageText>Prevent </Col>those dirtbags from <Col: StageText>dropping off </Col>their haul. We can't let this operation go to waste.";
            contentsSimple[221] = "TaskObjectives_DB_BCM1_Vlo1_G_Stage05_OwnerBrief=Looks like we need weed killer; the Prentiss Tigers are back! New guys, that is, not the dead ones. The only zombie you'll find round here is our mohawked mascot. <Col: StageText>Kill them</Col> and <Col: StageText>protect your leader</Col>.";
            contentsSimple[222] = "TaskObjectives_DB_BCM1_Vlo1_G_Stage05_DispatchBrief=The whole smuggling operation is a bust. The G-Kings have robbed us of all our potential leads! <Col: StageText>Kill or arrest their leader</Col>.";
            contentsSimple[223] = "TaskObjectives_DB_BCM2_Vlo1_Stage01_OwnerBrief=A weapons shipment of ours came under Enforcer scrutiny, so our smugglers split the cache between different vehicle dead drops. <Col: StageText>Break in </Col>to these <Col: StageText>vans </Col>and recover the stash.";
            contentsSimple[224] = "TaskObjectives_DB_BCM2_Vlo1_Stage01_DispatchBrief=An arms shipment we were tracking just arrived in San Paro - now two gangs are fighting over the guns. <Col: StageText>Stop </Col>gang members from <Col: StageText>breaking in </Col>to <Col: StageText>vehicles </Col>holding the weapons.";
            contentsSimple[225] = "TaskObjectives_DB_BCM2_Vlo1_Stage02_OwnerBrief=A weapons shipment of ours came under Enforcer scrutiny, so our smugglers split the cache between different vehicle dead drops. <Col: StageText>Break in </Col>to these <Col: StageText>vans </Col>and recover the stash.";
            contentsSimple[226] = "TaskObjectives_DB_BCM2_Vlo1_Stage02_DispatchBrief=An arms shipment we were tracking just arrived in San Paro - now two gangs are fighting over the guns. <Col: StageText>Stop </Col>gang members from <Col: StageText>breaking in </Col>to <Col: StageText>vehicles </Col>holding the weapons.";
            contentsSimple[227] = "TaskObjectives_DB_BCM2_Vlo1_Stage03_OwnerBrief=Good job. <Col: StageText>Drop </Col>the <Col: StageText>shipment </Col>back <Col: StageText>at the hideout </Col>for processing. I can't wait to get my hands on a first generation CR 7.62. Then we'll see who's boss!";
            contentsSimple[228] = "TaskObjectives_DB_BCM2_Vlo1_Stage03_DispatchBrief=That weapons shipment is on its way to a secure distribution point. <Col: StageText>Prevent </Col>the gang members from <Col: StageText>dropping off </Col>their haul. We can't let weapons of that quality hit the streets.";
            contentsSimple[229] = "TaskObjectives_DB_BCM2_Vlo1_Stage04_OwnerBrief=Looks like you've stirred up a hornet's nest getting those weapons back. <Col: StageText>Kill </Col> the assholes chasing you, <Col: StageText>";
            contentsSimple[230] = "TaskObjectives_DB_BCM2_Vlo1_Stage04_DispatchBrief=That arms shipment is beyond our reach now, but we can still hurt the punks who made off with it! <Col: StageText>Kill or arrest </Col>as many gang members as you can. Make an example of them.";
            contentsSimple[231] = "TaskObjectives_DB_BCS0_Ars1_B_Stage01_OwnerBrief=Some insurance salesmen think they don't have to pay for Blood Rose protection. It's time for their sales to dry up. <Col: StageText>Burn </Col>their unattended delivery of <Col: StageText>product literature</Col>.";
            contentsSimple[232] = "TaskObjectives_DB_BCS0_Ars1_B_Stage01_DispatchBrief=Some insurance salesmen are refusing to pay the Blood Roses for 'protection'. Ironic. <Col: StageText>Prevent the Roses </Col>following through with their threat of <Col: StageText>burning the insurance documents</Col>.";
            contentsSimple[233] = "TaskObjectives_DB_BCS0_Ars1_B_Stage02_OwnerBrief=<Col: StageText>Burn </Col>these nearby <Col: StageText>ticket machines</Col>. They're owned by the same insurance company refusing our protection. That should sting their bottom line.";
            contentsSimple[234] = "TaskObjectives_DB_BCS0_Ars1_B_Stage02_DispatchBrief=The company who refused to pay 'protection' money to the Blood Roses are having vending machines they own attacked. <Col: StageText>Stop the Roses </Col>from <Col: StageText>burning these machines</Col>.";
            contentsSimple[235] = "TaskObjectives_DB_BCS0_Ars1_B_Stage03_OwnerBrief=That was a convincing demonstration of why they should take out our insurance. But selling insurance is about making it personal - so <Col: StageText>burn their vehicles </Col>to bring the message closer to home.";
            contentsSimple[236] = "TaskObjectives_DB_BCS0_Ars1_B_Stage03_DispatchBrief=The attacks on the insurance salesmen have become more personal, the Blood Roses are targeting their cars. <Col: StageText>Stop the Roses </Col>from <Col: StageText>burning these vehicles</Col>.";
            contentsSimple[237] = "TaskObjectives_DB_BCS0_Ars1_B_Stage04_OwnerBrief=Stick around the area to show the salesmen that we control the neighborhood, and no-one's coming out here to help them but us. <Col: StageText>Control and defend this area.</Col>";
            contentsSimple[238] = "TaskObjectives_DB_BCS0_Ars1_B_Stage04_DispatchBrief=Stick around, to show the salesmen that we control the area and no-one's coming out here to hurt them. <Col: StageText>Control and defend this location.</Col>";
            contentsSimple[239] = "TaskObjectives_DB_BCS0_Ars1_G_Stage01_OwnerBrief=A company wants to run some new bus routes through here, and haven't asked the G-Kings for permission. <Col: StageText>Burn </Col>their <Col: StageText>planning documents </Col>and we can stop this idea from ever leaving the depot.";
            contentsSimple[240] = "TaskObjectives_DB_BCS0_Ars1_G_Stage01_DispatchBrief=The advisory board for the new metro routes has been getting hassled by the G-Kings. <Col: StageText>Prevent the gang </Col>from <Col: StageText>burning </Col>all the documentation of plans the City has already invested in.";
            contentsSimple[241] = "TaskObjectives_DB_BCS0_Ars1_G_Stage02_OwnerBrief=<Col: StageText>Burn </Col>the nearby <Col: StageText>ticket machines </Col>that have been installed. We don't want bus routes going through our territory, at least not without our taking a cut.";
            contentsSimple[242] = "TaskObjectives_DB_BCS0_Ars1_G_Stage02_DispatchBrief=The transport advisory board refused to bow to G-King pressure about the new bus routes. Now the thugs are destroying their infrastructure. <Col: StageText>Stop the Gs </Col>from <Col: StageText>torching the ticket machines</Col>.";
            contentsSimple[243] = "TaskObjectives_DB_BCS0_Ars1_G_Stage03_OwnerBrief=These suits just aren't getting the message. <Col: StageText>Burn their cars </Col>to scare them into moving the bus route. Or else my Mom will be coming down to visit me every day... Can't have that!";
            contentsSimple[244] = "TaskObjectives_DB_BCS0_Ars1_G_Stage03_DispatchBrief=The violence towards the metro advisory board has grown personal. <Col: StageText>Stop the Gs </Col>from <Col: StageText>burning these vehicles</Col>. Buses may be environmentally friendly; burning cars ain't.";
            contentsSimple[245] = "TaskObjectives_DB_BCS0_Ars1_G_Stage04_OwnerBrief=Stick around the area to show the board that this hood is G-Town, and no-one's coming out here to help them but us. <Col: StageText>Control and defend this area.</Col>";
            contentsSimple[246] = "TaskObjectives_DB_BCS0_Ars1_G_Stage04_DispatchBrief=Stick around, to show the board that we control the area and no-one's coming out here to hurt them. <Col: StageText>Control and defend this location.</Col>";
            contentsSimple[247] = "TaskObjectives_DB_BCS0_Del1_B_Stage01_OwnerBrief=We've been asked by the Conte de Turemar to acquire some new jewelry for the Contessa. <Col: StageText>Burgle this building</Col>. It is where a local jeweler stores his merchandise.";
            contentsSimple[248] = "TaskObjectives_DB_BCS0_Del1_B_Stage01_DispatchBrief=An old friend of the Blood Roses is in town. Loves diamond accessories - or at least, his wife does. <Col: StageText>Stop the Roses </Col>from <Col: StageText>burglarizing this store room </Col>full of jewelry.";
            contentsSimple[249] = "TaskObjectives_DB_BCS0_Del1_B_Stage02_OwnerBrief=I hope you got an elegant piece; the Contessa craves the finer things. That's how come I know her. <Col: StageText>Drop off the jewelry</Col> here.";
            contentsSimple[250] = "TaskObjectives_DB_BCS0_Del1_B_Stage02_DispatchBrief=Just as I thought: the Roses broke in to steal a diamond necklace. <Col: StageText>Stop them </Col>from <Col: StageText>delivering the jewelry </Col>back to their pickup point.";
            contentsSimple[251] = "TaskObjectives_DB_BCS0_Del1_B_Stage03_OwnerBrief=What's the matter with you? That necklace used square-cut diamonds and the Contessa likes princess cut, you uncivilized clod. The Conte has seen one he likes, so <Col: StageText>burgle the store room </Col>of this shop.";
            contentsSimple[252] = "TaskObjectives_DB_BCS0_Del1_B_Stage03_DispatchBrief=The Blood Roses obviously want to treat their friend. They are back out stealing for him again. <Col: StageText>Stop them </Col>from <Col: StageText>burglarizing </Col>this jeweler's <Col: StageText>store room</Col>. And don't try anything on while you're there!";
            contentsSimple[253] = "TaskObjectives_DB_BCS0_Del1_B_Stage04_OwnerBrief=Great, we will have a happy Conte now. <Col: StageText>Deliver the necklace </Col>and he can pick it up later. This was cool - like a good old-fashioned heist!";
            contentsSimple[254] = "TaskObjectives_DB_BCS0_Del1_B_Stage04_DispatchBrief=This high-value robbery will buy a lot of bullets that can be aimed in our direction. You need to <Col: StageText>stop the Roses </Col>from <Col: StageText>delivering the diamond necklace</Col>.";
            contentsSimple[255] = "TaskObjectives_DB_BCS0_Del1_B_Stage05_OwnerBrief=<Col: StageText>Capture and hold our drop-off</Col> location and kill any Enforcers who try to get near it. The Conte's connections are worth their weight in bling.";
            contentsSimple[256] = "TaskObjectives_DB_BCS0_Del1_B_Stage05_DispatchBrief=<Col: StageText>Capture and hold </Col>the drop-off location the Blood Roses are heading to, and kill any of them who try to get near it. Any friend of the Roses is a target of ours!";
            contentsSimple[257] = "TaskObjectives_DB_BCS0_Del1_G_Stage01_OwnerBrief=A flashy dude I know is in the market for some hot new jewelry and he knows <Col: StageText>burglary </Col>is a G-King specialty. Head over to the <Col: StageText>storeroom</Col> of a local jeweler.";
            contentsSimple[258] = "TaskObjectives_DB_BCS0_Del1_G_Stage01_DispatchBrief=A well-known gem smuggler is in town, and we suspect he'll get the G-Kings to source his new shipment. <Col: StageText>Stop anyone </Col>from <Col: StageText>burglarizing this store room</Col> full of jewelry. It's the prime target.";
            contentsSimple[259] = "TaskObjectives_DB_BCS0_Del1_G_Stage02_OwnerBrief=I hope you got some flashy pieces; this guy is the bling king. <Col: StageText>Drop off the sparkle </Col>here.";
            contentsSimple[260] = "TaskObjectives_DB_BCS0_Del1_G_Stage02_DispatchBrief=Just as we suspected, the gem smuggler hired the G-Kings to steal jewelry for him. <Col: StageText>Prevent them</Col> from <Col: StageText>delivering the stolen goods</Col>.";
            contentsSimple[261] = "TaskObjectives_DB_BCS0_Del1_G_Stage03_OwnerBrief=The bling king wants your head on the block. You picked up a fake necklace instead of a real one. I know of another jeweler - <Col: StageText>burgle his store room</Col> and get the real deal this time.";
            contentsSimple[262] = "TaskObjectives_DB_BCS0_Del1_G_Stage03_DispatchBrief=The G-Kings are either brave or stupid: they are going back out for another necklace. Someone must have a big neck! <Col: StageText>Stop the Gs </Col>from <Col: StageText>burglarizing this store room</Col>.";
            contentsSimple[263] = "TaskObjectives_DB_BCS0_Del1_G_Stage04_OwnerBrief=The guy has gotta like that piece, it's from the best jeweler in town. <Col: StageText>Bring the necklace </Col>back here, and we'll get his majesty to empty his coffers.";
            contentsSimple[264] = "TaskObjectives_DB_BCS0_Del1_G_Stage04_DispatchBrief=We can't let the G-Kings escape with such a valuable item. <Col: StageText>Stop them</Col> from <Col: StageText>delivering the necklace</Col>, or we'll lose profitable security contracts.";
            contentsSimple[265] = "TaskObjectives_DB_BCS0_Del1_G_Stage05_OwnerBrief=Capture and hold our drop-off location and kill any Enforcers who try to get near it.";
            contentsSimple[266] = "TaskObjectives_DB_BCS0_Del1_G_Stage05_DispatchBrief=<Col: StageText>Capture and hold </Col>the drop-off location the G-Kings are heading to, and kill any of them who try to get near it. Any friend of the Gs is a target of ours!";
            contentsSimple[267] = "TaskObjectives_DB_BCS0_Del2_B_Stage01_OwnerBrief=Some of Jeung's crew were caught on camera during a shootout at the Kestrel Club. If we pay the head doorman, he'll make the images disappear. <Col: StageText>Break into these cars </Col>and steal enough to bribe him.";
            contentsSimple[268] = "TaskObjectives_DB_BCS0_Del2_B_Stage01_DispatchBrief=Our stakeout reports that the Blood Roses just left large amounts of cash in two vehicles. It's to pay off the doorman at the Kestrel Club, the location of last night's shoot out. <Col: StageText>Stop anyone </Col>from <Col: StageText>picking up the cash</Col>.";
            contentsSimple[269] = "TaskObjectives_DB_BCS0_Del2_B_Stage02_OwnerBrief=The Kestrel’s doorman wants <Col: StageText>the money delivered here </Col>before he tells us where the video files are. I guess he doesn't earn enough taking bribes from 16-year-olds trying to get in the club...";
            contentsSimple[270] = "TaskObjectives_DB_BCS0_Del2_B_Stage02_DispatchBrief=The Blood Roses are moving large amounts of money around. It will be to bribe any witnesses to last night's shootings at the Kestrel Club. <Col: StageText>Stop them </Col>from <Col: StageText>delivering the bribes</Col>.";
            contentsSimple[271] = "TaskObjectives_DB_BCS0_Del2_B_Stage03_OwnerBrief=The doorman left the video files for us on a USB stick. <Col: StageText>Acquire it</Col>, so we can make sure it's never seen again. We'll have a peek first; Jeung's boasting about how many shots he fired.";
            contentsSimple[272] = "TaskObjectives_DB_BCS0_Del2_B_Stage03_DispatchBrief=The Blood Roses have just delivered a large amount of cash to the doorman of the Kestrel Club. He sold them tape of last night's shootings. <Col: StageText>Prevent the Roses </Col>from <Col: StageText>retrieving the security files</Col>.";
            contentsSimple[273] = "TaskObjectives_DB_BCS0_Del2_B_Stage04_OwnerBrief=<Col: StageText>Take the memory stick </Col>with those video files back to the Blood Rose hideout and we can erase it. <Col: StageText>Don’t get caught, </Col>or the Enforcers'll have a field day with that evidence.";
            contentsSimple[274] = "TaskObjectives_DB_BCS0_Del2_B_Stage04_DispatchBrief=If the Blood Roses paid so much for the Kestrel Club video file, it must be full of evidence. They're trying to take it back to their hideout to wipe it. <Col: StageText>Stop them.</Col>";
            contentsSimple[275] = "TaskObjectives_DB_BCS0_Del2_B_Stage05_OwnerBrief=The security guard screwed us! That wasn't the tape. Luckily, we suspected this and had a tail on him after he grabbed the cash. He stashed the tape around here. <Col: StageText>Find and hold onto that tape.</Col>";
            contentsSimple[276] = "TaskObjectives_DB_BCS0_Del2_B_Stage05_DispatchBrief=The security guard screwed them. That wasn't the tape. We expected this and had guys tail him after he grabbed the cash, he actually stashed the tape here. Find and hold onto that tape.";
            contentsSimple[277] = "TaskObjectives_DB_BCS0_Del2_G_Stage01_OwnerBrief=Scandal-tastic. Sofia from the Tigers has been caught on camera 'licking the love lollipop'. We gotta buy the master copy before her publicist can. <Col: StageText>Break into these cars </Col>where we hide emergency cash.";
            contentsSimple[278] = "TaskObjectives_DB_BCS0_Del2_G_Stage01_DispatchBrief=The G-Kings move cash around by car in this area. Two cars near you now are parked suspiciously. The Gs must be up to something. <Col: StageText>Stop anyone </Col>from <Col: StageText>picking up the cash</Col>.";
            contentsSimple[279] = "TaskObjectives_DB_BCS0_Del2_G_Stage02_OwnerBrief=The owner of the salacious video is the security guard from Sofia's building. He gave us a rendezvous point where we can <Col: StageText>drop off the money</Col>.";
            contentsSimple[280] = "TaskObjectives_DB_BCS0_Del2_G_Stage02_DispatchBrief=The G-Kings are trying to buy a video supposedly showing Sofia in a compromising situation. <Col: StageText>Stop the Gs </Col>from <Col: StageText>delivering the cash </Col>and buying the master tape.";
            contentsSimple[281] = "TaskObjectives_DB_BCS0_Del2_G_Stage03_OwnerBrief=The security guard received his payment and has given us the location of the Sofiagate video. <Col: StageText>Pick up the video </Col>before anyone blurs out all the good parts.";
            contentsSimple[282] = "TaskObjectives_DB_BCS0_Del2_G_Stage03_DispatchBrief=Even if the Sofia sex tape is a fake, it could damage the whole Enforcer community if the G-Kings release it. <Col: StageText>Prevent them </Col>from <Col: StageText>collecting the tape </Col>at all costs.";
            contentsSimple[283] = "TaskObjectives_DB_BCS0_Del2_G_Stage04_OwnerBrief=<Col: StageText>Deliver the video </Col>to the G-King tech guys. <Col: StageText>Don’t get caught </Col>though - we all want to see that video before we upload it!";
            contentsSimple[284] = "TaskObjectives_DB_BCS0_Del2_G_Stage04_DispatchBrief=The G-Kings have the libelous video of Sofia and are taking it to be digitized and uploaded. <Col: StageText>Stop them.</Col>";
            contentsSimple[285] = "TaskObjectives_DB_BCS0_Del2_G_Stage05_OwnerBrief=The security guard screwed us! That wasn't the tape. Luckily, we suspected this and had a tail on him after he grabbed the cash. He stashed the tape around here. <Col: StageText>Find and hold onto that tape.</Col>";
            contentsSimple[286] = "TaskObjectives_DB_BCS0_Del2_G_Stage05_DispatchBrief=The security guard screwed them. That wasn't the tape. We expected this and had guys tail him after he grabbed the cash, he actually stashed the tape here. Find and hold onto that tape.";
            contentsSimple[287] = "TaskObjectives_DB_BCS1_Del1_B_Stage01_OwnerBrief=Pagan is stuck at a restaurant with her family. Her uncle, Commissioner of the SPPD, is asking awkward questions. Do Pagan a favor and <Col: StageText>break in </Col>to where she keeps her Blood Rose cash and remove it.";
            contentsSimple[288] = "TaskObjectives_DB_BCS1_Del1_B_Stage01_DispatchBrief=We've figured out where Pagan Bloodrose keeps her ill-gotten gains. Roses are trying to clear the place out before we can get there. <Col: StageText>Stop them breaking in </Col>and taking the money.";
            contentsSimple[289] = "TaskObjectives_DB_BCS1_Del1_B_Stage02_OwnerBrief=Good work. Pagan wouldn't want all those hard-earned dollars falling into the right hands. <Col: StageText>Drop them off </Col>near the restaurant; she'll come out and pick them up later.";
            contentsSimple[290] = "TaskObjectives_DB_BCS1_Del1_B_Stage02_DispatchBrief=The Roses made off with Pagan Bloodrose's dirty money before we could get a warrant to search the stash house. <Col: StageText>Prevent them</Col> from <Col: StageText>delivering it </Col>to her.";
            contentsSimple[291] = "TaskObjectives_DB_BCS1_Del1_B_Stage03_OwnerBrief=Pagan is getting grilled by her Uncle, Commissioner Carter. Create an emergency for him to have to deal with. <Col: StageText>Burn these businesses, </Col>but <Col: StageText>avoid getting killed</Col>: that'd only raise further questions.";
            contentsSimple[292] = "TaskObjectives_DB_BCS1_Del1_B_Stage03_DispatchBrief=Pagan Bloodrose knows we're onto her; she's looking to cause a major distraction as a decoy. <Col: StageText>Protect these buildings </Col>from being torched. <Col: StageText>Eliminate </Col>anyone who tries to stop you.";
            contentsSimple[293] = "TaskObjectives_DB_BCS1_Del1_B_Stage04_OwnerBrief=Those fires aren't cutting it. Get out there and make your presence known by <Col: StageText>controlling this area</Col>. Some Enforcer bloodshed will give Comissioner Carter something other'n Pagan to worry about.";
            contentsSimple[294] = "TaskObjectives_DB_BCS1_Del1_B_Stage04_DispatchBrief=Those fires aren't the Commissioner's problem but Criminals openly causing havoc would be cause for concern. Keep them out of this area before people start panicking.";
            contentsSimple[295] = "TaskObjectives_DB_BCS1_Del1_G_Stage01_OwnerBrief=Terri Quan is good friends with TV chef Rick Shank. A company has refused to pay their bill at Rick's restaurant. <Col: StageText>Break into their offices </Col>and <Col: StageText>take </Col>what they owe. And get a tip too.";
            contentsSimple[296] = "TaskObjectives_DB_BCS1_Del1_G_Stage01_DispatchBrief=The G-Kings have been called in to recover a debt for a local restaurant. <Col: StageText>Stop the G-Kings </Col>from <Col: StageText>breaking into the office </Col>of the alleged debtors.";
            contentsSimple[297] = "TaskObjectives_DB_BCS1_Del1_G_Stage02_OwnerBrief=<Col: StageText>Drop off the payment </Col>next to Rick's resturant. He'll come out and pick it up later, so we aren't connected. Any friend of Terri's...";
            contentsSimple[298] = "TaskObjectives_DB_BCS1_Del1_G_Stage02_DispatchBrief=The G-Kings have looted over $5000 and are delivering it the restaurant that hired them to steal it. <Col: StageText>Prevent the Gs </Col>from <Col: StageText>delivering the money</Col>.";
            contentsSimple[299] = "TaskObjectives_DB_BCS1_Del1_G_Stage03_OwnerBrief=To make sure no one is around to see Rick picking up the money, go create a G-King style ruckus. <Col: StageText>Burn these businesses, </Col>but <Col: StageText>avoid getting killed</Col>; Rick is a strict vegetarian.";
            contentsSimple[300] = "TaskObjectives_DB_BCS1_Del1_G_Stage03_DispatchBrief=To conceal where and when they will drop off the cash to the restaurant, the G-Kings're planning a distraction. <Col: StageText>Protect these buildings </Col>from being torched. <Col: StageText>Kill anyone </Col>who gets in the way.";
            contentsSimple[301] = "TaskObjectives_DB_BCS1_Del1_G_Stage04_OwnerBrief=Rick Shank's being kinda paranoid and wants more of a distraction than just a few housefires. Get out there and make your presence known by <Col: StageText>controlling this area.</Col>";
            contentsSimple[302] = "TaskObjectives_DB_BCS1_Del1_G_Stage04_DispatchBrief=Those fires aren't big enough for a distraction and looks like these Criminals are eager for a bigger distraction. Keep them out of this area before they incite mass panic.";
            contentsSimple[303] = "TaskObjectives_DB_BCS2_VTh1_Stage01_OwnerBrief=An information broker called Pierre Dorval is selling us personal information on key Enforcer figures. <Col: StageText>Loot </Col>the first <Col: StageText>file </Col>from this <Col: StageText>vehicle </Col>and make sure you keep it safe.";
            contentsSimple[304] = "TaskObjectives_DB_BCS2_VTh1_Stage01_DispatchBrief=An information broker called Pierre Dorval has sold family information belonging to various CSA operatives. <Col: StageText>Protect the vehicle</Col> that the items are hidden in, we'll send tech's round later to investigate.";
            contentsSimple[305] = "TaskObjectives_DB_BCS2_VTh1_Stage02_OwnerBrief=Pierre isn't dumb: this being San Paro, and in case something happened to you, his information has been stashed at a few different drops. <Col: StageText>Pick up </Col>the second set of <Col: StageText>files </Col>hidden nearby.";
            contentsSimple[306] = "TaskObjectives_DB_BCS2_VTh1_Stage02_DispatchBrief=The information broker also sold details of Derren's schedule for public appearances - that's an assassination risk. <Col: StageText>Stop the Crims </Col>from <Col: StageText>retrieving that information</Col>.";
            contentsSimple[307] = "TaskObjectives_DB_BCS2_VTh1_Stage03_OwnerBrief=Pierre Dorval is nicely thorough - but then the quality of his information always makes anyone carrying it a target. <Col: StageText>Pick up </Col>the last set of <Col: StageText>files </Col>he dropped off. And watch your back!";
            contentsSimple[308] = "TaskObjectives_DB_BCS2_VTh1_Stage03_DispatchBrief=The last information sold by the broker details potential security blackspots at Fourche Bank. We don't want them in our base, killing our dudes. <Col: StageText>Stop the Criminals </Col>from <Col: StageText>gathering that final piece of intel</Col>.";
            contentsSimple[309] = "TaskObjectives_DB_BCS2_VTh1_Stage04_OwnerBrief=Files gathered. <Col: StageText>Deliver </Col>the <Col: StageText>information </Col>to the <Col: StageText>hideout</Col>. When we get this up on i-Net, everyone'll know just how corrupt the 'Forcers really are!";
            contentsSimple[310] = "TaskObjectives_DB_BCS2_VTh1_Stage04_DispatchBrief=Taken together, that information poses a major security risk for the entire Enforcement faction. <Col: StageText>Stop the Criminals </Col>before they <Col: StageText>deliver the information </Col>to their <Col: StageText>hideout</Col>.";
            contentsSimple[311] = "TaskObjectives_DB_BCS2_VTh1_Stage05_OwnerBrief=Aw, hell. The Enforcers figured out where Dorval's laptop is. If we can protect it we'll gain a powerful ally and hopefully a future discount! <Col: StageText>Protect </Col>this <Col: StageText>location</Col>.";
            contentsSimple[312] = "TaskObjectives_DB_BCS2_VTh1_Stage05_DispatchBrief=";
            contentsSimple[313] = "TaskObjectives_DB_BCS2_VTh1_Stage05_Opp_OwnerBrief=Aw, hell. The Enforcers figured out where Dorval's laptop is. If we can protect it we'll gain a powerful ally and hopefully a future discount! <Col: StageText>Protect </Col>this <Col: StageText>location</Col>.";
            contentsSimple[314] = "TaskObjectives_DB_BCS2_VTh1_Stage05_Opp_DispatchBrief=We need to shut down that chicken-shit Pierre Dorval - <Col: StageText>raid </Col>his <Col: StageText>address </Col>and see if you can find his <Col:StageText>laptop</Col>. He's been selling way too many Enforcement secrets. He's a danger to this city!";
            contentsSimple[315] = "TaskObjectives_DB_BCS2_VTh1_Stage06_OwnerBrief=Those fucking 'Forcers have found Dorval's <Col:StageText>laptop</Col>. <Col:StageText>Retrieve it</Col> and return it to our new safehouse.";
            contentsSimple[316] = "TaskObjectives_DB_BCS2_VTh1_Stage06_DispatchBrief=";
            contentsSimple[317] = "TaskObjectives_DB_BCS2_VTh1_Stage06_Opp_OwnerBrief=Those fucking 'Forcers have found Dorval's <Col:StageText>laptop</Col>. <Col:StageText>Retrieve it</Col> and return it to our new safehouse.";
            contentsSimple[318] = "TaskObjectives_DB_BCS2_VTh1_Stage06_Opp_DispatchBrief=You've found his files; good job.  <Col:StageText> Recover the laptop</Col> and take it to our techs.";
            contentsSimple[319] = "TaskObjectives_DB_BCS2_VTh3_Stage01_OwnerBrief=Wanna play doctors and nurses? A specialist job requires some specialist medical equipment. Before you get the gear, you're gonna need to <Col: StageText>sabotage </Col>this <Col: StageText>GPS transmitter</Col>.";
            contentsSimple[320] = "TaskObjectives_DB_BCS2_VTh3_Stage01_DispatchBrief=We just intercepted a message that Criminals are planning on sabotaging a GPS receiver - it tracks every EMT vehicle for two miles around. <Col: StageText>Stop the Criminals </Col>from <Col: StageText>disabling </Col>the <Col: StageText>GPS device</Col>.";
            contentsSimple[321] = "TaskObjectives_DB_BCS2_VTh3_Stage02_OwnerBrief=Good. The <Col: StageText>ambulance </Col>we need has a tracking device which is now offline. We've remotely overridden the locks so go <Col: StageText>pick it up </Col>and I’ll send you our next objective once it’s secured..";
            contentsSimple[322] = "TaskObjectives_DB_BCS2_VTh3_Stage02_DispatchBrief=Gang members have disabled the transmitter that tracks emergency vehicles. Now they're trying to steal an <Col: StageText>ambulance </Col>specialized for organ transport. <Col: StageText>Stop the Criminals</Col>.";
            contentsSimple[323] = "TaskObjectives_DB_BCS2_VTh3_Stage03_OwnerBrief=Hope you're not squeamish! You've gotta <Col: StageText>pick up </Col>some black market <Col: StageText>organs</Col>. It's probably best you don't ask where they came from, or who bought them. Just know we're being paid well for this.";
            contentsSimple[324] = "TaskObjectives_DB_BCS2_VTh3_Stage03_DispatchBrief=The Criminals have stolen the ambulance and are heading to <Col: StageText>pick up </Col>some... black market organs, looks like? Gross. <Col: StageText>Stop the Criminals </Col>from making the pick-up.";
            contentsSimple[325] = "TaskObjectives_DB_BCS2_VTh3_Stage04_OwnerBrief=<Col: StageText>Deliver the organs </Col>to The Doctor's <Col: StageText>Office</Col>. Just leave them out the front of the place... Buyer wrote me an e-mail - Subj: 'It reminds me of chicken.' Still... The money's good.";
            contentsSimple[326] = "TaskObjectives_DB_BCS2_VTh3_Stage04_DispatchBrief=The Criminals are transporting the stolen organs. <Col: StageText>Stop them </Col>from <Col: StageText>delivering the organs </Col>to their buyer. Man, stealing people parts? This has to be a new all-time low, even for them.";
            contentsSimple[327] = "TaskObjectives_DB_BCS2_VTh3_Stage05_OwnerBrief=Enforcers found out about more of our other, more legitimate, buyers and are going to raid their homes. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>raiding </Col>these <Col: StageText>buildings </Col>.";
            contentsSimple[328] = "TaskObjectives_DB_BCS2_VTh3_Stage05_DispatchBrief=I'm all for a free market economy, but selling corpse parts is really fucked up. <Col: StageText>Raid </Col>these suspected <Col: StageText>organ farms </Col>and shut them down.";
            contentsSimple[329] = "TaskObjectives_DB_BCS2_VTh3_Stage05_Opp_OwnerBrief=";
            contentsSimple[330] = "TaskObjectives_DB_BCS2_VTh3_Stage05_Opp_DispatchBrief=I'm all for a free market economy, but selling corpse parts is really fucked up. <Col: StageText>Raid </Col>the suspected <Col: StageText>organ farm </Col>and shut them down.";
            contentsSimple[331] = "TaskObjectives_DB_BCS2_VTh3_Stage06_OwnerBrief=Ok, now <Col:StageText>return those Organs</Col> back to base before the Enforcers can get them.";
            contentsSimple[332] = "TaskObjectives_DB_BCS2_VTh3_Stage06_DispatchBrief=Ok, now <Col:StageText>return those Organs</Col> back to base before the Criminals can get them.";
            contentsSimple[333] = "TaskObjectives_DB_BCS2_VTh3_Stage06_Opp_OwnerBrief=Ok, now <Col:StageText>return those Organs</Col> back to base before the Enforcers can get them.";
            contentsSimple[334] = "TaskObjectives_DB_BCS2_VTh3_Stage06_Opp_DispatchBrief=Ok, now <Col:StageText>return those Organs</Col> back to base before the Criminals can get them.";
            contentsSimple[335] = "TaskObjectives_DB_BCS2_Van1_Stage01_OwnerBrief=We got cars burnin' through our turf night and day - turns out some street racers are doing a triathlon in different cars. Stop the Exec Express stage of the race. <Col: StageText>Shoot up </Col>their <Col: StageText>rides</Col>.";
            contentsSimple[336] = "TaskObjectives_DB_BCS2_Van1_Stage01_DispatchBrief=A gang has stolen several executive vehicles belonging to WP Media board members. <Col: StageText>Stop </Col>them from <Col: StageText>destroying </Col>the <Col: StageText>saloon cars</Col>, so we can return them to their rightful owners.";
            contentsSimple[337] = "TaskObjectives_DB_BCS2_Van1_Stage02_OwnerBrief=Good job shooting up those cars. To be sure those racers get the message, <Col: StageText>spray some graffiti </Col>up in their neighborhood. Make sure they know our turf ain't a race track.";
            contentsSimple[338] = "TaskObjectives_DB_BCS2_Van1_Stage02_DispatchBrief=Those thieving bastards are trying to leave anti-CSA graffiti in the area. <Col: StageText>Stop them tagging </Col>up these walls. We can't lose two cars AND have them putting up propaganda!";
            contentsSimple[339] = "TaskObjectives_DB_BCS2_Van1_Stage03_OwnerBrief=Those street racers say we ain't worth dick and they ain't gonna be intimidated. Bullshit! <Col: StageText>Torch these cars </Col>too. <Col: StageText>Avoid getting killed </Col>- no way to teach mofos respect if you're dead!";
            contentsSimple[340] = "TaskObjectives_DB_BCS2_Van1_Stage03_DispatchBrief=Ah, hell. The thieves have found out about cars owned by the chairman of WP Media - Brandon Hay. Odds are they're going to burn them. <Col: StageText>Stop the thieves </Col>from <Col: StageText>burning the cars</Col>.";
            contentsSimple[341] = "TaskObjectives_DB_BCS2_Van1_Stage04_OwnerBrief=We marked our turf and made a show of force. <Col: StageText>Defend the area</Col> for a while; some of those racers aren't gonna be happy about our presence.";
            contentsSimple[342] = "TaskObjectives_DB_BCS2_Van1_Stage04_DispatchBrief=This gang thinks it can just come in and tell us what we can and can't do in this hood. Keep them away from here by <Col: StageText>controlling the area</Col>.";
            contentsSimple[343] = "TaskObjectives_DB_BCS3_Ars1_Stage01_OwnerBrief=Our target is a collector's home, and two vintage Colby 1864 box-lock rifles kept there: real Civil War shit, and heavily protected. To start with, <Col: StageText>burn this building </Col>to create a distraction.";
            contentsSimple[344] = "TaskObjectives_DB_BCS3_Ars1_Stage01_DispatchBrief=The Hazuki and Zhao families have long been at war. Things have escalated with the death of a Hazuki clan member. <Col: StageText>Stop </Col>their hired henchmen from <Col: StageText>burning this office </Col>owned by the Zhao.";
            contentsSimple[345] = "TaskObjectives_DB_BCS3_Ars1_Stage02_OwnerBrief=While that blaze is keeping people busy, <Col: StageText>bomb this electrical box </Col>powering the security systems and locks at the collector's home.";
            contentsSimple[346] = "TaskObjectives_DB_BCS3_Ars1_Stage02_DispatchBrief=The Hazuki family still wants vengeance. They're bombing electrical junctions to knock out the security system of the Zhao family stronghold. <Col: StageText>Stop the bombing attack</Col>.";
            contentsSimple[347] = "TaskObjectives_DB_BCS3_Ars1_Stage03_OwnerBrief=With the power down, you should be able to <Col: StageText>break into the house </Col>without every Enforcer in the city turning up at the doorstep.";
            contentsSimple[348] = "TaskObjectives_DB_BCS3_Ars1_Stage03_DispatchBrief=With the security systems taken down by the bombing attack, the Zhao stronghold is open to attack. <Col: StageText>Stop the Hazuki </Col>from <Col: StageText>breaking in </Col>to the building.";
            contentsSimple[349] = "TaskObjectives_DB_BCS3_Ars1_Stage04_OwnerBrief=Sounds like everyone was right: you're just the person I needed for this job. <Col: StageText>Deliver the antique guns </Col>to our drop-off and I'll make sure they find a nice new home.";
            contentsSimple[350] = "TaskObjectives_DB_BCS3_Ars1_Stage04_DispatchBrief=The Hazuki stole the Zhao's family heirloom: an ancient mirror of pure silver. <Col: StageText>Stop the thieves </Col>before they <Col: StageText>deliver the mirror </Col>- this feud will get even worse if the Hazuki get away with it.";
            contentsSimple[351] = "TaskObjectives_DB_BCS3_Ars1_Stage05_OwnerBrief=The buyer apparently anticipated your success - our payment is prepped and ready for transport. <Col: StageText>Pick up the cash</Col>.";
            contentsSimple[352] = "TaskObjectives_DB_BCS3_Ars1_Stage05_DispatchBrief=The Hazuki have sold the ancient mirror. <Col: StageText>Retrieve the payment </Col>before the their henchmen do. We might be able to trace the buyer and stop all-out war between the Hazuki and the Zhao.";
            contentsSimple[353] = "TaskObjectives_DB_BCS3_Ars1_Stage06_OwnerBrief=<Col: StageText>Deliver the cash </Col>back the hideout. Don't let the Enforcers get their hands on it. Excellent work.";
            contentsSimple[354] = "TaskObjectives_DB_BCS3_Ars1_Stage06_DispatchBrief=<Col: StageText>Get the cash to our lockup </Col> - <Col: StageText>don't let the Hazuki deliver it to their drop-off</Col>. Without that money, we have no hope of retrieving the mirror and ending this feud.";
            contentsSimple[355] = "TaskObjectives_DB_BCS3_Ars1_Stage06_Opp_OwnerBrief=";
            contentsSimple[356] = "TaskObjectives_DB_BCS3_Ars1_Stage06_Opp_DispatchBrief=<Col: StageText>Get the cash to our lockup </Col> - <Col: StageText>don't let the Hazuki deliver it to their drop-off</Col>. Without that money, we have no hope of retrieving the mirror and ending this feud.";
            contentsSimple[357] = "TaskObjectives_DB_BCS4_Del1_Stage01_OwnerBrief=Our crew are complaining about the state of the hideout. We need extra cash to 'bling it up a bit'. <Col: StageText>Steal a van </Col>- you're going to need space for the safe you'll be stealing next.";
            contentsSimple[358] = "TaskObjectives_DB_BCS4_Del1_Stage01_DispatchBrief=Lou Konchoda just got out of the state pen after a ten-year stretch. Now looks like his gang is scoping out transport. Get over there and <Col: StageText>stop them </Col>from <Col: StageText>stealing a van</Col>.";
            contentsSimple[359] = "TaskObjectives_DB_BCS4_Del1_Stage02_OwnerBrief=Break into this store and <Col: StageText>steal the safe. </Col>Get the van close, because those things are heavy and hard to carry! And if you see any DVDs of 'Pimp My Crib', uh, bring those along too...";
            contentsSimple[360] = "TaskObjectives_DB_BCS4_Del1_Stage02_DispatchBrief=Lou Konchoda's gang just stole a van, and we suspect they are going to break into a couple of stores. If they follow their usual MO, you'll have to <Col: StageText>stop them </Col>from <Col: StageText>stealing the store safes</Col>.";
            contentsSimple[361] = "TaskObjectives_DB_BCS4_Del1_Stage03_OwnerBrief=Nice, the guys are gonna love having some cash to improve the place - I've got a mini-fridge all picked out for our beer. <Col: StageText>Drop the safe </Col>at the hideout; we've got a safe-cracker there who can open it.";
            contentsSimple[362] = "TaskObjectives_DB_BCS4_Del1_Stage03_DispatchBrief=Too late! The gang made off with some safes. <Col: StageText>Stop them </Col>from <Col: StageText>delivering the safes </Col>to their hideout. Lou Konchoda can crack 'em like walnuts if he gets his hands on them.";
            contentsSimple[363] = "TaskObjectives_DB_BCS4_Del1_Stage04_OwnerBrief=Get rid of those hot wheels - don't want that thing tracked back to us. <Col: StageText>Drop the van off.</Col> Then you can pick up the gold lamé wallpaper we ordered...";
            contentsSimple[364] = "TaskObjectives_DB_BCS4_Del1_Stage04_DispatchBrief=After dropping off those stolen safes, Lou Konchoda's gang is trying to ditch the vehicle used in the robbery so it can't be combed for evidence. <Col: StageText>Stop them </Col>from <Col: StageText>abandoning the van</Col>.";
            contentsSimple[365] = "TaskObjectives_DB_BCS4_Del1_Stage05_OwnerBrief=Shit, you were spotted dumping the van. Create a distraction and <Col: StageText>burn these vehicles</Col>. Don't take long - Pimp My Crib is on in twenty minutes...";
            contentsSimple[366] = "TaskObjectives_DB_BCS4_Del1_Stage05_DispatchBrief=Lou's gang ditched their van near the freeway construction and are trying to burn other vehicles to disrupt our attempts to capture them. <Col: StageText>Stop the gang </Col>from <Col: StageText>burning the vehicles</Col>.";
            contentsSimple[367] = "TaskObjectives_DB_BCS4_Del1_Stage06_OwnerBrief=The safe you stole must've been seriously packed, because the heat is stuck on you. You need something big to get these guys off your back. <Col: StageText>Plant a bomb</Col> here, and <Col: StageText>protect it</Col> till it goes off.";
            contentsSimple[368] = "TaskObjectives_DB_BCS4_Del1_Stage06_DispatchBrief=The gang is trying to drop off the radar by causing a mass panic with a bombing. <Col: StageText>Stop them</Col> from <Col: StageText>planting that bomb</Col>.";
            contentsSimple[369] = "TaskObjectives_DB_BEL0_Del1_P_Stage01_OwnerBrief=The local gangs are getting rich by selling off their stolen goods. Now we've discovered who's helping them. Go <Col: StageText>raid the location</Col> of the local fence.";
            contentsSimple[370] = "TaskObjectives_DB_BEL0_Del1_P_Stage01_DispatchBrief=We have a guy who moves the goods you acquire for us. The Praetorians are searching his place right now. <Col: StageText>Stop the raid</Col>.";
            contentsSimple[371] = "TaskObjectives_DB_BEL0_Del1_P_Stage02_OwnerBrief=There was no stolen property there. Could have been tipped off, though... <Col: StageText>Pick up</Col> the fence's <Col: StageText>cars</Col> to be impounded.";
            contentsSimple[372] = "TaskObjectives_DB_BEL0_Del1_P_Stage02_DispatchBrief=The Praetorians found nothing. But they are heading to search the vehicles that belong to our fence. <Col: StageText>Stop them</Col> from <Col: StageText>picking up the cars</Col>.";
            contentsSimple[373] = "TaskObjectives_DB_BEL0_Del1_P_Stage03_OwnerBrief=<Col: StageText>Get those cars</Col> back to the Praetorian lockup and we can begin a thorough search for evidence.";
            contentsSimple[374] = "TaskObjectives_DB_BEL0_Del1_P_Stage03_DispatchBrief=It will be bad for us if the Praetorians find any of our goods in the fence's vehicles. <Col: StageText>Stop them</Col> from <Col: StageText>delivering this car</Col> to their vehicle lockup.";
            contentsSimple[375] = "TaskObjectives_DB_BEL0_Del1_P_Stage04_OwnerBrief=A package showed up outside the fence's house. <Col: StageText>Pick up the package</Col> and bring it in. We suspect it's dirty money.";
            contentsSimple[376] = "TaskObjectives_DB_BEL0_Del1_P_Stage04_DispatchBrief=Some dickhead just left his payment outside our fence's house. You can't leave it there - or be caught with it. So you better <Col: StageText>stop the Praetorians</Col> from <Col: StageText>recovering that money</Col>.";
            contentsSimple[377] = "TaskObjectives_DB_BEL0_Del1_P_Stage05_OwnerBrief=<Col: StageText>Deliver that package</Col> back to the Praetorian lockup. It will connect the fence to the car and the stolen goods.";
            contentsSimple[378] = "TaskObjectives_DB_BEL0_Del1_P_Stage05_DispatchBrief=That money will link our fence to some of our robberies and get him put away for years. <Col: StageText>Stop the Praetorians</Col> from <Col: StageText>delivering the money</Col> to their evidence lockup.";
            contentsSimple[379] = "TaskObjectives_DB_BEL0_Del1_P_Stage06_OwnerBrief=Looks like the fence keeps info on who he works with. We found out where it's stashed, and need you to <Col: StageText>recover it and keep it</Col> away from any criminals.";
            contentsSimple[380] = "TaskObjectives_DB_BEL0_Del1_P_Stage06_DispatchBrief=Looks like the fence stashed the info on who he works with. We've found out where it's been stashed and need to <Col: StageText>recover it and keep it</Col> away from any Enforcers.";
            contentsSimple[381] = "TaskObjectives_DB_BEL0_Del1_T_Stage01_OwnerBrief=The Tigers have collected a lot of dirt on a dealer of stolen goods. Now we're ready to take her down. Go <Col: StageText>raid the shop</Col> this fence deals out of.";
            contentsSimple[382] = "TaskObjectives_DB_BEL0_Del1_T_Stage01_DispatchBrief=Evita has been selling on the goods you stole for us. Now she may get busted by the Prentiss Tigers. <Col: StageText>Stop their raid</Col> on her shop.";
            contentsSimple[383] = "TaskObjectives_DB_BEL0_Del1_T_Stage02_OwnerBrief=She looks clean, but we know she ain't. While you're in the area, <Col: StageText>pick up </Col>the fence's <Col: StageText>cars</Col> and we can search them for stolen goods.";
            contentsSimple[384] = "TaskObjectives_DB_BEL0_Del1_T_Stage02_DispatchBrief=Evita already emptied her shop of our stolen goods, but now the Tigers are going to search her car. They could find evidence of our whole operation. <Col: StageText>Stop them</Col> from <Col: StageText>picking up her cars</Col>.";
            contentsSimple[385] = "TaskObjectives_DB_BEL0_Del1_T_Stage03_OwnerBrief=Rock on! The Tigers are taking this Criminal fence out of action. <Col: StageText>Deliver those cars</Col> back to our lockup, and we'll strip them for evidence.";
            contentsSimple[386] = "TaskObjectives_DB_BEL0_Del1_T_Stage03_DispatchBrief=Evita definitely has some of our stolen goods in those cars. <Col: StageText>Stop the Tigers</Col> from <Col: StageText>delivering the cars </Col>to their vehicle lockup and searching it for evidence.";
            contentsSimple[387] = "TaskObjectives_DB_BEL0_Del1_T_Stage04_OwnerBrief=Ha ha! Someone slipped up and left a packet of money for the fence. <Col: StageText>Pick up the package</Col> and bring it in. That's prime evidence.";
            contentsSimple[388] = "TaskObjectives_DB_BEL0_Del1_T_Stage04_DispatchBrief=One of Evita's customers has left his payments for her with a note that mentions us by name. <Col: StageText>Stop the Tigers </Col>from <Col: StageText>recovering that money</Col>.";
            contentsSimple[389] = "TaskObjectives_DB_BEL0_Del1_T_Stage05_OwnerBrief=It's a home run! <Col: StageText>Deliver that package </Col>back to the Tiger lockup. It should link the fence to a whole folder full of unsolved crimes.";
            contentsSimple[390] = "TaskObjectives_DB_BEL0_Del1_T_Stage05_DispatchBrief=If the Prentiss Tigers link us to Evita and the stolen goods, we're all going down. <Col: StageText>Stop the Tigers </Col>from <Col: StageText>delivering the money</Col> to their evidence lockup.";
            contentsSimple[391] = "TaskObjectives_DB_BEL0_Del1_T_Stage06_OwnerBrief=Looks like the fence keeps info on who she works with. We found out where it's stashed, and need you to <Col: StageText>recover it and keep it</Col> away from any criminals.";
            contentsSimple[392] = "TaskObjectives_DB_BEL0_Del1_T_Stage06_DispatchBrief=Evita has been fencing under a different name so Enforcers can't tie the note to her, but they did find out about her stashed book of contacts. <Col: StageText>Recover it and keep it</Col> away from any Enforcers.";
            contentsSimple[393] = "TaskObjectives_DB_BEL0_For1_P_Stage01_OwnerBrief=Some of the City Council don't like seeing Praetorians on the streets. Prove to them we can tackle the drug problem. <Col: StageText>Raid these drug operations</Col> to demonstrate our effectiveness.";
            contentsSimple[394] = "TaskObjectives_DB_BEL0_For1_P_Stage01_DispatchBrief=The Praetorians are trying to look vaguely competent in front of the press by kicking down our doors. <Col: StageText>Stop them</Col> from <Col: StageText>raiding</Col> our businesses.";
            contentsSimple[395] = "TaskObjectives_DB_BEL0_For1_P_Stage02_OwnerBrief=They must have been tipped off. The place was empty, aside from some passed-out junkies. New intel points to a second location you need to check out. <Col: StageText>Raid this building</Col>.";
            contentsSimple[396] = "TaskObjectives_DB_BEL0_For1_P_Stage02_DispatchBrief=The Praetorians are trying to prove to the press how tough they are. Embarrass them. <Col: StageText>Stop them </Col>from <Col: StageText>raiding</Col> our businesses.";
            contentsSimple[397] = "TaskObjectives_DB_BEL0_For1_P_Stage03_OwnerBrief=A guy from the first place you busted confessed that there is a stash of drugs in two nearby cars. <Col: StageText>Retrieve the drugs</Col> from the cars.";
            contentsSimple[398] = "TaskObjectives_DB_BEL0_For1_P_Stage03_DispatchBrief=We've loaded up our supplies so the Praetorians won't find them when they raid our properties. <Col: StageText>Stop them </Col>from <Col: StageText>searching our cars </Col>until we move out.";
            contentsSimple[399] = "TaskObjectives_DB_BEL0_For1_P_Stage04_OwnerBrief=Good work. <Col: StageText>Drop off the drugs</Col> at the lockup. Hea Choi says this bust will make us look good at our next press conference.";
            contentsSimple[400] = "TaskObjectives_DB_BEL0_For1_P_Stage04_DispatchBrief=The Praetorians have got our car, with our supplies in the trunk. <Col: StageText>Prevent them </Col>from <Col: StageText>delivering it</Col> back to their lockup.";
            contentsSimple[401] = "TaskObjectives_DB_BEL0_For1_P_Stage05_OwnerBrief=These Criminals aren't happy about you raiding their drug operation. <Col: StageText>Protect your leader </Col> while trying to <Col: StageText>kill or arrest them</Col>, so they can't re-open for business.";
            contentsSimple[402] = "TaskObjectives_DB_BEL0_For1_P_Stage05_DispatchBrief=The Praetorians are going to keep disrupting our operations unless we can stop them, permanently. <Col: StageText>Take out their leader</Col>.";
            contentsSimple[403] = "TaskObjectives_DB_BEL0_For1_T_Stage01_OwnerBrief=Some of the City Council don't like the whole Prentiss Tiger vibe. <Col: StageText>Raid these drug operations</Col> and show the squares how we play it.";
            contentsSimple[404] = "TaskObjectives_DB_BEL0_For1_T_Stage01_DispatchBrief=The Tigers are out trying to impress their mommies again. Adorable. <Col: StageText>Stop </Col>the brats from <Col: StageText>raiding our business</Col>.";
            contentsSimple[405] = "TaskObjectives_DB_BEL0_For1_T_Stage02_OwnerBrief=They must've heard we were coming; the place was emptier than the Tiger's chess club. We have a second location. <Col: StageText>Raid the building</Col>.";
            contentsSimple[406] = "TaskObjectives_DB_BEL0_For1_T_Stage02_DispatchBrief=The Tigers are on a PR mission to prove their street smarts - yeah, right! Let's kick their asses while they stand around posing. <Col: StageText>Stop them </Col>from <Col: StageText>raiding our businesses</Col>.";
            contentsSimple[407] = "TaskObjectives_DB_BEL0_For1_T_Stage03_OwnerBrief=Pick up those drugs. The rest appear to have been loaded up into vehicles so they can be shipped out. <Col: StageText>Retrieve the drugs </Col>from these two cars.";
            contentsSimple[408] = "TaskObjectives_DB_BEL0_For1_T_Stage03_DispatchBrief=We've loaded up our supplies so the Tigers won't find them during their raids. <Col: StageText>Stop them </Col>from <Col: StageText>searching our cars </Col>until we can move 'em out.";
            contentsSimple[409] = "TaskObjectives_DB_BEL0_For1_T_Stage04_OwnerBrief=Nice job! <Col: StageText>Drop off the drugs</Col> at the lockup. This bust will definitely make us look awesome on the TV. Wave to your mom. I will be.";
            contentsSimple[410] = "TaskObjectives_DB_BEL0_For1_T_Stage04_DispatchBrief=The Tigers got our cars with the supplies in the trunk, the joy-riding bastards. <Col: StageText>Prevent them </Col>from <Col: StageText>delivering our supplies </Col>back to their frat house.";
            contentsSimple[411] = "TaskObjectives_DB_BEL0_For1_T_Stage05_OwnerBrief=These Criminals aren't happy about you spoiling their drug party. <Col: StageText>Kill or arrest them</Col> or they'll keep coming back - like herpes. <Col: StageText>Protect your leader </Col>, they'll be gunning for their head.";
            contentsSimple[412] = "TaskObjectives_DB_BEL0_For1_T_Stage05_DispatchBrief=The Tigers are going to keep on pissing in our punchbowl unless we stop them, permanently. <Col: StageText>Take out their leader</Col>!";
            contentsSimple[413] = "TaskObjectives_DB_BEL0_For2_Stage01_OwnerBrief=The San Paro Standard published a story about stores selling DIY plastic surgery kits. Investigate the claims. <Col: StageText>Hack </Col>into this <Col: StageText>network point </Col>and look for abnormal transactions by local stores.";
            contentsSimple[414] = "TaskObjectives_DB_BEL0_For2_Stage01_DispatchBrief=Some brave and courageous reporters at the Standard ran a story about 'under the counter' DIY plastic surgery kits. Stop the Enforcers <Col: StageText>hacking </Col>into our network to investigate the claims.";
            contentsSimple[415] = "TaskObjectives_DB_BEL0_For2_Stage02_OwnerBrief=Two stores show unusual 'under the counter trading' activity. One of the owners has also been cruising a lot of internet surgery sites. <Col: StageText>Search </Col>these <Col: StageText>locations </Col>for any DIY surgery materials.";
            contentsSimple[416] = "TaskObjectives_DB_BEL0_For2_Stage02_DispatchBrief=Enforcers have found information on some of our stores' illegal activities and are headed to search their storage units. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>searching </Col>the units.";
            contentsSimple[417] = "TaskObjectives_DB_BEL0_For2_Stage03_OwnerBrief=Not the bust we hoped for - The owner's son wants to impress the ladies; hence the web traffic. But you did find stolen car parts. Confiscate the parts and <Col: StageText>drop them off </Col>at this <Col: StageText>lockup</Col>.";
            contentsSimple[418] = "TaskObjectives_DB_BEL0_For2_Stage03_DispatchBrief=The Enforcers searched the storage units. Luckily our DIY surgery stuff ain't stashed there, but they've taken some stolen car parts and are going to <Col: StageText>drop them off </Col>at their lockup. <Col: StageText>Stop them</Col>.";
            contentsSimple[419] = "TaskObjectives_DB_BEL0_For2_Stage04_OwnerBrief=The techs re-ran that network data you pulled and turned up a hospital orderly. He mentioned a deal with a local store. We have cause to <Col: StageText>raid these storerooms </Col>and look for any medical supplies.";
            contentsSimple[420] = "TaskObjectives_DB_BEL0_For2_Stage04_DispatchBrief=Damnit! The Enforcers found out about the main stash of surgical materials. They're on their way to <col: StageText>raid these storerooms</col>. Do whatever you need to do to <Col: StageText>stop them</col>.";
            contentsSimple[421] = "TaskObjectives_DB_BEL0_For2_Stage05_OwnerBrief=That's a hell of a thing: irradiated lipo fat and collagen powder. Wouldn't want that in my ass. This shop is definitely dealing. Pick up that contraband and <Col: StageText>drop it off </Col>at our <Col: StageText>lockup</Col>.";
            contentsSimple[422] = "TaskObjectives_DB_BEL0_For2_Stage05_DispatchBrief=Too late! The Enforcers raided the storerooms and confiscated our DIY surgery materials. They even took the Little Kiki 'My First Botox' kits! <Col: StageText>Stop them </Col>from <Col: StageText>delivering </Col>it to their lockup.";
            contentsSimple[423] = "TaskObjectives_DB_BEL0_For2_Stage05_Opp_OwnerBrief=";
            contentsSimple[424] = "TaskObjectives_DB_BEL0_For2_Stage05_Opp_DispatchBrief=Too late! The Enforcers raided the storerooms and confiscated our DIY surgery materials. They even took the Little Kiki 'My First Botox' kits! <Col: StageText>Stop them </Col>from <Col: StageText>delivering </Col>it to their lockup and Bring it to ours.";
            contentsSimple[425] = "TaskObjectives_DB_BEL0_Inv1_P_Stage01_OwnerBrief=We just got a garbled emergency call - sounded like 'you should pray for the damn paramedics'. Caller was near you. <Col: StageText>Search the nearby ambulance </Col>for anything suspicious.";
            contentsSimple[426] = "TaskObjectives_DB_BEL0_Inv1_P_Stage01_DispatchBrief=We just sent in a bomb threat to the hospital to act as a decoy. <Col: StageText>Stop the Praetorians </Col>from <Col: StageText>searching this vehicle </Col>and discovering it's a hoax.";
            contentsSimple[427] = "TaskObjectives_DB_BEL0_Inv1_P_Stage02_OwnerBrief=Nothing suspicious there, so relocate to where the call was made from and see if anyone is hanging around; <Col: StageText>search the payphone </Col>for intel.";
            contentsSimple[428] = "TaskObjectives_DB_BEL0_Inv1_P_Stage02_DispatchBrief='Torians are scattered around the hospital looking for bombs and generally being distracted, but they could be onto us. <Col: StageText>Stop the Praetorians </Col>from <Col: StageText>searching the payphone </Col>we made the call from.";
            contentsSimple[429] = "TaskObjectives_DB_BEL0_Inv1_P_Stage03_OwnerBrief=Nothing there either. Now it sounds more like the anonymous caller said 'pay' rather than 'pray'. <Col: StageText>Search these ATMs</Col> in case. I'll send the audio files off for analysis, or we'll be chasing this all day.";
            contentsSimple[430] = "TaskObjectives_DB_BEL0_Inv1_P_Stage03_DispatchBrief=The Praetorians are closing in on us by tracing the account I used to pay for the hoax phone calls. <Col: StageText>Stop them </Col>from <Col: StageText>searching this ATM </Col>and getting fingerprints off of it.";
            contentsSimple[431] = "TaskObjectives_DB_BEL0_Inv1_P_Stage04_OwnerBrief=The audio analysis is back. The call was just from some graffiti nuts saying they would 'spray that San Paro relic' rather than 'pray for damn paramedics'. Just go and <Col: StageText>cover up their graffiti tag</Col>.";
            contentsSimple[432] = "TaskObjectives_DB_BEL0_Inv1_P_Stage04_DispatchBrief=The Praetorians have finally figured out there were no bombs. But in that time some of our guys had time to put up an awesome new tag. <Col: StageText>Stop the Praetorians </Col>from <Col: StageText>covering up this new graffiti</Col>.";
            contentsSimple[433] = "TaskObjectives_DB_BEL0_Inv1_T_Stage01_OwnerBrief=We just got a call from the hospital about a threat towards their ambulances. Maybe someone doesn't like the sirens. Get your fat ass over there and <Col: StageText>search their emergency vehicle </Col>for bombs.";
            contentsSimple[434] = "TaskObjectives_DB_BEL0_Inv1_T_Stage01_DispatchBrief=We need some time to set up the weekly tag competition. My bomb threat to the hospital should keep the Tigers busy. <Col: StageText>Stop them </Col>from <Col: StageText>searching this ambulance </Col>and discovering it's all a hoax.";
            contentsSimple[435] = "TaskObjectives_DB_BEL0_Inv1_T_Stage02_OwnerBrief=No bombs on the vehicle, but now we got a call about a suspicious <Col: StageText>payphone. </Col>Ya know what that is, right? It's a phone for poor people. Hangs on the wall. Go <Col: StageText>search </Col>it.";
            contentsSimple[436] = "TaskObjectives_DB_BEL0_Inv1_T_Stage02_DispatchBrief=I've made another hoax call to the Prentiss Tigers, this time about a payphone. Watch them run down there like eager little schoolgirls. Ha ha! <Col: StageText>Stop them </Col>from <Col: StageText>searching the payphone </Col>and spoiling the fun.";
            contentsSimple[437] = "TaskObjectives_DB_BEL0_Inv1_T_Stage03_OwnerBrief=The payphone looks clean, as in 'nothing suspicious'. But go wash your hands and rinse away the poverty. On your way, <Col: StageText>search the local ATM </Col>as this is near to where the phone card was purchased.";
            contentsSimple[438] = "TaskObjectives_DB_BEL0_Inv1_T_Stage03_DispatchBrief=The weekly tag-off graffiti-fest is under way, and we want to keep the Prentiss Tigers as far away as possible. I've told them there is a skimmer on this cash machine. <Col: StageText>Stop the Tigers </Col>from <Col: StageText>searching the ATM</Col>.";
            contentsSimple[439] = "TaskObjectives_DB_BEL0_Inv1_T_Stage04_OwnerBrief=All that running around for nothing; just like gym class. It looks like these calls were a decoy to distract the Tigers while hooligans tagged the area. <Col: StageText>Cover up their graffiti</Col>.";
            contentsSimple[440] = "TaskObjectives_DB_BEL0_Inv1_T_Stage04_DispatchBrief=Looks like the Tigers finally figured out that it was us making all the hoax calls. But we had enough time to hold the graffiti competition. <Col: StageText>Stop the Tigers </Col>from <Col: StageText>covering up the winner's graffiti</Col>.";
            contentsSimple[441] = "TaskObjectives_DB_BEL0_Inv2_Stage01_OwnerBrief=Head to this shipping center: we've intercepted communications about explosives being shipped into the city - and without help from the Blood Roses, for once. <Col: StageText>Search this crate </Col>for any contraband.";
            contentsSimple[442] = "TaskObjectives_DB_BEL0_Inv2_Stage01_DispatchBrief=Get over to this shipping center - we had explosives imported into the city but Enforcers are coming to confiscate them. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>searching this crate</Col>.";
            contentsSimple[443] = "TaskObjectives_DB_BEL0_Inv2_Stage02_OwnerBrief=Yeah, those explosives are Somatic XAGs; definitely not for demolition work of any legal kind. Confiscate the explosives and <Col: StageText>drop them off </Col>at the <Col: StageText>lockup </Col>facility.";
            contentsSimple[444] = "TaskObjectives_DB_BEL0_Inv2_Stage02_DispatchBrief=Enforcers have confiscated explosives from one of the crates we had shipped into the city. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>dropping off </Col>the explosives at their lockup facility.";
            contentsSimple[445] = "TaskObjectives_DB_BEL0_Inv2_Stage03_OwnerBrief=Kamotsu Imports shipped the crates you just seized. We checked with them and there were more containers from this shipment sent around the city. <Col: StageText>Check this crate </Col>for any more explosives.";
            contentsSimple[446] = "TaskObjectives_DB_BEL0_Inv2_Stage03_DispatchBrief=One of our shipments has already been seized and Enforcers are going to check our other shipments for explosive devices. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>searching this crate</Col>.";
            contentsSimple[447] = "TaskObjectives_DB_BEL0_Inv2_Stage04_OwnerBrief=Whoa! Those are Somatic's highest-grade explosives. Good thing you got to them before they hit the streets. Confiscate them and <Col: StageText>drop them off </Col>at the <Col: StageText>lockup </Col>facility.";
            contentsSimple[448] = "TaskObjectives_DB_BEL0_Inv2_Stage04_DispatchBrief=<Col: StageText>Stop the Enforcers </Col>before they <Col: StageText>drop off </Col>the explosives at their lockup facility. <Col:StageText> Grab the Explosives</Col> and take them to the dead drop.";
            contentsSimple[449] = "TaskObjectives_DB_BEL0_Inv2_Stage04_Opp_OwnerBrief=";
            contentsSimple[450] = "TaskObjectives_DB_BEL0_Inv2_Stage04_Opp_DispatchBrief=<Col: StageText>Stop the Enforcers </Col>before they <Col: StageText>drop off </Col>the explosives at their lockup facility. <Col:StageText> Grab the Explosives</Col> and take them to the dead drop.";
            contentsSimple[451] = "TaskObjectives_DB_BEL1_Bus1_P_Stage01_OwnerBrief=A Trexxon exec vanished after it was discovered he was embezzling from the company. Trexxon has asked us to recoup the loss from the employee's assets; start by <Col: StageText>raiding </Col>the <Col: StageText>exec's lockup</Col>.";
            contentsSimple[452] = "TaskObjectives_DB_BEL1_Bus1_P_Stage01_DispatchBrief=One of our crew - Alonzo - just got busted by the D.A. and the 'Torians are going to confiscate items from his home. <Col: StageText>Stop the Praetorians</Col> from finding anything that will incriminate us.";
            contentsSimple[453] = "TaskObjectives_DB_BEL1_Bus1_P_Stage02_OwnerBrief=The exec's cars are nearby; flashy Sungnyemun Moirais. <Col: StageText>Pick up the cars</Col>; they're to be sent back to the company. Take the stuff from the lockup with you.";
            contentsSimple[454] = "TaskObjectives_DB_BEL1_Bus1_P_Stage02_DispatchBrief=There is information on some of our businesses in Alonzo's cars. Praetorians are heading to impound them. <Col: StageText>Stop them </Col>from <Col: StageText>picking up </Col>the <Col: StageText>cars</Col>.";
            contentsSimple[455] = "TaskObjectives_DB_BEL1_Bus1_P_Stage03_OwnerBrief=Before you deliver the cars, <Col: StageText>drop off </Col>the <Col: StageText>exec's assets </Col>from the lockup. Millions of dollars' worth of possessions, in such small boxes; the exec's watch alone probably cost more than my house.";
            contentsSimple[456] = "TaskObjectives_DB_BEL1_Bus1_P_Stage03_DispatchBrief=Praetorians have raided Alonzo's hideouts and taken his cars for impounding. <Col: StageText>Stop them </Col>from <Col: StageText>delivering the evidence </Col>from the hideouts to the lockup.";
            contentsSimple[457] = "TaskObjectives_DB_BEL1_Bus1_P_Stage04_OwnerBrief=I always wanted a car like the Moirai... Though the Praetorians pay well, that car matches Justin Teng's paygrade more than mine. <Col: StageText>Drop off </Col>the <Col: StageText>cars </Col>here.";
            contentsSimple[458] = "TaskObjectives_DB_BEL1_Bus1_P_Stage04_DispatchBrief=The Praetorians haven't realized that the cars they've seized hold important evidence. <Col: StageText>Stop them </Col>from <Col: StageText>delivering the cars </Col>to the impound lot where it'll be thoroughly searched.";
            contentsSimple[459] = "TaskObjectives_DB_BEL1_Bus1_P_Stage05_OwnerBrief=The cars contained some evidence of drug use. <Col: StageText>Take </Col>the <Col: StageText>criminal evidence </Col>found in the cars and <Col: StageText>drop it off </Col>at the lockup facility.";
            contentsSimple[460] = "TaskObjectives_DB_BEL1_Bus1_P_Stage05_DispatchBrief=The Praetorians have found some incriminating evidence in Alonzo's confiscated possessions. <Col: StageText>Stop them </Col>from <Col: StageText>delivering the evidence </Col>to the D.A.'s office.";
            contentsSimple[461] = "TaskObjectives_DB_BEL1_Bus1_P_Stage06_OwnerBrief=Looks like the ex-exec is still around, and used some of his embezzlement money to hire some muscle. Guess he wants his stuff back. <Col: StageText>Protect your leader</Col> while you <Col: StageText>get rid of these scum.</Col>";
            contentsSimple[462] = "TaskObjectives_DB_BEL1_Bus1_P_Stage06_DispatchBrief=These Praetorians really think they can stop us by taking down just one member of our crew...? Make them pay for messing with us, <Col: StageText>takeout their leader</Col>.";
            contentsSimple[463] = "TaskObjectives_DB_BEL1_Bus1_T_Stage01_OwnerBrief=Just got a call: the Prentiss Rifleman's Club had a break-in and some firearms were stolen. Ballsy stuff. We've promised to get the guns back, so start off by <Col: StageText>raiding </Col>this <Col: StageText>thief's home</Col>.";
            contentsSimple[464] = "TaskObjectives_DB_BEL1_Bus1_T_Stage01_DispatchBrief=We just ambushed a CUP politician - one of Mayor Derren's party. Left him with nothing but his striped boxers. Now their Enforcers are out to retrieve his things; <Col: StageText>stop them</Col>!";
            contentsSimple[465] = "TaskObjectives_DB_BEL1_Bus1_T_Stage02_OwnerBrief=The thief was seen moving rifles to a set of cars nearby: Sungnyemun Moirais. <Col: StageText>Pick up the cars </Col>so we can check them for firearms. Take stuff from the thief's home with you, too.";
            contentsSimple[466] = "TaskObjectives_DB_BEL1_Bus1_T_Stage02_DispatchBrief=The Enforcers are out to retrieve the politician's stolen Sungnyemun Moirais now. I've grown rather attached to them, though so <Col: StageText>stop them </Col>from <Col: StageText>picking up </Col>the <Col: StageText>cars</Col>.";
            contentsSimple[467] = "TaskObjectives_DB_BEL1_Bus1_T_Stage03_OwnerBrief=Before you deliver the cars, <Col: StageText>drop off </Col>the <Col: StageText>box of firearms </Col>from the thief's home.";
            contentsSimple[468] = "TaskObjectives_DB_BEL1_Bus1_T_Stage03_DispatchBrief=As unattractive as it is having the politician we robbed waddle around in his underwear, it's better he stay that way. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>delivering the politician's things </Col>to their lockup.";
            contentsSimple[469] = "TaskObjectives_DB_BEL1_Bus1_T_Stage04_OwnerBrief=The Moirais you're driving are too expensive for a lowly thief; I'm guessing the Blood Roses had a hand in this. <Col: StageText>Drop off </Col>the <Col: StageText>cars </Col>here and we'll get a forensic team on it.";
            contentsSimple[470] = "TaskObjectives_DB_BEL1_Bus1_T_Stage04_DispatchBrief=The Enforcers still need to take the politician's shiny Sungnyemun Moirais to safety. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>delivering the cars </Col>to the impound lot; I want those Moirais!";
            contentsSimple[471] = "TaskObjectives_DB_BEL1_Bus1_T_Stage05_OwnerBrief=The cars contained documents detailing a Blood Rose op: filing the serial numbers off stolen guns and reselling them. <Col: StageText>Take </Col>the <Col: StageText>documents </Col>found in the cars and <Col: StageText>drop them off </Col>at our lockup.";
            contentsSimple[472] = "TaskObjectives_DB_BEL1_Bus1_T_Stage05_DispatchBrief=Seems the politician had some papers regarding CSA policy in the car. We have friends who could use those... <Col: StageText>Stop </Col>the Enforcers from <Col: StageText>delivering the papers </Col>back to the politician.";
            contentsSimple[473] = "TaskObjectives_DB_BEL1_Bus1_T_Stage06_OwnerBrief=The gun thieves look like they want a fight; they're back and armed with Blood Rose weaponry. <Col: StageText>Protect your leader</Col> while you <Col: StageText>get rid of these scum.</Col>";
            contentsSimple[474] = "TaskObjectives_DB_BEL1_Bus1_T_Stage06_DispatchBrief=The Enforcers have done an annoyingly good job of ruining our fun and making you look bad. If you <Col: StageText>takeout their squad leader</Col> though, I'll forgive you. Maybe.";
            contentsSimple[475] = "TaskObjectives_DB_BEL1_For1_P_Stage01_OwnerBrief=The CEO of Colby is on a rare visit to San Paro to meet Justin Teng, our leader. <Col: StageText>Raid </Col>these known Criminal <Col: StageText>hideouts </Col>along the route he'll take. We don't want any Criminal interference.";
            contentsSimple[476] = "TaskObjectives_DB_BEL1_For1_P_Stage01_DispatchBrief=The Praetorians have some sorta big cheese visiting them. Just because we have a few hangouts along his route, they're <Col: StageText>raiding our places</Col>. <Col: StageText>Stop them</Col>.";
            contentsSimple[477] = "TaskObjectives_DB_BEL1_For1_P_Stage02_OwnerBrief=Good work, soldier; the last thing we want is a dead billionaire on the streets during our watch. <Col: StageText>Spray over that tag in the area </Col>too, to show we have this location locked down tight.";
            contentsSimple[478] = "TaskObjectives_DB_BEL1_For1_P_Stage02_DispatchBrief=The Praetorians don't want their visiting corporate buddies to see any graffiti on the way to their meeting. <Col: StageText>Go stop the Enforcers </Col>from <Col: StageText>covering up our tags</Col>.";
            contentsSimple[479] = "TaskObjectives_DB_BEL1_For1_P_Stage03_OwnerBrief=A couple of heavily armed thugs have just been spotted on our CCTV. <Col: StageText>Raid </Col>their last known <Col: StageText>locations </Col>so I can tell Mr. Teng that the visit can go ahead.";
            contentsSimple[480] = "TaskObjectives_DB_BEL1_For1_P_Stage03_DispatchBrief=Some of our hideouts were raided and our tags covered up by Praetorians - just so they can show off in front of some visiting CEO. <Col: StageText>Stop them </Col>from <Col: StageText>raiding any more of our hideouts</Col>.";
            contentsSimple[481] = "TaskObjectives_DB_BEL1_For1_P_Stage04_OwnerBrief=The route has been altered. Our guest, Mr. Colby Jr., will be coming into the City via another route. <Col: StageText>Cover up the tag in that area </Col>too.";
            contentsSimple[482] = "TaskObjectives_DB_BEL1_For1_P_Stage04_DispatchBrief=The Praetorians now want to <Col: StageText>cover up more tags </Col>as they'd spoil a big business photo opp. <Col: StageText>Stop them</Col>. At least we're honest about our activities - unlike these corporate criminals.";
            contentsSimple[483] = "TaskObjectives_DB_BEL1_For1_P_Stage05_OwnerBrief=The Criminals have found us, despite our detailed preparation. <Col: StageText>Control this area</Col>, give Colby Jr. a show of how we like to use his company's weaponry.";
            contentsSimple[484] = "TaskObjectives_DB_BEL1_For1_P_Stage05_DispatchBrief=The Praetorians don't want anything to spoil their high-powered corporate pow-wow. <Col: StageText>Takeover the area</Col> of their meeting to show them how we take care of business.";
            contentsSimple[485] = "TaskObjectives_DB_BEL1_For1_T_Stage01_OwnerBrief=Pansy-ass elected officials think we're not producing results fast enough. <Col: StageText>Raid </Col>these <Col: StageText>drug dens </Col>and show them that Prentiss Tigers have claws.";
            contentsSimple[486] = "TaskObjectives_DB_BEL1_For1_T_Stage01_DispatchBrief=The Pubescent Tigers are trying to drum up political support by smashing up some of our hangouts. <Col: StageText>Stop them </Col>from <Col: StageText>raiding our places</Col>.";
            contentsSimple[487] = "TaskObjectives_DB_BEL1_For1_T_Stage02_OwnerBrief=That's an A+ result, duderino; maybe those fat-ass do-nothing politicians will leave us alone a while. <Col: StageText>Spray over the tag in the area</Col> too. Let's make a clean sweep.";
            contentsSimple[488] = "TaskObjectives_DB_BEL1_For1_T_Stage02_DispatchBrief=The Prentiss Tigers who raided our hangouts obviously haven't finished annoying us yet. <Col: StageText>Go stop them </Col>from <Col: StageText>covering up our tags</Col> - they are a symbol of our resistance.";
            contentsSimple[489] = "TaskObjectives_DB_BEL1_For1_T_Stage03_OwnerBrief=The press will love this. <Col: StageText>Raid </Col>these other <Col: StageText>drug dens</Col>, while I tell Byeong Lee - he wants to order a press conference. Wipe the blood off your clothes for the cameras... On second thought - don't.";
            contentsSimple[490] = "TaskObjectives_DB_BEL1_For1_T_Stage03_DispatchBrief=Our hideouts have been raided and our tags covered up, just so the Tigers can show off for some politicians. Enough already! <Col: StageText>Stop them </Col>from <Col: StageText>raiding </Col>any more of our <Col: StageText>hideouts</Col>.";
            contentsSimple[491] = "TaskObjectives_DB_BEL1_For1_T_Stage04_OwnerBrief=The press conference will be in your area in 15 minutes. Remember, shiny buildings make for good pro-Tigers photos. <Col: StageText>Cover up the tag in this area </Col>too.";
            contentsSimple[492] = "TaskObjectives_DB_BEL1_For1_T_Stage04_DispatchBrief=The Prentiss Tigers want to hold a pro-Enforcer press conference. <Col: StageText>Stop them </Col>from <Col: StageText>covering up </Col>more of our <Col: StageText>tags</Col>. They say our graffiti spoils their photo opportunity.";
            contentsSimple[493] = "TaskObjectives_DB_BEL1_For1_T_Stage05_OwnerBrief=A gang of camera-shy Criminals are trying to scare away the press. Losing this photo opportunity could cost us big time - <Col: StageText>control the area</Col>.";
            contentsSimple[494] = "TaskObjectives_DB_BEL1_For1_T_Stage05_DispatchBrief=Let's give the gathered media a real photo opportunity, and something to write about! <Col: StageText>Takeover the area</Col> to show everyone who really runs things around here.";
            contentsSimple[495] = "TaskObjectives_DB_BEL1_Pic2_P_Stage01_OwnerBrief=We've got some C.S.I.s in the field in need of supplies; they're busy scraping a prosecutor's brain matter off the walls. <Col: StageText>Pick up the supplies </Col>and get them to the C.S.I. team.";
            contentsSimple[496] = "TaskObjectives_DB_BEL1_Pic2_P_Stage01_DispatchBrief=The Praetorians are trying to dig up some information on a hit our crew did. Go <Col: StageText>prevent them </Col>from <Col: StageText>picking up the supplies </Col>needed for their investigation.";
            contentsSimple[497] = "TaskObjectives_DB_BEL1_Pic2_P_Stage02_OwnerBrief=<Col: StageText>Deliver the supplies </Col>to the C.S.I.s and see if they've found anything. Reports say the murder victim was working on a case against Green Planet; conveniently for them, he's now G-King fodder.";
            contentsSimple[498] = "TaskObjectives_DB_BEL1_Pic2_P_Stage02_DispatchBrief=The Praetorians are <Col: StageText>delivering </Col>forensic supplies to some C.S.I.s they've got in the field investigating our hit. <Col: StageText>Stop them</Col>.";
            contentsSimple[499] = "TaskObjectives_DB_BEL1_Pic2_P_Stage03_OwnerBrief=<Col: StageText>Pick up the evidence </Col>the C.S.I.s have collected so far, then <Col: StageText>drop it off </Col>at the lockup; they'll pass it on to the lab.";
            contentsSimple[500] = "TaskObjectives_DB_BEL1_Pic2_P_Stage03_DispatchBrief=Evidence on our crew that carried out the hit has been found by the Praetorians and they're bringing it to their lockup facility. <Col: StageText>Stop the Praetorians </Col>from <Col: StageText>delivering that evidence</Col>.";
            contentsSimple[501] = "TaskObjectives_DB_BEL1_Pic2_P_Stage04_OwnerBrief=The G-Kings have noticed our investigation; they've sent out some of their gang-bangers to contaminate other crime scenes related to the shooting. <Col: StageText>Stop them </Col>from <Col: StageText>controlling these areas</Col>.";
            contentsSimple[502] = "TaskObjectives_DB_BEL1_Pic2_P_Stage04_DispatchBrief=The evidence those Praetorians picked up might lead them to some other crimes we've committed. <Col: StageText>Control and defend these areas </Col>so we can remove any evidence.";
            contentsSimple[503] = "TaskObjectives_DB_BEL1_Pic2_T_Stage01_OwnerBrief=A mobster's bloated body turned up just an hour ago. We've got some C.S.I.s in to check it out; they need special gear. <Col: StageText>Pick up the supplies </Col>and get it out to them.";
            contentsSimple[504] = "TaskObjectives_DB_BEL1_Pic2_T_Stage01_DispatchBrief=The Tigers are trying to investigate a mobster corpse we'd prefer stay dead and buried. Go <Col: StageText>prevent them </Col>from <Col: StageText>picking up the materials </Col>needed for their investigation.";
            contentsSimple[505] = "TaskObjectives_DB_BEL1_Pic2_T_Stage02_OwnerBrief=<Col: StageText>Deliver the supplies </Col>to the C.S.I.s and <Col: StageText>pick up the evidence </Col>they've collected so far. The corpse had info about some 'Sal Murino' guy, I hear. Maybe it's the reason he got whacked?";
            contentsSimple[506] = "TaskObjectives_DB_BEL1_Pic2_T_Stage02_DispatchBrief=The Tigers are <Col: StageText>delivering </Col>forensic supplies to C.S.I.s investigating the dead mobster we put in the ground. <Col: StageText>Stop them</Col>.";
            contentsSimple[507] = "TaskObjectives_DB_BEL1_Pic2_T_Stage03_OwnerBrief=<Col: StageText>Pick up the evidence </Col>the C.S.I.s have gathered and <Col: StageText>deliver </Col>it to our <Col: StageText>lockup </Col>. I don't think we'll find this Sal guy, though; he disappeared from Witness Protection years ago.";
            contentsSimple[508] = "TaskObjectives_DB_BEL1_Pic2_T_Stage03_DispatchBrief=The Tigers found evidence on that mobster's corpse that links him to the crew we got to whack him. <Col: StageText>Stop the Tigers </Col>from <Col: StageText>delivering that evidence</Col> to their lockup facility.";
            contentsSimple[509] = "TaskObjectives_DB_BEL1_Pic2_T_Stage04_OwnerBrief=A few of the Mob seem to think we capped the dead mobster. They're heading out to rough up some of our people. <Col: StageText>Stop them </Col>from <Col: StageText>controlling these areas.</Col>.";
            contentsSimple[510] = "TaskObjectives_DB_BEL1_Pic2_T_Stage04_DispatchBrief=The evidence those Tigers picked up might lead them to some other crimes we've committed. <Col: StageText>Control and defend these areas </Col>so we can remove any evidence.";
            contentsSimple[511] = "TaskObjectives_DB_BEL1_VLo1_P_Stage01_OwnerBrief=OCA reported a shipment of prototype <Col: StageText>HPM Devices </Col>missing; we're talking serious EMP weaponry here. We traced the prototypes to this location; <Col: StageText>retrieve </Col>the devices from these <Col: StageText>vehicles</Col>. ASAP.";
            contentsSimple[512] = "TaskObjectives_DB_BEL1_VLo1_P_Stage01_DispatchBrief=The Praetorians are trying to <Col: StageText>recover </Col>a shipment of <Col: StageText>EMP weaponry </Col>we hijacked. Use your more conventional weapons to <Col: StageText>stop them</Col>.";
            contentsSimple[513] = "TaskObjectives_DB_BEL1_VLo1_P_Stage02_OwnerBrief=We've found a few more of the missing <Col: StageText>HPM Devices </Col>in these <Col: StageText>vehicles</Col>. Go <Col: StageText>retrieve them </Col>and make sure you don't set any off; they fire beams that fry the electronics in everything they touch.";
            contentsSimple[514] = "TaskObjectives_DB_BEL1_VLo1_P_Stage02_DispatchBrief=The Praetorians have already recovered half of the arms shipment we lifted, <Col: StageText>stop them </Col>from <Col: StageText>picking up the rest of the EMP weapons</Col>.";
            contentsSimple[515] = "TaskObjectives_DB_BEL1_VLo1_P_Stage03_OwnerBrief=Nice work! <Col: StageText>Drop off </Col>the <Col: StageText>HPM Devices </Col>at our lockup. I'm glad we found them; in the wrong hands they could decimate security systems, kill vehicles or fry comms in mere seconds.";
            contentsSimple[516] = "TaskObjectives_DB_BEL1_VLo1_P_Stage03_DispatchBrief=The Praetorians have the last of the <Col: StageText>EMP weaponry</Col>. <Col: StageText>Stop them </Col>from <Col: StageText>delivering </Col>the shipment back to their lockup.";
            contentsSimple[517] = "TaskObjectives_DB_BEL1_VLo1_P_Stage04_OwnerBrief=We've got the HPM Devices secured, but it doesn't look we're out of the woods yet; the outfit that stole them are looking for their stolen goods. <Col: StageText>Protect your leader.</Col>";
            contentsSimple[518] = "TaskObjectives_DB_BEL1_VLo1_P_Stage04_DispatchBrief=These Praetorians think they can just take our weaponry? <Col: StageText>Take out their leader </Col>to show them who really runs things around here.";
            contentsSimple[519] = "TaskObjectives_DB_BEL1_VLo1_T_Stage01_OwnerBrief=I don't know if you've met Nora or not - a fellow Tiger. She lent us some <Col: StageText>surveillance equipment </Col>from her father's stores, but now it's been stolen. Go <Col: StageText>retrieve it </Col>from these <Col: StageText>vehicles</Col>.";
            contentsSimple[520] = "TaskObjectives_DB_BEL1_VLo1_T_Stage01_DispatchBrief=The Prentiss Tigers are trying to retrieve some surveillance equipment we stole. <Col: StageText>Stop them </Col>from <Col: StageText>raiding our car</Col>.";
            contentsSimple[521] = "TaskObjectives_DB_BEL1_VLo1_T_Stage02_OwnerBrief=There's more <Col: StageText>surveillance equipment </Col>in these <Col: StageText>vehicles</Col>. Go <Col: StageText>retrieve it</Col>. If Nora knew it had been stolen, she'd be out with her minigun! Interesting to watch, but the collateral damage...";
            contentsSimple[522] = "TaskObjectives_DB_BEL1_VLo1_T_Stage02_DispatchBrief=The Tigers are after the remaining stolen <Col: StageText>surveillance equipment</Col>. <Col: StageText>Protect </Col>the <Col: StageText>van </Col>we're using to move the equipment; last thing we need is their dirty paw-marks all over it.";
            contentsSimple[523] = "TaskObjectives_DB_BEL1_VLo1_T_Stage03_OwnerBrief=Good Job! <Col: StageText>Drop off </Col>Nora's <Col: StageText>surveillance equipment </Col>at our lockup. It's a good thing she's visiting Fourche Bank right now; this way she doesn't even have to know it's gone missing.";
            contentsSimple[524] = "TaskObjectives_DB_BEL1_VLo1_T_Stage03_DispatchBrief=The Tigers are running off with the stolen <Col: StageText>surveillance equipment </Col>The nerve! <Col: StageText>stop them </Col>from getting the equipment to their <Col: StageText>lockup</Col>.";
            contentsSimple[525] = "TaskObjectives_DB_BEL1_VLo1_T_Stage04_OwnerBrief=We've tracked down the Blood Roses who managed to steal Nora's surveillance equipment. We might have the equipment back, but they'll be coming for payback. <Col: StageText>Protect your leader.</Col>";
            contentsSimple[526] = "TaskObjectives_DB_BEL1_VLo1_T_Stage04_DispatchBrief=The Tigers got the stolen equipment and messed up two of our vans; we can't stand for that! <Col: StageText>Take out their leader</Col>. It'll go some way towards making up for them screwing us over.";
            contentsSimple[527] = "TaskObjectives_DB_BEL3_Del1_Stage01_OwnerBrief=We just got an unusual call from an undercover cop - Officer Albert Finn of the SPPD. He's hidden a PDA with details about an urgent mission. Go <Col: StageText>pick up </Col>the <Col: StageText>PDA</Col>.";
            contentsSimple[528] = "TaskObjectives_DB_BEL3_Del1_Stage01_DispatchBrief=The Enforcers are using this area as a dead drop for their undercover operatives. Make the area too dangerous for them and <Col: StageText>interrupt any pick-ups </Col>they attempt.";
            contentsSimple[529] = "TaskObjectives_DB_BEL3_Del1_Stage02_OwnerBrief=Officer Finn is working deep, deep, deep undercover. Several SPPD officers just turned up to a drug deal with his gang. He left us information on who: <Col: StageText>search these vehicles </Col>for his <Col: StageText>files</col>.";
            contentsSimple[530] = "TaskObjectives_DB_BEL3_Del1_Stage02_DispatchBrief=Looks like we've got a mole - that PDA was dropped off by somebody in our gang. Now the Enforcers are after files incriminating some of our members. <Col: StageText>Stop the pick-up</Col>.";
            contentsSimple[531] = "TaskObjectives_DB_BEL3_Del1_Stage03_OwnerBrief=Officer Finn left another set of <Col: StageText>files </Col>for <Col: StageText>pick up </Col>at these addresses, <Col: StageText>raid them</Col>. With corrupt SPPD officers involved, Officer Finn knows its only us he can trust. Don't let him down.";
            contentsSimple[532] = "TaskObjectives_DB_BEL3_Del1_Stage03_DispatchBrief=That undercover mole has been busy - the Enforcers are picking up more information on our gang operations. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>recovering those files</Col>.";
            contentsSimple[533] = "TaskObjectives_DB_BEL3_Del1_Stage04_OwnerBrief=You've got all the files. <Col: StageText>Deliver everything </Col>here and we'll start moving in on the suspects. Watch out for reprisals - corrupt cops have a lotta dangerous friends.";
            contentsSimple[534] = "TaskObjectives_DB_BEL3_Del1_Stage04_DispatchBrief=If we ever want to find out who our Enforcer mole is, we need to recover that information. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>delivering the files </Col>to their lockup.";
            contentsSimple[535] = "TaskObjectives_DB_BEL3_Del1_Stage05_OwnerBrief=As a clincher, Officer Finn wants us to <Col: StageText>retrieve and hold </Col>onto his <Col: StageText>laptop </Col>until we download all the corroborating data.";
            contentsSimple[536] = "TaskObjectives_DB_BEL3_Del1_Stage05_DispatchBrief=This is getting out of hand - now the mole has taken a laptop detailing our latest plans. <Col: StageText>Retrieve and hold </Col>onto our <Col: StageText>laptop </Col>until we can delete all the data.";
            contentsSimple[537] = "TaskObjectives_DB_BEL3_DelV1_Stage01_OwnerBrief=We've got some Pit Vipers in the lockup and we need you to <Col: StageText>pick up </Col>their street racing <Col: StageText>cars </Col>and <Col: StageText>deliver them </Col>to the impound lot.";
            contentsSimple[538] = "TaskObjectives_DB_BEL3_DelV1_Stage01_DispatchBrief=<Col: StageText>Stop Enforcers </Col>from <Col: StageText>picking up </Col>our street racing <Col: StageText>cars </Col>and <Col: StageText>impounding </Col>them. At this point in the season, we'd miss the Silver Street leg of the race for sure. ↵";
            contentsSimple[539] = "TaskObjectives_DB_BEL3_DelV1_Stage02_OwnerBrief=<Col: StageText>Drop off </Col>those Pit Viper <Col: StageText>cars </Col>at the <Col: StageText>impound lot</Col>. If we don't keep bustin' their shit, they'll just keep riding all over us.";
            contentsSimple[540] = "TaskObjectives_DB_BEL3_DelV1_Stage02_DispatchBrief=Enforcers picked up three of our street racing <Col: StageText>cars </Col>- <Col: StageText>stop them </Col>from <Col: StageText>dropping </Col>them back to their <Col: StageText>impound </Col>lot. I still can't see what their beef is with street racing through pedestrian areas!";
            contentsSimple[541] = "TaskObjectives_DB_BEL3_DelV1_Stage03_OwnerBrief=Nice work, we just picked up more racers - White Blades this time. They hate the Pit Vipers, so we're gonna throw 'em in a cell together. <Col: StageText>Pick up their cars </Col>.";
            contentsSimple[542] = "TaskObjectives_DB_BEL3_DelV1_Stage03_DispatchBrief=Enforcers are trying to impound more of our street racing cars. <Col: StageText>Stop them </Col>from <Col: StageText>picking up the cars</Col>. Don't they know we're three wins away from a championship placing?";
            contentsSimple[543] = "TaskObjectives_DB_BEL3_DelV1_Stage04_OwnerBrief=<Col: StageText>Drop off </Col>those White Blade cars <Col: StageText>here </Col>and we'll lock them up. You should hear the ruckus down in the cell block. Blades hate Vipers, that's a fact.";
            contentsSimple[544] = "TaskObjectives_DB_BEL3_DelV1_Stage04_DispatchBrief=We need those street racing <Col: StageText>cars </Col>- <Col: StageText>stop </Col>the Enforcers <Col: StageText>dropping them off </Col>at their <Col: StageText>impound </Col>lot. We're already behind after forfeiting the Silver Street leg of the race.";
            contentsSimple[545] = "TaskObjectives_DB_BEL3_DelV1_Stage05_OwnerBrief=Looks like our luck could be back in - we just located a garbage truck containing evidence from some White Blades that got busted.<Col:StageText> Grab that truck</Col> and <Col:StageText>Protect it</Col> until we can get a confession.";
            contentsSimple[546] = "TaskObjectives_DB_BEL3_DelV1_Stage05_DispatchBrief=There's a garbage truck out there containing proof we've been using illegal mods.  If the other racers find out we'll be out for sure.  <Col:StageText> Capture and Hold the Dumpster</Col> while the crusher does its work.";
            contentsSimple[547] = "TaskObjectives_DB_BEL4_Dis1_Stage01_OwnerBrief=Bomb threats have been made against some local <Col: StageText>drugstores </Col>that have been selling an anti-gang pepper spray called 'Gangrenous'. <Col: StageText>Investigate </Col>the targets thoroughly.";
            contentsSimple[548] = "TaskObjectives_DB_BEL4_Dis1_Stage01_DispatchBrief=Stores have been colluding with drug companies to screw customers into buying expensive medicine. We've planted bombs in front of two drugstores. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>finding </Col>them.";
            contentsSimple[549] = "TaskObjectives_DB_BEL4_Dis1_Stage02_OwnerBrief=These bombs are well-hidden: they're actually fitted under the sidewalk slabs! <Col: StageText>Defuse the bombs </Col>and let these drugstores continue stocking the pro-'Forcer pepper spray.";
            contentsSimple[550] = "TaskObjectives_DB_BEL4_Dis1_Stage02_DispatchBrief=Enforcers found the bombs we planted in front of those corrupt drugstores. <Col: StageText>Stop them </Col>from <Col: StageText>defusing </Col>the bombs. The 'Forcers are helping to prop up these corrupt pharmaceutical companies.";
            contentsSimple[551] = "TaskObjectives_DB_BEL4_Dis1_Stage03_OwnerBrief=Makers of the 'Gangrenous' pepper spray are going to be holding a large publicity event soon. Get over there and <Col: StageText>investigate the area </Col>for possible sabotage before they set up.";
            contentsSimple[552] = "TaskObjectives_DB_BEL4_Dis1_Stage03_DispatchBrief=We've already planted some hidden bombs near the HQs of the drug companies. <Col: StageText>Stop </Col>the corporate puppet <Col: StageText>Enforcers </Col>from <Col: StageText>searching </Col>for the <Col: StageText>bombs</Col>.";
            contentsSimple[553] = "TaskObjectives_DB_BEL4_Dis1_Stage04_OwnerBrief=It's another one of those slim-line IEDs. <Col: StageText>Defuse the bomb</Col>. Pick it up when it's been made safe; obviously, we need to gain a better understanding of this dangerous new device.";
            contentsSimple[554] = "TaskObjectives_DB_BEL4_Dis1_Stage04_DispatchBrief=A bomb we planted outside the corrupt pharmaceutical company has been found by Enforcers. <Col: StageText>Stop them </Col>from <Col: StageText>defusing </Col>the bomb and examining it for clues.";
            contentsSimple[555] = "TaskObjectives_DB_BEL4_Dis1_Stage05_OwnerBrief=We're closing in on the gang responsible for this new bomb design. Meanwhile, <Col: StageText>search the area </Col>where 'Gangrenous' are going to be holding product demonstrations. Hidden bombs could be there too.";
            contentsSimple[556] = "TaskObjectives_DB_BEL4_Dis1_Stage05_DispatchBrief=To distract Enforcers from investigating the previous bombing, we planted another IED. They're in such a panic their whole investigation into our bombs might unravel - or so we hope. <Col: StageText>Stop them searching for the bomb</Col>.";
            contentsSimple[557] = "TaskObjectives_DB_BEL4_Dis1_Stage06_OwnerBrief=These bombs are almost invisible. I wonder how they get the profile so thin. <Col: StageText>Defuse the bomb </Col>and keep it safe. We need to get a closer look.";
            contentsSimple[558] = "TaskObjectives_DB_BEL4_Dis1_Stage06_DispatchBrief=One of our guys has been getting sloppy with the bomb-planting and forgot to wear gloves. <Col: StageText>Stop this squad </Col>from <Col: StageText>defusing </Col>the bomb and recovering it for fingerprints.";
            contentsSimple[559] = "TaskObjectives_DB_BEL4_Dis1_Stage07_OwnerBrief=Now <Col: StageText>drop off </Col>the defused <Col: StageText>bombs </Col>you've found to the nearby <Col: StageText>lockup</Col>. We'll examine them carefully and find out what makes them tick.";
            contentsSimple[560] = "TaskObjectives_DB_BEL4_Dis1_Stage07_DispatchBrief=Enough of our bombs have been recovered for the Enforcers to be able to trace them back to us. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>dropping off </Col>our bombs to their forensics <Col: StageText>lockup</Col>.";
            contentsSimple[561] = "TaskObjectives_DB_BEL4_Dis1_Stage08_OwnerBrief=The gang want to set off another of their stealth bombs near the location of a big 'Gangrenous' pepper spray publicity shindig. <Col: StageText>Prevent </Col>the <Col: StageText>bombing </Col>.";
            contentsSimple[562] = "TaskObjectives_DB_BEL4_Dis1_Stage08_DispatchBrief=I'm sick of these Enforcers propping up corrupt drug companies. <Col: StageText>Defend the bomb </Col>I planted in this public area to highlight their ruthless disregard for people's health.";
            contentsSimple[563] = "TaskObjectives_DB_BEM0_Pic1_P_Stage01_OwnerBrief=Praetorian high command needs some quick wins for our anti-drug campaign. <Col: StageText>Search this neighborhood. </Col>The stats show a high level of drug dealing in the area.";
            contentsSimple[564] = "TaskObjectives_DB_BEM0_Pic1_P_Stage01_DispatchBrief=These street corners belong to some drug dealing friends of ours who pay us to provide security. They've recently seen the Praetorians patrolling. <Col: StageText>Keep the Praetorians out</Col>.";
            contentsSimple[565] = "TaskObjectives_DB_BEM0_Pic1_P_Stage02_OwnerBrief=The campaign is working. An informant saw your patrol and has given us the location of a drug stash. <Col: StageText>Retrieve the drugs</Col>.";
            contentsSimple[566] = "TaskObjectives_DB_BEM0_Pic1_P_Stage02_DispatchBrief=The Praetorians have found out about the local drug drop-off point and have been searching for it. <Col: StageText>Stop them </Col>from <Col: StageText>picking up</Col> anything our dealer friends have stashed.";
            contentsSimple[567] = "TaskObjectives_DB_BEM0_Pic1_P_Stage03_OwnerBrief=Better move quick: those dealers have another distribution point. <Col: StageText>Retrieve </Col>their secondary stash of <Col: StageText>drugs</Col>.";
            contentsSimple[568] = "TaskObjectives_DB_BEM0_Pic1_P_Stage03_DispatchBrief=Tragic! The Praetorians found the drug drop-off point and confiscated the stash. <Col: StageText>Stop them </Col>from  <Col: StageText>seizing </Col>any more or we could lose the dealers as clients.";
            contentsSimple[569] = "TaskObjectives_DB_BEM0_Pic1_P_Stage04_OwnerBrief=Clean work out there. Now <Col: StageText>transfer the drugs </Col>to our facility.";
            contentsSimple[570] = "TaskObjectives_DB_BEM0_Pic1_P_Stage04_DispatchBrief=The Praetorians have searched all the drop-off points in this area and seized the drugs. <Col: StageText>Stop them </Col>from <Col: StageText>delivering </Col>it to their <Col: StageText>lockup.</Col>";
            contentsSimple[571] = "TaskObjectives_DB_BEM0_Pic1_P_Stage05_OwnerBrief=The anti-drug campaign is working. <Col: StageText>Protect your leader </Col> some dealers are looking to show how pissed off they are!";
            contentsSimple[572] = "TaskObjectives_DB_BEM0_Pic1_P_Stage05_DispatchBrief=The Praetorians seized our client's goods. They are not happy. Make sure the Praetorians can never do this again. <Col: StageText>Take out their leader.</Col>";
            contentsSimple[573] = "TaskObjectives_DB_BEM0_Pic1_T_Stage01_OwnerBrief=Byeong Lee wants us to <Col: StageText>go</Col> and to make our presence felt. This <Col: StageText>neighborhood</Col> is making a ton of complaints that drug dealing is going down.";
            contentsSimple[574] = "TaskObjectives_DB_BEM0_Pic1_T_Stage01_DispatchBrief=These street corners belong to some drug dealing friends of ours who pay us to provide security. We hear the Prentiss Tigers have suddenly got heavy in this neighborhood. <Col: StageText>Keep them out</Col> by making it too dangerous for them to stick around.";
            contentsSimple[575] = "TaskObjectives_DB_BEM0_Pic1_T_Stage02_OwnerBrief=Byeong was right. Some dudes saw you around and tipped us off where a drop point is. Get out there and <Col: StageText>snatch the drugs</Col>.";
            contentsSimple[576] = "TaskObjectives_DB_BEM0_Pic1_T_Stage02_DispatchBrief=Those Prentiss frat boys found out about the local drug drop-off points and have been searching them. <Col: StageText>Stop them </Col>from <Col: StageText>picking up</Col> the stash.";
            contentsSimple[577] = "TaskObjectives_DB_BEM0_Pic1_T_Stage03_OwnerBrief=This reminds me of what Coach Patchings used to say about locker searches: find one stash and it points to the next one. Go and <Col: StageText>retrieve the drugs</Col>.";
            contentsSimple[578] = "TaskObjectives_DB_BEM0_Pic1_T_Stage03_DispatchBrief=Are the Tigers planning a party? They've got half of the drug supply for this area already! <Col: StageText>Stop them </Col>from <Col: StageText>picking up </Col>any more of our client's product.";
            contentsSimple[579] = "TaskObjectives_DB_BEM0_Pic1_T_Stage04_OwnerBrief=Time for a race to the end zone: <Col: StageText>drop off the drugs </Col>at our lockup.";
            contentsSimple[580] = "TaskObjectives_DB_BEM0_Pic1_T_Stage04_DispatchBrief=It's not too late to recover our reputation with the client. I know where the Tigers' local lockup is. <Col: StageText>Stop the Tigers </Col>from <Col: StageText>delivering the drugs </Col>and we are back in favor.";
            contentsSimple[581] = "TaskObjectives_DB_BEM0_Pic1_T_Stage05_OwnerBrief=All right! Seems like the dirty dealers want to make a fight out of it. Suits me. <Col: StageText>Protect your leader </Col> and <Col: StageText>arrest them or take them out.</Col> Game on!";
            contentsSimple[582] = "TaskObjectives_DB_BEM0_Pic1_T_Stage05_DispatchBrief=We've got one last chance to resolve this matter for our clients and eliminate the problem at source. <Col: StageText>Take their leader out.</Col>";
            contentsSimple[583] = "TaskObjectives_DB_BEM0_Pic2_P_Stage01_OwnerBrief=Thieves robbed a bank that was under Praetorian protection. They were seen escaping into this neighborhood. <Col: StageText>Check this building </Col>for any sign of the perpetrators.";
            contentsSimple[584] = "TaskObjectives_DB_BEM0_Pic2_P_Stage01_DispatchBrief=Some of our guys robbed a bank and are hiding out in this neighborhood. <Col: StageText>Stop the Praetorians </Col>from <Col: StageText>searching </Col>the area.";
            contentsSimple[585] = "TaskObjectives_DB_BEM0_Pic2_P_Stage02_OwnerBrief=That address wasn't occupied, but a neighbor reported armed men abandoning gear and equipment before entering another building. <Col: StageText>Raid the building </Col>before their backup arrives.";
            contentsSimple[586] = "TaskObjectives_DB_BEM0_Pic2_P_Stage02_DispatchBrief=The 'Forcers have found the location of our bank robbery squad. <Col: StageText>Stop the Praetorians </Col>from <Col: StageText>raiding </Col>their location.";
            contentsSimple[587] = "TaskObjectives_DB_BEM0_Pic2_P_Stage03_OwnerBrief=The thieves fled, but we found personal effects that will help us track them down. <Col: StageText>Pick up the evidence </Col>and <Col: StageText>drop it off </Col>at our <Col: StageText>lockup.</Col>";
            contentsSimple[588] = "TaskObjectives_DB_BEM0_Pic2_P_Stage03_DispatchBrief=Thankfully our robbery team made a quick exit, but the Praetorians now have some abandoned tools and clothing that may lead to their capture. <Col: StageText>Stop the Praetorians </Col>from <Col: StageText>delivering the evidence </Col>to their <Col: StageText>lockup</Col>.";
            contentsSimple[589] = "TaskObjectives_DB_BEM0_Pic2_P_Stage04_OwnerBrief=The Criminals are in the open and on the run. They've dropped <Col: StageText>cash </Col>whilst being pursued. <Col: StageText>Retrieve the cash </Col>before they do!";
            contentsSimple[590] = "TaskObjectives_DB_BEM0_Pic2_P_Stage04_DispatchBrief=That escape didn't go quite as planned. Our team were forced to drop some bags of cash while being pursued. <Col: StageText>Retrieve the cash </Col>before the Praetorians can use it to build a case.";
            contentsSimple[591] = "TaskObjectives_DB_BEM0_Pic2_P_Stage05_OwnerBrief=The Criminals are in the open and on the run. They've dropped <Col: StageText>cash </Col>whilst being pursued. <Col: StageText>Retrieve the cash </Col>before they do!";
            contentsSimple[592] = "TaskObjectives_DB_BEM0_Pic2_P_Stage05_DispatchBrief=That escape didn't go quite as planned. Our team were forced to drop some bags of cash while being pursued. <Col: StageText>Retrieve the cash </Col>before the Praetorians can use it to build a case.";
            contentsSimple[593] = "TaskObjectives_DB_BEM0_Pic2_P_Stage06_OwnerBrief=All the discarded money from the robbery has been picked up. <Col: StageText>Drop off the cash </Col>at the Praetorian lockup ASAP. Don't let the Criminals get hold of it!";
            contentsSimple[594] = "TaskObjectives_DB_BEM0_Pic2_P_Stage06_DispatchBrief=";
            contentsSimple[595] = "TaskObjectives_DB_BEM0_Pic2_P_Stage06_Opp_OwnerBrief=";
            contentsSimple[596] = "TaskObjectives_DB_BEM0_Pic2_P_Stage06_Opp_DispatchBrief=All the cash dropped from the robbery has been picked up. <Col: StageText>Take the cash </Col>to our hideout, and don't let the Praetorians recover it. It is damning evidence against us - as well as our bonus!";
            contentsSimple[597] = "TaskObjectives_DB_BEM0_Pic2_T_Stage01_OwnerBrief=Thieves from a recent bank robbery have been seen fleeing into this neighborhood. It's Tiger time! Go <Col: StageText>check out these buildings </Col>for any clues.";
            contentsSimple[598] = "TaskObjectives_DB_BEM0_Pic2_T_Stage01_DispatchBrief=Some of our guys robbed a bank recently and are hiding out in this neighborhood. <Col: StageText>Stop the Prentiss Tigers </Col>from <Col: StageText>searching </Col>the area.";
            contentsSimple[599] = "TaskObjectives_DB_BEM0_Pic2_T_Stage02_OwnerBrief=We got some partial prints and an address where the robbers may be hiding out. <Col: StageText>Raid the building.</Col>";
            contentsSimple[600] = "TaskObjectives_DB_BEM0_Pic2_T_Stage02_DispatchBrief=Damn! The Prentiss killjoys have found where we concealed the bank robbers. <Col: StageText>Stop the Tigers </Col>from <Col: StageText>raiding </Col>their refuge.";
            contentsSimple[601] = "TaskObjectives_DB_BEM0_Pic2_T_Stage03_OwnerBrief=The robbers must have been tipped off. But they abandoned some specialist equipment that should help us hunt them down. <Col: StageText>Pick up the evidence </Col>and <Col: StageText>drop it off </Col>at our <Col: StageText>lockup</Col>.";
            contentsSimple[602] = "TaskObjectives_DB_BEM0_Pic2_T_Stage03_DispatchBrief=Our guys escaped, but they left some specialist kit behind that connects them to the robbery. <Col: StageText>Stop the Prentiss Tigers </Col>from <Col: StageText>delivering the evidence </Col>to their <Col: StageText>lockup</Col>.";
            contentsSimple[603] = "TaskObjectives_DB_BEM0_Pic2_T_Stage04_OwnerBrief=The bank robbers are feeling the power of the Tigers. They fled like rabbits and dropped some bags of money. Go <Col: StageText>get the cash </Col>before the criminals grow a pair and get their grubby hands on it.";
            contentsSimple[604] = "TaskObjectives_DB_BEM0_Pic2_T_Stage04_DispatchBrief=What is it? Amateur hour around here? Our guys dropped some of the stolen cash as they made a tactical retreat. <Col: StageText>Go get the cash </Col>before the Tigers get their claws into it.";
            contentsSimple[605] = "TaskObjectives_DB_BEM0_Pic2_T_Stage05_OwnerBrief=The bank robbers are feeling the power of the Tigers. They fled like rabbits and dropped some bags of money. Go <Col: StageText>get the cash </Col>before the criminals grow a pair and get their grubby hands on it.";
            contentsSimple[606] = "TaskObjectives_DB_BEM0_Pic2_T_Stage05_DispatchBrief=What is it? Amateur hour around here? Our guys dropped some of the stolen cash as they made a tactical retreat. <Col: StageText>Go get the cash </Col>before the Tigers get their claws into it.";
            contentsSimple[607] = "TaskObjectives_DB_BEM0_Pic2_T_Stage06_OwnerBrief=All the cash dropped from the robbery has been picked up. <Col: StageText>Get the cash</Col> to the local Prentiss Tiger  lockup, and don't let the Criminals get in your way!";
            contentsSimple[608] = "TaskObjectives_DB_BEM0_Pic2_T_Stage06_DispatchBrief=";
            contentsSimple[609] = "TaskObjectives_DB_BEM0_Pic2_T_Stage06_Opp_OwnerBrief=";
            contentsSimple[610] = "TaskObjectives_DB_BEM0_Pic2_T_Stage06_Opp_DispatchBrief=All the cash dropped from the robbery has been reclaimed. You might just save the day. <Col: StageText>Get the cash </Col>to our hideout, and don't embarrass yourself by letting the Tigers get it back to their stinking lair.";
            contentsSimple[611] = "TaskObjectives_DB_BEM1_For1_Stage01_OwnerBrief=An SPPD Narcotics officer has gone missing while deep undercover. We agreed to look into it. <Col: StageText>Hack this antenna </Col>so we can track the officer's cell phone.";
            contentsSimple[612] = "TaskObjectives_DB_BEM1_For1_Stage01_DispatchBrief=An undercover cop infiltrated our operation - find him before the SPPD uses Enforcers to extract him. <Col: StageText>Stop Enforcers </Col>from <Col: StageText>hacking this aerial </Col>in order to track the informant's cell phone.";
            contentsSimple[613] = "TaskObjectives_DB_BEM1_For1_Stage02_OwnerBrief=We're not getting any signal from that Narcotics officer's phone. It may just be out of range. <Col: StageText>Bug this satellite dish </Col>to search a larger range.";
            contentsSimple[614] = "TaskObjectives_DB_BEM1_For1_Stage02_DispatchBrief=They're still trying to extract that undercover SPPD cop. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>rigging this satellite </Col>to boost the range of their cell phone scan. They're using it to search for him.";
            contentsSimple[615] = "TaskObjectives_DB_BEM1_For1_Stage03_OwnerBrief=Still no signal, so either the cell phone is turned off or he left the city. We do have the last known position of the cell signal, though. <Col: StageText>Raid this building </Col>- and keep a sharp eye out for any signs of trouble.";
            contentsSimple[616] = "TaskObjectives_DB_BEM1_For1_Stage03_DispatchBrief=We've dealt with the cop who infiltrated our operation, but now Enforcers are searching for him at his last phoned-in location. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>raiding this smuggling office</Col>.";
            contentsSimple[617] = "TaskObjectives_DB_BEM1_For1_Stage04_OwnerBrief=The officer wasn't there, but something obviously went down. Looks like a drug deal gone bad... Whoa, hold on: the officer's cell was just turned on, and we've located it. <Col: StageText>Raid this location</Col>.";
            contentsSimple[618] = "TaskObjectives_DB_BEM1_For1_Stage04_DispatchBrief=Enforcers are still searching for information on the SPPD cop we 'took care of'. Don't let them search this building until we've cleaned it up. <Col: StageText>Stop Enforcers </Col>from <Col: StageText>raiding this storehouse</Col>.";
            contentsSimple[619] = "TaskObjectives_DB_BEM1_For1_Stage05_OwnerBrief=The evidence suggests that the Narcotics officer was brought here and killed. He must've turned his phone on while they were moving him. <Col: StageText>Deliver </Col>any <Col: StageText>clues </Col>you've found to our <Col: StageText>lockup</Col>.";
            contentsSimple[620] = "TaskObjectives_DB_BEM1_For1_Stage05_DispatchBrief=Enforcers have found evidence for what we did to that SPPD officer. Wasn't hard - the crime scene wasn't too pretty... <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>delivering the evidence </Col>to their lockup.";
            contentsSimple[621] = "TaskObjectives_DB_BEM1_For1_Stage06_OwnerBrief=We've received enough evidence that a warrant is out on the criminals involved.  <Col:StageText> Take them down</Col>, dead or alive.";
            contentsSimple[622] = "TaskObjectives_DB_BEM1_For1_Stage06_DispatchBrief=The Enforcers recovered evidence on the killing of that SPPD officer, we're going to go down for life. <Col: StageText>Kill the Assholes </Col>before they can testify.";
            contentsSimple[623] = "TaskObjectives_DB_BEM2_Del1_Stage01_OwnerBrief=Due to the sensitive nature of this task, your briefing will be given in the field. Move out and <Col: StageText>pick up </Col>the <Col: StageText>encrypted PDA</Col>.";
            contentsSimple[624] = "TaskObjectives_DB_BEM2_Del1_Stage01_DispatchBrief=Enforcers are picking up an encrypted PDA that contains orders for their next covert mission. <Col: StageText>Stop them </Col>from <Col: StageText>picking up the PDA </Col>and we can stop their mission before it starts.";
            contentsSimple[625] = "TaskObjectives_DB_BEM2_Del1_Stage02_OwnerBrief=A deep cover informant within the Blood Roses has compiled detailed files on several key figures. <Col: StageText>Search for </Col>the <Col: StageText>files </Col>left in these <Col: StageText>cars</Col>.";
            contentsSimple[626] = "TaskObjectives_DB_BEM2_Del1_Stage02_DispatchBrief=As part of 'Operation Killjoy', the Enforcer's are planning to eliminate gang leaders. Their first objective is to retrieve various case files. <Col: StageText>Stop the Enforcers searching the cars for files</Col>.";
            contentsSimple[627] = "TaskObjectives_DB_BEM2_Del1_Stage03_OwnerBrief=Another set of <Col: StageText>files </Col>have been left for <Col: StageText>retrieval </Col>; raid this <Col: StageText>address</Col> to get at them. The longer these files are left out, the more chance there is of them falling into the wrong hands.";
            contentsSimple[628] = "TaskObjectives_DB_BEM2_Del1_Stage03_DispatchBrief=That covert team is picking up a sniper rifle and various targets' bios. <Col: StageText>Stop them </Col>from <Col: StageText>retrieving the assassination kit</Col>.";
            contentsSimple[629] = "TaskObjectives_DB_BEM2_Del1_Stage04_OwnerBrief=You've gathered all the files left by our Blood Rose informant. <Col: StageText>Drop everything off </Col>here. Make sure you're not followed.";
            contentsSimple[630] = "TaskObjectives_DB_BEM2_Del1_Stage04_DispatchBrief=<Col: StageText>Stop </Col>the Enforcers from <Col: StageText>getting </Col>the <Col: StageText>assassination kit </Col>back <Col: StageText>to their lockup</Col>. That rifle and target information could be used to take out some of our high-ranking members.";
            contentsSimple[631] = "TaskObjectives_DB_BEM2_Del1_Stage05_OwnerBrief=You've gathered enough evidence to put away quite a few Blood Rose gang members. <Col: StageText>Protect </Col>your squad <Col: StageText>leader </Col>until you can <Col: StageText>kill off the enemies </Col>out to silence you.";
            contentsSimple[632] = "TaskObjectives_DB_BEM2_Del1_Stage05_DispatchBrief=We lost track of the assassination kit, but we can still <Col: StageText>take out </Col>the Enforcer <Col: StageText>group leader </Col>- that's the government spook who's going to pull the trigger for those assassinations.";
            contentsSimple[633] = "TaskObjectives_DB_BEM2_Del2_Stage01_OwnerBrief=The SPPD just handed us a cold case from a few years back. <Col: StageText>Take </Col>these <Col: StageText>files </Col>and go over them at the crime scene. The victim was an SPPD cop, back in the time of Commissioner Thirly.";
            contentsSimple[634] = "TaskObjectives_DB_BEM2_Del2_Stage01_DispatchBrief=A buddy of ours got away with murder a long time ago, but it looks like the Enforcers are re-opening his cold case. <Col: StageText>Prevent Enforcers </Col>from <Col: StageText>picking up </Col>any new <Col: StageText>evidence</Col>.";
            contentsSimple[635] = "TaskObjectives_DB_BEM2_Del2_Stage02_OwnerBrief=New witnesses say they saw the cop in two other locations the night he was killed. <Col: StageText>Go to the crime scenes</Col>, and update the case files with any new evidence you find.";
            contentsSimple[636] = "TaskObjectives_DB_BEM2_Del2_Stage02_DispatchBrief=Where the gun was stashed and the body turned up were key locations in that cold homicide case. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>investigating </Col>those <Col: StageText>crime scenes</Col>. Unless someone's built a mall on them...";
            contentsSimple[637] = "TaskObjectives_DB_BEM2_Del2_Stage03_OwnerBrief=Great work - looks like that SPPD cop was murdered by a colleague. Tsk tsk... <Col: StageText>Deliver </Col>the <Col: StageText>updated files </Col>back <Col: StageText>to the lockup </Col>for analysis.";
            contentsSimple[638] = "TaskObjectives_DB_BEM2_Del2_Stage03_DispatchBrief=The 'Forcers have just finished going over those crime scenes and updated a bunch of cold case files. <Col: StageText>Stop them </Col>from <Col: StageText>dropping off </Col>the <Col: StageText>files </Col>for analysis.";
            contentsSimple[639] = "TaskObjectives_DB_BEM2_Del2_Stage04_OwnerBrief=That cop-killing cop has hired help to screw up the last of the evidence. <Col: StageText>Secure these crime scenes</Col>: if we can uncover solid DNA evidence, we’ll be able run it against the SPPD databases to find our killer.";
            contentsSimple[640] = "TaskObjectives_DB_BEM2_Del2_Stage04_DispatchBrief=Our homicidal friend is leaving town, but he told us where the 'Forcers will head next. <Col: StageText>Go to these crime scenes</Col> and contaminate them. Ruin the evidence so it will be inadmissable in court.";
            contentsSimple[641] = "TaskObjectives_DB_BEM2_VLo1_Stage01_OwnerBrief=A Blood Rose is blogging about a new shipment of grenades. They were sent as components to stay undetected, but we found them. <Col: StageText>Retrieve the grenade body assemblies </Col>from this <Col: StageText>vehicle</Col>.";
            contentsSimple[642] = "TaskObjectives_DB_BEM2_VLo1_Stage01_DispatchBrief=We had a shipment of grenades sent as separate components but the Enforcers still found them! <Col: StageText>Stop </Col>the 'Forcers from <Col: StageText>retrieving the box </Col>of grenade casings we hid in this <Col: StageText>vehicle</Col>.";
            contentsSimple[643] = "TaskObjectives_DB_BEM2_VLo1_Stage02_OwnerBrief=The rest of the grenade shipment had been stashed around the city. <Col: StageText>Pick up </Col>the charges and fuses that are stored in these <Col: StageText>boxes</Col>.";
            contentsSimple[644] = "TaskObjectives_DB_BEM2_VLo1_Stage02_DispatchBrief=The Enforcers have found our box of grenade casings. <Col: StageText>Stop them </Col>from <Col: StageText>picking up this box </Col>of fuses.";
            contentsSimple[645] = "TaskObjectives_DB_BEM2_VLo1_Stage03_OwnerBrief=The last part of the grenade shipment contains the pins and pull rings. <Col: StageText>Pick up </Col>the <Col: StageText>case </Col>here. Without them, the gangs may as well be throwing stones.";
            contentsSimple[646] = "TaskObjectives_DB_BEM2_VLo1_Stage03_DispatchBrief=Enforcers have confiscated all but one of the components from our grenade shipment. <Col: StageText>Stop them </Col>from <Col: StageText>retrieving the case </Col>full of pins and pull rings.";
            contentsSimple[647] = "TaskObjectives_DB_BEM2_VLo1_Stage04_OwnerBrief=Great Work! <Col: StageText>Drop off </Col>those deadly <Col: StageText>components </Col>at our <Col: StageText>lockup</Col>, so they can be melted down into promotional CSA paperclips or something.";
            contentsSimple[648] = "TaskObjectives_DB_BEM2_VLo1_Stage04_DispatchBrief=All our grenade components have been taken by the Enforcers. <Col: StageText>Stop them </Col>from <Col: StageText>delivering the shipment </Col>to their lockup. Make the 'Forcers go - boom!";
            contentsSimple[649] = "TaskObjectives_DB_BEM2_VLo1_Stage05_OwnerBrief=The Criminals obviously still need their frag fix. They want to steal replacement grenades from our supplies. <Col: StageText>Stop them</Col>. Don't they know: never touch another dude's pineapple.";
            contentsSimple[650] = "TaskObjectives_DB_BEM2_VLo1_Stage05_DispatchBrief=There are easier ways to get hold of grenades - like stealing them from the Enforcers! <Col: StageText>Force your way in </Col>to their supply depot. We need those grenades.";
            contentsSimple[651] = "TaskObjectives_DB_BEM2_VLo1_Stage05_Opp_OwnerBrief=";
            contentsSimple[652] = "TaskObjectives_DB_BEM2_VLo1_Stage05_Opp_DispatchBrief=There are easier ways to get hold of grenades - like stealing them from the Enforcers! <Col: StageText>Force your way in </Col>to their supply depot. We need those grenades.";
            contentsSimple[653] = "TaskObjectives_DB_BEM2_VLo1_Stage06_OwnerBrief=They've raided one of our supply depots! <Col:StageText>Retreive the weapons</Col> and drop them off at the secure location.";
            contentsSimple[654] = "TaskObjectives_DB_BEM2_VLo1_Stage06_DispatchBrief=There's more than just grenades here! Take the weapons back to the <Col:StageText>drop-off</Col>. You'll be handsomely rewarded.";
            contentsSimple[655] = "TaskObjectives_DB_BEM2_VLo1_Stage06_Opp_OwnerBrief=";
            contentsSimple[656] = "TaskObjectives_DB_BEM2_VLo1_Stage06_Opp_DispatchBrief=There's more than just grenades here! Take the weapons back to the <Col:StageText>drop-off</Col>. You'll be handsomely rewarded.";
            contentsSimple[657] = "TaskObjectives_DB_BEM3_Inv1_Stage01_OwnerBrief=Lonny Dorengo came over here from the Concession and has been cutting himself into the action - hitting gangs as much as cops and 'Forcers. He's planning a meeting. <Col: StageText>Plant bugs in the area</Col>.";
            contentsSimple[658] = "TaskObjectives_DB_BEM3_Inv1_Stage01_DispatchBrief=Lonny Dorengo came here from the Concession, and has been hitting up gangs and 'Forcers alike. We're gonna deal with him, but need privacy for the hit - <Col: StageText>stop Enforcers </Col>from <Col: StageText>bugging these payphones</Col>.";
            contentsSimple[659] = "TaskObjectives_DB_BEM3_Inv1_Stage02_OwnerBrief=Lonny Dorengo ain't particularly stable - he'll pull shit on anyone with gold, drugs or cash. Needless to say he ain't got many friends left. <Col: StageText>Plant a bug on his window</Col>.";
            contentsSimple[660] = "TaskObjectives_DB_BEM3_Inv1_Stage02_DispatchBrief=Lonny Dorengo ain't stable - he'll pull shit on anyone with gold, drugs or cash. You can imagine how much we hate his guts. <Col: StageText>Stop Enforcers </Col>from <Col: StageText>bugging this shop front </Col>so we can kill him discreetly.";
            contentsSimple[661] = "TaskObjectives_DB_BEM3_Inv1_Stage03_OwnerBrief=Just in case Lonny gives his You see Derren's face on this dollar? I wipe my ass on it speech, <Col: StageText>plant a bug on this ATM</Col>. That should cover enough of the area to overhear anything talked about.";
contentsSimple[662] = "TaskObjectives_DB_BEM3_Inv1_Stage03_DispatchBrief=This is always the problem when you want to do a hit - no fucking privacy. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>bugging this ATM</Col>. Lonny Dorengo has to die today!";
            contentsSimple[663] = "TaskObjectives_DB_BEM3_Inv1_Stage04_OwnerBrief=We've got a listening post set up. <Col: StageText>Head there and wait </Col>until we hear something we can use. Lonny's on the way, driving that big-ass car of his.";
            contentsSimple[664] = "TaskObjectives_DB_BEM3_Inv1_Stage04_DispatchBrief=Lonny thinks we've built up our loot again after his last raid - he's on the way to claim it. We gotta kill him now, so <Col: StageText>stop Enforcers </Col>from <Col: StageText>getting to their bug-listening post</Col>.";
            contentsSimple[665] = "TaskObjectives_DB_BEM3_Inv1_Stage05_OwnerBrief=Lonny's pretty excited - a gang he fucked over last week has rebuilt their stash. <Col: StageText>Pick up the stash </Col>before he gets to it. Meanwhile, we're gonna bust every last one of these mofos for racketeering.";
            contentsSimple[666] = "TaskObjectives_DB_BEM3_Inv1_Stage05_DispatchBrief=Lonny Dorengo is dead! Unfortunately, the 'Forcer surveillance squad heard the whole thing, and are coming to bust us. No love there, then. <Col: StageText>Recover the bag of loot </Col>we used to bait Dorengo.";
            contentsSimple[667] = "TaskObjectives_DB_BEM3_Inv1_Stage06_OwnerBrief=That loot was bait to get Lonny out in the open. His own gang and the target gang both turned on him. <Col: StageText>Deliver </Col>the bag of <Col: StageText>loot </Col>to the <Col: StageText>lockup</Col>. Make sure Criminals don't get hold of the bag.";
            contentsSimple[668] = "TaskObjectives_DB_BEM3_Inv1_Stage06_DispatchBrief=<Col: StageText>Deliver the bag of loot </Col>back to our hideout and make sure the Enforcers don't get hold of it. With Lonny out of the way, we ought to be able to build up a much larger stash.";
            contentsSimple[669] = "TaskObjectives_DB_BEM3_Inv1_Stage06_Opp_OwnerBrief=That loot was bait to get Lonny out in the open. His own gang and the target gang both turned on him. <Col: StageText>Deliver </Col>the bag of <Col: StageText>loot </Col>to the <Col: StageText>lockup</Col>. Make sure Criminals don't get hold of the bag.";
            contentsSimple[670] = "TaskObjectives_DB_BEM3_Inv1_Stage06_Opp_DispatchBrief=<Col: StageText>Deliver the bag of loot </Col>back to our hideout and make sure the Enforcers don't get hold of it. With Lonny out of the way, we ought to be able to build up a much larger stash.";
            contentsSimple[671] = "TaskObjectives_DB_BEM3_Pic1_Stage01_OwnerBrief=Our CSA license allows us to carry out private security work, and a few of our clients need cash to be picked up and delivered. <Col: StageText>Collect the armored truck </Col>we'll use to transport the money.";
            contentsSimple[672] = "TaskObjectives_DB_BEM3_Pic1_Stage01_DispatchBrief=Several business owners want to avoid paying us protection by using Enforcers to transport their daily takings. Bad idea. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>picking up the security truck </Col>they use.";
            contentsSimple[673] = "TaskObjectives_DB_BEM3_Pic1_Stage02_OwnerBrief=<Col: StageText>Pick up </Col>the <Col: StageText>cash </Col>and keep it safe. Like a lot of places in San Paro, this neighborhood has some bad elements, so make sure you keep the money out of Criminal hands.";
            contentsSimple[674] = "TaskObjectives_DB_BEM3_Pic1_Stage02_DispatchBrief=I hear those business owners will be transporting a lot of money - in this neighborhood, that could easily go missing. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>picking up the cash</Col>.";
            contentsSimple[675] = "TaskObjectives_DB_BEM3_Pic1_Stage03_OwnerBrief=Nice work! That's why we've always had satisfied clients. Now <Col: StageText>drop off the money </Col>to its destination.";
            contentsSimple[676] = "TaskObjectives_DB_BEM3_Pic1_Stage03_DispatchBrief=The Enforcers are going to drop off those bags of cash. <Col: StageText>Stop them </Col>from <Col: StageText>delivering the money </Col>and completing their contract.";
            contentsSimple[677] = "TaskObjectives_DB_BEM3_Pic1_Stage04_OwnerBrief=Welcome to the crazy world of private security: one of our clients just dropped their money en route to the pick-up. Klutz. <Col: StageText>Search this area </Col>and see if you can <Col: StageText>recover </Col>the <Col: StageText>bag of cash</Col>.";
            contentsSimple[678] = "TaskObjectives_DB_BEM3_Pic1_Stage04_DispatchBrief=Lucky us! The Enforcers were doing one last pick-up, but a klutz shop assistant dropped the money on the way. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>searching this area </Col>for the missing money.";
            contentsSimple[679] = "TaskObjectives_DB_BEM3_Pic1_Stage05_OwnerBrief=Good job - all the <Col: StageText>cash </Col>has been picked up and accounted for. <Col: StageText>Drop it off </Col>here, and don't let any Criminals get hold of it.";
            contentsSimple[680] = "TaskObjectives_DB_BEM3_Pic1_Stage05_DispatchBrief=The Enforcers are going to drop off that <Col: StageText>bag of cash</Col>. Stop them. <Col: StageText>Deliver it </Col>back to our hideout instead. Meanwhile, we'll double the protection payments for those shop owners!";
            contentsSimple[681] = "TaskObjectives_DB_BEM3_Pic1_Stage05_Opp_OwnerBrief=Good job - all the <Col: StageText>cash </Col>has been picked up and accounted for. <Col: StageText>Drop it off </Col>here, and don't let any Criminals get hold of it.";
            contentsSimple[682] = "TaskObjectives_DB_BEM3_Pic1_Stage05_Opp_DispatchBrief=The Enforcers are going to drop off that <Col: StageText>bag of cash</Col>. Stop them. <Col: StageText>Deliver it </Col>back to our hideout instead. Meanwhile, we'll double the protection payments for those shop owners!";
            contentsSimple[683] = "TaskObjectives_DB_BES0_Del1_P_Stage01_OwnerBrief=We know that the gangs collect their account sheets at this time of year. <Col: StageText>Break into </Col>their accountant's <Col: StageText>vehicle </Col>and get that vital <Col: StageText>information</Col>.";
            contentsSimple[684] = "TaskObjectives_DB_BES0_Del1_P_Stage01_DispatchBrief=The Praetorians have targeted our accountant to see where we're earning our money. Make sure they don't get hold of the <Col: StageText>account sheets </Col>he has in his car.";
            contentsSimple[685] = "TaskObjectives_DB_BES0_Del1_P_Stage02_OwnerBrief=The gangs use drop-off points to relay their financial information. If we <Col: StageText>pick up </Col>their <Col: StageText>account sheets </Col>first, we'll get a valuable overview of their organization.";
            contentsSimple[686] = "TaskObjectives_DB_BES0_Del1_P_Stage02_DispatchBrief='Torians are collecting financial intel on our organization. <Col: StageText>Stop 'em </Col>from <Col: StageText>picking up </Col>the <Col: StageText>account sheets</Col>. They'll only be jealous of our wealth, anyway.";
            contentsSimple[687] = "TaskObjectives_DB_BES0_Del1_P_Stage03_OwnerBrief=The final set of <Col: StageText>account sheets </Col>was left for <Col: StageText>pick up </Col>here. Don't even bother trying to read 'em, though: you'll fall asleep on the job.";
            contentsSimple[688] = "TaskObjectives_DB_BES0_Del1_P_Stage03_DispatchBrief=The Praetorians have gathered most of the financial files they need to make a case against us. <Col: StageText>Stop them </Col>from <Col: StageText>picking up </Col>the last of the <Col: StageText>accounts</Col>.";
            contentsSimple[689] = "TaskObjectives_DB_BES0_Del1_P_Stage04_OwnerBrief=Get those <Col: StageText>account sheets </Col>and <Col: StageText>deliver </Col>them back here now. We'll be able to examine every element of the gang's business.";
            contentsSimple[690] = "TaskObjectives_DB_BES0_Del1_P_Stage04_DispatchBrief=The Praetorians have a wealth of evidence against us now. <Col: StageText>Stop them </Col>from <Col: StageText>bringing </Col>the <Col: StageText>account sheets </Col>back to their <Col: StageText>lockup</col>.";
            contentsSimple[691] = "TaskObjectives_DB_BES0_Del1_P_Stage05_OwnerBrief=Another successful Praetorian case, and the gangs don't like it! You'd think they'd be proud of their 'figures'. They'll attempt to save face by controlling this corner. Go <Col: StageText>secure the area</Col>.";
            contentsSimple[692] = "TaskObjectives_DB_BES0_Del1_P_Stage05_DispatchBrief=The Praetorians won't be able to use that financial evidence against us if they can't present it in person. Prevent that - <Col: StageText>control this corner</Col> to scare off any possible jurors; extreme prejudice encouraged.";
            contentsSimple[693] = "TaskObjectives_DB_BES0_Del1_T_Stage01_OwnerBrief=A Tiger's deep undercover in a local gang. He's gonna leave details of their armaments for us, hiding them in normal looking packages. <Col: StageText>Pick up </Col>the first set of <Col: StageText>intel files </Col>from this <Col: StageText>vehicle</Col>.";
            contentsSimple[694] = "TaskObjectives_DB_BES0_Del1_T_Stage01_DispatchBrief=I suspect the Tigers are being sold info on our armaments. I made a dummy <Col: StageText>document </Col> and left it in this vehicle. See if its location gets leaked. If anyone tries to collect it, <Col: StageText>stop them</Col> .";
            contentsSimple[695] = "TaskObjectives_DB_BES0_Del1_T_Stage02_OwnerBrief=The next set of <Col: StageText>intel files </Col>was left for <Col: StageText>pick up </Col>at this address. Nothing concrete in these reports yet.";
            contentsSimple[696] = "TaskObjectives_DB_BES0_Del1_T_Stage02_DispatchBrief=We need to find out who is leaking our plans to the Prentiss Tigers. In the meantime, <Col: StageText>stop them </Col>from <Col: StageText>picking up </Col>more <Col: StageText>files </Col>on us.";
            contentsSimple[697] = "TaskObjectives_DB_BES0_Del1_T_Stage03_OwnerBrief=The last set of <Col: StageText>intel files </Col>from our inside guy was left for <Col: StageText>pick up </Col>at this address. Hopefully they'll give us an idea of the gang's offensive capabilities.";
            contentsSimple[698] = "TaskObjectives_DB_BES0_Del1_T_Stage03_DispatchBrief=We're closing in on our mole, but they're still evading us for now. <Col: StageText>Stop the Tigers </Col>from <Col: StageText>picking up </Col>the last of the <Col: StageText>files </Col>that have been leaked.";
            contentsSimple[699] = "TaskObjectives_DB_BES0_Del1_T_Stage04_OwnerBrief=You've got all the <Col: StageText>intel files</Col>. <Col: StageText>Drop them off </Col>at this Tiger safe house.";
            contentsSimple[700] = "TaskObjectives_DB_BES0_Del1_T_Stage04_DispatchBrief=We've plugged our leak - permanently - but the Tigers already have a lot of information. Stop them from getting the intel <Col: StageText>files </Col>back to their <Col: StageText>lockup</col>.";
            contentsSimple[701] = "TaskObjectives_DB_BES0_Del1_T_Stage05_OwnerBrief=The gang knew they had a mole in their midst and killed him. The mole's last tip pointed us here. <Col: StageText>Control this area</Col>; we'll be there soon with a team to sweep the area.";
            contentsSimple[702] = "TaskObjectives_DB_BES0_Del1_T_Stage05_DispatchBrief=I put a tracker in those files, so we can trace who's been stealing our armament documents. It definitely was the Tigers, and now they're searching for files. <Col: StageText>Control this area</Col> and neuter those pussies.";
            contentsSimple[703] = "TaskObjectives_DB_BES0_Del2_P_Stage01_OwnerBrief=A Praetorian informant has discovered some human organs being sold on the black market. We'll intercept them. First step - <Col: StageText>collect</Col> an <Col: StageText>ambulance.</Col>";
            contentsSimple[704] = "TaskObjectives_DB_BES0_Del2_P_Stage01_DispatchBrief=The Praetorians are <Col: StageText>picking up an ambulance</Col> to recover our cache of blackmarket organs. The doc ain't happy. <Col: StageText>Stop them</Col> from mobilizing.";
            contentsSimple[705] = "TaskObjectives_DB_BES0_Del2_P_Stage02_OwnerBrief=Now you're in possession of the ambulance, go <Col: StageText>pick up</Col> the <Col: StageText>organs</col> so they can't be sold.";
            contentsSimple[706] = "TaskObjectives_DB_BES0_Del2_P_Stage02_DispatchBrief=Praetorian Enforcers are <Col: StageText>seizing </Col> our <Col: StageText>organs</Col>. The doc wants you to <Col: StageText>stop them</Col>. It took him ages to hack them out.";
            contentsSimple[707] = "TaskObjectives_DB_BES0_Del2_P_Stage03_OwnerBrief=<Col: StageText>Drop off </Col>the <Col: StageText>organs </Col>at this <Col: StageText>lockup </Col>with a cold storage facility. We'll have the forensics team pick them up later and expose the doctor responsible.";
            contentsSimple[708] = "TaskObjectives_DB_BES0_Del2_P_Stage03_DispatchBrief=The Praetorians are <Col: StageText>taking</Col> the doc's high value <Col: StageText>organs</col> to be destroyed. <Col: StageText>Stop them</Col> before they get to where they're going. Unless you want to supply the replacements!";
            contentsSimple[709] = "TaskObjectives_DB_BES0_Del2_P_Stage04_OwnerBrief=Clean work. Now <Col: StageText>drop off</Col> the <Col: StageText>ambulance</Col>; it's still city property, you know.";
            contentsSimple[710] = "TaskObjectives_DB_BES0_Del2_P_Stage04_DispatchBrief=This isn't good - the Praetorians are trashing our organ trade. <Col: StageText>Stop them delivering </Col>the <Col: StageText>ambulance</Col> so they can't use it again.";
            contentsSimple[711] = "TaskObjectives_DB_BES0_Del2_P_Stage05_OwnerBrief=A second batch of organs has been found but these appear to be animal parts. We've made the traffickers desperate to close a sale. <Col: StageText>Pick up the organs</Col> before they can hit the black market.";
            contentsSimple[712] = "TaskObjectives_DB_BES0_Del2_P_Stage05_DispatchBrief=The Praetorians could be on to our whole organ trafficking network. <Col: StageText>Stop them </Col>from <Col: StageText>picking up our latest shipment</Col>.";
            contentsSimple[713] = "TaskObjectives_DB_BES0_Del2_P_Stage06_OwnerBrief=<Col: StageText>Deliver these organs</Col> to our lockup. We will destroy them before someone gets a pig's heart sewn inside them.";
            contentsSimple[714] = "TaskObjectives_DB_BES0_Del2_P_Stage06_DispatchBrief=Make or break! The Praetorians could shut down our whole organ trafficking operation with that evidence. <Col: StageText>Stop them</Col> from <Col: StageText>delivering</Col> it to their warehouse.";
            contentsSimple[715] = "TaskObjectives_DB_BES0_Del2_P_Stage07_OwnerBrief=A set of organs from a recently escaped orangutan were found in transit. <Col: StageText>Pick up</Col> the organs before the gang get them to that Crim, Dr. Borious.";
            contentsSimple[716] = "TaskObjectives_DB_BES0_Del2_P_Stage07_DispatchBrief=<Col: StageText>Pick up</Col> these organs, harvested from an escaped orangutan, before Enforcers can get their hands on them. Dr. Borious will pay good money for this mad scientist shit.";
            contentsSimple[717] = "TaskObjectives_DB_BES0_Del2_T_Stage01_OwnerBrief=The official Tigers M.D. has confiscated some black market organs he was offered. But you don't want blood all over your ride. <Col: StageText>Pick up</Col> an <Col: StageText>ambulance</Col> first.";
            contentsSimple[718] = "TaskObjectives_DB_BES0_Del2_T_Stage01_DispatchBrief=Some dickhead tried to sell our latest shipment of organs to a doctor who is loyal to the Prentiss Tigers. He is turning the organs in. <Col: StageText>Stop</Col> the Tigers from <Col: StageText>picking up</Col> the transfer <Col: StageText>ambulance</Col>.";
            contentsSimple[719] = "TaskObjectives_DB_BES0_Del2_T_Stage02_OwnerBrief=Nice wheels you got now buddy! The Doc has left the human spare parts out for us to collect. So go <Col: StageText>pick up</Col> the <Col: StageText>organs</col>.";
            contentsSimple[720] = "TaskObjectives_DB_BES0_Del2_T_Stage02_DispatchBrief=The Tigers are on their way to <Col: StageText>confiscate</Col> our shipment of <Col: StageText>organs</Col>. Make sure you <Col: StageText>stop them</Col>.";
            contentsSimple[721] = "TaskObjectives_DB_BES0_Del2_T_Stage03_OwnerBrief=Hey, you got guts. Literally. Ha ha.  <Col: StageText>Drop off </Col>the <Col: StageText>organs </Col>at this <Col: StageText>lockup</Col>. Forensics will deal with them there. Don't want them stinking up the place back here.";
            contentsSimple[722] = "TaskObjectives_DB_BES0_Del2_T_Stage03_DispatchBrief=The Tigers have seized our shipment of <Col: StageText>organs</col> and are <Col: StageText>dropping them off </Col>to their forensics team. <Col: StageText>Stop the transfer</Col>.";
            contentsSimple[723] = "TaskObjectives_DB_BES0_Del2_T_Stage04_OwnerBrief=It's not cool to be seen driving that wreck. Probably smells like a meat counter too. So <Col: StageText>drop off</Col> the <Col: StageText>ambulance</Col>.";
            contentsSimple[724] = "TaskObjectives_DB_BES0_Del2_T_Stage04_DispatchBrief=That meat wagon has to go. Make sure the <Col: StageText>ambulance </Col>doesn't get <Col: StageText>dropped off </Col>and put back into service.";
            contentsSimple[725] = "TaskObjectives_DB_BES0_Del2_T_Stage05_OwnerBrief=Gross! Doc uncovered more organs, but they appear to be animal parts. We need to get them before some kid ends up shitting through a baboon's anus. <Col: StageText>Pick up the organs</Col> so we can get rid of them.";
            contentsSimple[726] = "TaskObjectives_DB_BES0_Del2_T_Stage05_DispatchBrief=We had to think fast for our 'budget' transplant patients and get a shipment of animal organs instead. But the Prentiss Tigers are onto us and monkey hearts don't cost peanuts you know!. <Col: StageText>Stop them</Col> from <Col: StageText>collecting </Col> the organs.";
            contentsSimple[727] = "TaskObjectives_DB_BES0_Del2_T_Stage06_OwnerBrief=<Col: StageText>Deliver those organs</Col> to our lockup. We've found a legitimate buyer for them. Just... don't buy any oxtail soup for a while, yeah?";
            contentsSimple[728] = "TaskObjectives_DB_BES0_Del2_T_Stage06_DispatchBrief=The Tigers could halt our trade in animal organs with all the evidence they have. <Col: StageText>Prevent them</Col> from <Col: StageText>delivering</Col> those monkey tickers.";
            contentsSimple[729] = "TaskObjectives_DB_BES0_Del2_T_Stage07_OwnerBrief=A set of organs from a recently escaped orangutan were found in transit. <Col: StageText>Pick up</Col> the organs before the gang get them to that Crim, Dr. Borious.";
            contentsSimple[730] = "TaskObjectives_DB_BES0_Del2_T_Stage07_DispatchBrief=<Col: StageText>Pick up</Col> these organs, harvested from an escaped orangutan, before Enforcers can get their hands on them. Dr. Borious will pay good money for this mad scientist shit.";
            contentsSimple[731] = "TaskObjectives_DB_BES0_For1_P_Stage01_OwnerBrief=The San Paro Standard has been hammering us over the way we do things. Praetorian PR wants you to <Col: StageText>raid these gang premises</Col> and showcase our firm-but-fair community policing.";
            contentsSimple[732] = "TaskObjectives_DB_BES0_For1_P_Stage01_DispatchBrief=The Praetorians are desperately trying to kiss ass with the San Paro Standard by <Col: StageText>raiding our hangouts</Col>. Go <Col: StageText>stop them</Col>!";
            contentsSimple[733] = "TaskObjectives_DB_BES0_For1_P_Stage02_OwnerBrief=A bust like this will make us look great in the press. <Col: StageText>Take the evidence</Col> from the bust back to the lockup for some 'impromptu' photo opportunities.";
            contentsSimple[734] = "TaskObjectives_DB_BES0_For1_P_Stage02_DispatchBrief=The Praetorians are calling the refuse they recovered  'evidence of positive policing'. WTF? Time to hijack their big press conference. <Col: StageText>Stop them</Col> from <Col: StageText>delivering</Col> that trash back to their lockup.";
            contentsSimple[735] = "TaskObjectives_DB_BES0_For1_P_Stage03_OwnerBrief=Our press release hits and journalists're gonna swarm this place - they'll see it's a shithole! Cover up any tags... Gotta show 'em this place is 100% under our control.";
            contentsSimple[736] = "TaskObjectives_DB_BES0_For1_P_Stage03_DispatchBrief='Torians like to pretend to the press they've wiped us out for a while, by nuking our graffiti. But we pop back like roaches. <Col: StageText>Stop them</Col> from <Col: StageText>covering our tags</Col>.";
            contentsSimple[737] = "TaskObjectives_DB_BES0_For1_T_Stage01_OwnerBrief=Mikko Wong at the San Paro Standard has been saying we only sign up as Enforcers to get laid. <Col: StageText>Raid these gang hideouts </Col>and show her the Tigers can go all the way.";
            contentsSimple[738] = "TaskObjectives_DB_BES0_For1_T_Stage01_DispatchBrief=The Prentiss Tigers are trying to be tough guys for the cameras. They're <Col: StageText>raiding our hangouts</Col>. Go <Col: StageText>stop them</Col>.";
            contentsSimple[739] = "TaskObjectives_DB_BES0_For1_T_Stage02_OwnerBrief=A bust like this will make us look great. <Col: StageText>Drop off the evidence</Col> from the raid at the lockup. We'll get started on writing the press release.";
            contentsSimple[740] = "TaskObjectives_DB_BES0_For1_T_Stage02_DispatchBrief=The Tigers grabbed our stuff so they could say their stupid raid was a success to the local press. <Col: StageText>Stop them</Col> from <Col: StageText>delivering</Col> it back to their lockup. I got a birthday card from my mom in there.";
            contentsSimple[741] = "TaskObjectives_DB_BES0_For1_T_Stage03_OwnerBrief=That successful raid is generating some press heat. <Col: StageText>Cover up these tags</Col> to show we're in control of the neighborhood. Hell yeah, we're SO all gettin' laid tonight!";
            contentsSimple[742] = "TaskObjectives_DB_BES0_For1_T_Stage03_DispatchBrief=Now the Tigers are pretending to be all goody-goody for the camera by wiping off our graffiti. <Col: StageText>Stop them</Col> from <Col: StageText>covering our tags</Col>.";
            contentsSimple[743] = "TaskObjectives_DB_BES0_Inv1_P_Stage01_OwnerBrief=<Col: StageText>Investigate</Col> this property. Its alarm is linked to a Praetorian security network, and has been going off.";
            contentsSimple[744] = "TaskObjectives_DB_BES0_Inv1_P_Stage01_DispatchBrief=We needed to cause a distraction, so I've hacked into the 'Torian security network and I'm setting off random burgular alarms. Help me out. <Col: StageText>Stop the Praetorians</Col> from <Col: StageText>investigating</Col> the alarms.";
            contentsSimple[745] = "TaskObjectives_DB_BES0_Inv1_P_Stage02_OwnerBrief=We've just had another alarm go off. <Col: StageText>Investigate</Col> and try to apprehend the burglar in situ.";
            contentsSimple[746] = "TaskObjectives_DB_BES0_Inv1_P_Stage02_DispatchBrief=The Praetorians' security network is wide open. I'm setting off more alarms as a distraction. The 'Forcers are running around like headless chickens! <Col: StageText>Don't let them investigate</Col> further.";
            contentsSimple[747] = "TaskObjectives_DB_BES0_Inv1_P_Stage03_OwnerBrief=Another alarm is going off... this can't be a coincidence. <Col: StageText>Force your way inside</Col> and check the place out. Something's going on.";
            contentsSimple[748] = "TaskObjectives_DB_BES0_Inv1_P_Stage03_DispatchBrief=The Praetorians may be on to the fact I've hacked their network. Buy me time to log out. <Col: StageText>Stop them</Col> from <Col: StageText>investigating</Col> my set up.";
            contentsSimple[749] = "TaskObjectives_DB_BES0_Inv1_P_Stage04_OwnerBrief=Someone is hacking into the Praetorian system and setting off random alarms, probably to distract us from other crimes. <Col: StageText>Sabotage the antenna</Col> they're using for their uplink.";
            contentsSimple[750] = "TaskObjectives_DB_BES0_Inv1_P_Stage04_DispatchBrief=The Praetorians have discovered my hacking uplink. <Col: StageText>Stop them</Col> from <Col: StageText>disabling the antenna</Col> I use to disrupt their systems.";
            contentsSimple[751] = "TaskObjectives_DB_BES0_Inv1_P_Stage05_OwnerBrief=Good work, you've disconnected them from our security system. We've got a fix on the laptop they used to hack the Praetorian server. <Col: StageText>Retrieve the hard drive</Col> from it.";
            contentsSimple[752] = "TaskObjectives_DB_BES0_Inv1_P_Stage05_DispatchBrief=They've got a fix on my security alarm hack and tracked down the PC I was using. <Col: StageText>Stop them </Col>from <Col: StageText>taking the hard drive</Col>.";
            contentsSimple[753] = "TaskObjectives_DB_BES0_Inv1_P_Stage06_OwnerBrief=<Col: StageText>Deliver that hard drive </Col>to the lockup. Once it's analyzed, we should be able to find out who did this and how.  Make sure the criminals don't get their hands on it.";
            contentsSimple[754] = "TaskObjectives_DB_BES0_Inv1_P_Stage06_DispatchBrief=The laptop I used to hack the security systems was confiscated by the Praetorians. <Col: StageText>Recover the drive</Col> and return it before they drop it off.";
            contentsSimple[755] = "TaskObjectives_DB_BES0_Inv1_P_Stage06_Opp_OwnerBrief=";
            contentsSimple[756] = "TaskObjectives_DB_BES0_Inv1_P_Stage06_Opp_DispatchBrief=The laptop I used to hack the security systems was confiscated by the Praetorians. <Col: StageText>Recover the drive</Col> and return it before they drop it off.";
            contentsSimple[757] = "TaskObjectives_DB_BES0_Inv1_T_Stage01_OwnerBrief=<Col: StageText>Investigate</Col> this property. A high number of burglar alarms have been going off in that neighborhood lately.";
            contentsSimple[758] = "TaskObjectives_DB_BES0_Inv1_T_Stage01_DispatchBrief=Tigers are giving us grief over in Midtown. I'm creating a distraction here by hacking into alarm systems and setting them off. <Col: StageText>Stop them </Col>from <Col: StageText>investigating </Col>the alarmed properties and discovering what I'm up to.";
            contentsSimple[759] = "TaskObjectives_DB_BES0_Inv1_T_Stage02_OwnerBrief=We've just had another alarm go off. <Col: StageText>Investigate </Col>this one and try to catch the burglar red-handed.";
            contentsSimple[760] = "TaskObjectives_DB_BES0_Inv1_T_Stage02_DispatchBrief=The security alarm I set off is distracting the Prentiss Tigers. Don't let them find out it's been hacked. <Col: StageText>Stop them</Col> from <Col: StageText>investigating</Col> the alarms.";
            contentsSimple[761] = "TaskObjectives_DB_BES0_Inv1_T_Stage03_OwnerBrief=The bells, the bells! Another alarm is going off... this can't be a coincidence. <Col: StageText>Force your way inside</Col> and check the place out: something funky is going down.";
            contentsSimple[762] = "TaskObjectives_DB_BES0_Inv1_T_Stage03_DispatchBrief=It's fun to watch those Tigers run around chasing their tails. Keep them clueless. <Col: StageText>Stop them </Col>from <Col: StageText>investigating</Col> the rigged alarms.";
            contentsSimple[763] = "TaskObjectives_DB_BES0_Inv1_T_Stage04_OwnerBrief=Someone is hacking into the alarm systems and setting them off, probably to make sure they keep Tigers off their tail elsewhere. <Col: StageText>Sabotage this antenna </Col>and disconnect them.";
            contentsSimple[764] = "TaskObjectives_DB_BES0_Inv1_T_Stage04_DispatchBrief=For a bunch of jocks, those Tigers sure are killjoys. They found out how I've been remotely rigging their security alarms. <Col: StageText>Stop them </Col>from <Col: StageText>disabling </Col>the uplink <Col: StageText>antenna</Col> I used.";
            contentsSimple[765] = "TaskObjectives_DB_BES0_Inv1_T_Stage05_OwnerBrief=Good work, you've taken the nerds offline. We've tracked the laptop they've been using to hack the alarm systems. <Col: StageText>Retrieve the hard drive</Col> from it.";
            contentsSimple[766] = "TaskObjectives_DB_BES0_Inv1_T_Stage05_DispatchBrief=The Tigers disabled all my security alarm distractions and tracked down the hardware I was using. <Col: StageText>Stop them </Col>from <Col: StageText>picking up the hard drive</Col> from the laptop I used for the programming.";
            contentsSimple[767] = "TaskObjectives_DB_BES0_Inv1_T_Stage06_OwnerBrief=<Col: StageText>Deliver that hard drive </Col>to the lockup. Its cache will tell us who did this and how. Plus, we can see what websites they look at, the dirty bastards.";
            contentsSimple[768] = "TaskObjectives_DB_BES0_Inv1_T_Stage06_DispatchBrief=The laptop I used to hack the security systems has been confiscated by the Prentiss Tigers. <Col: StageText>Stop them</Col> from <Col: StageText>delivering the hard drive</Col> to their lockup and bring it back to me.";
            contentsSimple[769] = "TaskObjectives_DB_BES0_Inv1_T_Stage06_Opp_OwnerBrief=";
            contentsSimple[770] = "TaskObjectives_DB_BES0_Inv1_T_Stage06_Opp_DispatchBrief=The laptop I used to hack the security systems has been confiscated by the Prentiss Tigers. <Col: StageText>Stop them</Col> from <Col: StageText>delivering the hard drive</Col> to their lockup and bring it back to me.";
            contentsSimple[771] = "TaskObjectives_DB_BES0_Inv2_P_Stage01_OwnerBrief=LaRocha arrested some G-Kings earlier today and found a list of car bomb targets. This is definitely worth further investigation. Check out this vehicle for any explosives, if a bomb is present <Col: StageText>defuse</Col> it.";
            contentsSimple[772] = "TaskObjectives_DB_BES0_Inv2_P_Stage01_DispatchBrief=Goddamn Praetorians! LaRocha just stumbled upon our list of city officials targeted for car-bombing. <Col: StageText>Guard the bombs </Col>and make sure no one stops them from going boooom!";
            contentsSimple[773] = "TaskObjectives_DB_BES0_Inv2_P_Stage02_OwnerBrief=The list LaRocha found must be legit. <Col: StageText>Defuse </Col>the <Col: StageText>bombs </Col>from these other vehicles on the target list.";
            contentsSimple[774] = "TaskObjectives_DB_BES0_Inv2_P_Stage02_DispatchBrief=The Praetorians are trying to defuse the car bombs we've planted around the city. <Col: StageText>Guard the bombs </Col>and make sure no one stops them from going off.";
            contentsSimple[775] = "TaskObjectives_DB_BES0_Inv2_P_Stage03_OwnerBrief=The group behind those car bombs is still looking to cause a major distruption. <Col: StageText>Stop them </Col>from <Col: StageText>planting more car bombs</Col>.";
            contentsSimple[776] = "TaskObjectives_DB_BES0_Inv2_P_Stage03_DispatchBrief=";
            contentsSimple[777] = "TaskObjectives_DB_BES0_Inv2_P_Stage03_Opp_OwnerBrief=";
            contentsSimple[778] = "TaskObjectives_DB_BES0_Inv2_P_Stage03_Opp_DispatchBrief=The Praetorians may have defused the bombs we already planted, but we've still got a few new ones left. <Col: StageText>Plant more car bombs</Col> and send the message to the Praetorians that no-one can stop us!";
            contentsSimple[779] = "TaskObjectives_DB_BES0_Inv2_P_Stage04_OwnerBrief=One of the bombers dropped the PDA they've been using to program the bombs. <Col: StageText>Retrieve and hold onto that PDA.</Col>";
            contentsSimple[780] = "TaskObjectives_DB_BES0_Inv2_P_Stage04_DispatchBrief=One of our bombers dropped the PDA we've been using to program the bombs. <Col: StageText>Retrieve and hold onto that PDA.</Col>";
            contentsSimple[781] = "TaskObjectives_DB_BES0_Inv2_T_Stage01_OwnerBrief=It looks like the gangs have a vendetta against pro-Derren journalists, and want to cause a news blackout. <Col: StageText>Defuse</Col> any car bombs on this vehicle -  it's top of a hit list we found.";
            contentsSimple[782] = "TaskObjectives_DB_BES0_Inv2_T_Stage01_DispatchBrief=We've selected several Enforcer-friendly journalists for a bit of 'censorship' - car bomb style. <Col: StageText>Guard this car </Col>to make sure no one discovers the bomb we've planted in it.";
            contentsSimple[783] = "TaskObjectives_DB_BES0_Inv2_T_Stage02_OwnerBrief=This list must be the real deal. <Col: StageText>Defuse </Col>the <Col: StageText>bombs </Col>from these other vehicles on the hit list we found. They all belong to journalists.";
            contentsSimple[784] = "TaskObjectives_DB_BES0_Inv2_T_Stage02_DispatchBrief=The Tigers must have found out about our car bombing plan. <Col: StageText>Guard the bombs </Col>to make sure they go off.";
            contentsSimple[785] = "TaskObjectives_DB_BES0_Inv2_T_Stage03_OwnerBrief=Someone is still looking to drop a cherry bomb down the crapper that is San Paro. Protect the journalists. <Col: StageText>Stop </Col>whoever is behind this from <Col: StageText>planting more car bombs</Col>.";
            contentsSimple[786] = "TaskObjectives_DB_BES0_Inv2_T_Stage03_DispatchBrief=";
            contentsSimple[787] = "TaskObjectives_DB_BES0_Inv2_T_Stage03_Opp_OwnerBrief=";
            contentsSimple[788] = "TaskObjectives_DB_BES0_Inv2_T_Stage03_Opp_DispatchBrief=The Tigers have defused all our car bombs. But there are still plenty of journalists to kill - they breed like San Parian roaches. <Col: StageText>Plant car bombs </Col>in the cars of a sub-editor and his assistant.";
            contentsSimple[789] = "TaskObjectives_DB_BES0_Inv2_T_Stage04_OwnerBrief=One of the bombers dropped the PDA they've been using to program the bombs. <Col: StageText>Retrieve and hold onto that PDA.</Col>";
            contentsSimple[790] = "TaskObjectives_DB_BES0_Inv2_T_Stage04_DispatchBrief=One of our bombers dropped the PDA we've been using to program the bombs. <Col: StageText>Retrieve and hold onto that PDA.</Col>";
            contentsSimple[791] = "TaskObjectives_DB_BES0_Pic1_P_Stage01_OwnerBrief=A Praetorian client needs some money transported. <Col: StageText>Pick up the vehicle </Col>they've supplied. Take care of it - you'll need to return it when the job's finished.";
            contentsSimple[792] = "TaskObjectives_DB_BES0_Pic1_P_Stage01_DispatchBrief='Torians are starting up a new cash delivery service for local businesses. We don't want them running it through our turf. <Col: StageText>Stop the Praetorians </Col>from <Col: StageText>getting their vehicle</Col>.";
            contentsSimple[793] = "TaskObjectives_DB_BES0_Pic1_P_Stage02_OwnerBrief=<Col: StageText>Pick up </Col>the first batch of <Col: StageText>money </Col>our client needs to have transported, and keep it safe.";
            contentsSimple[794] = "TaskObjectives_DB_BES0_Pic1_P_Stage02_DispatchBrief=The Praetorians will ruin our sources of easy money with their armed escorts. Disrupt their activities. <Col: StageText>Stop them </Col>from <Col: StageText>picking up the cash</Col>.";
            contentsSimple[795] = "TaskObjectives_DB_BES0_Pic1_P_Stage03_OwnerBrief=Now collect the second bag of money our client needs to have transported. <Col: StageText>Pick up </Col>the <Col: StageText>cash </Col>and keep it safe.";
            contentsSimple[796] = "TaskObjectives_DB_BES0_Pic1_P_Stage03_DispatchBrief=It looks like the Praetorians have only sent a small team to protect all that money. This is our chance to do some damage to their reputation. <Col: StageText>Stop them </Col>from <Col: StageText>picking up the cash</Col>.";
            contentsSimple[797] = "TaskObjectives_DB_BES0_Pic1_P_Stage04_OwnerBrief=This is the last collection. <Col: StageText>Pick up</Col> the <Col: StageText>money </Col>and keep it safe. This is how the Praetorians will take back the city: one job at a time.";
            contentsSimple[798] = "TaskObjectives_DB_BES0_Pic1_P_Stage04_DispatchBrief=We don't want all of San Paro's money to be so well guarded. Show the local businesses that hiring armed protection won't do them any good. <Col: StageText>Stop the Praetorians </Col>from <Col: StageText>collecting the cash </Col>successfully.";
            contentsSimple[799] = "TaskObjectives_DB_BES0_Pic1_P_Stage05_OwnerBrief=Good work out there. Now <Col: StageText>drop off the cash</Col> and <Col: StageText>return the vehicle</Col> to its owners.";
            contentsSimple[800] = "TaskObjectives_DB_BES0_Pic1_P_Stage05_DispatchBrief=Praetorians're spending a lotta time collecting money from local businesses - convenient. <Col: StageText>Stop 'em </Col>from <Col: StageText>delivering the cash</Col> - we could shut down their whole op in this area.";
            contentsSimple[801] = "TaskObjectives_DB_BES0_Pic1_P_Stage06_OwnerBrief=Great work completing the job, even with those Criminals giving you trouble. <Col: StageText>Stop them robbing </Col>our client's business!";
            contentsSimple[802] = "TaskObjectives_DB_BES0_Pic1_P_Stage06_DispatchBrief=";
            contentsSimple[803] = "TaskObjectives_DB_BES0_Pic1_P_Stage06_Opp_OwnerBrief=";
            contentsSimple[804] = "TaskObjectives_DB_BES0_Pic1_P_Stage06_Opp_DispatchBrief=Where were you? The 'Torians are gaining an even stronger reputation in this area. Forget the cash. <Col: StageText>Break into this store </Col>and remind everyone who's really in charge.";
            contentsSimple[805] = "TaskObjectives_DB_BES0_Pic1_P_Stage07_OwnerBrief=The Criminals are looking for our client. <Col: StageText>Hold and Protect</Col> his property! Praetorians see that the job's done, all the way to the end.";
            contentsSimple[806] = "TaskObjectives_DB_BES0_Pic1_P_Stage07_DispatchBrief=<Col: StageText>Capture and Hold</Col> the area. Those toy soldier Praetorians aren't half as tough as they wish they were.";
            contentsSimple[807] = "TaskObjectives_DB_BES0_Pic1_T_Stage01_OwnerBrief=Local business owners are tired of paying protection money to gangs. They've hired the Tigers to protect their weekly cash collection. <Col: StageText>Pick up the vehicle </Col>they've supplied. Don't trash it, dude - it's on loan.";
            contentsSimple[808] = "TaskObjectives_DB_BES0_Pic1_T_Stage01_DispatchBrief=Some businesses think we charge too much to 'protect' them from all the nasty gangs around here. They've hired the Tigers to make their weekly cash collection. <Col: StageText>Stop the Tigers </Col>from picking up their vehicle.";
            contentsSimple[809] = "TaskObjectives_DB_BES0_Pic1_T_Stage02_OwnerBrief=Head on over to the first collection point. <Col: StageText>Pick up </Col>the <Col: StageText>money </Col>and keep it safe. And don't scratch the paint job on that loaner too badly.";
            contentsSimple[810] = "TaskObjectives_DB_BES0_Pic1_T_Stage02_DispatchBrief=Those business owners are really asking for trouble, calling in those Prentiss brats to protect their cash. <Col: StageText>Stop the Tigers </Col>from <Col: StageText>picking up the money</Col>.";
            contentsSimple[811] = "TaskObjectives_DB_BES0_Pic1_T_Stage03_OwnerBrief=No one messes with the Tigers. The next cash drop can be found here. <Col: StageText>Pick up </Col>the <Col: StageText>money </Col>and keep it safe.";
            contentsSimple[812] = "TaskObjectives_DB_BES0_Pic1_T_Stage03_DispatchBrief=If anybody should be getting protection money from the local businesses, it's us. <Col: StageText>Stop the Prentiss Tigers </Col>from <Col: StageText>picking up the money </Col>they're supposed to be transporting safely.";
            contentsSimple[813] = "TaskObjectives_DB_BES0_Pic1_T_Stage04_OwnerBrief=All right! This is the final collection. <Col: StageText>Pick up</Col> the <Col: StageText>money</Col> and keep it safe. With your help, local businesses now see they no longer need to pay gangs for protection.";
            contentsSimple[814] = "TaskObjectives_DB_BES0_Pic1_T_Stage04_DispatchBrief=I bet the local businesses are paying the Tigers more for protection than we were charging to leave them alone. <Col: StageText>Stop the Tigers </Col>from <Col: StageText>picking up the money</Col> and successfully transporting it.";
            contentsSimple[815] = "TaskObjectives_DB_BES0_Pic1_T_Stage05_OwnerBrief=Nice work. Now <Col: StageText>drop off the cash </Col>and <Col: StageText>return the vehicle </Col>we were loaned. Hope you didn't scratch it.";
            contentsSimple[816] = "TaskObjectives_DB_BES0_Pic1_T_Stage05_DispatchBrief=You gotta hand it to the Prentiss Tigers. They're good delivery boys. But <Col: StageText>stop them </Col>from <Col: StageText>delivering the money </Col>or other businesses might stop paying us protection money too.";
            contentsSimple[817] = "TaskObjectives_DB_BES0_Pic1_T_Stage06_OwnerBrief=Looks like the gangs have turned up to enforce penalties for non-payment. <Col: StageText>Take them out </Col>and <Col: StageText>stop them from robbing </Col>our clients' businesses!";
            contentsSimple[818] = "TaskObjectives_DB_BES0_Pic1_T_Stage06_DispatchBrief=";
            contentsSimple[819] = "TaskObjectives_DB_BES0_Pic1_T_Stage06_Opp_OwnerBrief=";
            contentsSimple[820] = "TaskObjectives_DB_BES0_Pic1_T_Stage06_Opp_DispatchBrief=We can't have the Tigers ruining our income stream. Get back to basics! <Col: StageText>Break into these stores</Col>. Remind local businesses why they should be paying us to leave them alone.";
            contentsSimple[821] = "TaskObjectives_DB_BES0_Pic1_T_Stage07_OwnerBrief=The Criminals are looking for our the local business owners. <Col: StageText>Hold the area</Col> Tigers are in it to win it, all the way to the end of the game.";
            contentsSimple[822] = "TaskObjectives_DB_BES0_Pic1_T_Stage07_DispatchBrief=<Col: StageText>Take over the area</Col> Those local business-men have it comin' to them - serve it up, nice and hot.";
            contentsSimple[823] = "TaskObjectives_DB_BES1_For1_P_Stage01_OwnerBrief=Most of San Paro's live TV Networks think the Praetorians are corporate and boring. <Col: StageText>Raid this drug factory </Col>and show the press just how exciting our methods can be.";
            contentsSimple[824] = "TaskObjectives_DB_BES1_For1_P_Stage01_DispatchBrief=What the hell? Every time the Praetorians go looking for news coverage, they pick on us. They're going to <Col: StageText>raid </Col>some of our <Col: StageText>phone hacking dens</Col>. Go <Col: StageText>stop them</Col>.";
            contentsSimple[825] = "TaskObjectives_DB_BES1_For1_P_Stage02_OwnerBrief=Good job! We're getting some good cross-channel coverage after pillaging that drug factory. Now <Col: StageText>raid </Col>this <Col: StageText>drug den.</Col> The use of extreme violence is encouraged.";
            contentsSimple[826] = "TaskObjectives_DB_BES1_For1_P_Stage02_DispatchBrief=The Praetorians really don't encourage you to make a profit in this town. They're going to <Col: StageText>raid </Col>more of our <Col: StageText>phone hacking dens</Col>. Go <Col: StageText>stop them</Col>.";
            contentsSimple[827] = "TaskObjectives_DB_BES1_For1_P_Stage03_OwnerBrief=The Networks love what they're seeing. You look awesome in slow-mo! A nearby <Col: StageText>car </Col>was used by the dealers to transport the drugs. <Col: StageText>Retrieve any drugs </Col>it contains.";
            contentsSimple[828] = "TaskObjectives_DB_BES1_For1_P_Stage03_DispatchBrief=I keep saying: don't leave the shit in the cars! So guess what? The Praetorians found a shipment of <Col: StageText>modified SIMs </Col>hidden in one of our courier's vehicles. < Col: StageText > Stop them </ Col > from getting to it!";
            contentsSimple[829] = "TaskObjectives_DB_BES1_For1_P_Stage04_OwnerBrief=<Col: StageText>Drop off </Col>the <Col: StageText>drugs </Col>you recovered. This much dope should get convictions for the criminals - and secure us a bunch of prime-time news slots.";
            contentsSimple[830] = "TaskObjectives_DB_BES1_For1_P_Stage04_DispatchBrief=The Praetorians have recovered enough illegal phone numbers to get us all convicted. Unless you want to be implicated, <Col: StageText>stop the Praetorians </Col>from <Col: StageText>dropping those SIMs off</Col>.";
            contentsSimple[831] = "TaskObjectives_DB_BES1_For1_P_Stage05_OwnerBrief=Nice job - the Praetorians are headline news. Unfortunately the dealers we pissed off want some prime-time coverage of their own. <Col: StageText>Stop them</Col> from <Col: StageText>planting bombs</Col> in the area.";
            contentsSimple[832] = "TaskObjectives_DB_BES1_For1_P_Stage05_DispatchBrief=Our humiliation is all over TV. We could just stay in with a can of Tyler and watch our phone-hacking empire go up in flames, or we could <Col: StageText>blow up</Col> these pissant Praetorian scumbags!";
            contentsSimple[833] = "TaskObjectives_DB_BES1_For1_T_Stage01_OwnerBrief=We're onto a middle-class couple who 'keep themselves to themselves' - apart from the multi-million-dollar drug ring they're running out of the family home. <Col: StageText>Raid their drug-packing operation</Col>.";
            contentsSimple[834] = "TaskObjectives_DB_BES1_For1_T_Stage01_DispatchBrief=The Tigers just found $800k in one of our bank accounts. It's from our diamond smuggling operation. Buy us time. <Col: StageText>Stop </Col>those Prentiss pussies from <Col: StageText>raiding our accountant's office</Col>.";
            contentsSimple[835] = "TaskObjectives_DB_BES1_For1_T_Stage02_OwnerBrief=We busted the husband, but the wife's missing, as are the drug profits. Maybe she's holed up in the other property the couple own. <Col: StageText>Raid </Col>their rented <Col: StageText>office</Col>.";
            contentsSimple[836] = "TaskObjectives_DB_BES1_For1_T_Stage02_DispatchBrief=Shit! The accounts have been frozen, but we can still sell the remaining diamonds. <Col: StageText>Stop </Col>the Tigers <Col: StageText>raiding </Col>our smuggling <Col: StageText>office</Col>.";
            contentsSimple[837] = "TaskObjectives_DB_BES1_For1_T_Stage03_OwnerBrief=The Tigers just picked up the wife. She broke down and admitted that she was on her way to get her money laundered. <Col: StageText>Retrieve the dirty cash </Col>from her <Col: StageText>car</Col>.";
            contentsSimple[838] = "TaskObjectives_DB_BES1_For1_T_Stage03_DispatchBrief=Ah, crap. We got most of the diamonds out the back of the building, but there's one pack left in a courier vehicle. It's always the details! <Col: StageText>Stop </Col>the 'Forcers <Col: StageText>retrieving the merchandise</Col>.";
            contentsSimple[839] = "TaskObjectives_DB_BES1_For1_T_Stage04_OwnerBrief=<Col: StageText>Drop off </Col>the <Col: StageText>money</Col>. The wife transferred most of it to Colombian accounts but you have enough there to convict Mr. & Mrs. 'Nice Neighbors'.";
            contentsSimple[840] = "TaskObjectives_DB_BES1_For1_T_Stage04_DispatchBrief=The Tigers only have a small sample of our solitaires, but it's enough for a conviction. Stop them <Col: StageText>dropping off </Col>the <Col: StageText>diamonds</Col>.";
            contentsSimple[841] = "TaskObjectives_DB_BES1_For1_T_Stage05_OwnerBrief=Problem with these supposedly nice, respectable criminals is that they always mix with gnarly-looking dudes. <Col: StageText>Stop them</Col> from <Col: StageText>planting bombs</Col> in the area.";
            contentsSimple[842] = "TaskObjectives_DB_BES1_For1_T_Stage05_DispatchBrief=What are we, diamond smugglers or scared newbies? When faced with a crappy jail sentence complete with the Tigers on the other side of the bars, I vote we fight! <Col: StageText>Blow up</Col> the Tigers!";
            contentsSimple[843] = "TaskObjectives_DB_BES1_Pic4_P_Stage01_OwnerBrief=Two Praetorian colleagues were killed in the line of duty. Forensics are at the scene right now. <Col: StageText>Collect </Col>the <Col: StageText>evidence </Col>they've uncovered so far.";
            contentsSimple[844] = "TaskObjectives_DB_BES1_Pic4_P_Stage01_DispatchBrief='Torians planted drugs on us and then - surprise, surprise - found them. <Col: StageText>Stop the Praetorians </Col>from <Col: StageText>picking up the drugs </Col>they planted.";
            contentsSimple[845] = "TaskObjectives_DB_BES1_Pic4_P_Stage02_OwnerBrief=Our fallen colleagues were chasing down a gang of criminals. <Col: StageText>Pick up </Col>the <Col: StageText>money </Col>the cowardly gang dropped as they fled; it could be vital evidence.";
            contentsSimple[846] = "TaskObjectives_DB_BES1_Pic4_P_Stage02_DispatchBrief=The cheating Enforcers have gone through several of our homes and taken any savings they can find, saying it's blood money. <Col: StageText>Stop the Praetorians </Col>from <Col: StageText>picking up our cash</Col>.";
            contentsSimple[847] = "TaskObjectives_DB_BES1_Pic4_P_Stage03_OwnerBrief=The gang has sprayed up victory <Col: StageText>graffiti </Col>gloating about killing our colleagues. <Col: StageText>Cover </Col>it up.";
            contentsSimple[848] = "TaskObjectives_DB_BES1_Pic4_P_Stage03_DispatchBrief=These Enforcers clearly don't like it that we have our own territory. <Col: StageText>Stop the Praetorians </Col>from <Col: StageText>covering up our graffiti</Col>. Or next time they come back, they'll be trying to evict us.";
            contentsSimple[849] = "TaskObjectives_DB_BES1_Pic4_P_Stage04_OwnerBrief=<Col: StageText>Deliver </Col>the damning <Col: StageText>evidence </Col>you have to our <Col: StageText>lockup</Col>. We'll get those murderers yet.";
            contentsSimple[850] = "TaskObjectives_DB_BES1_Pic4_P_Stage04_DispatchBrief=Now they have their falsified evidence, the Enforcers are heading back to their plush, cozy offices. <Col: StageText>Stop the Praetorians </Col>from <Col: StageText>making it back </Col>with their sham <Col: StageText>evidence</Col>.";
            contentsSimple[851] = "TaskObjectives_DB_BES1_Pic4_P_Stage05_OwnerBrief=The murdering gang knows you're onto them. The best thing you can do for San Paro is to <Col: StageText>take their territory </Col>away from these Criminals. <";
            contentsSimple[852] = "TaskObjectives_DB_BES1_Pic4_P_Stage05_DispatchBrief=We need to make a stand, or the Praetorians will be pushing us around forever. <Col: StageText>Capture this territory and hold it</Col>. This is our turf!";
            contentsSimple[853] = "TaskObjectives_DB_BES1_Pic4_T_Stage01_OwnerBrief=The local gang is trying to expand their domain. They even hired extra graffiti artists to tag more turf up fast. <Col: StageText>Pick up </Col>the deposited <Col: StageText>box </Col>of spray cans so they can't be used.";
            contentsSimple[854] = "TaskObjectives_DB_BES1_Pic4_T_Stage01_DispatchBrief=We got some extra guys coming in to help us tag up the area. <Col: StageText>Stop the Tigers </Col>from <Col: StageText>picking up the box </Col>of spray paint we already distributed.";
            contentsSimple[855] = "TaskObjectives_DB_BES1_Pic4_T_Stage02_OwnerBrief=The gang got together a lot of cash to hire some expensive graffiti legends. But no money, no paint. So <Col: StageText>pick up </Col>their <Col: StageText>cash </Col>and we can stop this land grab in its tracks.";
            contentsSimple[856] = "TaskObjectives_DB_BES1_Pic4_T_Stage02_DispatchBrief=The graffiti artists we've hired won't work for nothing. We hid their money near where we want the tags. The 'Forcers found it, though. <Col: StageText>Stop the Tigers taking the cash</Col>.";
            contentsSimple[857] = "TaskObjectives_DB_BES1_Pic4_T_Stage03_OwnerBrief=Good work. It looks like only one of the graffiti posse got through. If you can <Col: StageText>cover up that graffiti </Col>then this is well on its way to being another Tiger triumph.";
            contentsSimple[858] = "TaskObjectives_DB_BES1_Pic4_T_Stage03_DispatchBrief=The Prentiss posers aren't just stopping us from tagging the area, they're covering up the work we've already done! <Col: StageText>Stop the Tigers </Col>from <Col: StageText>covering up our graffiti </Col>or we're moving backwards.";
            contentsSimple[859] = "TaskObjectives_DB_BES1_Pic4_T_Stage04_OwnerBrief=<Col: StageText>Bring </Col>all the <Col: StageText>evidence </Col>you have - the paint and the cash - back to the  <Col: StageText>lockup </Col>and we can build a case against the taggers-for-hire.";
            contentsSimple[860] = "TaskObjectives_DB_BES1_Pic4_T_Stage04_DispatchBrief=The Tigers are going back to their hairy lair with our cash and some old paint, presumably to fabricate a court case. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>dropping off </Col>their <Col: StageText>evidence</Col>.";
            contentsSimple[861] = "TaskObjectives_DB_BES1_Pic4_T_Stage05_OwnerBrief=The local gang didn't appreciate the Tigers halting their expansion. Now it’s 'mano a mano'. <Col: StageText>Stop them taking </Col>back this <Col: StageText>territory</Col>.";
            contentsSimple[862] = "TaskObjectives_DB_BES1_Pic4_T_Stage05_DispatchBrief=The Tigers want to grab our turf any way they can; maybe one of their daddies wants the real estate! <Col: StageText>Keep our territory</Col> for us.";
            contentsSimple[863] = "TaskObjectives_DB_BES2_Inv1_Stage01_OwnerBrief=We have a crazed bomber in lockup, mumbling about angels delivering burning justice to San Paro. Whatever. <Col: StageText>Search his house </Col>and see if you can find some evidence against this nut.";
            contentsSimple[864] = "TaskObjectives_DB_BES2_Inv1_Stage01_DispatchBrief=The Enforcers have arrested our good friend Azrael. Okay, he is a bomb nut seeking to bring about the end of days, but he's still is a good laugh. <Col: StageText>Stop the 'Forcers </Col>from <Col: StageText>raiding his house</Col>.";
            contentsSimple[865] = "TaskObjectives_DB_BES2_Inv1_Stage02_OwnerBrief=Inside were incendiary devices and a list suggesting he has already planted some righteous <Col: StageText>bombs</Col>. Get to the first location and <Col: StageText>defuse the bomb </Col>while I bring forth a most furious justice.";
            contentsSimple[866] = "TaskObjectives_DB_BES2_Inv1_Stage02_DispatchBrief=Looks like Azrael was up to his old tricks. <Col: StageText>Guard his bombs </Col>and make sure the 'Forcers don't defuse them. We owe him for his good work in supplying our explosives.";
            contentsSimple[867] = "TaskObjectives_DB_BES2_Inv1_Stage03_OwnerBrief=The bomb nut demands to be called Azrael. He has confessed that there are two more <Col: StageText>bombs</Col>. Get to these locations and <Col: StageText>defuse them </Col>before anyone gets hurt.";
            contentsSimple[868] = "TaskObjectives_DB_BES2_Inv1_Stage03_DispatchBrief=The Enforcers are really leaning on our buddy Azrael and have discovered his bombing campaign. <Col: StageText>Guard his bombs </Col>and make sure they go off. Whoever Azrael is bombing, they deserve it.";
            contentsSimple[869] = "TaskObjectives_DB_BES2_Inv1_Stage04_OwnerBrief=Looks like 'Azrael' has some accomplices who may attempt to bomb these last two locations. <Col: StageText>Stop them </Col>from <Col: StageText>planting these bombs </Col>.";
            contentsSimple[870] = "TaskObjectives_DB_BES2_Inv1_Stage04_DispatchBrief=";
            contentsSimple[871] = "TaskObjectives_DB_BES2_Inv1_Stage04_Opp_OwnerBrief=";
            contentsSimple[872] = "TaskObjectives_DB_BES2_Inv1_Stage04_Opp_DispatchBrief=Looks like our friend Azrael was bombing the supporters of a new gun ownership law. <Col: StageText>Plant bombs on the power supply unit </Col>to their campaign HQ and we can finish the job for him.";
            contentsSimple[873] = "TaskObjectives_DB_BES2_Inv1_Stage05_OwnerBrief=The bomber has gotten away with this for the last time. <Col:StageText>Take him down</Col>, lethal force is authorized..";
            contentsSimple[874] = "TaskObjectives_DB_BES2_Inv1_Stage05_DispatchBrief=Bombing sure is fun, but it's time for something a little more personal. <Col:StageText>Take out the Enforcers</Col> that are trying to stop you.";
            contentsSimple[875] = "TaskObjectives_DB_BES2_Inv2_Stage01_OwnerBrief=A bookie has gone on the run after being unable to cover his bets. If you offer him protection he'll become one of our star informants. Find him! <Col: StageText>Check out </Col>his business and <Col: StageText>hack </Col>his car's GPS.";
            contentsSimple[876] = "TaskObjectives_DB_BES2_Inv2_Stage01_DispatchBrief=A bookie owing us money has gone on the run. Make sure the Enforcers don't find him before we do. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>investigating </Col>his business and <Col: StageText>hacking </Col>his car's GPS.";
            contentsSimple[877] = "TaskObjectives_DB_BES2_Inv2_Stage02_OwnerBrief=A bookie has gone on the run after being unable to cover his bets. If you offer him protection he'll become one of our star informants. Find him! <Col: StageText>Check out </Col>his business and <Col: StageText>hack </Col>his car's GPS.";
            contentsSimple[878] = "TaskObjectives_DB_BES2_Inv2_Stage02_DispatchBrief=A bookie owing us money has gone on the run. Make sure the Enforcers don't find him before we do. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>investigating </Col>his business and <Col: StageText>hacking </Col>his car's GPS.";
            contentsSimple[879] = "TaskObjectives_DB_BES2_Inv2_Stage03_OwnerBrief=According to the vehicle's GPS data it looks like the bookie's been frequenting this address. It's not on our list of possible hideouts. <Col: StageText>Raid </Col>the place and look for signs of the bookie.";
            contentsSimple[880] = "TaskObjectives_DB_BES2_Inv2_Stage03_DispatchBrief=The Enforcers have found clues suggesting the bookie on the run is hiding out here. <Col: StageText>Stop them </Col>from <Col: StageText>raiding </Col>the place; we need to find him and his money first.";
            contentsSimple[881] = "TaskObjectives_DB_BES2_Inv2_Stage04_OwnerBrief=Someone else got to the bookie and took their 'slice' of his profits. <Col: StageText>Drop off </Col>the <Col: StageText>weapon </Col>they used. We've got a murder on our hands now.";
            contentsSimple[882] = "TaskObjectives_DB_BES2_Inv2_Stage04_DispatchBrief=We found the bookie and taught him a lesson. Unfortunately, the 'Forcers found the weapon. <Col: StageText>Stop them </Col>from <Col: StageText>dropping off </Col>the forensics that could implicate us.";
            contentsSimple[883] = "TaskObjectives_DB_BES2_Inv2_Stage05_OwnerBrief=You've gathered enough evidence for us to move forward with an investigation into who killed the bookie. <Col: StageText>Investigate </Col>the suspected <Col: StageText>location </Col>of the murder.";
            contentsSimple[884] = "TaskObjectives_DB_BES2_Inv2_Stage05_DispatchBrief=Our hit guys stashed their bloodstained clothes nearby. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>searching </Col>for this evidence.";
            contentsSimple[885] = "TaskObjectives_DB_BES2_Inv2_Stage06_OwnerBrief=<Col: StageText>Drop off </Col>all that <Col: StageText>evidence </Col>back to the lockup. Looks like the bookie has evened the odds in our battle against the Criminals - this will go to court for sure.";
            contentsSimple[886] = "TaskObjectives_DB_BES2_Inv2_Stage06_DispatchBrief=";
            contentsSimple[887] = "TaskObjectives_DB_BES2_Inv2_Stage06_Opp_OwnerBrief=";
            contentsSimple[888] = "TaskObjectives_DB_BES2_Inv2_Stage06_Opp_DispatchBrief=<Col: StageText>Drop off </Col>all that evidence at our hideout. Without it, the Enforcers have no way of proving who killed the bookie.";
            contentsSimple[889] = "TaskObjectives_DB_BES3_For1_Stage01_OwnerBrief=Drug dealers are using payphones to keep in contact with their suppliers. Put a <Col: StageText>wiretap on this phone </Col>and we should be able to discover where the dealers are working from.";
            contentsSimple[890] = "TaskObjectives_DB_BES3_For1_Stage01_DispatchBrief=A dealing operation uses payphones to keep in contact with their suppliers. They hired us to <Col: StageText>stop the Enforcers </Col>from <Col: StageText>tapping </Col>their usual <Col: StageText>payphone</Col>.";
            contentsSimple[891] = "TaskObjectives_DB_BES3_For1_Stage02_OwnerBrief=The dealers are smart enough to not divulge too much information to any one contact. We need more info. The conversations on this other phone may help. Put a <Col: StageText>tap on this payphone </Col>as well.";
            contentsSimple[892] = "TaskObjectives_DB_BES3_For1_Stage02_DispatchBrief=Too late. Enforcers hacked the main phone line to the dealer's suppliers, and are now trying to tap a back-up line. <Col: StageText>Stop Enforcers </Col>from <Col: StageText>tapping this payphone</Col>.";
            contentsSimple[893] = "TaskObjectives_DB_BES3_For1_Stage03_OwnerBrief=Great, we put two and two together and got the addresses of a coupla drug dens. Enough listening: time for some action. <Col: StageText>Raid this building</Col> - it's first on the list.";
            contentsSimple[894] = "TaskObjectives_DB_BES3_For1_Stage03_DispatchBrief=Both lines have been tapped. The dealers won't pay us our protection fee now unless we can <Col: StageText>stop the Enforcers </Col>from <Col: StageText>raiding their building</Col>.";
            contentsSimple[895] = "TaskObjectives_DB_BES3_For1_Stage04_OwnerBrief=That intel was right on the money: drugs are definitely being stored at this location. <Col: StageText>Raid this next building </Col>and <Col: StageText>recover any drugs </Col>found.";
            contentsSimple[896] = "TaskObjectives_DB_BES3_For1_Stage04_DispatchBrief=Having so easily found those stored drugs, our dealer clients are worried the Enforcers will find the location of their other stashes. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>raiding this building</Col>.";
            contentsSimple[897] = "TaskObjectives_DB_BES3_For1_Stage05_OwnerBrief=Great work. Those phone taps are really gonna keep these dealers in check - until they get wise, that is. <Col: StageText>Deliver the drugs </Col>back to the lockup.";
            contentsSimple[898] = "TaskObjectives_DB_BES3_For1_Stage05_DispatchBrief=Most of the dealer's stashes have been raided and their drugs confiscated. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>delivering the drugs </Col>back to their lockup, and we might get our fee out of this yet.";
            contentsSimple[899] = "TaskObjectives_DB_BES3_For1_Stage06_OwnerBrief=The dealers have no idea how we're on to them and are panicking. They're going to burn any remaining drug stashes. These are in populated areas. <Col: StageText>Stop the arson.</Col>";
            contentsSimple[900] = "TaskObjectives_DB_BES3_For1_Stage06_DispatchBrief=";
            contentsSimple[901] = "TaskObjectives_DB_BES3_For1_Stage06_Opp_OwnerBrief=";
            contentsSimple[902] = "TaskObjectives_DB_BES3_For1_Stage06_Opp_DispatchBrief=Enforcers found out about the dealer's backup drug stash locations. <Col: StageText>Burn the stash </Col>before the Enforcers are able to confiscate it.";
            contentsSimple[903] = "TaskObjectives_DB_BES3_For1_Stage07_OwnerBrief=Looks like a safe survived the fire. <Col:StageText>Confiscate</Col> and </Col:StageText>deliver it</Col> to our lockup.";
            contentsSimple[904] = "TaskObjectives_DB_BES3_For1_Stage07_DispatchBrief=A safe survived the fire! <Col:StageText>Take it to our safe house</Col> and we'll destroy it ourselves.";
            contentsSimple[905] = "TaskObjectives_DB_BES3_For1_Stage07_Opp_OwnerBrief=";
            contentsSimple[906] = "TaskObjectives_DB_BES3_For1_Stage07_Opp_DispatchBrief=A safe survived the fire! <Col:StageText>Take it to our safe house</Col> and we'll destroy it ourselves.";
            contentsSimple[907] = "TaskObjectives_DB_BES3_Inv1_Stage01_OwnerBrief=We suspect a small group of SPPD officers are working with criminal organizations. <Col: StageText>Investigate </Col>these recent break-ins where the suspected officers were first on the scene.";
            contentsSimple[908] = "TaskObjectives_DB_BES3_Inv1_Stage01_DispatchBrief=We have a group of SPPD officers in our pocket, feeding us information and making evidence disappear. <Col: StageText>Stop Enforcers </Col>from <Col: StageText>investigating </Col>these recent break-ins and uncovering our sources.";
            contentsSimple[909] = "TaskObjectives_DB_BES3_Inv1_Stage02_OwnerBrief=We suspect a small group of SPPD officers are working with criminal organizations. <Col: StageText>Investigate </Col>these recent break-ins where the suspected officers were first on the scene.";
            contentsSimple[910] = "TaskObjectives_DB_BES3_Inv1_Stage02_DispatchBrief=We have a group of SPPD officers in our pocket, feeding us information and making evidence disappear. <Col: StageText>Stop Enforcers </Col>from <Col: StageText>investigating </Col>these recent break-ins and uncovering our sources.";
            contentsSimple[911] = "TaskObjectives_DB_BES3_Inv1_Stage03_OwnerBrief=The officers' prints and shoe marks are in places that would suggest they committed the break-ins. <Col: StageText>Scan </Col>this <Col: StageText>antenna </Col>to tap into their police radios and download any useful evidence.";
            contentsSimple[912] = "TaskObjectives_DB_BES3_Inv1_Stage03_DispatchBrief=Enforcers suspect our paid-off cops and are looking for evidence of our having contacted them. <Col: StageText>Stop the 'Forcers </Col>from <Col: StageText>scanning </Col>this <Col: StageText>antenna </Col>to tap into our cell phone records.";
            contentsSimple[913] = "TaskObjectives_DB_BES3_Inv1_Stage04_OwnerBrief=The SPPD officers made the calls through one of SPiN's encrypted VOIP services. <Col: StageText>Scan </Col>this <Col: StageText>satellite dish </Col>to access the archived calls.";
            contentsSimple[914] = "TaskObjectives_DB_BES3_Inv1_Stage04_DispatchBrief=Recordings of our cell phone conversations with the SPPD officers have been found on the telecom company's servers. <Col: StageText>Stop the Enforcers </Col>from <Col: StageText>hacking </Col>this <Col: StageText>satellite dish </Col>and downloading them.";
            contentsSimple[915] = "TaskObjectives_DB_BES3_Inv1_Stage05_OwnerBrief=The VOIP service still had the calls in their archives - irrefutable proof of the SPPD officers' involvement with Criminals. <Col: StageText>Raid </Col>the last known <Col: StageText>locations </Col>of the officers and <Col: StageText>pick up evidence</Col>.";
            contentsSimple[916] = "TaskObjectives_DB_BES3_Inv1_Stage05_DispatchBrief=The archived calls prove that several SPPD officers are working with us. <Col: StageText>Stop the 'Forcers raiding </Col>the last known <Col: StageText>locations </Col>of the officers until we can get them out of San Paro.";
            contentsSimple[917] = "TaskObjectives_DB_BES3_Inv1_Stage06_OwnerBrief=The officers must've felt that someone was closing in on them, and they fled San Paro. <Col: StageText>Drop off </Col>the <Col: StageText>police badges </Col>you found to our <Col: StageText>lockup</Col>. SPPD Internal Affairs can take it from there.";
            contentsSimple[918] = "TaskObjectives_DB_BES3_Inv1_Stage06_DispatchBrief=Shit! The Enforcers found a cop's badge. It could lead them to our other friends within the SPPD. <Col: StageText>Stop the 'Forcers </Col>from <Col: StageText>dropping off </Col>the <Col: StageText>evidence </Col>they found.";
            contentsSimple[919] = "TaskObjectives_DB_BES3_Inv1_Stage07_OwnerBrief=The intel we recovered points to the Criminal staging ground. <Col: StageText>Capture</Col> the <Col: StageText>area</Col>.";
            contentsSimple[920] = "TaskObjectives_DB_BES3_Inv1_Stage07_DispatchBrief=Those calls must've given the Enforcers enough evidence to lead them to us. <Col: StageText>Defend</Col> our <Col: StageText>block</Col> and don't let them get close.";
            contentsSimple[921] = "TaskObjectives_DB_BES3_Pic1_Stage01_OwnerBrief=Our drug enforcement arm need you to take point in an undercover sting operation. It's a San Paro classic: <Col: StageText>pick up this vehicle </Col>and use it to pose as a drug dealer.";
            contentsSimple[922] = "TaskObjectives_DB_BES3_Pic1_Stage01_DispatchBrief=A plainclothes cop was seen scouting a car nearby. We think the vehicle is going to be used as part of a sting. <Col: StageText>Stop Enforcers </Col>from <Col: StageText>picking up that car</Col>.";
            contentsSimple[923] = "TaskObjectives_DB_BES3_Pic1_Stage02_OwnerBrief=<Col: StageText>Drive your vehicle into this area</Col>. Meanwhile, we'll contact some suppliers and see if they'll offer you some drugs to pick up.";
            contentsSimple[924] = "TaskObjectives_DB_BES3_Pic1_Stage02_DispatchBrief=Enforcers are in an undercover vehicle posing as drug dealers. Now they're cruising round our neighborhood looking for some action. <Col: StageText>Keep them away </Col>from our area.";
            contentsSimple[925] = "TaskObjectives_DB_BES3_Pic1_Stage03_OwnerBrief=A supplier has some Angel Ace he stole off his girlfriend... Bad boy. It's stashed it in an alley near your location. <Col: StageText>Pick up the drugs.</Col>";
            contentsSimple[926] = "TaskObjectives_DB_BES3_Pic1_Stage03_DispatchBrief=A local scuzzball thought the undercover Enforcer was a bonafide dealer, and sold him some Angel Ace. <Col: StageText>Stop the Enforcers</Col> from <Col: StageText>picking up the drugs</Col>.";
            contentsSimple[927] = "TaskObjectives_DB_BES3_Pic1_Stage04_OwnerBrief=Okay, we've got some more calls to make. <Col: StageText>Drive your vehicle into this area.</Col> Keep your phone on - the drug pick-up should be close by.";
            contentsSimple[928] = "TaskObjectives_DB_BES3_Pic1_Stage04_DispatchBrief=Now the Enforcers think our whole neighborhood is full of drug dealers. Their undercover guy is heading into this area to search for a dead-drop. <Col: StageText>Keep them out</Col>.";
            contentsSimple[929] = "TaskObjectives_DB_BES3_Pic1_Stage05_OwnerBrief=Next up, a job-lot of 'Nimbus', 'G-Sliders', and 'Goma Tabs'. The drop is in an alley near your location. <Col: StageText>Pick up the drugs</Col>. You play the part of an underworld scuzzbag well... Kinda worries me...";
            contentsSimple[930] = "TaskObjectives_DB_BES3_Pic1_Stage05_DispatchBrief=The Enforcers have been sold a load of Goma Tabs, to be picked up. <Col: StageText>Stop them</Col> from <Col: StageText>retrieving the drugs</Col>. They'll use this an excuse to get heavy in our neighborhood.";
            contentsSimple[931] = "TaskObjectives_DB_BES3_Pic1_Stage06_OwnerBrief=Man, this is just like picking candy offa kids. Especially if those kids went to Border High. <Col: StageText>Deliver the vehicle and drugs </Col>back to our lockup.";
            contentsSimple[932] = "TaskObjectives_DB_BES3_Pic1_Stage06_DispatchBrief=We can reclaim the honor of the neighborhood if we can <Col: StageText>stop the Enforcers </Col>from <Col: StageText>delivering the drugs </Col>back to their lockup! They'll use them to stereotype everyone here as a drug addict.";
            contentsSimple[933] = "TaskObjectives_DB_BES3_Pic1_Stage07_OwnerBrief=Those suppliers were so happy with you 'pushing so much product' that they want you to make a new pickup of drugs and money. Don't blow your cover: <Col: StageText>retrieve the bag </Col>and <Col: StageText>hold </Col>onto it.";
            contentsSimple[934] = "TaskObjectives_DB_BES3_Pic1_Stage07_DispatchBrief=With all the Enforcer heat in the area, a dealer just bailed and abandoned a bag full of drugs and money. <Col: StageText>Retrieve and hold that bag</Col> - we can't just leave it out there! There are kids in this 'hood.";
            contentsSimple[935] = "TaskObjectives_Holiday_HW_Escort_Stage01_OwnerBrief=We're going to do some trick or treating, but last year all we got was hollowpoint. <Col:StageText>Destroy the cameras</Col> to make sure any Enforcers don't spot us and give us a repeat this year.";
            contentsSimple[936] = "TaskObjectives_Holiday_HW_Escort_Stage01_DispatchBrief=There's an enforcer gang trying to Trick and Treat on our turf! That Candy is ours! We've several cameras in the are to discourage this, so <Col:StageText>Protect the Cameras</Col>.";
            contentsSimple[937] = "TaskObjectives_Holiday_HW_Escort_Stage02_OwnerBrief=We're going to do some trick or treating, but last year all we got was hollowpoint. <Col:StageText>Destroy the cameras</Col> to make sure any Enforcers don't spot us and give us a repeat this year.";
            contentsSimple[938] = "TaskObjectives_Holiday_HW_Escort_Stage02_DispatchBrief=There's an enforcer gang trying to Trick and Treat on our turf! That Candy is ours! We've several cameras in the are to discourage this, so <Col:StageText>Protect the Cameras</Col>.";
            contentsSimple[939] = "TaskObjectives_Holiday_HW_Escort_Stage03_OwnerBrief=The cameras spotted us, but we're not stopping.  Escort the <Col:Target_VIP_Friend>VIP</Col> to the first <Col:StageText>Checkpoint</Col>. The enemy have a bead on the VIP, but they don't know where we're going.";
            contentsSimple[940] = "TaskObjectives_Holiday_HW_Escort_Stage03_DispatchBrief=They got the cameras, but not before they could determine which of the gang is the Trick or Treater! We don't know where he's going, but we have a lock on him. <Col:StageText>Kill the VIP</Col> before he reaches all his targets.";
            contentsSimple[941] = "TaskObjectives_Holiday_HW_Escort_Stage04_OwnerBrief=Escort the <Col:Target_VIP_Friend>VIP</Col> to the second <Col:StageText>Checkpoint</Col>. The enemy have a bead on the VIP, but they don't know where we're going.";
            contentsSimple[942] = "TaskObjectives_Holiday_HW_Escort_Stage04_DispatchBrief=<Col:StageText>Kill the VIP</Col> before he reaches all his targets. We've no idea where he's going, so intercept him and take him down.";
            contentsSimple[943] = "TaskObjectives_Holiday_HW_Escort_Stage05_OwnerBrief=Escort the <Col:Target_VIP_Friend>VIP</Col> to the third <Col:StageText>Checkpoint</Col>. The enemy have a bead on the VIP, but they don't know where we're going.";
            contentsSimple[944] = "TaskObjectives_Holiday_HW_Escort_Stage05_DispatchBrief=<Col:StageText>Kill the VIP</Col> before he reaches all his targets. We've no idea where he's going, so intercept him and take him down.";
            contentsSimple[945] = "TaskObjectives_Holiday_HW_Escort_Stage06_OwnerBrief=Escort the <Col:Target_VIP_Friend>VIP</Col> to the last <Col:StageText>Checkpoint</Col>. The enemy have a bead on the VIP, but they don't know where we're going.";
            contentsSimple[946] = "TaskObjectives_Holiday_HW_Escort_Stage06_DispatchBrief=<Col:StageText>Kill the VIP</Col> before he reaches the last target. We've no idea where he's going, so intercept him and take him down.";
            contentsSimple[947] = "TaskObjectives_Holiday_HW_Escort_Stage07_OwnerBrief=<Col:Opponent_Player>LAST STAGE!</Col>↵The Trick or Treating is Complete! Protect the <Col:Target_VIP_Friend>VIP</Col> and <Col:StageText>Kill the Enforcers</Col> so we can go retire to gorge on candy.";
            contentsSimple[948] = "TaskObjectives_Holiday_HW_Escort_Stage07_DispatchBrief=<Col:Opponent_Player>LAST STAGE!</Col>↵The VIP has completed his Trick of Treating! <Col:StageText>Kill the VIP</Col> but make sure to <Col:Target_VIP_Friend>Stay Alive</Col>.";
            contentsSimple[949] = "TaskObjectives_JG_BCL0_Exemplar_B_Stage01_OwnerBrief=A contact of Tyron's paid him with counterfeit bills, and you've been picked to be our avenging angels. <Col: StageText>Rob </Col>the contact's <Col: StageText>buildings </Col>and take back the goods we sold him.";
            contentsSimple[950] = "TaskObjectives_JG_BCL0_Exemplar_B_Stage01_DispatchBrief=A recent spate of burglaries by the Blood Roses has local businesses running scared. <Col: StageText>Defend </Col>these <Col: StageText>buildings </Col>from would-be burglars.";
            contentsSimple[951] = "TaskObjectives_JG_BCL0_Exemplar_B_Stage02_OwnerBrief=<Col: StageText>Deliver </Col>Tyron's recovered <Col: StageText>loot </Col>to the <Col: StageText>drop-off </Col>nearby. Lucky for the contact, we can't find him; crossing the Roses is the last thing you ever do... just ask Darryl Kent's dead girlfriend.";
            contentsSimple[952] = "TaskObjectives_JG_BCL0_Exemplar_B_Stage02_DispatchBrief=Blood Roses have managed to get away with some goods. <Col: StageText>Prevent </Col>the <Col: StageText>loot </Col>from being taken to the <Col: StageText>drop-off</Col>.";
            contentsSimple[953] = "TaskObjectives_JG_BCL0_Exemplar_B_Stage03_OwnerBrief=The contact has two more <Col: StageText>buildings </Col>nearby. <Col: StageText>Burgle </Col>them too, and take the *real* cash the contact should've paid us with in the first place.";
            contentsSimple[954] = "TaskObjectives_JG_BCL0_Exemplar_B_Stage03_DispatchBrief=More local businesses are now at risk from the Blood Roses. <Col: StageText>Defend </Col>these <Col: StageText>buildings </Col>from them and ensure that the burglars don't get away with anything. Particularly money.";
            contentsSimple[955] = "TaskObjectives_JG_BCL0_Exemplar_B_Stage04_OwnerBrief=<Col: StageText>Deliver </Col>the <Col: StageText>cash </Col>to the same <Col: StageText>drop-off </Col>that you took the loot to. Tyron says he's already set up another deal for the goods you recovered; looks like we've come up smelling of roses as usual.";
            contentsSimple[956] = "TaskObjectives_JG_BCL0_Exemplar_B_Stage04_DispatchBrief=The Blood Roses are hauling ass with the money they just stole from those businesses. <Col: StageText>Prevent </Col>the Roses from getting that <Col: StageText>cash</Col> to their <Col: StageText>drop-off</Col>.";
            contentsSimple[957] = "TaskObjectives_JG_BCL0_Exemplar_B_Stage05_OwnerBrief=Seems the contact might have made a deal for protection after ripping us off. <Col: StageText>Kill </Col>all <Col: StageText>opponents </Col>in the area.";
            contentsSimple[958] = "TaskObjectives_JG_BCL0_Exemplar_B_Stage05_DispatchBrief=The Blood Roses think that they've gotten away with this - we need to show them otherwise. <Col: StageText>Eliminate </Col>all <Col: StageText>opponents </Col>in the area.";
            contentsSimple[959] = "TaskObjectives_JG_BCL0_Exemplar_G_Stage01_OwnerBrief=Know the shit we steal in our skate-bys? A fence we use to move it has been paying us with fake bills! I never did like that greasy-haired cockbag. <Col: StageText>Burgle </Col>his <Col: StageText>dens </Col>and reclaim our goods.";
            contentsSimple[960] = "TaskObjectives_JG_BCL0_Exemplar_G_Stage01_DispatchBrief=A recent spate of burglaries by the G-Kings has local businesses running scared. <Col: StageText>Defend </Col>these <Col: StageText>buildings </Col>from would-be burglars.";
            contentsSimple[961] = "TaskObjectives_JG_BCL0_Exemplar_G_Stage02_OwnerBrief=<Col: StageText>Deliver </Col>our recovered <Col: StageText>loot </Col>to the <Col: StageText>drop-off </Col>nearby. I'll start hooking us up with someone more worthy of the G's trust.";
            contentsSimple[962] = "TaskObjectives_JG_BCL0_Exemplar_G_Stage02_DispatchBrief=G-Kings have managed to get away with some goods. <Col: StageText>Prevent </Col>the <Col: StageText>loot </Col>from reaching the <Col: StageText>drop-off</Col>.";
            contentsSimple[963] = "TaskObjectives_JG_BCL0_Exemplar_G_Stage03_OwnerBrief=The fence has two more <Col: StageText>buildings </Col>nearby. <Col: StageText>Burgle </Col>them and take the cash the fucker should've paid us in the first place.";
            contentsSimple[964] = "TaskObjectives_JG_BCL0_Exemplar_G_Stage03_DispatchBrief=Now those G-Kings are all fired up, more local businesss are likely to be at risk. <Col: StageText>Defend </Col>these <Col: StageText>buildings </Col>from them and ensure that the burglars don't get away with any cash or goods.";
            contentsSimple[965] = "TaskObjectives_JG_BCL0_Exemplar_G_Stage04_OwnerBrief=<Col: StageText>Deliver </Col>the <Col: StageText>cash </Col>to the same <Col: StageText>drop-off </Col>that you took the loot to. This is the Gresty way; double or nothing. If you rip someone off then you better get ready to hold on to your shit.";
            contentsSimple[966] = "TaskObjectives_JG_BCL0_Exemplar_G_Stage04_DispatchBrief=The G-Kings are making a run for it with the money stolen from those businesses. <Col: StageText>Prevent </Col>the <Col: StageText>cash </Col>from reaching this <Col: StageText>drop-off</Col>.";
            contentsSimple[967] = "TaskObjectives_JG_BCL0_Exemplar_G_Stage05_OwnerBrief=Seems the fence might have made a deal for protection after ripping us off. <Col: StageText>Kill </Col>any and all <Col: StageText>opponents </Col>who show up in the area.";
            contentsSimple[968] = "TaskObjectives_JG_BCL0_Exemplar_G_Stage05_DispatchBrief=The G-Kings think that they've gotten away with this - we need to show them otherwise. <Col: StageText>Eliminate </Col>all <Col: StageText>opponents </Col>in the area.";
            contentsSimple[969] = "TaskObjectives_JG_BCL0_Gra1_B_Stage01_OwnerBrief=We had tags up in this area for a while, marking this as Blood Rose turf, but some highly un-chic locals cleaned them off and installed security cameras.<Col: StageText> Destroy those cameras </Col>so we can bring sexy back.";
            contentsSimple[970] = "TaskObjectives_JG_BCL0_Gra1_B_Stage01_DispatchBrief=A couple of local blocks are under threat from those rich-bitch Blood Roses. <Col: StageText>Defend </Col>the <Col: StageText>cameras </Col>we installed to protect the neighborhood from their Nu-Bushido bullshit.";
            contentsSimple[971] = "TaskObjectives_JG_BCL0_Gra1_B_Stage02_OwnerBrief=Excellent. With those cameras neutralized, we can re-claim this block for the Roses. Now go <Col: StageText>spray </Col>these <Col: StageText>locations </Col>as a statement of our intent to colonize.";
            contentsSimple[972] = "TaskObjectives_JG_BCL0_Gra1_B_Stage02_DispatchBrief=Those arrogant Blood Rose snobs think they can just waltz into this neighborhood and spray their graffiti. <Col: StageText>Protect </Col>these <Col: StageText>locations</Col>; we'll make them pay for every incursion.";
            contentsSimple[973] = "TaskObjectives_JG_BCL0_Gra1_B_Stage03_OwnerBrief=Good job; now <Col: StageText>destroy </Col>some more <Col: StageText>cameras </Col>nearby and we can claim that block, too. You're handling yourself with a certain amount of style, so far. Good to see.";
            contentsSimple[974] = "TaskObjectives_JG_BCL0_Gra1_B_Stage03_DispatchBrief=Damn, with that last block lost, it's twice as important that we hold this one. <Col: StageText>Defend </Col>these <Col: StageText>cameras </Col>so we can coordinate a counter-attack. Street by street, we'll take San Paro back...";
            contentsSimple[975] = "TaskObjectives_JG_BCL0_Gra1_B_Stage04_OwnerBrief=Toss up and maintain two more<Col: StageText> tags </Col>for a little while, and you're done. You're eager to please... Wouldn't make that too obvious to Charlotte; you'll end up recruiting in Breakwater Marina, wearing too little leather.";
            contentsSimple[976] = "TaskObjectives_JG_BCL0_Gra1_B_Stage04_DispatchBrief=Damn, those Roses are hard to kill. They're planning to tag this block too. <Col: StageText>Protect </Col>these <Col: StageText>locations </Col>and keep their Ravanelle-wearin' asses out of this neighborhood.";
            contentsSimple[977] = "TaskObjectives_JG_BCL0_Gra1_G_Stage01_OwnerBrief=We had tags up in this area for a while, marking this as skater turf, but some locals cleaned 'em off and installed security cameras. <Col: StageText>Destroy </Col>those <Col: StageText>cameras </Col>so we can re-write our tags.";
            contentsSimple[978] = "TaskObjectives_JG_BCL0_Gra1_G_Stage01_DispatchBrief=The G-Kings are threatening some security cams we put up to deter their graffiti. <Col: StageText>Defend </Col>those <Col: StageText>cameras </Col>before the damn skater-punks destroy them.";
            contentsSimple[979] = "TaskObjectives_JG_BCL0_Gra1_G_Stage02_OwnerBrief=Nice work. With those cameras nuked, this turf'll be ours again. <Col: StageText>Tag up </Col>these <Col: StageText>walls </Col>and make sure they stay tagged - let the locals know they live in G-Town now!";
            contentsSimple[980] = "TaskObjectives_JG_BCL0_Gra1_G_Stage02_DispatchBrief=The G-Kings think they can just waltz into a decent, law-abiding neighborhood and spray their graffiti. Big mistake. <Col: StageText>Protect </Col>these <Col: StageText>locations</Col>; show them that street resistance is alive and well.";
            contentsSimple[981] = "TaskObjectives_JG_BCL0_Gra1_G_Stage03_OwnerBrief=You can handle yourself; here's another area for you. <Col: StageText>Destroy these cameras </Col>and we'll claim that block too. Watch your back: this time they're ready for you, G.";
            contentsSimple[982] = "TaskObjectives_JG_BCL0_Gra1_G_Stage03_DispatchBrief=Damn, with that block lost we need to hold the next. <Col: StageText>Defend </Col>these <Col: StageText>cameras </Col>and protect the neighborhood from those jumped-up Gresty board rats.";
            contentsSimple[983] = "TaskObjectives_JG_BCL0_Gra1_G_Stage04_OwnerBrief=Rock. OK, <Col: StageText>spray </Col>up a last few<Col: StageText> tags</Col> and <Col: StageText>make sure Enforcers don't spray over them</Col>. Maybe a design inspired by Shift's latest: Mayor Derren trying to skateboard, arms flailing and skirt flying up.";
            contentsSimple[984] = "TaskObjectives_JG_BCL0_Gra1_G_Stage04_DispatchBrief=This is it. The G-King tag team is out to claim this final block:<Col: StageText> protect</Col> these <Col: StageText>walls </Col>from their bad art - they have some new design showing Mayor Derren trying to skateboard, and it's... undignified.";
            contentsSimple[985] = "TaskObjectives_JG_BCL0_VTh1_B_Stage01_OwnerBrief=A senior politician needs a liver transplant: too many champagne lunches on the taxpayer's dime, it seems. We plan to hold that liver to ransom.<Col: StageText> Hack </Col>this<Col: StageText> antenna </Col>to access the ambulance comms.";
            contentsSimple[986] = "TaskObjectives_JG_BCL0_VTh1_B_Stage01_DispatchBrief=The Blood Roses have taken to hijacking organs on their way to and from hospitals, for sale on the black market. <Col: StageText>Protect</Col> this <Col: StageText>antenna</Col> from Byron's hackers, or they'll be able to tap into ambulance radio chatter.";
            contentsSimple[987] = "TaskObjectives_JG_BCL0_VTh1_B_Stage02_OwnerBrief=We found an ambulance near you. Head there and <Col: StageText>steal</Col> the <Col: StageText>ambulance</Col>. They won't hand over the liver unless you look the part; you'll have to ditch the custom wheels and drive - ugh - a plain EMS van.";
            contentsSimple[988] = "TaskObjectives_JG_BCL0_VTh1_B_Stage02_DispatchBrief=An ambulance has been misdirected into Blood Rose territory and is now at risk. <Col: StageText>Defend</Col> this <Col: StageText>ambulance</Col> and ensure that it isn't stolen for use in their organ-trafficking racket.";
            contentsSimple[989] = "TaskObjectives_JG_BCL0_VTh1_B_Stage03_OwnerBrief=The donor liver was recovered from a morgue. <Col: StageText>Head over there</Col> with the <Col: StageText>ambulance</Col> and make the pick-up. He'd be waiting forever for a match without the Roses - this politician will owe us.";
            contentsSimple[990] = "TaskObjectives_JG_BCL0_VTh1_B_Stage03_DispatchBrief=With that ambulance stolen, looks like the Blood Roses are on their way to meet a black market contact. <Col: StageText>Stop</Col> the <Col: StageText>ambulance</Col>; don't let it reach the <Col: StageText>drop-off</Col>.";
            contentsSimple[991] = "TaskObjectives_JG_BCL0_VTh1_B_Stage04_OwnerBrief=<Col: StageText>Deliver</Col> the <Col: StageText>liver</Col> and bring the <Col: StageText>ambulance</Col> back to our <Col: StageText>drop-off</Col>. We've already made the call - we get that politician his liver, and Simeone gets a friend in government.";
            contentsSimple[992] = "TaskObjectives_JG_BCL0_VTh1_B_Stage04_DispatchBrief=Damn, it looks like the Roses got those organs - and with this city's E.R.s full of gunshot victims in need of transplants, they'll clean up! <Col: StageText>Intercept</Col> the <Col: StageText>ambulance</Col> and <Col: StageText>organs</Col> before they reach the <Col: StageText>drop-off</Col>.";
            contentsSimple[993] = "TaskObjectives_JG_BCL0_VTh1_B_Stage05_OwnerBrief=There's a bag full of cash nearby - call it a goodwill gesture, from our new political buddy. <Col: StageText>Grab</Col> the <Col: StageText>bag</Col> and <Col: StageText>keep</Col> it away from anyone makes a play for it.";
            contentsSimple[994] = "TaskObjectives_JG_BCL0_VTh1_B_Stage05_DispatchBrief=There's a bag full of cash nearby - it must be the payment for the Roses' trouble. <Col: StageText>Recover</Col> the <Col: StageText>bag</Col> and <Col: StageText>keep</Col> it away from them - those rich kids hate having their toys taken away.";
            contentsSimple[995] = "TaskObjectives_JG_BCL0_VTh1_G_Stage01_OwnerBrief=A senior politico needs a liver transplant: too many 3-martini lunches on the taxpayer's dime, hey? We're gonna hold that liver to ransom.<Col: StageText> Hack</Col> this <Col: StageText>antenna </Col>to access the ambulance comms.";
            contentsSimple[996] = "TaskObjectives_JG_BCL0_VTh1_G_Stage01_DispatchBrief=The G-Kings have taken to hijacking organs on their way to and from hospitals, for sale on the black market. <Col: StageText>Protect</Col> this <Col: StageText>antenna</Col> from their hackers, or they'll be able to tap into ambulance radio chatter.";
            contentsSimple[997] = "TaskObjectives_JG_BCL0_VTh1_G_Stage02_OwnerBrief=We found an ambulance near you. Head there and <Col: StageText>steal</Col> the <Col: StageText>ambulance</Col>. They won't hand over the liver unless you look the part - with the right wheels, they won't look twice, G.";
            contentsSimple[998] = "TaskObjectives_JG_BCL0_VTh1_G_Stage02_DispatchBrief=An ambulance has been misdirected into G-King territory and is now at risk. <Col: StageText>Defend</Col> this <Col: StageText>ambulance</Col> and ensure that it isn't stolen for use in their organ-trafficking racket.";
            contentsSimple[999] = "TaskObjectives_JG_BCL0_VTh1_G_Stage03_OwnerBrief=The donor liver was recovered from a morgue. <Col: StageText>Head over there</Col> with the <Col: StageText>ambulance</Col> and make the pick-up. Takes forever to find a match - this'll keep this politician in debt to the G-Kings.";
            contentsSimple[1000] = "TaskObjectives_JG_BCL0_VTh1_G_Stage03_DispatchBrief=With that ambulance stolen, looks like the G-Kings are on their way to meet a black market contact. <Col: StageText>Stop</Col> the <Col: StageText>ambulance</Col>; don't let it reach the <Col: StageText>drop-off</Col>.";
            contentsSimple[1001] = "TaskObjectives_JG_BCL0_VTh1_G_Stage04_OwnerBrief=All right - <Col: StageText>deliver</Col> the <Col: StageText>liver</Col> and bring the <Col: StageText>ambulance</Col> back to our <Col: StageText>drop-off</Col>. We've already made the call - we get that politician his liver, and we get a friend to the Benjamins in government.";
            contentsSimple[1002] = "TaskObjectives_JG_BCL0_VTh1_G_Stage04_DispatchBrief=Damn, it looks like the Gs got those organs - and with this city's E.R.s full of gunshot victims in need of transplants, they'll clean up! <Col: StageText>Intercept</Col> the <Col: StageText>ambulance</Col> and <Col: StageText>organs</Col> before they reach the <Col: StageText>drop-off</Col>.";
            contentsSimple[1003] = "TaskObjectives_JG_BCL0_VTh1_G_Stage05_OwnerBrief=There's a bag full of cash nearby - call it a goodwill gesture from our new political buddy. <Col: StageText>Grab</Col> the <Col: StageText>bag</Col> and <Col: StageText>keep</Col> it away from anyone makes a play for it.";
            contentsSimple[1004] = "TaskObjectives_JG_BCL0_VTh1_G_Stage05_DispatchBrief=Nearby is what must be the payment for the G-Kings' trouble. <Col: StageText>Recover the bag of cash</Col> and <Col: StageText>keep </Col>it away from them. Maybe they'll realize there are easier ways to earn money.";
            contentsSimple[1005] = "TaskObjectives_JG_BCL1_Ars1_B_Stage01_OwnerBrief=Michael Simeone has a meeting with some big international buyers at the airport. He has asked us to ensure that 'Forcer attention is elsewhere. <Col: StageText>Set fire </Col>to this <Col: StageText>vehicle</Col>.";
            contentsSimple[1006] = "TaskObjectives_JG_BCL1_Ars1_B_Stage01_DispatchBrief=The Blood Roses have been seen filling up the gas tank of a nearby car. That always means arson. <Col: StageText>Protect </Col>the <Col: StageText>vehicle</Col>, to show we're clamping down on wanton destruction.";
            contentsSimple[1007] = "TaskObjectives_JG_BCL1_Ars1_B_Stage02_OwnerBrief=Good start, but we need more, so <Col: StageText>burn </Col>these <Col: StageText>vehicles</Col>. Michael's meeting is going well, but there are a lot of stolen items to trade, thanks to you kleptomaniacs.";
            contentsSimple[1008] = "TaskObjectives_JG_BCL1_Ars1_B_Stage02_DispatchBrief=More vehicles are under threat of arson. <Col: StageText>Protect </Col>these <Col: StageText>vehicles</Col>, and I'll put extra teams on patrol at gas stations so this won't escalate.";
            contentsSimple[1009] = "TaskObjectives_JG_BCL1_Ars1_B_Stage03_OwnerBrief=Michael reports that both Praetorians and Prentiss Tigers are closing in on his meeting. Time to orchestrate a serious decoy. <Col: StageText>Burn these buildings </Col>to the ground.";
            contentsSimple[1010] = "TaskObjectives_JG_BCL1_Ars1_B_Stage03_DispatchBrief=Good news: the car burning has stopped. Bad news: the Blood Roses have moved on to burning buildings. <Col: StageText>Protect </Col>the <Col: StageText>buildings </Col>from these pyromaniacs.";
            contentsSimple[1011] = "TaskObjectives_JG_BCL1_Ars1_B_Stage04_OwnerBrief=Looks like that got the attention of our valiant Enforcers. Keep them busy by shooting them in the face till they give up! <Col: StageText>Fight </Col>for the next 5 minutes, until Michael signs the deal.";
            contentsSimple[1012] = "TaskObjectives_JG_BCL1_Ars1_B_Stage04_DispatchBrief=Looks like we flushed the Roses out and have them on the run. <Col: StageText>Eliminate </Col>all of the <Col: StageText>arsonists</Col> - they can go burn in hell instead.";
            contentsSimple[1013] = "TaskObjectives_JG_BCL1_Ars1_G_Stage01_OwnerBrief=We used to get a lot of money for protecting this neighborhood, but now everyone's complacent. <Col: StageText>Set fire </Col>to this <Col: StageText>vehicle</Col>: it's in a prime spot to scare the locals.";
            contentsSimple[1014] = "TaskObjectives_JG_BCL1_Ars1_G_Stage01_DispatchBrief=Local citizens are under threat from arsonists: <Col: StageText>protect </Col>the targeted <Col: StageText>vehicle </Col>and show the populace that we can keep the city safe.";
            contentsSimple[1015] = "TaskObjectives_JG_BCL1_Ars1_G_Stage02_OwnerBrief=Further intimidation is needed to stop the locals thinking that was a one-off. <Col: StageText>Set fire </Col>to these other <Col: StageText>vehicles</Col>, too. Burn to earn.";
            contentsSimple[1016] = "TaskObjectives_JG_BCL1_Ars1_G_Stage02_DispatchBrief=More vehicles are under threat of arson. Must be attempted intimidation by the G-Kings. <Col: StageText>Protect </Col>the <Col: StageText>vehicles </Col>and it'll demonstrate the worth of Enforcement.";
            contentsSimple[1017] = "TaskObjectives_JG_BCL1_Ars1_G_Stage03_OwnerBrief=That kinda worked... the locals sought out protection - from Enforcers rather than G-Kings! We need to make this more personal. <Col: StageText>Set fire </Col>to some of the resident's <Col: StageText>buildings</Col>.";
            contentsSimple[1018] = "TaskObjectives_JG_BCL1_Ars1_G_Stage03_DispatchBrief=The recent G-King arson attacks were designed to intimidate the local citizens into paying for protection. Their next target is these nearby <Col: StageText>buildings</Col>; show everyone what <Col: StageText>protection </Col>really means.";
            contentsSimple[1019] = "TaskObjectives_JG_BCL1_Ars1_G_Stage04_OwnerBrief=Looks like the 'Forcers have been called in. Go <Col:StageText>fill 'em full of holes</Col>.";
            contentsSimple[1020] = "TaskObjectives_JG_BCL1_Ars1_G_Stage04_DispatchBrief=We can't go around firefighting the problem like this - literally. <Col: StageText>Eliminate </Col>the <Col: StageText>G-King arsonists </Col>once and for all.";
            contentsSimple[1021] = "TaskObjectives_JG_BCL1_Bom1_B_Stage01_OwnerBrief=A chopshop owner hasn't paid for vehicles that your fellow Blood Roses stole for him. <Col: StageText>Blow up </Col>the first <Col: StageText>car </Col>we stole for him; maybe that will scare him into compensating us.";
            contentsSimple[1022] = "TaskObjectives_JG_BCL1_Bom1_B_Stage01_DispatchBrief=One of our undercover operatives posed as a chopshop owner to entrap the Blood Roses, but they may be onto him. <Col: StageText>Protect </Col>one of his <Col: StageText>cars </Col>from being <Col: StageText>bombed</Col>: it's prime evidence.";
            contentsSimple[1023] = "TaskObjectives_JG_BCL1_Bom1_B_Stage02_OwnerBrief=No remuneration yet from our friend Mr. Chopshop. The second stolen <Col: StageText>vehicle </Col>is nearby. <Col: StageText>Blow up </Col>that one too. And I'll send him an email saying that we'll keep blowing cars up - until he coughs up.";
            contentsSimple[1024] = "TaskObjectives_JG_BCL1_Bom1_B_Stage02_DispatchBrief=The Blood Roses are trying to destroy a chopshop owner's <Col: StageText>van </Col>nearby. They must know he is one of our moles. <Col: StageText>Protect it </Col>and draw the Roses out into the open.";
            contentsSimple[1025] = "TaskObjectives_JG_BCL1_Bom1_B_Stage03_OwnerBrief=Our chopshop friend is about to sell a sweet <Col: StageText>car. </Col>Go and <Col: StageText>bomb</Col> it before his customer gets there. If we hurt his income he should change his wallet-shy behavior.";
            contentsSimple[1026] = "TaskObjectives_JG_BCL1_Bom1_B_Stage03_DispatchBrief=We are trying to gather photographic evidence of Blood Roses attempting to bomb cars. <Col: StageText>Protect </Col>this <Col: StageText>car </Col>until we can get into place with our telephoto lens.";
            contentsSimple[1027] = "TaskObjectives_JG_BCL1_Bom1_B_Stage04_OwnerBrief=Instead of paying us what's owed, the chopshop owner replied to my email with 'Screw you, Blood Poseurs'. <Col: StageText>Blow up </Col>this <Col: StageText>shop </Col>he uses as a front: let's make this personal.";
            contentsSimple[1028] = "TaskObjectives_JG_BCL1_Bom1_B_Stage04_DispatchBrief=The Blood Roses have outsmarted us. They must know it's a set-up and have now targeted our other fronts in this area. Change of tactic: <Col: StageText>protect </Col>this <Col: StageText>shop </Col>from the bombers.";
            contentsSimple[1029] = "TaskObjectives_JG_BCL1_Bom1_B_Stage05_OwnerBrief=We need to wipe out all of this guy's businesses or no one will take the Roses seriously in San Paro again. Another team has planted some explosives on his electronics shop. <Col: StageText>Protect the Explosives</Col> from the bomb squad.";
            contentsSimple[1030] = "TaskObjectives_JG_BCL1_Bom1_B_Stage05_DispatchBrief=The Blood Roses could expose our whole undercover operation. They seem to know exactly which shops our people own. <Col: StageText>Defuse our Property</Col>.";
            contentsSimple[1031] = "TaskObjectives_JG_BCL1_Bom1_B_Stage05_Opp_OwnerBrief=We need to wipe out all of this guy's businesses or no one will take the Roses seriously in San Paro again. Another team has planted some explosives on his electronics shop. <Col: StageText>Protect the Explosives</Col> from the bomb squad.";
            contentsSimple[1032] = "TaskObjectives_JG_BCL1_Bom1_B_Stage05_Opp_DispatchBrief=The Blood Roses could expose our whole undercover operation. They seem to know exactly which shops our people own. <Col: StageText>Defuse our Property</Col>.";
            contentsSimple[1033] = "TaskObjectives_JG_BCL1_Bom1_B_Stage06_OwnerBrief=The chopshop owner has called in some expert armed help. For the cost of all this, he coulda just paid us in the first place. <Col: StageText>Eliminate everyone </Col>he has hired.";
            contentsSimple[1034] = "TaskObjectives_JG_BCL1_Bom1_B_Stage06_DispatchBrief=Enough is enough. We need to <Col: StageText>take out </Col>this group of <Col: StageText>Blood Roses </Col> before our whole undercover operation is blown wide open.";
            contentsSimple[1035] = "TaskObjectives_JG_BCL1_Bom1_G_Stage01_OwnerBrief=The names of the jury in a pending case against the G-Kings has been leaked. Arlon Benjamin wants us to make sure they vote the right way. <Col: StageText>Plant a car bomb </Col>under the <Col: StageText>vehicle</Col> of the head juror.";
            contentsSimple[1036] = "TaskObjectives_JG_BCL1_Bom1_G_Stage01_DispatchBrief=A big trial against the G-Kings is about to get under way, and they have a reputation for leaning on jury members. <Col: StageText>Protect the car </Col>of the head juror from car bomb attacks.";
            contentsSimple[1037] = "TaskObjectives_JG_BCL1_Bom1_G_Stage02_OwnerBrief=This second jury member is a gardener, apparently. <Col: StageText>Plant the bomb in his van</Col>. Geddit? Plant? I crack myself up sometimes.";
            contentsSimple[1038] = "TaskObjectives_JG_BCL1_Bom1_G_Stage02_DispatchBrief=A second <Col: StageText>vehicle </Col>is nearby, belonging to another witness. <Col: StageText>Protect </Col>the vehicle from the G-King bombers.";
            contentsSimple[1039] = "TaskObjectives_JG_BCL1_Bom1_G_Stage03_OwnerBrief=The law says you should be judged by your peers, but no one is as awesome as the G-Kings. So <Col: StageText>plant the car bomb </Col>under this juror's car and he can feel the awesomeness for himself.";
            contentsSimple[1040] = "TaskObjectives_JG_BCL1_Bom1_G_Stage03_DispatchBrief=If you don't stop these car bombs, the G-Kings on trial will be aquitted. This <Col: StageText>vehicle</Col>, belonging to a jury member,  will probably be the next target. <Col: StageText>Protect </Col>it.";
            contentsSimple[1041] = "TaskObjectives_JG_BCL1_Bom1_G_Stage04_OwnerBrief=Who lives in San Paro and doesn't own wheels? This loser juror, apparently - but the G-Kings are equal opportunity bombers. <Col: StageText>Blow up </Col>her <Col: StageText>shop </Col>and we'll have one more vote of 'not guilty'.";
            contentsSimple[1042] = "TaskObjectives_JG_BCL1_Bom1_G_Stage04_DispatchBrief=The G-Kings have changed tactic and targeted the workplace of a jury member. <Col: StageText>Protect </Col>this <Col: StageText>shop </Col>from the bombers. We must give every citizen the confidence to stand up against thugs.";
            contentsSimple[1043] = "TaskObjectives_JG_BCL1_Bom1_G_Stage05_OwnerBrief=One of the jury members dropped out; you intimidated well, my friend. We've planted a bomb at the store of his replacement. <Col: StageText>Protect the bomb</Col> from the authorities.";
            contentsSimple[1044] = "TaskObjectives_JG_BCL1_Bom1_G_Stage05_DispatchBrief=Well done; the big trial is going ahead as planned. But the G-Kings have changed tactic to focus on the star witness instead. <Col: StageText>Defuse the bomb</Col> that has been set at his place of work.";
            contentsSimple[1045] = "TaskObjectives_JG_BCL1_Bom1_G_Stage05_Opp_OwnerBrief=One of the jury members dropped out; you intimidated well, my friend. We've planted a bomb at the store of his replacement.<Col: StageText>Protect the bomb </Col> from the authorities.";
            contentsSimple[1046] = "TaskObjectives_JG_BCL1_Bom1_G_Stage05_Opp_DispatchBrief=Well done; the big trial is going ahead as planned. But the G-Kings have changed tactic to focus on the star witness instead. <Col: StageText>Defuse the bomb</Col> that has been set at his place of work";
            contentsSimple[1047] = "TaskObjectives_JG_BCL1_Bom1_G_Stage06_OwnerBrief=Your campaign of friendly influencing has drawn attention - big time. <Col: StageText>Kill </Col>the incoming <Col: StageText>opposition</Col> - or the jury will feel free to vote anyway they see fit. Can't have that.";
            contentsSimple[1048] = "TaskObjectives_JG_BCL1_Bom1_G_Stage06_DispatchBrief=The G-Kings are set on intimidating everyone involved in this trial. San Paro must have faith in its Enforcers. <Col: StageText>Fight </Col>and defeat any <Col: StageText>G-Kings </Col>involved in perverting the course of justice.";
            contentsSimple[1049] = "TaskObjectives_JG_BCL1_Gra1_G_Stage01_OwnerBrief=A city street-cleaner removed all our graffiti from the nearby block. Other gangs may try to take this territory. <Col: StageText>Spray </Col>our tags back onto the <Col: StageText>walls </Col>and keep it G-King.";
            contentsSimple[1050] = "TaskObjectives_JG_BCL1_Gra1_G_Stage01_DispatchBrief=The graffiti removal team has been in and cleaned off all the G-King filth. But the Gs are stubborn and stupid enough to come back to reapply their tags. <Col: StageText>Protect </Col>this <Col: StageText>location </Col>from being sprayed.";
            contentsSimple[1051] = "TaskObjectives_JG_BCL1_Gra1_G_Stage02_OwnerBrief=Are you on a sightseeing trip or something? Pick up the pace. Get over to our territory and <Col: StageText>tag these two spots, </Col>before some local bad boys take a shine to it. Don't let anyone try and mess with your work.";
            contentsSimple[1052] = "TaskObjectives_JG_BCL1_Gra1_G_Stage02_DispatchBrief=<Col: StageText>Prevent </Col>the G-Kings from respraying their tags on to these two <Col: StageText>locations</Col>. I don't go around putting graffiti on *their* stuff. Well... one time I did.";
            contentsSimple[1053] = "TaskObjectives_JG_BCL1_Gra1_G_Stage03_OwnerBrief=It's starting to look a bit more G around here again. Broken-in, like wearing your favorite leather knife sheath. <Col: StageText>Spray </Col>these last couple of <Col: StageText>locations </Col>and don't let anyone beat you to it.";
            contentsSimple[1054] = "TaskObjectives_JG_BCL1_Gra1_G_Stage03_DispatchBrief=<Col: StageText>Protect </Col>the last couple of <Col: StageText>locations </Col>from the G-King graffiti crew who want to claim this area.";
            contentsSimple[1055] = "TaskObjectives_JG_BCL1_Gra1_G_Stage04_OwnerBrief=Job done - now muzzle your nozzle and <Col: StageText>lock and load</Col>. A graffiti removal team is headed your way - <Col: StageText>take them out</Col>.";
            contentsSimple[1056] = "TaskObjectives_JG_BCL1_Gra1_G_Stage04_DispatchBrief=Washing off graffiti isn't the answer: it just comes back. We need to wash the G-King scum themselves into the sewer. <Col: StageText>Eliminate </Col>the <Col: StageText>G-King taggers</Col>.";
            contentsSimple[1057] = "TaskObjectives_JG_BCL1_VTh1_B_Stage01_OwnerBrief=The Tigers have been an annoyance lately, interfering with our contracts. We need to slow them down, and luckily we've got a plan. Start by <Col: StageText>raiding </Col>these minor dealers' <Col: StageText>drug dens</Col>.";
            contentsSimple[1058] = "TaskObjectives_JG_BCL1_VTh1_B_Stage01_DispatchBrief=The Blood Roses seem to be 'rehearsing' for Hell Night; we've had reports of Blood Rose crime all over San Paro. <Col: StageText>Prevent </Col>Blood Rose <Col: StageText>raids </Col>on these local <Col: StageText>properties</Col>.";
            contentsSimple[1059] = "TaskObjectives_JG_BCL1_VTh1_B_Stage02_OwnerBrief=Excellent. Those drugs are going to appear in Orlenz's possession soon. For that trick to happen, you need to <Col: StageText>steal </Col>his <Col: StageText>car</Col>. He's visiting Tigers in the area, so it should be easy to find.";
            contentsSimple[1060] = "TaskObjectives_JG_BCL1_VTh1_B_Stage02_DispatchBrief=Jeung usually only lets his Roses off the leash once a year, but he must be feeling generous today. Lucky us. <Col: StageText>Protect </Col>this <Col: StageText>vehicle </Col>from Blood Roses out to steal it.";
            contentsSimple[1061] = "TaskObjectives_JG_BCL1_VTh1_B_Stage03_OwnerBrief=Good, good. Now <Col: StageText>deliver </Col>the car and <Col: StageText>drugs </Col>to the stage we've set. Will this setup get that smarmy asshole Orlenz' convicted? I doubt it, but it'll cause the Tigers some trauma at least.";
            contentsSimple[1062] = "TaskObjectives_JG_BCL1_VTh1_B_Stage03_DispatchBrief=The Blood Rose revelers are escaping with <Col: StageText>loot </Col>to their <Col: StageText>drop-off</Col>. <Col: StageText>Stop </Col>them. These Hell Night events can rival the CSA riots for mass looting. Thank heck this isn't the actual Hell Night...";
            contentsSimple[1063] = "TaskObjectives_JG_BCL1_VTh1_B_Stage04_OwnerBrief=An anonymous tip would make them suspect the Blood Roses straight away. <Col: StageText>Hack </Col>this <Col: StageText>antenna, </Col>and we'll use the Tiger radio network to send false reports guiding them to the drugs instead.";
            contentsSimple[1064] = "TaskObjectives_JG_BCL1_VTh1_B_Stage04_DispatchBrief=The Roses have worked themselves up into a frenzy with all the other crimes they've committed, and now they're targeting our property as well. <Col: StageText>Protect </Col>our <Col: StageText>antenna </Col>from the Blood Rose revelers.";
            contentsSimple[1065] = "TaskObjectives_JG_BCL1_VTh1_B_Stage05_OwnerBrief=Our play is set, but you've left a loose end. Some people spotted you with the car. You need to <Col: StageText>kill the witnesses, </Col>or our little plan won't work. You do want the plan to work... don't you?";
            contentsSimple[1066] = "TaskObjectives_JG_BCL1_VTh1_B_Stage05_DispatchBrief=Trying to stop these Hell Night 'rehearsal' crimes is getting us nowhere. Time to treat the disease rather than the symptoms: <Col: StageText>take out </Col>the <Col: StageText>Blood Roses</Col>. They can't cause trouble if they're dead.";
            contentsSimple[1067] = "TaskObjectives_JG_BCL1_VTh1_G_Stage01_OwnerBrief=Some of my compañeros got hooked on Fade, and now they just sit around spaced out and doing fuck-all. It's boring, so I want to help them kick the habit. <Col: StageText>Raid </Col>their <Col: StageText>houses </Col>and steal the Fade.";
            contentsSimple[1068] = "TaskObjectives_JG_BCL1_VTh1_G_Stage01_DispatchBrief=A rookie of ours made the mistake of trying to hit on Double-B. We're still finding pieces of him. His unit retaliated, and now we've got a war on our hands. <Col: StageText>Stop </Col>the G's <Col: StageText>attacks </Col>on our <Col: StageText>homes</Col>.";
            contentsSimple[1069] = "TaskObjectives_JG_BCL1_VTh1_G_Stage02_OwnerBrief=I know Monkeywrench hides Fade in his ride. He's out cold at the moment, so just <Col: StageText>steal </Col>his <Col: StageText>car </Col>for now. I'll get it back to him later, so don't fuck the car up too much - or you're paying for it.";
            contentsSimple[1070] = "TaskObjectives_JG_BCL1_VTh1_G_Stage02_DispatchBrief=This little war that's broken out between us and the G-Kings is worse than usual. Getting too close to home for me, even. <Col: StageText>Protect </Col>my <Col: StageText>car</Col>: those bastards are eyeing it.";
            contentsSimple[1071] = "TaskObjectives_JG_BCL1_VTh1_G_Stage03_OwnerBrief=Cool. <Col: StageText>Deliver </Col>the car and <Col: StageText>drugs </Col>over here. I'll get our Gs to clean the car out and dump the Fade. Sucks to do this, but those asshole friends of mine will thank me for it later.";
            contentsSimple[1072] = "TaskObjectives_JG_BCL1_VTh1_G_Stage03_DispatchBrief=The G-Kings are a pain to deal with at the best of times; if only that rookie hadn't stirred up the hive. <Col: StageText>Stop </Col>G-King looters from bringing the <Col: StageText>loot and my car </Col>to their <Col: StageText>drop-off</Col>.";
            contentsSimple[1073] = "TaskObjectives_JG_BCL1_VTh1_G_Stage04_OwnerBrief=I want to make sure my compañeros don't get heat for this - <Col: StageText>hack </Col>this <Col: StageText>antenna </Col>so I can check Enforcer chatter and make sure it's all cool. If they don't ID Monkeywrench's car then we're good.";
            contentsSimple[1074] = "TaskObjectives_JG_BCL1_VTh1_G_Stage04_DispatchBrief=They've gotten away with people's goods, and my car - and now they're screwing with our equipment! <Col: StageText>Protect </Col>our <Col: StageText>antenna </Col>from those G-Kings.";
            contentsSimple[1075] = "TaskObjectives_JG_BCL1_VTh1_G_Stage05_OwnerBrief=Fuck! There's Praetorians on their way. Did you get spotted with the car? <Col: StageText>Kill the Enforcers </Col>so they can't interfere. I'll have to hook Monkeywrench up with new license plates now.";
            contentsSimple[1076] = "TaskObjectives_JG_BCL1_VTh1_G_Stage05_DispatchBrief=Ok, enough is enough. <Col: StageText>Take out </Col>the <Col: StageText>G-Kings</Col>. They really need to emphasize those 'do not fraternize with the enemy' lessons in our training. Anything to avoid another G-King incident like this.";
            contentsSimple[1077] = "TaskObjectives_JG_BCL2_Ars1_Stage01_OwnerBrief=A local loser is going to testify against us in court. He saw us trying to attach a tug to the Mist restaurant and tow it down the canal. <Col: StageText>Torch </Col>his <Col: StageText>vehicle </Col>to make him change his story.";
            contentsSimple[1078] = "TaskObjectives_JG_BCL2_Ars1_Stage01_DispatchBrief=The Mist floating restaurant called us in to provide protection. A gang got busted trying to tow it down the canal with a tug boat and are now intimating witnesses. <Col: StageText>Protect </Col>this witness's <Col: StageText>vehicle</Col>.";
            contentsSimple[1079] = "TaskObjectives_JG_BCL2_Ars1_Stage02_OwnerBrief=I guess people don't like it when you try to tow away the floating restaurant they're eating in. <Col: StageText>Burn </Col>the customers' <Col: StageText>cars </Col>to help them 'forget' the whole thing ever happened.";
            contentsSimple[1080] = "TaskObjectives_JG_BCL2_Ars1_Stage02_DispatchBrief=Who would be dumb enough to try to steal a whole floating restaurant - with people in it? These maniacs need to face their day in court. <Col: StageText>Protect </Col>the <Col: StageText>vehicles </Col>of the witnesses.";
            contentsSimple[1081] = "TaskObjectives_JG_BCL2_Ars1_Stage03_OwnerBrief=It would have been so cool to have our own floating restaurant. Shame they moored it so tightly. <Col: StageText>Burn </Col>these <Col: StageText>properties </Col>belonging to the owner and she may drop the charges against us.";
            contentsSimple[1082] = "TaskObjectives_JG_BCL2_Ars1_Stage03_DispatchBrief=This gang would be dangerous if they weren't so dumb. <Col: StageText>Protect these buildings </Col>that belong to the Mist restaurant's owner. The gang don't want her to testify in court against them.";
            contentsSimple[1083] = "TaskObjectives_JG_BCL2_Ars1_Stage04_OwnerBrief=The Mist restaurant has called in some bounty hunters. Looks like they want to see us either in court - or dead. <Col: StageText>Kill </Col>the <Col: StageText>bounty hunters </Col>and try to <Col: StageText>stay alive</Col>. They'll get bored eventually.";
            contentsSimple[1084] = "TaskObjectives_JG_BCL2_Ars1_Stage04_DispatchBrief=This gang are a cucumber short of a salad, you know what I mean? <Col: StageText>Eliminate the gang</Col>. You'll be doing us all a favor by removing them from the gene pool.";
            contentsSimple[1085] = "TaskObjectives_JG_BCL2_Bom1_Stage01_OwnerBrief=An unknown group did a series of drive-bys on our friends. <Col: StageText>Bomb </Col>the <Col: StageText>vehicle </Col>they used. This doesn't seem to be the work of the usual local gangs.";
            contentsSimple[1086] = "TaskObjectives_JG_BCL2_Bom1_Stage01_DispatchBrief=I love watching San Paro's Big Talent. Now the contestants who got voted off are on the hunt for revenge. <Col: StageText>Protect the car </Col>of the show's winner from being bombed by this gang of losers.";
            contentsSimple[1087] = "TaskObjectives_JG_BCL2_Bom1_Stage02_OwnerBrief=The second vehicle used in the drive-by against our fellow members is nearby. <Col: StageText>Blow up </Col>the <Col: StageText>vehicle </Col>and get some payback. I'll look into who these drive-by dickwads are.";
            contentsSimple[1088] = "TaskObjectives_JG_BCL2_Bom1_Stage02_DispatchBrief=A second car is nearby, belonging to one of the Big Talent TV show judges. <Col: StageText>Protect the car </Col>from the 'The Bombastics'. If they wanted to do even more damage they'd sing instead.";
            contentsSimple[1089] = "TaskObjectives_JG_BCL2_Bom1_Stage03_OwnerBrief=These drive-by nuts are planning an even bigger attack. They have started to weld gun mounts onto the inside of a car. <Col: StageText>Bomb the vehicle </Col>so it can never be used against us.";
            contentsSimple[1090] = "TaskObjectives_JG_BCL2_Bom1_Stage03_DispatchBrief=These losers have a lot of anger, maybe if they'd put this much energy into their band they wouldn't have been voted off San Paro's Big Talent. <Col: StageText>Protect </Col>the TV exec's <Col: StageText>car </Col>from their bombs.";
            contentsSimple[1091] = "TaskObjectives_JG_BCL2_Bom1_Stage04_OwnerBrief=These drive-by dickwads are legitimate businessmen just killing for kicks. <Col: StageText>Blow up </Col>their <Col: StageText>store </Col>and see how they like it when we come and mess with their business.";
            contentsSimple[1092] = "TaskObjectives_JG_BCL2_Bom1_Stage04_DispatchBrief=These tuneless wonders have upped their game, unlike their talent. Now they're targeting the sponsors of the Big Talent show as revenge for being booted off. <Col: StageText>Protect </Col>the <Col: StageText>stores</Col>.";
            contentsSimple[1093] = "TaskObjectives_JG_BCL2_Bom1_Stage05_OwnerBrief=These guys don't even need the money! <Col: StageText>Protect the bomb</Col> we've planted at the other store that belongs to these part-time gangsters. They bring a whole new meaning to the term 'white collar crime'.";
            contentsSimple[1094] = "TaskObjectives_JG_BCL2_Bom1_Stage05_DispatchBrief=This group of talentless singers really hold a grudge. They are attacking the sponsor of their main rival. <Col: StageText>Defuse the bomb</Col> that's been placed at the sponsor's property.";
            contentsSimple[1095] = "TaskObjectives_JG_BCL2_Bom1_Stage05_Opp_OwnerBrief=These guys don't even need the money! <Col: StageText>Protect the bomb</Col> we've planted at the other store that belongs to these part-time gangsters. They bring a whole new meaning to the term 'white collar crime'.";
            contentsSimple[1096] = "TaskObjectives_JG_BCL2_Bom1_Stage05_Opp_DispatchBrief=This group of talentless singers really hold a grudge. They are attacking the sponsor of their main rival. <Col: StageText>Defuse the bomb</Col> that's been placed at the sponsor's property.";
            contentsSimple[1097] = "TaskObjectives_JG_BCL2_Bom1_Stage06_OwnerBrief=Looks like these 'hobby hoods' fancy a real fight. <Col: StageText>Fight </Col>off the part-time <Col: StageText>gangsters </Col>and humiliate them into never messing with real Criminals again.";
            contentsSimple[1098] = "TaskObjectives_JG_BCL2_Bom1_Stage06_DispatchBrief=We can't let these bunch of talentless singers go around causing wanton destruction. If they want to sing - it should be like a jailbird. <Col: StageText>Fight </Col>and defeat the <Col: StageText>group</Col>.";
            contentsSimple[1099] = "TaskObjectives_JG_BCL2_Bom2_Stage01_OwnerBrief=You know how Al Capone eventually got caught, income tax evasion. We could eliminate other gangs in the same way, by bombing their accounts. <Col: StageText>Steal </Col>bomb <Col: StageText>components </Col>from this <Col: StageText>building</Col>.";
            contentsSimple[1100] = "TaskObjectives_JG_BCL2_Bom2_Stage01_DispatchBrief=A small, specialist gang of bombers has arrived in the area. This could be bad news. <Col: StageText>Defend </Col>this <Col: StageText>building </Col>from any attempt to break in, it contains bomb casings.";
            contentsSimple[1101] = "TaskObjectives_JG_BCL2_Bom2_Stage02_OwnerBrief=<Col: StageText>Steal </Col>more bomb <Col: StageText>components </Col>from this <Col: StageText>building</Col>. We need it to be extra incendiary to make sure the accounting paperwork catches fire.";
            contentsSimple[1102] = "TaskObjectives_JG_BCL2_Bom2_Stage02_DispatchBrief=These bombers mean business, they plan to steal some ultra high density ballistics. <Col: StageText>Defend </Col>the <Col: StageText>building </Col>where it's kept.";
            contentsSimple[1103] = "TaskObjectives_JG_BCL2_Bom2_Stage03_OwnerBrief=<Col: StageText>Steal </Col>the specialist <Col: StageText>bomb fuse </Col>from this <Col: StageText>building </Col>and we'll have everything we need to destroy the other gang's accounts. Who says accounting is boring?";
            contentsSimple[1104] = "TaskObjectives_JG_BCL2_Bom2_Stage03_DispatchBrief=The bomb makers are hitting another <Col: StageText>building </Col>- get there and <Col: StageText>defend </Col>it. It contains the fuses they'd need. No news on the bomb target yet.";
            contentsSimple[1105] = "TaskObjectives_JG_BCL2_Bom2_Stage04_OwnerBrief=<Col: StageText>Take </Col>all of the bomb <Col: StageText>components </Col>to the <Col: StageText>drop-off </Col>so they can be assembled. I wonder if we can claim them on expenses - they are work related.";
            contentsSimple[1106] = "TaskObjectives_JG_BCL2_Bom2_Stage04_DispatchBrief=<Col: StageText>Prevent </Col>the bombers <Col: StageText>taking </Col>their <Col: StageText>components </Col>to their <Col: StageText>drop-off</Col>. They have everything they need to build some serious ordinance.";
            contentsSimple[1107] = "TaskObjectives_JG_BCL2_Bom2_Stage05_OwnerBrief=Head to the <Col: StageText>building </Col>where some of the gang store their accounts and plant the <Col: StageText>bomb </Col>to blow it up.";
            contentsSimple[1108] = "TaskObjectives_JG_BCL2_Bom2_Stage05_DispatchBrief=The bombers are on their way to blow up our bomb squad offices. The squad itself has been called out on a job so you'll have to <Col: StageText>defend the building </Col>from attack.";
            contentsSimple[1109] = "TaskObjectives_JG_BCL2_Bom2_Stage06_OwnerBrief=The other gang know we've hit them hard and they don't like it. <Col: StageText>Wipe out </Col>any <Col: StageText>opponents </Col>who remain in the area and we can take their territory. I'm sure Al Capone would approve.";
            contentsSimple[1110] = "TaskObjectives_JG_BCL2_Bom2_Stage06_DispatchBrief=With our bomb squad temporarily out of action the bombers will be a powerful force in the area. <Col: StageText>Eliminate </Col>the <Col: StageText>bombers </Col>and halt their reign of terror before it starts.";
            contentsSimple[1111] = "TaskObjectives_JG_BCL2_Bur1_Stage01_OwnerBrief=An economic downturn means our revenue is dropping. We had to slash the prices of all our goods and now we need more. <Col: StageText>Burgle </Col>these <Col: StageText>buildings </Col>and grab some new stock.";
            contentsSimple[1112] = "TaskObjectives_JG_BCL2_Bur1_Stage01_DispatchBrief=The economic downturn has made the Criminals ever more desperate, they need to steal even more goods to sell. <Col: StageText>Defend these properties </Col>from being burgled.";
            contentsSimple[1113] = "TaskObjectives_JG_BCL2_Bur1_Stage02_OwnerBrief=WTF? Mink driving gloves? Okay, no biggie. We'll still shift 'em. <Col: StageText>Deliver </Col>the stolen <Col: StageText>goods </Col>to our <Col: StageText>drop-off</Col>.";
            contentsSimple[1114] = "TaskObjectives_JG_BCL2_Bur1_Stage02_DispatchBrief=<Col: StageText>Intercept </Col>those stolen <Col: StageText>goods </Col>before they reach the burglars' <Col: StageText>drop-off</Col>. Times are tough for everyone out there - why should the gangs get a free ride?";
            contentsSimple[1115] = "TaskObjectives_JG_BCL2_Bur1_Stage03_OwnerBrief=It turns out that the customers who would normally buy those mink driving gloves are staying home right now. <Col: StageText>Break into this <Col: StageText>buildings </Col>and steal something for home entertaining.";
            contentsSimple[1116] = "TaskObjectives_JG_BCL2_Bur1_Stage03_DispatchBrief=More civilian properties are under attack from local burglars. <Col: StageText>Defend this building </Col>from them, the Criminals seem to be picking out only high value items.";
            contentsSimple[1117] = "TaskObjectives_JG_BCL2_Bur1_Stage04_OwnerBrief=Okay, I didn't actually mean a box of gold plated sex toys, but whatever. <Col: StageText>Deliver </Col>the stolen <Col: StageText>goods </Col>to our specialist <Col: StageText>drop-off </Col>location.";
            contentsSimple[1118] = "TaskObjectives_JG_BCL2_Bur1_Stage04_DispatchBrief=<Col: StageText>Intercept </Col>the <Col: StageText>goods </Col>before those burglars get them to their <Col: StageText>drop-off</Col>. They must be planning something big with all the profit from this job.";
            contentsSimple[1119] = "TaskObjectives_JG_BCL2_Bur1_Stage05_OwnerBrief=A snitch has informed us of a rare guitar that's been spotted across town. <Col:StageText>Capture the item</Col> before the enforcers do, it should be worth a lot.";
            contentsSimple[1120] = "TaskObjectives_JG_BCL2_Bur1_Stage05_DispatchBrief=The burglars are planning on stealing a rare guitar. Get to it first and <Col:StageText>retreive the instrument</Col> to keep it out of criminal hands.";
            contentsSimple[1121] = "TaskObjectives_JG_BCL2_Bur1_Stage06_OwnerBrief=Art's also a commodity and a piece has turned up nearby. <Col: StageText>Race to </Col>this second <Col: StageText>item</Col> and <Col: StageText>claim it</Col>. I've been told it's an original 'Shift' print.";
            contentsSimple[1122] = "TaskObjectives_JG_BCL2_Bur1_Stage06_DispatchBrief=There's a piece of art nearby that the burglars are targeting. <Col: StageText>Race to </Col>the painting and <Col: StageText>claim it</Col>.";
            contentsSimple[1123] = "TaskObjectives_JG_BCL2_Bur1_Stage07_OwnerBrief=High-End Electronics sell for a fuck-ton. <Col: StageText>Get to </Col>this third <Col: StageText>item </Col>and <Col: StageText>claim it </Col>before your opponents do.";
            contentsSimple[1124] = "TaskObjectives_JG_BCL2_Bur1_Stage07_DispatchBrief=They're going for electronics now. <Col: StageText>Race </Col>the burglars to the <Col: StageText>gear </Col>and <Col: StageText>claim it </Col>for the people of San Paro.";
            contentsSimple[1125] = "TaskObjectives_JG_BCL2_Bur1_Stage08_OwnerBrief=There's a back-alley hospital that have foolishly left some organs unguarded. <Col: StageText>Race to </Col>the penultimate <Col: StageText>items</Col> and grab them before anyone else does. They'll sell for a fortune.";
            contentsSimple[1126] = "TaskObjectives_JG_BCL2_Bur1_Stage08_DispatchBrief=Shit, they've found an organ donor facility. <Col: StageText>Race </Col>to this <Col: StageText>location </Col>and <Col: StageText>claim the organs</Col>.";
            contentsSimple[1127] = "TaskObjectives_JG_BCL2_Bur1_Stage09_OwnerBrief=Screw this legal shit, there's a stash of drugs at the next place. <Col: StageText>Get to </Col>the <Col: StageText>drugs</Col> and <Col: StageText>claim them </Col>too.";
            contentsSimple[1128] = "TaskObjectives_JG_BCL2_Bur1_Stage09_DispatchBrief=Last one: The criminals have found a stash of narcotics that were hidden by a rival gang. Get there before them and <Col: StageText>grab</Col> the drugs before the crims get their hands on them.";
            contentsSimple[1129] = "TaskObjectives_JG_BCL2_Bur1_Stage10_OwnerBrief=Ok, collect the items and <Col:StageText>drop off the majority</Col> at our shop to sell.  We should make a lot of money off this lot.";
            contentsSimple[1130] = "TaskObjectives_JG_BCL2_Bur1_Stage10_DispatchBrief=Ok, now <Col:StageText>grab the items</Col> and <Col>drop them off</Col> at our facility. You'll need at least 3 of them.";
            contentsSimple[1131] = "TaskObjectives_JG_BCL2_Bur1_Stage10_Opp_OwnerBrief=Ok, collect the items and <Col:StageText>drop off the majority</Col> at our shop to sell.  We should make a lot of money off this lot.";
            contentsSimple[1132] = "TaskObjectives_JG_BCL2_Bur1_Stage10_Opp_DispatchBrief=Ok, now <Col:StageText>grab the items</Col> and <Col>drop them off</Col> at our facility. You'll need at least 3 of them.";
            contentsSimple[1133] = "TaskObjectives_JG_BCL2_Exemplar_Stage01_OwnerBrief=We've offered a generous protection deal to the locals, but not everyone has seen sense. <Col: StageText>Burgle </Col>these <Col: StageText>buildings </Col>and take a few things - just to highlight the value of our proposition.";
            contentsSimple[1134] = "TaskObjectives_JG_BCL2_Exemplar_Stage01_DispatchBrief=We've been asked to help protect this neighborhood. <Col: StageText>Defend </Col>these <Col: StageText>buildings</Col>. They're getting broken into on a regular basis.";
            contentsSimple[1135] = "TaskObjectives_JG_BCL2_Exemplar_Stage02_OwnerBrief=That's good, hopefully they'll see the value of our insurance policies. <Col: StageText>Deliver </Col>the <Col: StageText>loot </Col>to the <Col: StageText>drop-off</Col>. May as well sell it and get some cash.";
            contentsSimple[1136] = "TaskObjectives_JG_BCL2_Exemplar_Stage02_DispatchBrief=Burglars stole cash from several buildings that fall under our jurisdiction. <Col: StageText>Stop </Col>the <Col: StageText>stolen property </Col>from ever <Col: StageText>reaching </Col>the Criminals' <Col: StageText>drop-off</Col>.";
            contentsSimple[1137] = "TaskObjectives_JG_BCL2_Exemplar_Stage03_OwnerBrief=Two local businesses have consistently ignored our messages - so we need a bigger, more personal, persuasive gesture. <Col: StageText>Burgle </Col>their <Col: StageText>buildings </Col>and steal any cash or stock that you can carry.";
            contentsSimple[1138] = "TaskObjectives_JG_BCL2_Exemplar_Stage03_DispatchBrief=Those burglars are getting cocky, hitting the businesses of a couple of our major clients. <Col: StageText>Defend </Col>these <Col: StageText>buildings </Col>and ensure that nothing is stolen.";
            contentsSimple[1139] = "TaskObjectives_JG_BCL2_Exemplar_Stage04_OwnerBrief=Nicely done. The business owners probably expected the Enforcers to protect them, but this is our city, not theirs. <Col: StageText>Deliver </Col>the <Col: StageText>stock </Col>to the <Col: StageText>drop-off</Col>.";
            contentsSimple[1140] = "TaskObjectives_JG_BCL2_Exemplar_Stage04_DispatchBrief=The burglars managed to grab two large <Col: StageText>bags </Col>of cash and stock from those businesses. If you can <Col: StageText>stop </Col>them <Col: StageText>reaching </Col>the <Col: StageText>drop-off </Col>we can save face with our client.";
            contentsSimple[1141] = "TaskObjectives_JG_BCL2_Exemplar_Stage05_OwnerBrief=Our protection racket has proven lucrative and now everyone wants in on the action. <Col: StageText>Capture </Col>these <Col: StageText>locations </Col>to hold onto this neighborhood. Protect the protection racket.";
            contentsSimple[1142] = "TaskObjectives_JG_BCL2_Exemplar_Stage05_DispatchBrief=The burglars have threatened our income and our reputation. <Col: StageText>Capture </Col>these <Col: StageText>locations </Col>and prove that the Enforcers won't tolerate criminal behavior.";
            contentsSimple[1143] = "TaskObjectives_JG_BCL2_Gra1_Stage01_OwnerBrief=The Enforcers have been planning to hold a local fun day to draw in new recruits and win over the brain-dead citizens. Use <Col: StageText>graffiti </Col>to mark up this <Col: StageText>location </Col>and remind where the local power lies.";
            contentsSimple[1144] = "TaskObjectives_JG_BCL2_Gra1_Stage01_DispatchBrief=We have a community open day planned for this area. As usual, the gangs want to spoil it. <Col: StageText>Protect </Col>this <Col: StageText>location </Col>from the gang's graffiti artists.";
            contentsSimple[1145] = "TaskObjectives_JG_BCL2_Gra1_Stage02_OwnerBrief=<Col: StageText>Tag </Col>some more <Col: StageText>locations </Col>and then <Col: StageText>hold </Col>them; make sure that the locals know who's in charge around here. Clue; it ain't the 'Forcers.";
            contentsSimple[1146] = "TaskObjectives_JG_BCL2_Gra1_Stage02_DispatchBrief=Graffiti artists are also targeting these <Col: StageText>locations</Col>. <Col: StageText>Defend </Col>those locations and keep this neighborhood clean and safe for our forthcoming community open day.";
            contentsSimple[1147] = "TaskObjectives_JG_BCL2_Gra1_Stage03_OwnerBrief=Let the Enforcers have their stupid cutesy family fun day, just so long as our graffiti looms over it. <Col: StageText>Tag </Col>these <Col: StageText>locations </Col>and then <Col: StageText>hold </Col>them.";
            contentsSimple[1148] = "TaskObjectives_JG_BCL2_Gra1_Stage03_DispatchBrief=The local gangs are keen to make their presence known during our community open day. <Col: StageText>Protect </Col>these last <Col: StageText>locations </Col>from their ugly graffiti. Let's keep things clean.";
            contentsSimple[1149] = "TaskObjectives_JG_BCL2_Gra1_Stage04_OwnerBrief=You done the tagging, now comes the fragging. You've not be invited to the family fun day it seems. <Col: StageText>Take out </Col>these disrespectful <Col: StageText>opponents </Col>and make it clear that you don't need an invite.";
            contentsSimple[1150] = "TaskObjectives_JG_BCL2_Gra1_Stage04_DispatchBrief=You just know the gangs are going to ruin our community event if they get a chance. <Col: StageText>Eliminate </Col>the <Col: StageText>gang </Col>members, or the mofos will be back to burn down the coconut shy.";
            contentsSimple[1151] = "TaskObjectives_JG_BCL2_VLo1_Stage01_OwnerBrief=Our fence, Wes, is in the market for the Onset CDX-G1: an expensive media center car stereo with all the bells and whistles. Head to this <Col: StageText>vehicle </Col>and <Col: StageText>loot </Col>it: we're pretty sure it's got one.";
            contentsSimple[1152] = "TaskObjectives_JG_BCL2_VLo1_Stage01_DispatchBrief=The new multimedia car stereo from Onset is on sale today. It's already sold out, so everyone's after them. Our spotters saw thieves casing a nearby vehicle for the stereo. <Col: StageText>Protect </Col>the <Col: StageText>car</Col>.";
            contentsSimple[1153] = "TaskObjectives_JG_BCL2_VLo1_Stage02_OwnerBrief=Take the stolen Onset <Col: StageText> car stereo </Col>to the <Col: StageText>drop-off </Col>point for Wes to collect. Those things cost a fortune, and they're rare as hell. Should net us a pretty penny.";
            contentsSimple[1154] = "TaskObjectives_JG_BCL2_VLo1_Stage02_DispatchBrief=Thieves took an Onset CDX-G1! <Col: StageText>Stop </Col>them taking the stolen <Col: StageText>stereo </Col>to their <Col: StageText>drop-off</Col>. People go crazy for anything these days... I once saw a woman stab someone over the last Kiki doll in the store.";
            contentsSimple[1155] = "TaskObjectives_JG_BCL2_VLo1_Stage03_OwnerBrief=A couple of our spotters hit the streets and found some more Onset stereos. <Col: StageText>Loot </Col>these <Col: StageText>vehicles </Col>and get those Onset CDX-G1s - Wes is offering us a good price for them.";
            contentsSimple[1156] = "TaskObjectives_JG_BCL2_VLo1_Stage03_DispatchBrief=A patrol identified two more vehicles being cased by thieves. <Col: StageText>Guard </Col>the <Col: StageText>cars </Col>. Those thieves're trying to make a quick buck off desperate consumers willing to buy stolen Onset stereos.";
            contentsSimple[1157] = "TaskObjectives_JG_BCL2_VLo1_Stage04_OwnerBrief=Nice, a couple more Onset stereos - take the <Col: StageText>stereos </Col>to the <Col: StageText>drop-off </Col>. Y'know Wes is our fence, but did you know his name is actually Wesley D. Fence? Talk about being born to do a job.";
            contentsSimple[1158] = "TaskObjectives_JG_BCL2_VLo1_Stage04_DispatchBrief=The thieves got what they were after. <Col: StageText>Stop </Col>the stolen <Col: StageText>stereos </Col>reaching the <Col: StageText>drop-off</Col>. Some cities have to deal with riots over hunger or human rights... In San Paro? Car stereos. Seriously.";
            contentsSimple[1159] = "TaskObjectives_JG_BCL2_VLo1_Stage05_OwnerBrief=Wes left our <Col: StageText>payment here</Col>. <Col: StageText>Recover</Col> it and <Col: StageText>keep it</Col> away from our enemies.";
            contentsSimple[1160] = "TaskObjectives_JG_BCL2_VLo1_Stage05_DispatchBrief=We need to recover those stolen Onset stereos. Some are hidden nearby: <Col: StageText>take </Col>and then <Col: StageText>keep </Col>the <Col: StageText>bag </Col>containing the stereos away from the thieves, and any crazed consumers.";
            contentsSimple[1161] = "TaskObjectives_JG_BCL2_VLo2_Stage01_OwnerBrief=You heard of the Goody Gundrops? Yeah? Well, we've got collectors willing to pay for the latest Gundrops figurines. We spotted people buying some from a shop: <Col: StageText>loot </Col>their <Col: StageText>cars </Col>for the models.";
            contentsSimple[1162] = "TaskObjectives_JG_BCL2_VLo2_Stage01_DispatchBrief=New Goody Gundrops merchandise has hit the streets. They're so popular that we had a full-blown riot last time - so be careful. <Col: StageText>Defend </Col>the <Col: StageText>cars </Col>of those buying Gundrops merchandise.";
            contentsSimple[1163] = "TaskObjectives_JG_BCL2_VLo2_Stage02_OwnerBrief=You heard of the Goody Gundrops? Yeah? Well, we've got collectors willing to pay for the latest Gundrops figurines. We spotted people buying some from a shop: <Col: StageText>loot </Col>their <Col: StageText>cars </Col>for the models.";
            contentsSimple[1164] = "TaskObjectives_JG_BCL2_VLo2_Stage02_DispatchBrief=New Goody Gundrops merchandise has hit the streets. They're so popular that we had a full-blown riot last time - so be careful. <Col: StageText>Defend </Col>the <Col: StageText>cars </Col>of those buying Gundrops merchandise.";
            contentsSimple[1165] = "TaskObjectives_JG_BCL2_VLo2_Stage03_OwnerBrief=Ok, <Col: StageText>Deliver </Col>the first of those <Col: StageText>figure packs </Col>to this collector's <Col: StageText>drop-off</Col>. Funny how popular the Gundrops are with all ages; a friend of mine has a Peppermint Pistol handbag.";
            contentsSimple[1166] = "TaskObjectives_JG_BCL2_VLo2_Stage03_DispatchBrief=Rabid Gundrops fans have stolen figurines. <Col: StageText>Intercept </Col>the <Col: StageText>figure packs </Col>before they can be hidden at the <Col: StageText>drop-off</Col>. I promised my cousin a Peppermint Pistol doll... hope there's some left.";
            contentsSimple[1167] = "TaskObjectives_JG_BCL2_VLo2_Stage04_OwnerBrief=Our second <Col: StageText>drop-off </Col>isn't far from you. <Col: StageText>Deliver </Col>the second <Col: StageText>figure pack </Col>there. This collector wants the Maple Magnum figure - good choice!";
            contentsSimple[1168] = "TaskObjectives_JG_BCL2_VLo2_Stage04_DispatchBrief=The fans are taking stolen Gundrops figurines to another <Col: StageText>drop-off</Col>. <Col: StageText>Intercept </Col>the <Col: StageText>figure packs</Col>. Think this is bad? You missed the fight that broke out over a Strawberry Shotgun pencil case.";
            contentsSimple[1169] = "TaskObjectives_JG_BCL2_VLo2_Stage05_OwnerBrief=Ok, last one! Hurry and <Col: StageText>deliver </Col>the <Col: StageText>figure pack </Col>to the final collector's <Col: StageText>drop-off</Col>. That pack has Cookie Cannon and Raspberry Rifle in it, right? Whatever. They've already paid us, either way.";
            contentsSimple[1170] = "TaskObjectives_JG_BCL2_VLo2_Stage05_DispatchBrief=There's only one <Col: StageText>figure pack </Col>left! The Gundrops looters are taking it to a <Col: StageText>drop-off</Col>. <Col: StageText>Stop </Col>them. Hey - is that a Cookie Cannon pack they've got? That's my niece's favorite Gundrops character!";
            contentsSimple[1171] = "TaskObjectives_JG_BCL2_VLo2_Stage06_OwnerBrief=Shit. It seems we've attracted some attention: you've got desperate Gundrops fans after you. They must think you've still got the models. <Col: StageText>Kill </Col>some <Col: StageText>fans </Col>to scare off the rest.";
            contentsSimple[1172] = "TaskObjectives_JG_BCL2_VLo2_Stage06_DispatchBrief=There're people everywhere after Gundrops items; we need to bring things under control. <Col: StageText>Take out </Col>any <Col: StageText>looters </Col>you find. ...If you find a Peppermint Pistol doll, I'll - ahem - take it into custody.";
            contentsSimple[1173] = "TaskObjectives_JG_BCL2_VTh1_Stage01_OwnerBrief=We run a few gambling establishments, and one of the more profitable ones is our Mah-jong parlor. We need to collect gambling debts for it, so go <Col: StageText>break into </Col>this <Col: StageText>car </Col>and bring it to our <Col: StageText>drop-off</Col>.";
            contentsSimple[1174] = "TaskObjectives_JG_BCL2_VTh1_Stage01_DispatchBrief=A Mah-jong gambling ring is trying to 'collect' from customers who've run up a debt with them. <Col: StageText>Defend </Col>the targeted <Col: StageText>vehicle </Col>from the ring's thugs, and <Col: StageText>prevent </Col>its delivery to the <Col: StageText>drop-off</Col>.";
            contentsSimple[1175] = "TaskObjectives_JG_BCL2_VTh1_Stage02_OwnerBrief=We run a few gambling establishments, and one of the more profitable ones is our Mah-jong parlor. We need to collect gambling debts for it, so go <Col: StageText>break into </Col>this <Col: StageText>car </Col>and bring it to our <Col: StageText>drop-off</Col>.";
            contentsSimple[1176] = "TaskObjectives_JG_BCL2_VTh1_Stage02_DispatchBrief=A Mah-jong gambling ring is trying to 'collect' from customers who've run up a debt with them. <Col: StageText>Defend </Col>the targeted <Col: StageText>vehicle </Col>from the ring's thugs, and <Col: StageText>prevent </Col>its delivery to the <Col: StageText>drop-off</Col>.";
            contentsSimple[1177] = "TaskObjectives_JG_BCL2_VTh1_Stage03_OwnerBrief=A couple of gamblers went into hiding after we used their gambling debts to pump them for money. <Col: StageText>Break into </Col>their <Col: StageText>cars </Col>and take them to the <Col: StageText>drop-off</Col>. We'll recoup what we can by selling 'em.";
            contentsSimple[1178] = "TaskObjectives_JG_BCL2_VTh1_Stage03_DispatchBrief=We've been after this Mah-jong gambling ring for months. They've got their thugs out to 'collect' from two more customers in debt. <Col: StageText>Defend </Col>their <Col: StageText>cars</Col>, and <Col: StageText>prevent </Col>their delivery to the <Col: StageText>drop-off</Col>.";
            contentsSimple[1179] = "TaskObjectives_JG_BCL2_VTh1_Stage04_OwnerBrief=A couple of gamblers went into hiding after we used their gambling debts to pump them for money. <Col: StageText>Break into </Col>their <Col: StageText>cars </Col>and take them to the <Col: StageText>drop-off</Col>. We'll recoup what we can by selling 'em.";
            contentsSimple[1180] = "TaskObjectives_JG_BCL2_VTh1_Stage04_DispatchBrief=We've been after this Mah-jong gambling ring for months. They've got their thugs out to 'collect' from two more customers in debt. <Col: StageText>Defend </Col>their <Col: StageText>cars</Col>, and <Col: StageText>prevent </Col>their delivery to the <Col: StageText>drop-off</Col>.";
            contentsSimple[1181] = "TaskObjectives_JG_BCL2_VTh1_Stage05_OwnerBrief=Looks like our Mah-jong losers were holding out; we've found a money stash they were hiding. Go grab the <Col: StageText>cash </Col>and deliver it to our <Col: StageText>drop-off</Col>. Make sure those losers don't escape with it.";
            contentsSimple[1182] = "TaskObjectives_JG_BCL2_VTh1_Stage05_DispatchBrief=One of the gambling ring's debtors left a payment at an arranged dead drop. We could use it as evidence. <Col: StageText>Recover </Col>the <Col: StageText>payment </Col>and <Col: StageText>deliver </Col>it to our <Col: StageText>drop-off</Col>. Don't let the ring's thugs get it!";
            contentsSimple[1183] = "TaskObjectives_JG_BCL2_VTh1_Stage06_OwnerBrief=Looks like our Mah-jong losers were holding out; we've found a money stash they were hiding. Go grab the <Col: StageText>cash </Col>and deliver it to our <Col: StageText>drop-off</Col>. Make sure those losers don't escape with it.";
            contentsSimple[1184] = "TaskObjectives_JG_BCL2_VTh1_Stage06_DispatchBrief=One of the gambling ring's debtors left a payment at an arranged dead drop. We could use it as evidence. <Col: StageText>Recover </Col>the <Col: StageText>payment </Col>and <Col: StageText>deliver </Col>it to our <Col: StageText>drop-off</Col>. Don't let the ring's thugs get it!";
            contentsSimple[1185] = "TaskObjectives_JG_BCL2_VTh1_Stage06_Opp_OwnerBrief=Looks like our Mah-jong losers were holding out; we've found a money stash they were hiding. Go grab the <Col: StageText>cash </Col>and deliver it to our <Col: StageText>drop-off</Col>. Make sure those losers don't escape with it.";
            contentsSimple[1186] = "TaskObjectives_JG_BCL2_VTh1_Stage06_Opp_DispatchBrief=One of the gambling ring's debtors left a payment at an arranged dead drop. We could use it as evidence. <Col: StageText>Recover </Col>the <Col: StageText>payment </Col>and <Col: StageText>deliver </Col>it to our <Col: StageText>drop-off</Col>. Don't let the ring's thugs get it!";
            contentsSimple[1187] = "TaskObjectives_JG_BCL3_Ars1_Stage01_OwnerBrief=A business is refusing to pay us protection, convinced it's safe behind its state-of-the-art security systems. Three <Col: StageText>transformers </Col>control the power to its area. <Col: StageText>Sabotage </Col>the first one.";
            contentsSimple[1188] = "TaskObjectives_JG_BCL3_Ars1_Stage01_DispatchBrief=A local gang is trying to disable our security and fire systems so they can attack our property. Three <Col: StageText>transformers </Col>control the power to this entire area. <Col: StageText>Defend </Col>the first one from saboteurs.";
            contentsSimple[1189] = "TaskObjectives_JG_BCL3_Ars1_Stage02_OwnerBrief=The second power <Col: StageText>transformer </Col>is nearby. <Col: StageText>Sabotage </Col>this one as well. Once we've disabled all three transformers the alarm systems in the buildings we targeted will go offline.";
            contentsSimple[1190] = "TaskObjectives_JG_BCL3_Ars1_Stage02_DispatchBrief=The second <Col: StageText>transformer </Col>is under attack from a local gang. <Col: StageText>Defend </Col>it from any attempt at sabotage - if all three transformers are disabled our properties will be vulnerable.";
            contentsSimple[1191] = "TaskObjectives_JG_BCL3_Ars1_Stage03_OwnerBrief=One power <Col: StageText>transformer </Col>remains. <Col: StageText>Sabotage </Col>this one and the alarms on our target buildings will be deactivated, clearing the way for our attack.";
            contentsSimple[1192] = "TaskObjectives_JG_BCL3_Ars1_Stage03_DispatchBrief=One <Col: StageText>transformer</Col> remains. <Col: StageText>Protect </Col>it from sabotage or our security and fire systems will be de-activated and the Criminals will attack our properties.";
            contentsSimple[1193] = "TaskObjectives_JG_BCL3_Ars1_Stage04_OwnerBrief=The alarms are down and the targets are vulnerable - <Col: StageText>destroy these buildings </Col>by setting them alight. This business is about to learn that the only reliable security is OUR security.";
            contentsSimple[1194] = "TaskObjectives_JG_BCL3_Ars1_Stage04_DispatchBrief=With the power down our alarms and sprinklers have been shut off - <Col: StageText>defend </Col>our <Col: StageText>buildings </Col>from the attacking arsonists.";
            contentsSimple[1195] = "TaskObjectives_JG_BCL3_Ars1_Stage05_OwnerBrief=Now we just need to drive the message home - <Col: StageText>wipe out </Col>any <Col: StageText>opponents </Col>who remain in the area. Make sure ALL the local businesses know their high-tech security don't mean shit.";
            contentsSimple[1196] = "TaskObjectives_JG_BCL3_Ars1_Stage05_DispatchBrief=Damn, they destroyed our buildings! We can't let this attack go unpunished. <Col: StageText>Wipe out </Col>any <Col: StageText>arsonists </Col>who remain in the area.";
            contentsSimple[1197] = "TaskObjectives_JG_BCL3_Exemplar_Stage01_OwnerBrief=The Enforcers have taken fraudulent account data from our friends at Wilk-Pastor Media. <Col: StageText>Search </Col>these Enforcer <Col: StageText>buildings </Col>until you find the <Col: StageText>two files </Col>and recover them.";
            contentsSimple[1198] = "TaskObjectives_JG_BCL3_Exemplar_Stage01_DispatchBrief=As part of a clampdown on white-collar crime, we seized two files from Wilk-Pastor Media. Now Criminals have been paid to recover them. <Col: StageText>Defend </Col>our <Col: StageText>buildings </Col>from being burgled.";
            contentsSimple[1199] = "TaskObjectives_JG_BCL3_Exemplar_Stage02_OwnerBrief=The Enforcers have taken fraudulent account data from our friends at Wilk-Pastor Media. <Col: StageText>Search </Col>these Enforcer <Col: StageText>buildings </Col>until you find the <Col: StageText>two files </Col>and recover them.";
            contentsSimple[1200] = "TaskObjectives_JG_BCL3_Exemplar_Stage02_DispatchBrief=As part of a clampdown on white-collar crime, we seized two files from Wilk-Pastor Media. Now Criminals have been paid to recover them. <Col: StageText>Defend </Col>our <Col: StageText>buildings </Col>from being burgled.";
            contentsSimple[1201] = "TaskObjectives_JG_BCL3_Exemplar_Stage03_OwnerBrief=Good work. <Col: StageText>Deliver </Col>those <Col: StageText>files </Col>to our <Col: StageText>drop-off </Col>and we'll get them back to Wilk-Pastor. Their accountants need those false accounts and bank transfer numbers.";
            contentsSimple[1202] = "TaskObjectives_JG_BCL3_Exemplar_Stage03_DispatchBrief=The <Col: StageText>files </Col>those burglars took contain key evidence in our case against several executives in Wilk-Pastor Media. <Col: StageText>Stop </Col>the Criminals before they get the laptops to their <Col: StageText>drop-off</Col>.";
            contentsSimple[1203] = "TaskObjectives_JG_BCL3_Exemplar_Stage04_OwnerBrief=The Enforcers also managed to take a <Col: StageText>safe </Col>which held several highly confidential files relating to executive off-shore slush funds. <Col: StageText>Search </Col>these <Col: StageText>buildings </Col>until you've located that safe.";
            contentsSimple[1204] = "TaskObjectives_JG_BCL3_Exemplar_Stage04_DispatchBrief=The remainder of our evidence against the Wilk-Pastor execs is still locked up in the <Col: StageText>safe </Col>we ripped out of the company boardroom wall. <Col: StageText>Protect </Col>our <Col: StageText>building </Col>and see that the safe stays secure.";
            contentsSimple[1205] = "TaskObjectives_JG_BCL3_Exemplar_Stage05_OwnerBrief=The Enforcers also managed to take a <Col: StageText>safe </Col>which held several highly confidential files relating to executive off-shore slush funds. <Col: StageText>Search </Col>these <Col: StageText>buildings </Col>until you've located that safe.";
            contentsSimple[1206] = "TaskObjectives_JG_BCL3_Exemplar_Stage05_DispatchBrief=The remainder of our evidence against the Wilk-Pastor execs is still locked up in the <Col: StageText>safe </Col>we ripped out of the company boardroom wall. <Col: StageText>Protect </Col>our <Col: StageText>building </Col>and see that the safe stays secure.";
            contentsSimple[1207] = "TaskObjectives_JG_BCL3_Exemplar_Stage06_OwnerBrief=All right. <Col: StageText>Deliver </Col>that <Col: StageText>safe </Col>back to our <Col: StageText>drop-off</Col>. It doesn't look like the Enforcers have opened it yet, so the WP execs will still be able transfer their money out of those 'ghost' accounts.";
            contentsSimple[1208] = "TaskObjectives_JG_BCL3_Exemplar_Stage06_DispatchBrief=Our case against those Wilk-Pastor execs rests on the accounts held in that <Col: StageText>safe</Col>. <Col: StageText>Stop </Col>the Criminals before they can get the safe to their <Col: StageText>drop-off</Col>.";
            contentsSimple[1209] = "TaskObjectives_JG_BCL3_Exemplar_Stage07_OwnerBrief=As agreed, an exec from Wilk-Pastor left our payment of unmarked bills in a <Col: StageText>briefcase</Col>. <Col: StageText>Recover </Col>the case and <Col: StageText>keep it </Col>away from any Enforcers who get in your way.";
            contentsSimple[1210] = "TaskObjectives_JG_BCL3_Exemplar_Stage07_DispatchBrief=Wilk-Pastor left payment for the Criminals in a <Col: StageText>briefcase</Col>. <Col: StageText>Recover </Col>that case and <Col: StageText>hold </Col>it away from the Criminals. It may be the last chance we get to link the WP execs to the gang.";
            contentsSimple[1211] = "TaskObjectives_JG_BCL4_Ars1_Stage01_OwnerBrief=The Enforcers seem whiter-than-white, but their allies are anything but: Valentine Foods have used toxic additives in their products for years to cut costs. <Col: StageText>Sabotage </Col>the <Col: StageText>power </Col>to their security.";
            contentsSimple[1212] = "TaskObjectives_JG_BCL4_Ars1_Stage01_DispatchBrief=Corporate spies have been hired to find the ingredients used in Mr. Clock's Corn Chips, and we need to stop them. <Col: StageText>Defend </Col>these <Col: StageText>electrical boxes </Col>which power our client's security grid.";
            contentsSimple[1213] = "TaskObjectives_JG_BCL4_Ars1_Stage02_OwnerBrief=The Enforcers seem whiter-than-white, but their allies are anything but: Valentine Foods have used toxic additives in their products for years to cut costs. <Col: StageText>Sabotage </Col>the <Col: StageText>power </Col>to their security.";
            contentsSimple[1214] = "TaskObjectives_JG_BCL4_Ars1_Stage02_DispatchBrief=Corporate spies have been hired to find the ingredients used in Mr. Clock's Corn Chips, and we need to stop them. <Col: StageText>Defend </Col>these <Col: StageText>electrical boxes </Col>which power our client's security grid.";
            contentsSimple[1215] = "TaskObjectives_JG_BCL4_Ars1_Stage03_OwnerBrief=Now you've cut the power to Valentine Foods' security, we've got a chance to expose them and discredit the Enforcers. <Col: StageText>Photograph </Col>the <Col: StageText>chemicals </Col>that go into Valentine's 'Bio Snacker' products.";
            contentsSimple[1216] = "TaskObjectives_JG_BCL4_Ars1_Stage03_DispatchBrief=The corporate spies are searching for the mildly addictive additives used in Mr. Clock products. <Col: StageText>Protect </Col>these <Col: StageText>additives </Col>from the spies and don't let them steal the information.";
            contentsSimple[1217] = "TaskObjectives_JG_BCL4_Ars1_Stage04_OwnerBrief=Now you've cut the power to Valentine Foods' security, we've got a chance to expose them and discredit the Enforcers. <Col: StageText>Photograph </Col>the <Col: StageText>chemicals </Col>that go into Valentine's 'Bio Snacker' products.";
            contentsSimple[1218] = "TaskObjectives_JG_BCL4_Ars1_Stage04_DispatchBrief=The corporate spies are searching for the mildly addictive additives used in Mr. Clock products. <Col: StageText>Protect </Col>these <Col: StageText>additives </Col>from the spies and don't let them steal the information.";
            contentsSimple[1219] = "TaskObjectives_JG_BCL4_Ars1_Stage05_OwnerBrief=Those photos are enough for the media to question who the Enforcers are really protecting: the people or the companies. For now though, <Col: StageText>burn </Col>those <Col: StageText>chemicals </Col>to keep them out of circulation.";
            contentsSimple[1220] = "TaskObjectives_JG_BCL4_Ars1_Stage05_DispatchBrief=The spies have what they need, but they must have gotten instructions to destroy our client's existing stock of ingredients. <Col: StageText>Protect </Col>these <Col: StageText>supplies </Col>from arson attacks.";
            contentsSimple[1221] = "TaskObjectives_JG_BCL4_Ars1_Stage06_OwnerBrief=Almost done. Now just <Col: StageText>burn </Col>the second batch of those 'Bio Snacker' <Col: StageText>chemicals </Col>to stop them reaching the public. Don't worry, I'm sure that the smoke is harmless... just, y'know. Try not to inhale any.";
            contentsSimple[1222] = "TaskObjectives_JG_BCL4_Ars1_Stage06_DispatchBrief=There's only one <Col: StageText>pallet </Col>of Mr. Clock Corn Chip additives left, so we need to <Col: StageText>defend </Col>it from the spies. Don't let them destroy our client's stockpile.";
            contentsSimple[1223] = "TaskObjectives_JG_BCL4_Ars1_Stage07_OwnerBrief=We've embarrassed the Enforcers, so now they need a high-profile success. That means you. They're coming after your group leader - <Col: StageText>Kill </Col>all <Col: StageText>opposition </Col>and <Col: StageText>ensure </Col>your <Col: StageText>group leader survives</Col>.";
            contentsSimple[1224] = "TaskObjectives_JG_BCL4_Ars1_Stage07_DispatchBrief=To stop those corporate spies, we need to target their ringleader. <Col: StageText>Eliminate </Col>their <Col: StageText>group leader </Col> and they'll think twice before attacking companies we're protecting again. <Col: StageText>Avoid taking casualties</Col>.";
            contentsSimple[1225] = "TaskObjectives_JG_BCL4_Exemplar_Stage01_OwnerBrief=We hired a crew from out of town to hit an armored car for us, but they've kept the haul for themselves. <Col: StageText>Get the gold back from </Col>their <Col: StageText>hideouts </Col>- it's rightfully ours!";
            contentsSimple[1226] = "TaskObjectives_JG_BCL4_Exemplar_Stage01_DispatchBrief=We heard that Criminals are planning a large-scale burglary of several local pawnbrokers all at once. These <Col: StageText>buildings</Col> are the most likely targets. <Col: StageText>Defend</Col> them from the burglars.";
            contentsSimple[1227] = "TaskObjectives_JG_BCL4_Exemplar_Stage02_OwnerBrief=We hired a crew from out of town to hit an armored car for us, but they've kept the haul for themselves. <Col: StageText>Get the gold back from </Col>their <Col: StageText>hideouts </Col>- it's rightfully ours!";
            contentsSimple[1228] = "TaskObjectives_JG_BCL4_Exemplar_Stage02_DispatchBrief=We heard that Criminals are planning a large-scale burglary of several local pawnbrokers all at once. These <Col: StageText>buildings</Col> are the most likely targets. <Col: StageText>Defend</Col> them from the burglars.";
            contentsSimple[1229] = "TaskObjectives_JG_BCL4_Exemplar_Stage03_OwnerBrief=Ain't that shiny. I can almost understand why they'd betray us... Almost. <Col: StageText>Deliver </Col>the <Col: StageText>gold </Col>to our <Col: StageText>drop-off</Col>.";
            contentsSimple[1230] = "TaskObjectives_JG_BCL4_Exemplar_Stage03_DispatchBrief=The Criminals managed to steal a lot of <Col: StageText>gold</Col> from under our noses. Unless you wanna be a laughingstock on N3's evening show, <Col: StageText>stop</Col> the Criminals before they get to their <Col: StageText>drop-off</Col>.";
            contentsSimple[1231] = "TaskObjectives_JG_BCL4_Exemplar_Stage04_OwnerBrief=The bulk of the haul is still with the leaders of the crew we hired. <Col: StageText>Burgle </Col>their <Col: StageText>buildings </Col>and <Col: StageText>take </Col>the <Col: StageText>loot </Col>.";
            contentsSimple[1232] = "TaskObjectives_JG_BCL4_Exemplar_Stage04_DispatchBrief=The perps are targeting more <Col: StageText>buildings</Col> - guess they think our heads are up our asses! <Col: StageText>Defend</Col> those targets and <Col: StageText>retreive</Col> any of the <Col: StageText>loot</Col> they manage to take.";
            contentsSimple[1233] = "TaskObjectives_JG_BCL4_Exemplar_Stage05_OwnerBrief=If I don't get those safes back you can kiss your asses goodbye. <Col:StageText>Drop them off</Col> at the safe house and <Col:StageText>stop the Enforcers</Col> retreiving them.";
            contentsSimple[1234] = "TaskObjectives_JG_BCL4_Exemplar_Stage05_DispatchBrief=Get those safes back and <Col:StageText>Drop them off</Col> at our safehouse. Stop the Criminals from capturing them.";
            contentsSimple[1235] = "TaskObjectives_JG_BCL4_Exemplar_Stage05_Opp_OwnerBrief=If I don't get those safes back you can kiss your asses goodbye. <Col:StageText>Drop them off</Col> at the safe house and <Col:StageText>stop the Enforcers</Col> retreiving them.";
            contentsSimple[1236] = "TaskObjectives_JG_BCL4_Exemplar_Stage05_Opp_DispatchBrief=Get those safes back and <Col:StageText>Drop them off</Col> at our safehouse. Stop the Criminals from capturing them.";
            contentsSimple[1237] = "TaskObjectives_JG_BCL4_Sab1_Stage01_OwnerBrief=A snitch is under 24-hour 'Forcer protection. A little chaos might lure his guards away: we're thinkin' hardcore porn, on three electronic billboards around the city. <Col: StageText>Hack this sat dish </Col>to start with.";
            contentsSimple[1238] = "TaskObjectives_JG_BCL4_Sab1_Stage01_DispatchBrief=Heads up. Something's going on. Suddenly we got Crim crews headed to almost every <Col: StageText>satellite dish </Col>in San Paro, all at once. I smell trouble. You're detailed to <Col: StageText>protect </Col>this one; get going.";
            contentsSimple[1239] = "TaskObjectives_JG_BCL4_Sab1_Stage02_OwnerBrief=Heh heh. Fender-benders galore... <Col: StageText>Hack this satellite </Col>to get us access to the second billboard. Another broadcast of raunchy porn oughtta draw the Enforcer protection away from that snitch's van.";
            contentsSimple[1240] = "TaskObjectives_JG_BCL4_Sab1_Stage02_DispatchBrief=Another Crim crew on the move. <Col: StageText>Protect </Col>this <Col: StageText>satellite dish </Col>from them; it belongs to an advertising company that does electronic billboards. We got rookies trolling Lie-Net for clues...";
            contentsSimple[1241] = "TaskObjectives_JG_BCL4_Sab1_Stage03_OwnerBrief=That snitch has intel on us in his van. From all the honking and laughing I can hear, our porn-billboard distraction is pretty effective. <Col: StageText>Hack one more dish </Col>to tie traffic right the fuck up.";
            contentsSimple[1242] = "TaskObjectives_JG_BCL4_Sab1_Stage03_DispatchBrief=Shit on toast. The hacked billboards are broadcasting hardcore porn at major intersections, causing all sorts of fender-benders and shit. <Col: StageText>Protect </Col>this <Col: StageText>dish </Col>nearby or it'll get even worse.";
            contentsSimple[1243] = "TaskObjectives_JG_BCL4_Sab1_Stage04_OwnerBrief=We're gonna make that snitch regret that betraying us even crossed his fuckin' mind. He hid his intel in bombproof cases somewhere in his van - let's test their craftsmanship. <Col: StageText>Blow up the van</Col>.";
            contentsSimple[1244] = "TaskObjectives_JG_BCL4_Sab1_Stage04_DispatchBrief=Looks like some sort of anti-corporate thing - all the hacked billboards were run by the same business. Lie-Net suggests Crims might target the company's courier <Col: StageText>van </Col>- <Col: StageText>protect </Col>it.";
            contentsSimple[1245] = "TaskObjectives_JG_BCL4_Sab1_Stage05_OwnerBrief=There's a fuck-ton of intel in those cases; that snitch must've been planning to rat us out for months. <Col: StageText>Get the cases to our drop </Col>before Enforcers can retrieve them.";
            contentsSimple[1246] = "TaskObjectives_JG_BCL4_Sab1_Stage05_DispatchBrief=That van contained sensitive marketing plans and private information, kept in secure cases. We need to <Col: StageText>get the cases here </Col>before the Crims get away with them.";
            contentsSimple[1247] = "TaskObjectives_JG_BCL4_Sab1_Stage05_Opp_OwnerBrief=There's a fuck-ton of intel in those cases; that snitch must've been planning to rat us out for months. <Col: StageText>Get the cases to our drop </Col>before Enforcers can retrieve them.";
            contentsSimple[1248] = "TaskObjectives_JG_BCL4_Sab1_Stage05_Opp_DispatchBrief=That van contained sensitive marketing plans and private information, kept in secure cases. We need to <Col: StageText>get the cases here </Col>before the Crims get away with them.";
            contentsSimple[1249] = "TaskObjectives_JG_BCL4_VCa1_Stage01_OwnerBrief=The Enforcers use a city-wide wireless network for their Dispatch.Net. We're going to attack that network but we need <Col: StageText>hardware</Col>. <Col: StageText>Burgle </Col>these <Col: StageText>buildings </Col>and <Col: StageText>deliver </Col>everything to the <Col: StageText>drop-off</Col>.";
            contentsSimple[1250] = "TaskObjectives_JG_BCL4_VCa1_Stage01_DispatchBrief=We've identified a gang of hackers gearing up for a cyber attack. They call themselves Zero Override. <Col: StageText>Protect </Col>these <Col: StageText>buildings </Col>and <Col: StageText>stop </Col>the hackers <Col: StageText>delivering PCs </Col>to their <Col: StageText>drop-off</Col>.";
            contentsSimple[1251] = "TaskObjectives_JG_BCL4_VCa1_Stage02_OwnerBrief=The Enforcers use a city-wide wireless network for their Dispatch.Net. We're going to attack that network but we need <Col: StageText>hardware</Col>. <Col: StageText>Burgle </Col>these <Col: StageText>buildings </Col>and <Col: StageText>deliver </Col>everything to the <Col: StageText>drop-off</Col>.";
            contentsSimple[1252] = "TaskObjectives_JG_BCL4_VCa1_Stage02_DispatchBrief=We've identified a gang of hackers gearing up for a cyber attack. They call themselves Zero Override. <Col: StageText>Protect </Col>these <Col: StageText>buildings </Col>and <Col: StageText>stop </Col>the hackers <Col: StageText>delivering PCs </Col>to their <Col: StageText>drop-off</Col>.";
            contentsSimple[1253] = "TaskObjectives_JG_BCL4_VCa1_Stage03_OwnerBrief=We need more computers for our denial-of-service attack on the Enforcer Dispatch.Net. Luckily, a shipment of TOSHIO server machines that we can use is being transported. <Col: StageText>Steal </Col>that server <Col: StageText>van</Col>.";
            contentsSimple[1254] = "TaskObjectives_JG_BCL4_VCa1_Stage03_DispatchBrief=Those hackers are trying to establish a network and are after even more hardware. We anticipate an attack on this <Col: StageText>van </Col>full of TOSHIO computers. <Col: StageText>Protect </Col>it from any attempted theft.";
            contentsSimple[1255] = "TaskObjectives_JG_BCL4_VCa1_Stage04_OwnerBrief=<Col: StageText>Deliver </Col>the <Col: StageText>computers </Col>in the back of that van to our <Col: StageText>drop-off</Col>. Try and get 'em there in one piece; bullet holes tend to cause a drop in hardware performance.";
            contentsSimple[1256] = "TaskObjectives_JG_BCL4_VCa1_Stage04_DispatchBrief=The Zero Override hackers stole a van full of TOSHIO <Col: StageText>computers </Col>and now they're headed to this <Col: StageText>drop-off</Col>. <Col: StageText>Stop </Col>those computers before the geek collective can put them to use.";
            contentsSimple[1257] = "TaskObjectives_JG_BCL4_VCa1_Stage05_OwnerBrief=We'll need to boost our signal for the DoS attack, and that takes a lot of power. First, <Col: StageText>sabotage </Col>this <Col: StageText>electrical box </Col>and fix it up to serve our needs.";
            contentsSimple[1258] = "TaskObjectives_JG_BCL4_VCa1_Stage05_DispatchBrief=The hackers plan to use those PCs to attack vulnerable networks throughout the city, harvesting them for any personal data they can sell. <Col: StageText>Defend </Col>this <Col: StageText>junction box </Col>and stop them patching in.";
            contentsSimple[1259] = "TaskObjectives_JG_BCL4_VCa1_Stage06_OwnerBrief=All right, that oughtta be enough power. Now <Col: StageText>hack </Col>this <Col: StageText>transmitter </Col>and we'll jack their network. I've got guys all over the city waiting to start crazy heists once the network is down.";
            contentsSimple[1260] = "TaskObjectives_JG_BCL4_VCa1_Stage06_DispatchBrief=The Zero Override hackers are after a number of vulnerable wireless networks with valuable personal data, and they plan to boost their signal using this <Col: StageText>satellite</Col>. <Col: StageText>Defend </Col>that target.";
            contentsSimple[1261] = "TaskObjectives_JG_BCL4_VCa1_Stage07_OwnerBrief=The Enforcement Dispatch.Net has slowed to a crawl. They'll try to shut us down, so we need to hold this location. <Col: StageText>Capture </Col>and <Col: StageText>hold </Col>the <Col: StageText>checkpoint </Col>.";
            contentsSimple[1262] = "TaskObjectives_JG_BCL4_VCa1_Stage07_DispatchBrief=Zero Override may have established their network, but we're going to move in and shut them down. <Col: StageText>Capture </Col>and <Col: StageText>hold </Col>the area while we move into position.";
            contentsSimple[1263] = "TaskObjectives_JG_BCM0_Bur1_B_Stage01_OwnerBrief=One of the starlets from the latest copy of Effigy has been somewhat... indiscreet, and we've been hired to clean up after her. First <Col: StageText>recover</Col> the <Col: StageText>drugs</Col> she left in this <Col: StageText>car</Col>.";
            contentsSimple[1264] = "TaskObjectives_JG_BCM0_Bur1_B_Stage01_DispatchBrief=We have a couple of kilos of narcotics from a recent raid, which could help put some Blood Roses behind bars. <Col: StageText>Protect </Col>our <Col: StageText>vehicle </Col>and see that looters don't take our evidence.";
            contentsSimple[1265] = "TaskObjectives_JG_BCM0_Bur1_B_Stage02_OwnerBrief=There are several photographs of the pop starlet doing something that definitely wouldn't fit with her angelic persona. <Col: StageText>Steal</Col> the photos from the photographer's <Col: StageText>studio</Col>.";
            contentsSimple[1266] = "TaskObjectives_JG_BCM0_Bur1_B_Stage02_DispatchBrief=A DVD of surveillance footage from an anti-narcotics sting operation is in this building, but Blood Roses have been sniffing around. <Col: StageText>Protect </Col>the <Col: StageText>building </Col>from Blood Rose burglars.";
            contentsSimple[1267] = "TaskObjectives_JG_BCM0_Bur1_B_Stage03_OwnerBrief=Now we need you to bring in the <Col: StageText>drugs</Col> and the <Col: StageText>photos</Col> and try to prevent another San Paro scandal. <Col: StageText>Deliver</Col> everything to our <Col: StageText>drop-off</Col>.";
            contentsSimple[1268] = "TaskObjectives_JG_BCM0_Bur1_B_Stage03_DispatchBrief=Damn, we need to get that <Col: StageText>evidence </Col>back. <Col: StageText>Prevent </Col>it from being delivered to the Blood Rose <Col: StageText>hideout</Col>.";
            contentsSimple[1269] = "TaskObjectives_JG_BCM0_Bur1_B_Stage04_OwnerBrief=All right, now we're going to <Col: StageText>burn</Col> the <Col: StageText>office</Col> of the reporter who's been poking his nose into other people's business.";
            contentsSimple[1270] = "TaskObjectives_JG_BCM0_Bur1_B_Stage04_DispatchBrief=Our base of operations for the recent investigation was a building nearby. <Col: StageText>Protect </Col>the <Col: StageText>building </Col>from arsonists sent by the Blood Roses to destroy our case notes.";
            contentsSimple[1271] = "TaskObjectives_JG_BCM0_Bur1_B_Stage05_OwnerBrief=All right, the starlet's agent has left us our payment as agreed. <Col: StageText>Take</Col> the <Col: StageText>money</Col> and <Col: StageText>keep</Col> it safe from any opposition.";
            contentsSimple[1272] = "TaskObjectives_JG_BCM0_Bur1_B_Stage05_DispatchBrief=Shit, there's only one chance left. We need to recover the proceeds from the recent drug dealing. <Col: StageText>Take </Col>that <Col: StageText>money </Col>and <Col: StageText>keep </Col>it from any opposition.";
            contentsSimple[1273] = "TaskObjectives_JG_BCM0_Bur1_G_Stage01_OwnerBrief=A friend of our very own Princess Bonita has been somewhat... indiscreet, and we're on clean-up duty for her. First <Col: StageText>recover</Col> the <Col: StageText>drugs</Col> which she left in this <Col: StageText>car</Col>.";
            contentsSimple[1274] = "TaskObjectives_JG_BCM0_Bur1_G_Stage01_DispatchBrief=We have a couple of kilos of narcotics from a recent raid which could help put some G-Kings behind bars. <Col: StageText>Protect </Col>our <Col: StageText>vehicle </Col>and see that the G-Kings don't take our evidence.";
            contentsSimple[1275] = "TaskObjectives_JG_BCM0_Bur1_G_Stage02_OwnerBrief=There are also several compromising photos of Bonita's good friend on her night out. <Col: StageText>Steal</Col> the photos from the paparazzo's <Col: StageText>office</Col>.";
            contentsSimple[1276] = "TaskObjectives_JG_BCM0_Bur1_G_Stage02_DispatchBrief=A DVD of surveillance footage from the anti-narcotics sting operation is in this building, but G-Kings have been sniffing around. <Col: StageText>Protect </Col>the <Col: StageText>building </Col>from burglars.";
            contentsSimple[1277] = "TaskObjectives_JG_BCM0_Bur1_G_Stage03_OwnerBrief=Now we need you to bring in the <Col: StageText>drugs</Col> and the <Col: StageText>photos</Col> and try to prevent another San Paro scandal. <Col: StageText>Deliver</Col> everything to our <Col: StageText>drop-off</Col>.";
            contentsSimple[1278] = "TaskObjectives_JG_BCM0_Bur1_G_Stage03_DispatchBrief=Damn, we need to get that <Col: StageText>evidence </Col>back. <Col: StageText>Prevent </Col>it from being delivered to the G-King <Col: StageText>lockup</Col>.";
            contentsSimple[1279] = "TaskObjectives_JG_BCM0_Bur1_G_Stage04_OwnerBrief=Hmmm... looks like the Paparazzo made copies, so we're going to have to <Col: StageText>burn</Col> the <Col: StageText>office</Col> of the reporter he sent them to. Make sure there's nothing left but cinders.";
            contentsSimple[1280] = "TaskObjectives_JG_BCM0_Bur1_G_Stage04_DispatchBrief=Our base of operations for the recent investigation was a building nearby. <Col: StageText>Protect </Col>the <Col: StageText>building </Col>from arsonists out to destroy our case notes.";
            contentsSimple[1281] = "TaskObjectives_JG_BCM0_Bur1_G_Stage05_OwnerBrief=All right, Bonita has left us a little payment for our good deeds. <Col: StageText>Take</Col> the <Col: StageText>money</Col> and <Col: StageText>keep</Col> it safe from any opposition.";
            contentsSimple[1282] = "TaskObjectives_JG_BCM0_Bur1_G_Stage05_DispatchBrief=Shit, there's only one chance left. We need to recover the proceeds from the recent drug dealing. <Col: StageText>Take </Col>that <Col: StageText>money </Col>and <Col: StageText>keep </Col>it from any opposition.";
            contentsSimple[1283] = "TaskObjectives_JG_BCM0_Exemplar_B_Stage01_OwnerBrief=A drug dealer has been peddling his shit on our turf, and it's affecting Blood Rose recruitment. <Col: StageText>Burgle</Col> these <Col: StageText>buildings</Col> and get hold of as much of his stash as you can.";
            contentsSimple[1284] = "TaskObjectives_JG_BCM0_Exemplar_B_Stage01_DispatchBrief=Thieves hired by the Blood Roses have been scoping out some properties nearby. <Col: StageText>Defend </Col>these <Col: StageText>buildings </Col>and ensure that none are burgled.";
            contentsSimple[1285] = "TaskObjectives_JG_BCM0_Exemplar_B_Stage02_OwnerBrief=<Col: StageText>Deliver</Col> all of those <Col: StageText>drugs</Col> to the <Col: StageText>drop-off</Col> and we'll leave an anonymous tip for the Enforcers. They can deal with that fool AND his stash.";
            contentsSimple[1286] = "TaskObjectives_JG_BCM0_Exemplar_B_Stage02_DispatchBrief=It seems like we've stumbled into a drug war. <Col: StageText>Prevent </Col>the <Col: StageText>drugs </Col>from being delivered to the Blood Roses' <Col: StageText>drop-off</Col>.";
            contentsSimple[1287] = "TaskObjectives_JG_BCM0_Exemplar_B_Stage03_OwnerBrief=Fucking Enforcers got the wrong idea, they seem to think that shit belongs to us. Oh well - <Col: StageText>kill</Col> your <Col: StageText>opponents</Col>, before they can put you down.";
            contentsSimple[1288] = "TaskObjectives_JG_BCM0_Exemplar_B_Stage03_DispatchBrief=We can't let those dealers get that shit into circulation. <Col: StageText>Eliminate </Col>all <Col: StageText>opponents </Col>in the area. We'll send in the narcotics boys later to clean up the mess.";
            contentsSimple[1289] = "TaskObjectives_JG_BCM0_Exemplar_G_Stage01_OwnerBrief=A drug dealer has been peddling his shit on our turf, and it's affecting G-King recruitment. <Col: StageText>Burgle</Col> these <Col: StageText>buildings</Col> and get hold of as much of his product as you can.";
            contentsSimple[1290] = "TaskObjectives_JG_BCM0_Exemplar_G_Stage01_DispatchBrief=G-King thieves have been scoping out some properties nearby. <Col: StageText>Defend </Col>these <Col: StageText>buildings </Col>and ensure that none are burgled.";
            contentsSimple[1291] = "TaskObjectives_JG_BCM0_Exemplar_G_Stage02_OwnerBrief=<Col: StageText>Deliver</Col> all of those <Col: StageText>drugs</Col> to the <Col: StageText>drop-off</Col> and we'll leave an anonymous tip for the Enforcers. They can deal with that jerk AND his stupid stash.";
            contentsSimple[1292] = "TaskObjectives_JG_BCM0_Exemplar_G_Stage02_DispatchBrief=It seems like we've stumbled into a drug war. <Col: StageText>Prevent </Col>the <Col: StageText>drugs </Col>from being delivered to the G-Kings' <Col: StageText>drop-off</Col>.";
            contentsSimple[1293] = "TaskObjectives_JG_BCM0_Exemplar_G_Stage03_OwnerBrief=Fucking Enforcers got the wrong idea; seem to think that shit belongs to us. Nothing for it now - <Col: StageText>kill</Col> all <Col: StageText>opponents</Col> before they can put you down.";
            contentsSimple[1294] = "TaskObjectives_JG_BCM0_Exemplar_G_Stage03_DispatchBrief=We can't let those dealers get that shit into circulation. <Col: StageText>Eliminate </Col>all <Col: StageText>opponents </Col>in the area. We'll send in the narcotics boys later to clear up the mess.";
            contentsSimple[1295] = "TaskObjectives_JG_BCM1_Exemplar_B_Stage01_OwnerBrief=The Enforcers hired a specialist IT company to hack into our emails. Not hard; half of us still have 'bloodrose' as our password. <Col: StageText>Burgle </Col>the IT <Col: StageText>offices </Col>and steal their network hard drive.";
            contentsSimple[1296] = "TaskObjectives_JG_BCM1_Exemplar_B_Stage01_DispatchBrief=We've hacked into the Blood Rose email system and are downloading all their messages. <Col: StageText>Defend </Col>the PC and hard drive in our <Col: StageText>offices</Col>. The Roses are bound to try and steal it.";
            contentsSimple[1297] = "TaskObjectives_JG_BCM1_Exemplar_B_Stage02_OwnerBrief=Good work. <Col: StageText>Deliver </Col>the <Col: StageText>hard drive </Col>to our <Col: StageText>drop-off</Col> and we'll ensure that it's dealt with delicately. With a giant, ladylike magnet.";
            contentsSimple[1298] = "TaskObjectives_JG_BCM1_Exemplar_B_Stage02_DispatchBrief=The Blood Roses knew exactly what they were after, and they've taken the <Col: StageText>hard drive</Col>. <Col: StageText>Intercept </Col>it before it reaches their <Col: StageText>drop-off</Col>.";
            contentsSimple[1299] = "TaskObjectives_JG_BCM1_Exemplar_B_Stage03_OwnerBrief=Shit. The Enforcers made copies of all the emails. <Col: StageText>Burgle </Col>these <Col: StageText>buildings </Col>and recover the DVD back ups. If you see anyone, CC: them with a bullet from me.";
            contentsSimple[1300] = "TaskObjectives_JG_BCM1_Exemplar_B_Stage03_DispatchBrief=Luckily we backed up all that data onto disk. But the Blood Roses must know that too. <Col: StageText>Defend </Col>these <Col: StageText>buildings </Col>from being broken into by the Roses. We've got the disks stored there.";
            contentsSimple[1301] = "TaskObjectives_JG_BCM1_Exemplar_B_Stage04_OwnerBrief=Excellent. <Col: StageText>Deliver </Col>the <Col: StageText>disks </Col>to our <Col: StageText>drop-off </Col>so Byron can examine them - he might be able to trace how they hacked us.";
            contentsSimple[1302] = "TaskObjectives_JG_BCM1_Exemplar_B_Stage04_DispatchBrief=Damn, we really can't afford to lose that data. <Col: StageText>Intercept </Col>the <Col: StageText>disks </Col>before they are delivered to the <Col: StageText>drop-off</Col>.";
            contentsSimple[1303] = "TaskObjectives_JG_BCM1_Exemplar_B_Stage05_OwnerBrief=The Enforcers are shifting from a cyber attack to a real attack. <Col:StageText>Kill the Enforcers </Col> and <Col: StageText>Survive </Col>long enough for us to wipe these disks and patch our network security issue.";
            contentsSimple[1304] = "TaskObjectives_JG_BCM1_Exemplar_B_Stage05_DispatchBrief=This is too high tech for me. Who wants to read their spam, anyway? <Col: StageText>Eliminate </Col>all <Col: StageText>Blood Roses </Col>in the area. Click 'Delete' on their asses.";
            contentsSimple[1305] = "TaskObjectives_JG_BCM1_Exemplar_G_Stage01_OwnerBrief=The G-King online poker room is going great, mainly 'cause it's fixed. 'Forcers have been monitoring it, though, and downloading all the stats. <Col: StageText>Burgle this building </Col>and take their hard drive.";
            contentsSimple[1306] = "TaskObjectives_JG_BCM1_Exemplar_G_Stage01_DispatchBrief=The G-Kings have been running an online poker site called 'Royal Kings'. We hacked their server and found the games are rigged. <Col: StageText>Defend </Col>our IT <Col: StageText>office </Col>while we download the incriminating data.";
            contentsSimple[1307] = "TaskObjectives_JG_BCM1_Exemplar_G_Stage02_OwnerBrief=Pocket aces, G. <Col: StageText>Deliver </Col>the <Col: StageText>hard drive </Col>to this <Col: StageText>drop-off </Col>and we can get back to fleecing Neo1984 and his buddies out of their cash.";
            contentsSimple[1308] = "TaskObjectives_JG_BCM1_Exemplar_G_Stage02_DispatchBrief=The G-Kings knew exactly what they were after, and they've taken the <Col: StageText>hard drive </Col>out of our machine. <Col: StageText>Intercept </Col>them before they reach the <Col: StageText>drop-off </Col>and wipe it.";
            contentsSimple[1309] = "TaskObjectives_JG_BCM1_Exemplar_G_Stage03_OwnerBrief=The Enforcers have raised the stakes. They made disk back-ups of the G-King poker site stats. <Col: StageText>Burgle </Col>these <Col: StageText>buildings </Col>and recover the disks.";
            contentsSimple[1310] = "TaskObjectives_JG_BCM1_Exemplar_G_Stage03_DispatchBrief=We have an offsite disk back-up of all incriminating data. The G-Kings must know this. <Col: StageText>Defend </Col>these <Col: StageText>buildings </Col>from being burgled, that's where the disks are stored.";
            contentsSimple[1311] = "TaskObjectives_JG_BCM1_Exemplar_G_Stage04_OwnerBrief=<Col: StageText>Deliver </Col>the <Col: StageText>disks </Col>to this <Col: StageText>drop-off </Col>so that we can give them a G-King royal flush! Nice.";
            contentsSimple[1312] = "TaskObjectives_JG_BCM1_Exemplar_G_Stage04_DispatchBrief=Damn, without that data we won't be able to shut down the G-King's rigged poker site. <Col: StageText>Intercept </Col>them and our <Col: StageText>back-up disks </Col>before they're delivered to the <Col: StageText>drop-off</Col>.";
            contentsSimple[1313] = "TaskObjectives_JG_BCM1_Exemplar_G_Stage05_OwnerBrief=I guess 'Forcers don't like cards. Their poker face is for shit, and they ain't bluffing. Show them we've got an ace up our sleeve by <Col: StageText>giving them the bullets</Col>.";
            contentsSimple[1314] = "TaskObjectives_JG_BCM1_Exemplar_G_Stage05_DispatchBrief=I bet the G-Kings rigged this game too. Not that I'm sore loser, but can you go and <Col: StageText>eliminate</Col> all the <Col: StageText>G-Kings </Col>in the area? Rockin'.";
            contentsSimple[1315] = "TaskObjectives_JG_BCM2_Bom1_Stage01_OwnerBrief=Devereaux made a new line of jewelry in honor of her latest conquest. The conquest's husband isn't pleased. Out of sympathy (a.k.a., he paid), we've agreed to <Col: StageText>bomb stores </Col>selling the jewels.";
            contentsSimple[1316] = "TaskObjectives_JG_BCM2_Bom1_Stage01_DispatchBrief=Our client, Devereaux, released a line of jewelry dedicated to her newest lover. The line is being targeted by the lover's unhappy husband. <Col: StageText>Protect </Col>the <Col: StageText>stores </Col>selling the jewels from bombing attacks.";
            contentsSimple[1317] = "TaskObjectives_JG_BCM2_Bom1_Stage02_OwnerBrief=Good thing we dig the jilted husband's cause (he paid us well), 'cause you just blew up a lot of valuable shit. <Col: StageText>Bomb </Col>this <Col: StageText>storehouse </Col>containing the remaining jewels to finish the job.";
            contentsSimple[1318] = "TaskObjectives_JG_BCM2_Bom1_Stage02_DispatchBrief=With the jewelry stores in ashes, the jilted husband's thugs are targeting the remaining jewels Devereaux is keeping in this storehouse. <Col: StageText>Protect </Col>the <Col: StageText>storehouse</Col>.";
            contentsSimple[1319] = "TaskObjectives_JG_BCM2_Bom1_Stage03_OwnerBrief=Good work! Now,<Col: StageText>kill</Col> any 'Forcers your bombs attracted. I don’t know about revenge being sweet, but it sure as hell pays well.";
            contentsSimple[1320] = "TaskObjectives_JG_BCM2_Bom1_Stage03_DispatchBrief=Those bombers destroyed a half a city block just to get back at Devereaux. We can't let them get away! Find the <Col: StageText>bombers</Col>, and <Col: StageText>eliminate </Col>them.";
            contentsSimple[1321] = "TaskObjectives_JG_BCM2_Bur1_Stage01_OwnerBrief=Select players have got early copies of the new Battle Kaiser IV game. It's so popular that people will pay big money for it. <Col: StageText>Break into </Col>these <Col: StageText>properties </Col>and steal the advance copies.";
            contentsSimple[1322] = "TaskObjectives_JG_BCM2_Bur1_Stage01_DispatchBrief=The Battle Kaiser tournament champions have received preview copies of Super Battle Kaiser IV. Desperate gamers plan to steal those copies, so <Col: StageText>protect </Col>the champions' <Col: StageText>properties</Col> from burglary.";
            contentsSimple[1323] = "TaskObjectives_JG_BCM2_Bur1_Stage02_OwnerBrief=All right! Bring those <Col: StageText>advance copies </Col>of Battle Kaiser to our <Col: StageText>drop-off</Col>. We'll sell two - and keep one for ourselves; some of our crew play that series at tournaments.";
            contentsSimple[1324] = "TaskObjectives_JG_BCM2_Bur1_Stage02_DispatchBrief=Those gamers ran off with the Super Battle Kaiser IV preview copies. <Col: StageText>Prevent </Col>delivery of the <Col: StageText>stolen games </Col>to the <Col: StageText>drop-off</Col>. Battle Kaiser III was ok, but fans are hyped for the new stuff in Super.";
            contentsSimple[1325] = "TaskObjectives_JG_BCM2_Bur1_Stage03_OwnerBrief=Rabid Battle Kaiser fans have agreed to pay us an extortionate amount for those two advance copies. They've left the <Col: StageText>money </Col>nearby - <Col: StageText>pick it up </Col>and keep it away from anyone trying to take it.";
            contentsSimple[1326] = "TaskObjectives_JG_BCM2_Bur1_Stage03_DispatchBrief=It looks like some major money changed hands over those Super Battle Kaiser IV preview copies. The dead drop for the <Col: StageText>payment </Col>is nearby - <Col: StageText>grab </Col>it and <Col: StageText>keep it </Col>out of the thieves' hands.";
            contentsSimple[1327] = "TaskObjectives_JG_BCM2_Exemplar_Stage01_OwnerBrief=That asshole Judas Goldwright is back and trading weapons again. Guy lives up to his name: last time we used him, he sold us out. Judas's got weapons in these <Col: StageText>lockups</Col>. <Col: StageText>Burgle </Col>them and take the whole shebang.";
            contentsSimple[1328] = "TaskObjectives_JG_BCM2_Exemplar_Stage01_DispatchBrief=An arms dealer, Judas Goldwright, has been working with us to take down the larger Criminal organizations. Another arms dealer, Harmony, is interfering. <Col: StageText>Defend </Col>Judas's <Col: StageText>buildings</Col>.";
            contentsSimple[1329] = "TaskObjectives_JG_BCM2_Exemplar_Stage02_OwnerBrief=Nice haul; mostly Somatic and Agrotech small arms. <Col: StageText>Deliver </Col>the <Col: StageText>handguns </Col>to the <Col: StageText>drop-off</Col>, but don't get too relaxed. Judas lured us into an Enforcer trap before, so he could be up to something.";
            contentsSimple[1330] = "TaskObjectives_JG_BCM2_Exemplar_Stage02_DispatchBrief=Thieves sent by Harmony have stolen Judas's <Col: StageText>ammo </Col>supplies. They're taking 'em to their <Col: StageText>drop-off</Col>. <Col: StageText>Stop </Col>the thieves before they get there. Judas needs the ammo to convince our target to meet him.";
            contentsSimple[1331] = "TaskObjectives_JG_BCM2_Exemplar_Stage03_OwnerBrief=Judas sold rifles around here for a while. <Col: StageText>Burgle </Col>this <Col: StageText>lockup </Col>and search for the stash. Watch for ambushes. Ever since Judas crossed us, we've stuck to Jericho for hardware.";
            contentsSimple[1332] = "TaskObjectives_JG_BCM2_Exemplar_Stage03_DispatchBrief=Judas has a large stash in another <Col: StageText>building</Col>. <Col: StageText>Defend </Col>it from Harmony's men. Without the weapons, Judas has no chance of luring our target to the meeting we plan to ambush.";
            contentsSimple[1333] = "TaskObjectives_JG_BCM2_Exemplar_Stage04_OwnerBrief=A whole bag full of rifles - this seems too easy, but I won't look a gift horse in the mouth. <Col: StageText>Deliver </Col>the <Col: StageText>bag </Col>to the <Col: StageText>drop-off</Col>. This'll stock up our weapon arsenal nicely 'til the next drop.";
            contentsSimple[1334] = "TaskObjectives_JG_BCM2_Exemplar_Stage04_DispatchBrief=Harmony's men are escaping with a <Col: StageText>bag </Col>containing weapons. <Col: StageText>Stop </Col>them before they get to the <Col: StageText>drop-off</Col>. It's bait for our ambush, and Harmony having more arms to sell isn't in our best interests.";
            contentsSimple[1335] = "TaskObjectives_JG_BCM2_Exemplar_Stage05_OwnerBrief=Fuck, I knew Judas would have something set up. The bag of rifles had a tracking chip sewn into the lining. Enforcers are headed this way. <Col: StageText>Capture </Col>these <Col: StageText>points </Col>to hold off the strike team.";
            contentsSimple[1336] = "TaskObjectives_JG_BCM2_Exemplar_Stage05_DispatchBrief=Shit, Judas is a genius. Even without the weapons Harmony stole, he managed to lure our targets to a meeting. <Col: StageText>Capture </Col>these <Col: StageText>points </Col>to prevent our targets escaping the ambush. We've got 'em now!";
            contentsSimple[1337] = "TaskObjectives_JG_BCM2_Gra1_Stage01_OwnerBrief=A CSA scrub-crew went round and took down our colors. We've repainted some of the walls with their blood, but you better go fix our <Col: StageText>tags </Col>before anyone gets the idea this place is up for grabs.";
            contentsSimple[1338] = "TaskObjectives_JG_BCM2_Gra1_Stage01_DispatchBrief=Blood Roses and G-Kings have been embroiled in a turf war, with us fighting it out in the middle. A patrol spotted a G-King crew <Col: StageText>tagging </Col>up the area with their colors. <Col: StageText>Stop </Col>them.";
            contentsSimple[1339] = "TaskObjectives_JG_BCM2_Gra1_Stage02_OwnerBrief=Cool. <Col: StageText>Tag </Col>the next area those CSA assholes scrubbed clean. They took days stripping off that paint, and now it's about to be tagged up again... ahh, the fickle joys of territory control.";
            contentsSimple[1340] = "TaskObjectives_JG_BCM2_Gra1_Stage02_DispatchBrief=That graffiti crew has moved on to tag another location - <Col: StageText>defend </Col>the <Col: StageText>area</Col>. We might be fighting a war on two fronts, but we can't let them just do whatever they feel like.";
            contentsSimple[1341] = "TaskObjectives_JG_BCM2_Gra1_Stage03_OwnerBrief=One more should do - toss up a <Col: StageText>tag </Col>to mark this turf as ours. Hopefully the Enforcers will get the message this time; as much as I love putting them in body bags, it's not like ammo is free.";
            contentsSimple[1342] = "TaskObjectives_JG_BCM2_Gra1_Stage03_DispatchBrief=Now Gs are <Col: StageText>tagging </Col>this area - <Col: StageText>stop </Col>them putting up gang colors. While I'd love to see the G-Kings and Blood Roses wipe each other out, the civilian casualties from the fights aren't worth it.";
            contentsSimple[1343] = "TaskObjectives_JG_BCM2_Gra1_Stage04_OwnerBrief=Looks like our example wasn't enough: there's another cleaning crew headed your way. <Col: StageText>capture </Col>these <Col: StageText>locations </Col>and <Col: StageText>hold </Col>them, so we can finish putting up tags.";
            contentsSimple[1344] = "TaskObjectives_JG_BCM2_Gra1_Stage04_DispatchBrief=The Blood Rose/G-King battle is headed your way - <Col: StageText>capture </Col>these key <Col: StageText>locations </Col>and keep Crims out of the area. We need to make a stand to minimize the number of civilians caught in the crossfire.";
            contentsSimple[1345] = "TaskObjectives_JG_BCM2_Sab1_Stage01_OwnerBrief=The campaign to re-elect Councillor Davis is gaining ground - too much ground. You need to get some dirt on this pro-CSA candidate. <Col: StageText>Disable </Col>the <Col: StageText>power </Col>to her campaign offices. The power is split between 3 boxes; this is the first.";
            contentsSimple[1346] = "TaskObjectives_JG_BCM2_Sab1_Stage01_DispatchBrief=Criminals don't want to see Councillor Davis re-elected, and her campaign offices could come under attack. <Col: StageText>Defend </Col>this <Col: StageText>electrical box </Col>which provides power to the office security systems.";
            contentsSimple[1347] = "TaskObjectives_JG_BCM2_Sab1_Stage02_OwnerBrief=<Col: StageText>Disable </Col>the second of three <Col: StageText>electrical boxes </Col>- once the power is fully down you can break into the 'Re-elect Davis' campaign headquarters undetected. ↵";
            contentsSimple[1348] = "TaskObjectives_JG_BCM2_Sab1_Stage02_DispatchBrief=<Col: StageText>Defend </Col>the next <Col: StageText>electrical box</Col> - if the system is powered down, the security on Councillor Davis' campaign offices will be exposed. She's a major Enforcement cheerleader.";
            contentsSimple[1349] = "TaskObjectives_JG_BCM2_Sab1_Stage03_OwnerBrief=One more <Col: StageText>electrical box </Col>should do it. <Col: StageText>Disable </Col>it and the power will finally go down. Then we can see what the Councillor Davis campaigners don't want the public to know.";
            contentsSimple[1350] = "TaskObjectives_JG_BCM2_Sab1_Stage03_DispatchBrief=The power will fail if another <Col: StageText>electrical box </Col>is disabled. <Col: StageText>Defend </Col>it from the attackers. With no power Councillor Davis' campaign offices will be open to attack.";
            contentsSimple[1351] = "TaskObjectives_JG_BCM2_Sab1_Stage04_OwnerBrief=Excellent. The power will stay down for 5 minutes. <Col: StageText>Burgle </Col>the three <Col: StageText>offices </Col>used by the committee to re-elect Councillor Davis. <Col: StageText>Pick up </Col>whatever you find.";
            contentsSimple[1352] = "TaskObjectives_JG_BCM2_Sab1_Stage04_DispatchBrief=The power will be down for 5 minutes so it's up to us: <Col: StageText>protect </Col>the political campaign <Col: StageText>offices </Col>from the burglars. It's like the Watergate break-in all over again.";
            contentsSimple[1353] = "TaskObjectives_JG_BCM2_Sab1_Stage05_OwnerBrief=Excellent. The power will stay down for 5 minutes. <Col: StageText>Burgle </Col>the three <Col: StageText>offices </Col>used by the committee to re-elect Councillor Davis. <Col: StageText>Pick up </Col>whatever you find.";
            contentsSimple[1354] = "TaskObjectives_JG_BCM2_Sab1_Stage05_DispatchBrief=The power will be down for 5 minutes so it's up to us: <Col: StageText>protect </Col>the political campaign <Col: StageText>offices </Col>from the burglars. It's like the Watergate break-in all over again.";
            contentsSimple[1355] = "TaskObjectives_JG_BCM2_Sab1_Stage06_OwnerBrief=What a find! An undeclared slush fund. I think maybe I should go into politics. I have most of the skills already! <Col: StageText>Deliver </Col>the <Col: StageText>cash </Col>to our <Col: StageText>drop-off</Col>.";
            contentsSimple[1356] = "TaskObjectives_JG_BCM2_Sab1_Stage06_DispatchBrief=The burglars have stolen money donated by honest citizens who want to see an end to Criminals in San Paro. <Col: StageText>Stop the gang </Col>from <Col: StageText>delivering </Col>it to their <Col: StageText>hideout</Col>.";
            contentsSimple[1357] = "TaskObjectives_JG_BCM2_Sab1_Stage07_OwnerBrief=Councillor Davis' supporters aren't content to fight just at the ballot box, they are on the streets and after you. <Col: StageText>Kill </Col>your <Col: StageText>opponents</Col>. Politics is a dirty game after all.";
            contentsSimple[1358] = "TaskObjectives_JG_BCM2_Sab1_Stage07_DispatchBrief=Those security systems have failed, the political system has failed - but we won't fail. <Col: StageText>Eliminate </Col>your <Col: StageText>opponents </Col>and show them a little democracy in action.";
            contentsSimple[1359] = "TaskObjectives_JG_BCM2_VLo1_Stage01_OwnerBrief=We've been hired by Tastyburger for a bit of corporate espionage. Break into this <Col: StageText>car </Col>and <Col: StageText>steal </Col>a product sample from Noodle Shack.";
            contentsSimple[1360] = "TaskObjectives_JG_BCM2_VLo1_Stage01_DispatchBrief=Noodle Shack have hired us for some professional corporate protection during their product launch. <Col: StageText>Defend </Col>the <Col: StageText>car </Col>belonging to the head of sales. An exclusive new product is inside.";
            contentsSimple[1361] = "TaskObjectives_JG_BCM2_VLo1_Stage02_OwnerBrief=<Col: StageText>Deliver </Col>that stolen fast food <Col: StageText>sample </Col>to a safe <Col: StageText>drop-off </Col>location. The guys from Tastyburger will be along to collect it later.";
            contentsSimple[1362] = "TaskObjectives_JG_BCM2_VLo1_Stage02_DispatchBrief=<Col: StageText>Prevent </Col>the <Col: StageText>delivery </Col>of the stolen Noodle Shack <Col: StageText>food sample </Col>to the <Col: StageText>drop-off </Col>location. We promised it would be safe under our protection.";
            contentsSimple[1363] = "TaskObjectives_JG_BCM2_VLo1_Stage03_OwnerBrief=The Tastyburger execs are worried about their competitor's new menu item. It tastes great. <Col: StageText>Break into </Col>the <Col: StageText>car </Col>of the CEO and <Col: StageText>steal </Col>the <Col: StageText>briefcase </Col>there that contains the secret recipe.";
            contentsSimple[1364] = "TaskObjectives_JG_BCM2_VLo1_Stage03_DispatchBrief=Keep security tight on this. Noodle Shack are paranoid that someone will steal their new secret recipe. <Col: StageText>Defend </Col>the CEO's <Col: StageText>car </Col>while he's in a meeting.";
            contentsSimple[1365] = "TaskObjectives_JG_BCM2_VLo1_Stage04_OwnerBrief=<Col: StageText>Deliver </Col>the stolen <Col: StageText>briefcase </Col>to our <Col: StageText>drop-off </Col>location. The Tastyburger bosses will pay a lot for Noodle Shack's secret recipe.";
            contentsSimple[1366] = "TaskObjectives_JG_BCM2_VLo1_Stage04_DispatchBrief=<Col: StageText>Prevent delivery </Col>of the stolen <Col: StageText>briefcase </Col>containing the Noodle Shack secret recipe. If it reaches the <Col: StageText>drop-off </Col>location, we'll lose them as clients.";
            contentsSimple[1367] = "TaskObjectives_JG_BCM2_VLo1_Stage05_OwnerBrief=Looks like Noodle Shack have hired in their own team of ninjas! <Col: StageText>Fight </Col>off any <Col: StageText>opponents </Col>who are foolish enough to try and eliminate you.";
            contentsSimple[1368] = "TaskObjectives_JG_BCM2_VLo1_Stage05_DispatchBrief=Corporate protection provides the income that funds our work. <Col: StageText>Fight </Col>off any <Col: StageText>opponents </Col>hired by Tastyburger and we can make this a good pay day.";
            contentsSimple[1369] = "TaskObjectives_JG_BCM2_VTh1_Stage01_OwnerBrief=One of our members has betrayed us and joined a rival gang. We're going to frame him and get him put away. First you'll need to <Col: StageText>steal </Col>his <Col: StageText>car</Col>, then we'll have fun with it - in his name!";
            contentsSimple[1370] = "TaskObjectives_JG_BCM2_VTh1_Stage01_DispatchBrief=Car thieves are targeting a lowrider parked in the area. <Col: StageText>Protect the car </Col>from the thieves.";
            contentsSimple[1371] = "TaskObjectives_JG_BCM2_VTh1_Stage02_OwnerBrief=<Col: StageText>Take </Col>the traitor's <Col: StageText>car </Col>to this <Col: StageText>location </Col>- and don't worry about the people of San Paro seeing it. In fact - the more, the merrier!";
            contentsSimple[1372] = "TaskObjectives_JG_BCM2_VTh1_Stage02_DispatchBrief=The gang are taking the stolen car to a nearby <Col: StageText>drop-off</Col>. <Col: StageText>Intercept </Col>the <Col: StageText>car </Col>before it arrives. It may contain clues.";
            contentsSimple[1373] = "TaskObjectives_JG_BCM2_VTh1_Stage03_OwnerBrief=A few more witnesses will help to further incriminate the traitor. <Col: StageText>Drive </Col>his <Col: StageText>car </Col>to this <Col: StageText>location</Col>. It's all part of the show.";
            contentsSimple[1374] = "TaskObjectives_JG_BCM2_VTh1_Stage03_DispatchBrief=The gang is trying to lose us - they're taking the <Col: StageText>lowrider </Col>to another <Col: StageText>drop-off</Col>. <Col: StageText>Stop them </Col>before they arrive.";
            contentsSimple[1375] = "TaskObjectives_JG_BCM2_VTh1_Stage04_OwnerBrief=This is how we treat traitors. <Col: StageText>Bring </Col>his <Col: StageText>car </Col>to this <Col: StageText>location </Col>and we'll make sure that there are no prints on it - apart from his, of course.";
            contentsSimple[1376] = "TaskObjectives_JG_BCM2_VTh1_Stage04_DispatchBrief=An informant just called in: those guys are planning to heist a distillery warehouse. Think that's the getaway vehicle? <Col: StageText>Intercept </Col>the <Col: StageText>car </Col>before it arrives at the new <Col: StageText>drop-off</Col>.";
            contentsSimple[1377] = "TaskObjectives_JG_BCM2_VTh1_Stage05_OwnerBrief=OK - now for the good part. <Col: StageText>Burn </Col>these two <Col: StageText>vehicles</Col>. The traitor's car being so close will place him at the scene. Genius.";
            contentsSimple[1378] = "TaskObjectives_JG_BCM2_VTh1_Stage05_DispatchBrief=Shit! That gang's gotta know we're onto their distillery job - they're setting fire to other vehicles to try and keep us busy. <Col: StageText>Protect </Col>those <Col: StageText>cars</Col>!";
            contentsSimple[1379] = "TaskObjectives_JG_BCM2_VTh1_Stage06_OwnerBrief=Great, break out the marshmallows. <Col: StageText>Torch </Col>these nearby <Col: StageText>buildings</Col>. When our traitorous friend is blamed for these arsons, he'll be put away for years.";
            contentsSimple[1380] = "TaskObjectives_JG_BCM2_VTh1_Stage06_DispatchBrief=This gang are getting out of hand. <Col: StageText>Defend </Col>these <Col: StageText>buildings</Col> - we can't allow civilians to be drawn into the crossfire.";
            contentsSimple[1381] = "TaskObjectives_JG_BCM2_VTh1_Stage07_OwnerBrief=A few people witnessed your recent activity and could link us to those fires, instead of the traitor. <Col: StageText>Wipe out </Col>any <Col: StageText>witnesses</Col>.";
            contentsSimple[1382] = "TaskObjectives_JG_BCM2_VTh1_Stage07_DispatchBrief=The distillery is locked down, but we need to bring order to this situation right now!  <Col: StageText>Eliminate </Col>the <Col: StageText>Criminals </Col>before anyone else gets hurt!";
            contentsSimple[1383] = "TaskObjectives_JG_BCM3_Bur1_Stage01_OwnerBrief=For years, Wex Corp has been donating funds to the Derren administration. We're going to dig up some dirt and discredit the company. <Col: StageText>Break into </Col>the director's <Col: StageText>car </Col>and take anything juicy.";
            contentsSimple[1384] = "TaskObjectives_JG_BCM3_Bur1_Stage01_DispatchBrief=Mayor Derren's administration has been generously funded by Wex Corp for years, but Criminals are planning to shut down the company using corporate espionage. <Col: StageText>Protect </Col>the MD's <Col: StageText>car </Col>from attack.";
            contentsSimple[1385] = "TaskObjectives_JG_BCM3_Bur1_Stage02_OwnerBrief=That PDA is a start, but we're gonna need something more incriminating than the fact that the MD of Wex Corp has Tastyburger on speed dial! <Col: StageText>Burgle </Col>the CEO's <Col: StageText>office </Col>and take any data you find.";
            contentsSimple[1386] = "TaskObjectives_JG_BCM3_Bur1_Stage02_DispatchBrief=The Criminals are moving on to a Wex Corp fabrication centre. They’re planning to burgle it and sell product information to the company's commercial rivals. <Col: StageText>Protect </Col>the <Col: StageText>building </Col>from burglars.";
            contentsSimple[1387] = "TaskObjectives_JG_BCM3_Bur1_Stage03_OwnerBrief=Hmmm... the CEO ain't stupid enough to keep the incriminating stuff at his office. Try his <Col: StageText>place</Col>. <Col: StageText>Burgle </Col>the building and grab any useful information, then <Col: StageText>deliver everything </Col>back to our <Col: StageText>drop-off</Col>.";
            contentsSimple[1388] = "TaskObjectives_JG_BCM3_Bur1_Stage03_DispatchBrief=Those burglars have valuable product data from the Wex Corp offices. Now they're heading towards the CEO's home. <Col: StageText>Protect </Col>the <Col: StageText>building </Col>and <Col: StageText>intercept </Col>the <Col: StageText>data </Col>before it reaches this <Col: StageText>drop-off</Col>.";
            contentsSimple[1389] = "TaskObjectives_JG_BCM3_Bur1_Stage04_OwnerBrief=Hmmm... the CEO ain't stupid enough to keep the incriminating stuff at his office. Try his <Col: StageText>place</Col>. <Col: StageText>Burgle </Col>the building and grab any useful information, then <Col: StageText>deliver everything </Col>back to our <Col: StageText>drop-off</Col>.";
            contentsSimple[1390] = "TaskObjectives_JG_BCM3_Bur1_Stage04_DispatchBrief=Those burglars have valuable product data from the Wex Corp offices. Now they're heading towards the CEO's home. <Col: StageText>Protect </Col>the <Col: StageText>building </Col>and <Col: StageText>intercept </Col>the <Col: StageText>data </Col>before it reaches this <Col: StageText>drop-off</Col>.";
            contentsSimple[1391] = "TaskObjectives_JG_BCM3_Bur1_Stage05_OwnerBrief=The data on that PC looks interesting - the CEO visits 'San Paro Ho Cam' a lot! We're more interested in the encrypted files, however. <Col: StageText>Guard </Col>the <Col: StageText>drop-off </Col>for <Col: StageText>5 minutes </Col>while we decrypt them.";
            contentsSimple[1392] = "TaskObjectives_JG_BCM3_Bur1_Stage05_DispatchBrief=";
            contentsSimple[1393] = "TaskObjectives_JG_BCM3_Bur1_Stage05_Opp_OwnerBrief=";
            contentsSimple[1394] = "TaskObjectives_JG_BCM3_Bur1_Stage05_Opp_DispatchBrief=The Criminals are analyzing the sensitive R&D data they stole from Wex Corp's CEO - we have about <Col: StageText>5 minutes</Col>to <Col: StageText>capture </Col>this <Col: StageText>location </Col>and recover that data before they can crack it.";
            contentsSimple[1395] = "TaskObjectives_JG_BCM3_Bur1_Stage06_OwnerBrief=Those files were eco impact reports: ground water contamination, fish stock depletion, rats with more heads than a football team... Good stuff. <Col: StageText>Return </Col>to the <Col: StageText>drop-off </Col>and <Col: StageText>collect </Col>the <Col: StageText>PC</Col>.";
            contentsSimple[1396] = "TaskObjectives_JG_BCM3_Bur1_Stage06_DispatchBrief=The Criminals have collected all the manufacturing secrets they gained from Wex Corp and put it on a PC ready for delivery to foreign buyers. <Col: StageText>Stop the gang </Col>from <Col: StageText>picking up </Col>the <Col: StageText>hard drive</Col>.";
            contentsSimple[1397] = "TaskObjectives_JG_BCM3_Bur1_Stage07_OwnerBrief=The San Paro Standard is gonna be real interested in the data you found regarding Wex Corp's toxic dumping record. <Col: StageText>Deliver </Col>the <Col: StageText>PC </Col>to our <Col: StageText>drop-off </Col>and <Col: StageText>keep it </Col>away from our enemies.";
            contentsSimple[1398] = "TaskObjectives_JG_BCM3_Bur1_Stage07_DispatchBrief=";
            contentsSimple[1399] = "TaskObjectives_JG_BCM3_Bur1_Stage07_Opp_OwnerBrief=";
            contentsSimple[1400] = "TaskObjectives_JG_BCM3_Bur1_Stage07_Opp_DispatchBrief=We have to <Col: StageText>recover </Col>the <Col: StageText>PC </Col>containing Wex Corps' commercial data. <Col: StageText>Deliver it </Col>to our <Col: StageText>drop-off </Col>and <Col: StageText>keep it </Col>away from the corporate thieves.";
            contentsSimple[1401] = "TaskObjectives_JG_BCM3_Exemplar_Stage01_OwnerBrief=Lindochem are putting experimental anti - aggression chemicals into our neighborhood water supply. Makes me wanna kill someone! <Col: StageText>Burgle </Col>their <Col: StageText>buildings </Col>and take the <Col: StageText>chemicals </Col>to the <Col: StageText>drop-off</Col>.";
            contentsSimple[1402] = "TaskObjectives_JG_BCM3_Exemplar_Stage01_DispatchBrief=Criminals have been paid to steal a new anti - aging formula that Lindochem spent millions developing. <Col: StageText>Defend </Col>these <Col: StageText>buildings </Col>and <Col: StageText>stop </Col>any stolen <Col: StageText>chemicals </Col>reaching the <Col: StageText>drop-off</Col>.";
            contentsSimple[1403] = "TaskObjectives_JG_BCM3_Exemplar_Stage02_OwnerBrief=Lindochem are putting experimental anti - aggression chemicals into our neighborhood water supply. Makes me wanna kill someone! <Col: StageText>Burgle </Col>their <Col: StageText>buildings </Col>and take the <Col: StageText>chemicals </Col>to the <Col: StageText>drop-off</Col>.";
            contentsSimple[1404] = "TaskObjectives_JG_BCM3_Exemplar_Stage02_DispatchBrief=Criminals have been paid to steal a new anti - aging formula that Lindochem spent millions developing. <Col: StageText>Defend </Col>these <Col: StageText>buildings </Col>and <Col: StageText>stop </Col>any stolen <Col: StageText>chemicals </Col>reaching the <Col: StageText>drop-off</Col>.";
            contentsSimple[1405] = "TaskObjectives_JG_BCM3_Exemplar_Stage03_OwnerBrief=I'm not sure what's in that formula, but my nipples are tender and my piss is blue. <Col: StageText>Burgle </Col>these Lindochem <Col: StageText>offices </Col>and take any relevant <Col: StageText>documents </Col>you find to our <Col: StageText>drop-off</Col> ASAP.";
            contentsSimple[1406] = "TaskObjectives_JG_BCM3_Exemplar_Stage03_DispatchBrief=Criminals have a sample of the anti-aging formula, but now they're after Lindochem's research notes. <Col: StageText>Defend </Col>these <Col: StageText>buildings </Col>and <Col: StageText>stop </Col>any <Col: StageText>chemicals </Col>reaching the <Col: StageText>drop-off</Col>.";
            contentsSimple[1407] = "TaskObjectives_JG_BCM3_Exemplar_Stage04_OwnerBrief=I'm not sure what's in that formula, but my nipples are tender and my piss is blue. <Col: StageText>Burgle </Col>these Lindochem <Col: StageText>offices </Col>and take any relevant <Col: StageText>documents </Col>you find to our <Col: StageText>drop-off</Col> ASAP.";
            contentsSimple[1408] = "TaskObjectives_JG_BCM3_Exemplar_Stage04_DispatchBrief=Criminals have a sample of the anti-aging formula, but now they're after Lindochem's research notes. <Col: StageText>Defend </Col>these <Col: StageText>buildings </Col>and <Col: StageText>stop </Col>any <Col: StageText>chemicals </Col>reaching the <Col: StageText>drop-off</Col>.";
            contentsSimple[1409] = "TaskObjectives_JG_BCM3_Exemplar_Stage05_OwnerBrief=We sold that information to the San Paro Standard - soon everyone's going to know about Lindochem's chemical experiments! <Col: StageText>Recover </Col>our <Col: StageText>payment </Col>and <Col: StageText>hold it </Col>so Lindochem's gang-for-hire doesn't get it.";
            contentsSimple[1410] = "TaskObjectives_JG_BCM3_Exemplar_Stage05_DispatchBrief=The Criminals were paid in cash for the anti-aging research they've stolen. <Col: StageText>Recover </Col>the <Col: StageText>payment </Col>and <Col: StageText>keep it </Col>away from the gang.";
            contentsSimple[1411] = "TaskObjectives_JG_BCM4_Exemplar_Stage01_OwnerBrief=A local video game studio has made a game about crime in San Paro. It's sure to be a hit, so a crafty rival studio has paid us to help steal their idea. <Col: StageText>Burgle </Col>these <Col: StageText>offices </Col>and <Col: StageText>take the data</Col>.";
            contentsSimple[1412] = "TaskObjectives_JG_BCM4_Exemplar_Stage01_DispatchBrief=We're developing new software that can trace criminals by matching faces from CCTV feeds. Now the crooks want to use it to track us! <Col: StageText>Defend </Col>these <Col: StageText>offices </Col>- don't let the crooks take the software.";
            contentsSimple[1413] = "TaskObjectives_JG_BCM4_Exemplar_Stage02_OwnerBrief=A local video game studio has made a game about crime in San Paro. It's sure to be a hit, so a crafty rival studio has paid us to help steal their idea. <Col: StageText>Burgle </Col>these <Col: StageText>offices </Col>and <Col: StageText>take the data</Col>.";
            contentsSimple[1414] = "TaskObjectives_JG_BCM4_Exemplar_Stage02_DispatchBrief=We're developing new software that can trace criminals by matching faces from CCTV feeds. Now the crooks want to use it to track us! <Col: StageText>Defend </Col>these <Col: StageText>offices </Col>- don't let the crooks take the software.";
            contentsSimple[1415] = "TaskObjectives_JG_BCM4_Exemplar_Stage03_OwnerBrief=I guess sometimes life really does imitate art. Crime in San Paro to steal a game about... Yeah. <Col: StageText>Deliver </Col>all of that <Col: StageText>data </Col>to our <Col: StageText>drop-off </Col>and we'll get it to our clients.";
            contentsSimple[1416] = "TaskObjectives_JG_BCM4_Exemplar_Stage03_DispatchBrief=The organized Crims in this city could give us major headaches with that stolen new software. <Col: StageText>Stop </Col>that <Col: StageText>data </Col>before the perps get it to their <Col: StageText>drop-off</Col>.";
            contentsSimple[1417] = "TaskObjectives_JG_BCM4_Exemplar_Stage04_OwnerBrief=Seems that most of the useful assets and data for the new video game are stored off-site. <Col: StageText>Rob </Col>this <Col: StageText>building </Col>and steal the PC on which that data is backed up.";
            contentsSimple[1418] = "TaskObjectives_JG_BCM4_Exemplar_Stage04_DispatchBrief=The software those Criminals stole is only really dangerous if they get hold of our personnel files. <Col: StageText>Protect </Col>this <Col: StageText>building </Col>and stop them from stealing the PC on which that data is stored.";
            contentsSimple[1419] = "TaskObjectives_JG_BCM4_Exemplar_Stage05_OwnerBrief=All right, we've agreed to make a direct hand-over of the PC. <Col: StageText>Deliver </Col>the <Col: StageText>computer </Col>to the arranged <Col: StageText>drop-off </Col>and we'll negotiate our payment. Maybe we can make Beta access part of the deal.";
            contentsSimple[1420] = "TaskObjectives_JG_BCM4_Exemplar_Stage05_DispatchBrief=We need to keep that data secure, or the Criminals will be able to identify and trace our agents throughout the city. <Col: StageText>Stop </Col>them getting our <Col: StageText>computer </Col>to their <Col: StageText>drop-off</Col>.";
            contentsSimple[1421] = "TaskObjectives_JG_BCM4_Exemplar_Stage06_OwnerBrief=Our payment is in this <Col: StageText>case </Col>but we got Enforcers all over us like white on rice. <Col: StageText>Deliver </Col>it to our <Col: StageText>drop-off </Col>before our opponents grab it and take it to theirs.";
            contentsSimple[1422] = "TaskObjectives_JG_BCM4_Exemplar_Stage06_DispatchBrief=";
            contentsSimple[1423] = "TaskObjectives_JG_BCM4_Exemplar_Stage06_Opp_OwnerBrief=";
            contentsSimple[1424] = "TaskObjectives_JG_BCM4_Exemplar_Stage06_Opp_DispatchBrief=Damn. The Criminals transferred everything onto a <Col: StageText>laptop </Col>for portability's sake. <Col: StageText>Recover </Col>it and <Col: StageText>deliver </Col>it to our <Col: StageText>drop-off </Col>before they can get it to theirs. Gotta protect our personnel.";
            contentsSimple[1425] = "TaskObjectives_JG_BCS0_Exemplar_B_Stage01_OwnerBrief=There's a dirty Tiger who's been trying to blackmail me, asking for money, and I want him dealt with. First <Col: StageText>burgle</Col> this <Col: StageText>building</Col> and steal his PDA.";
            contentsSimple[1426] = "TaskObjectives_JG_BCS0_Exemplar_B_Stage01_DispatchBrief=We recently recovered some gold stolen by the Blood Roses, but the perps got away. Now they're after the evidence we have against them - <Col: StageText>defend </Col>this <Col: StageText>building </Col>from burglars.";
            contentsSimple[1427] = "TaskObjectives_JG_BCS0_Exemplar_B_Stage02_OwnerBrief=Right! Now <Col: StageText>deliver</Col> the <Col: StageText>PDA</Col> to the <Col: StageText>drop-off</Col> and we'll check it over for anything we can use.";
            contentsSimple[1428] = "TaskObjectives_JG_BCS0_Exemplar_B_Stage02_DispatchBrief=Our evidence against the Blood Roses is on that stolen <Col: StageText>PDA</Col>. <Col: StageText>Prevent </Col>it from being delivered to the <Col: StageText>drop-off</Col>.";
            contentsSimple[1429] = "TaskObjectives_JG_BCS0_Exemplar_B_Stage03_OwnerBrief=Perfect - this idiot kept details of all of his business with San Paro gangs. <Col: StageText>Burgle</Col> this <Col: StageText>building</Col>. He's been stashing his gear there.";
            contentsSimple[1430] = "TaskObjectives_JG_BCS0_Exemplar_B_Stage03_DispatchBrief=We couldn't find the rest of that bullion haul, but it looks like more Blood Roses are coming back for it. <Col: StageText>Defend </Col>this <Col: StageText>building </Col>from them. The gold has to be stashed there.";
            contentsSimple[1431] = "TaskObjectives_JG_BCS0_Exemplar_B_Stage04_OwnerBrief=The gold you just found was stolen during a recent raid - and now it's here, with his prints all over it! <Col: StageText>Deliver</Col> the <Col: StageText>gold</Col> to the <Col: StageText>drop-off</Col> and we'll let the authorities know where they can find it.";
            contentsSimple[1432] = "TaskObjectives_JG_BCS0_Exemplar_B_Stage04_DispatchBrief=That <Col: StageText>gold </Col>could fund all sorts of criminal activity. <Col: StageText>Prevent </Col>it from being delivered to the <Col: StageText>drop-off</Col>.";
            contentsSimple[1433] = "TaskObjectives_JG_BCS0_Exemplar_B_Stage05_OwnerBrief=Seems that Tiger wasn't the only one on the take. <Col: StageText>Capture this area</Col>.";
            contentsSimple[1434] = "TaskObjectives_JG_BCS0_Exemplar_B_Stage05_DispatchBrief=We can't allow those Blood Roses to get away with the proceeds of their crime. <Col: StageText>Capture this block</Col>.";
            contentsSimple[1435] = "TaskObjectives_JG_BCS0_Exemplar_G_Stage01_OwnerBrief=There's a corrupt Praetorian who's been putting pressure on me for a while now, trying to get money outta me, and I want him dealt with. First <Col: StageText>burgle</Col> this <Col: StageText>building</Col> and steal his PDA.";
            contentsSimple[1436] = "TaskObjectives_JG_BCS0_Exemplar_G_Stage01_DispatchBrief=We recently recovered some gold stolen by the G-Kings, but the perps got away. Now they're after the evidence we have against them - <Col: StageText>defend </Col>this <Col: StageText>building </Col>from their burglars.";
            contentsSimple[1437] = "TaskObjectives_JG_BCS0_Exemplar_G_Stage02_OwnerBrief=Right! Now <Col: StageText>deliver</Col> the <Col: StageText>PDA</Col> to the <Col: StageText>drop-off</Col>, and we'll check it over for anything we can use.";
            contentsSimple[1438] = "TaskObjectives_JG_BCS0_Exemplar_G_Stage02_DispatchBrief=Our evidence against the G-Kings is on that <Col: StageText>PDA</Col>. <Col: StageText>Prevent </Col>it from being delivered to the <Col: StageText>drop-off</Col>.";
            contentsSimple[1439] = "TaskObjectives_JG_BCS0_Exemplar_G_Stage03_OwnerBrief=Perfect - this idiot kept details of all of his business with San Paro gangs. <Col: StageText>Burgle</Col> this <Col: StageText>building</Col>: it's where he's been stashing his bribes and loot.";
            contentsSimple[1440] = "TaskObjectives_JG_BCS0_Exemplar_G_Stage03_DispatchBrief=We couldn't recover some of the stolen bullion and now more G-Kings are coming back for it. <Col: StageText>Defend </Col>this <Col: StageText>building </Col>from the G-Kings. The bullion must be stashed there.";
            contentsSimple[1441] = "TaskObjectives_JG_BCS0_Exemplar_G_Stage04_OwnerBrief=The gold you just found was stolen in a recent raid - and now it's here, with his prints all over it! <Col: StageText>Deliver</Col> the <Col: StageText>gold</Col> to the <Col: StageText>drop-off</Col>, and we'll let the authorities know where they can find it.";
            contentsSimple[1442] = "TaskObjectives_JG_BCS0_Exemplar_G_Stage04_DispatchBrief=That <Col: StageText>gold bullion </Col>could fund all sorts of criminal activity. <Col: StageText>Prevent </Col>it from being delivered to the <Col: StageText>drop-off</Col>.";
            contentsSimple[1443] = "TaskObjectives_JG_BCS0_Exemplar_G_Stage05_OwnerBrief=Seems that Praetorian wasn't the only one on the take. <Col: StageText>Capture this area</Col>.";
            contentsSimple[1444] = "TaskObjectives_JG_BCS0_Exemplar_G_Stage05_DispatchBrief=We can't allow those G-Kings to get away with the proceeds of their crime. <Col: StageText>Capture this point</Col>.";
            contentsSimple[1445] = "TaskObjectives_JG_BCS0_FoE1_B_Stage01_OwnerBrief=The Enforcers have been working hard recently to win some hearts and minds. We need the public on our side. First, <Col: StageText>loot </Col>an Enforcer's <Col: StageText>car </Col>and take their <Col: StageText>ID Badge</Col>.";
            contentsSimple[1446] = "TaskObjectives_JG_BCS0_FoE1_B_Stage01_DispatchBrief=The Blood Roses have been spotted near an Enforcer's car - never a good sign. <Col: StageText>Protect </Col>the <Col: StageText>vehicle </Col>and don't let them break into it.";
            contentsSimple[1447] = "TaskObjectives_JG_BCS0_FoE1_B_Stage02_OwnerBrief=Now that you got the badge, you can act like a 'Forcer. <Col: StageText>Raid </Col>this random <Col: StageText>building</Col>, just like a Praetorian would. 'Confiscate' some 'evidence' - make it worth your while.";
            contentsSimple[1448] = "TaskObjectives_JG_BCS0_FoE1_B_Stage02_DispatchBrief=The Roses stole an Enforcer's badge from that car, and we fear they'll use it to stir up public opinion against us. <Col: StageText>Protect </Col>this <Col: StageText>building </Col>and don't let them fake an Enforcer raid on it.";
            contentsSimple[1449] = "TaskObjectives_JG_BCS0_FoE1_B_Stage03_OwnerBrief=Hey, this is fun - and legal! Well, kinda. <Col: StageText>Raid </Col>another <Col: StageText>building</Col>. We'll make San Paro see the Enforcers as we know they are: uncouth thugs with badges.";
            contentsSimple[1450] = "TaskObjectives_JG_BCS0_FoE1_B_Stage03_DispatchBrief=With that badge, Blood Roses are masquerading as Enforcers. They're dragging our reputation through the dirt. <Col: StageText>Protect </Col>this civilian <Col: StageText>building </Col>before they can raid it.";
            contentsSimple[1451] = "TaskObjectives_JG_BCS0_FoE1_B_Stage04_OwnerBrief=<Col: StageText>Deliver </Col>all that 'legally obtained' <Col: StageText>loot </Col>and the <Col: StageText>badge </Col>to our <Col: StageText>drop-off</Col>. Jeung wants to make a big press event out of this.";
            contentsSimple[1452] = "TaskObjectives_JG_BCS0_FoE1_B_Stage04_DispatchBrief=The Roses stole money and goods from those properties. Even worse, they made it look like we did it. <Col: StageText>Intercept </Col>them before they reach their <Col: StageText>drop-off </Col>and we can prove our innocence.";
            contentsSimple[1453] = "TaskObjectives_JG_BCS0_FoE1_B_Stage05_OwnerBrief=Some Enforcers found out about your recent activities and are after you, big time. Or maybe they want to recruit you? Heh heh. <Col: StageText>Control the area</Col>to keep our subterfuge hushed up.";
            contentsSimple[1454] = "TaskObjectives_JG_BCS0_FoE1_B_Stage05_DispatchBrief=This could be a PR disaster. <Col: StageText>Capture this area</Col> and bring everyone involved to justice - that's the best way to rebuild our reputation with the citizens of San Paro.";
            contentsSimple[1455] = "TaskObjectives_JG_BCS0_FoE1_G_Stage01_OwnerBrief=Got an easy one for you here. A <Col: StageText>car </Col>was left unattended. Crack out your famous slim jim, <Col: StageText>break in</Col>, and <Col: StageText>take </Col>whatever you can <Col: StageText>find </Col>inside.";
            contentsSimple[1456] = "TaskObjectives_JG_BCS0_FoE1_G_Stage01_DispatchBrief=I left my Enforcer's badge in my car - again. Last time it nearly got taken. Can you check it out and <Col: StageText>protect my car</Col>? I don't want it being broken into.";
            contentsSimple[1457] = "TaskObjectives_JG_BCS0_FoE1_G_Stage02_OwnerBrief=Cool, a shiny new cop badge! Some SPPD rookie is gonna get busted. He even put his address on the back - what a noob. <Col: StageText>Raid </Col>his <Col: StageText>building </Col>like you were a cop - and hey, see if he has any cash lying around.";
            contentsSimple[1458] = "TaskObjectives_JG_BCS0_FoE1_G_Stage02_DispatchBrief=Damn, G-Kings took my badge, and if they look on the back they'll find my address. <Col: StageText>Protect </Col>my <Col: StageText>home </Col>and keep the Gs out of my shit - and my money!";
            contentsSimple[1459] = "TaskObjectives_JG_BCS0_FoE1_G_Stage03_OwnerBrief=A G-King who can't have fun winding up the cops is a G-King who's tired of life. <Col: StageText>Raid </Col>the cop's <Col: StageText>office </Col>and grab his official SPPD phone: handy for calling in fake intel.";
            contentsSimple[1460] = "TaskObjectives_JG_BCS0_FoE1_G_Stage03_DispatchBrief=What the hell? The Gs broke in my house and are now probably heading for my undercover office. <Col: StageText>Protect </Col>the <Col: StageText>building </Col>from being ransacked by G-King vandals.";
            contentsSimple[1461] = "TaskObjectives_JG_BCS0_FoE1_G_Stage04_OwnerBrief=Fuckin' sweet. We got his badge, his money and his phone. <Col: StageText>Deliver </Col>all those <Col: StageText>goodies </Col>to the G-King <Col: StageText>drop-off</Col>. Bonita thinks she might be able to put them to good use.";
            contentsSimple[1462] = "TaskObjectives_JG_BCS0_FoE1_G_Stage04_DispatchBrief=This is a disaster. The G-Kings got my badge, my phone and my money. <Col: StageText>Intercept </Col>them before they reach their <Col: StageText>drop-off </Col>and no one need be the wiser, eh, compadre?";
            contentsSimple[1463] = "TaskObjectives_JG_BCS0_FoE1_G_Stage05_OwnerBrief=Looks like the SPPD have called in 'Forcers to get their missing badge back; probably too embarrassed to do it themselves. <Col: StageText>Capture the area</Col>.";
            contentsSimple[1464] = "TaskObjectives_JG_BCS0_FoE1_G_Stage05_DispatchBrief=There's a simple way to solve this problem... Simple, yet effective. <Col: StageText>Capture this area </Col> before they get a chance to use my stolen badge illegally.";
            contentsSimple[1465] = "TaskObjectives_JG_BCS0_VCa1_B_Stage01_OwnerBrief=We're going to steal a whole armored truck full of cash. First step, <Col: StageText>sabotage </Col>these two <Col: StageText>ATMs </Col>and hide some gas pellets amongst the money. The security van will pick them up soon.";
            contentsSimple[1466] = "TaskObjectives_JG_BCS0_VCa1_B_Stage01_DispatchBrief=The Blood Roses are planning to hit these <Col: StageText>ATMs</Col> - I guess Charlotte Bloodrose must need a new pair of frilly panties. <Col: StageText>Protect </Col>the bank's machines from the Roses.";
            contentsSimple[1467] = "TaskObjectives_JG_BCS0_VCa1_B_Stage02_OwnerBrief=The driver unwittingly picked up our gas pellets, hidden in the ATM's money. He's unconscious, so get over there and <Col: StageText>steal </Col>the <Col: StageText>truck </Col>and all its cash.";
            contentsSimple[1468] = "TaskObjectives_JG_BCS0_VCa1_B_Stage02_DispatchBrief=Robbing the ATMs was a decoy. The Blood Roses' real target is the bank's security van that came to refill them. <Col: StageText>Protect </Col>the <Col: StageText>truck</Col>: it's a sitting duck out there.";
            contentsSimple[1469] = "TaskObjectives_JG_BCS0_VCa1_B_Stage03_OwnerBrief=The plan is working, in perfect Blood Rose style. <Col: StageText>Deliver </Col>the <Col: StageText>cash </Col>to our <Col: StageText>drop-off</Col>. Bring the whole truck as well. 'Fortes fortuna adiuvat,' my friend.";
            contentsSimple[1470] = "TaskObjectives_JG_BCS0_VCa1_B_Stage03_DispatchBrief=The Roses have the <Col: StageText>security truck </Col>and all the cash it contains. If you can <Col: StageText>intercept </Col>them on the way back to their <Col: StageText>drop-off</Col>, the bank may just forgive us - and pay us!";
            contentsSimple[1471] = "TaskObjectives_JG_BCS0_VCa1_B_Stage04_OwnerBrief=One of the money bags from the truck you stole has a tracking device in it. <Col: StageText>Get </Col>the <Col: StageText>money bag </Col>and <Col: StageText>keep it </Col>away from the 'Forcers - and away from us.";
            contentsSimple[1472] = "TaskObjectives_JG_BCS0_VCa1_B_Stage04_DispatchBrief=Blood Roses? Dud Roses more like! They went and dropped one of the cash bags! <Col: StageText>Recover </Col>the <Col: StageText>cash </Col>and <Col: StageText>keep </Col>it away from them. We can use the money to draw them out into the open.";
            contentsSimple[1473] = "TaskObjectives_JG_BCS0_VCa1_G_Stage01_OwnerBrief=This war with the 'Forcers costs a fortune; we need cash for our upcoming property scam. <Col: StageText>Hack these ATMs </Col>so the bank'll think they're empty and send a truck fulla cash for us to steal.";
            contentsSimple[1474] = "TaskObjectives_JG_BCS0_VCa1_G_Stage01_DispatchBrief=We've been slowly turning up the heat on the G-Kings, cutting off their sources of revenue. They plan to hit these <Col: StageText>ATMs </Col>to get some fast cash. <Col: StageText>Protect </Col>the machines from being attacked.";
            contentsSimple[1475] = "TaskObjectives_JG_BCS0_VCa1_G_Stage02_OwnerBrief=Nice work, G. Your ATM hack fooled the bank into thinking their machines are empty; now an <Col: StageText>armored van </Col>is being filled with cash. Get there and <Col: StageText>steal </Col>it, and all the cash in the back.";
            contentsSimple[1476] = "TaskObjectives_JG_BCS0_VCa1_G_Stage02_DispatchBrief=The G-Kings hacked the ATMs, sending a signal back to the bank that more bills are needed. <Col: StageText>Protect </Col>the <Col: StageText>security truck </Col>that was dispatched - it's full of replacement cash.";
            contentsSimple[1477] = "TaskObjectives_JG_BCS0_VCa1_G_Stage03_OwnerBrief=Now you need to <Col: StageText>deliver </Col>the <Col: StageText>cash </Col>to our <Col: StageText>drop-off</Col>. It will probably be easier if you just bring the truck in as well - more fun, too!";
            contentsSimple[1478] = "TaskObjectives_JG_BCS0_VCa1_G_Stage03_DispatchBrief=That <Col: StageText>truck </Col>is full of used bills, and the G-Kings are driving it to their <Col: StageText>drop-off</Col>. <Col: StageText>Intercept </Col>them, or they'll have enough funds to wage a real war against us.";
            contentsSimple[1479] = "TaskObjectives_JG_BCS0_VCa1_G_Stage04_OwnerBrief=We've left your cut of the take nearby - <Col: StageText>recover </Col>your <Col: StageText>cash </Col>and <Col: StageText>keep it </Col>away from the 'Forcers. If they want cash, they can hijack their own security van.";
            contentsSimple[1480] = "TaskObjectives_JG_BCS0_VCa1_G_Stage04_DispatchBrief=The G's have bagged up their stolen cash for ease of transport, and now we have a chance to catch them. <Col: StageText>Recover </Col>the <Col: StageText>money </Col>and <Col: StageText>keep it </Col>out of their hands.";
            contentsSimple[1481] = "TaskObjectives_JG_BCS1_Exemplar3_B_Stage01_OwnerBrief=We're creating a highly potent tear gas but need two extra chemicals. <Col: StageText>Break into </Col>the XS Drink Company <Col: StageText>building </Col>and get the first one. Watch out, it's extremely hazadous.";
            contentsSimple[1482] = "TaskObjectives_JG_BCS1_Exemplar3_B_Stage01_DispatchBrief=An inside source says the Blood Roses are trying to make some bad ass tear gas. <Col: StageText>Defend </Col>this <Col: StageText>building </Col>as it contains a compound they'll need. <Col: StageText>Kill them </Col>if they resist.";
            contentsSimple[1483] = "TaskObjectives_JG_BCS1_Exemplar3_B_Stage02_OwnerBrief=Don't swallow it, it's what makes the drink green. <Col: StageText>Burgle </Col>the <Col: StageText>building </Col>of a company who make percussion grenades. They have the second chemical we need.";
            contentsSimple[1484] = "TaskObjectives_JG_BCS1_Exemplar3_B_Stage02_DispatchBrief=There is only one place the Roses can steal the other chemical they need for their tear gas. <Col: StageText>Defend </Col>this <Col: StageText>building </Col>and <Col: StageText>kill the Roses</Col>, rather than let them take the chemical.";
            contentsSimple[1485] = "TaskObjectives_JG_BCS1_Exemplar3_B_Stage03_OwnerBrief=Alright, now <Col: StageText>deliver </Col>the <Col: StageText>chemicals </Col>back to our <Col: StageText>drop-off </Col>and we can start making up some special Blood Rose tear gas.";
            contentsSimple[1486] = "TaskObjectives_JG_BCS1_Exemplar3_B_Stage03_DispatchBrief=The tear gas the Roses are planning to make will permiate even a gas mask. <Col: StageText>Intercept </Col>the Roses before they reach their <Col: StageText>drop-off </Col>with their lethal cocktail of ingredients.";
            contentsSimple[1487] = "TaskObjectives_JG_BCS1_Exemplar3_B_Stage04_OwnerBrief=Shit! I spilt some and now I can't see. And it's eating through the floor, I think. You have to go <Col: StageText>burgle this pharmaceutical building </Col>and get me some stuff that might work as an antidote.";
            contentsSimple[1488] = "TaskObjectives_JG_BCS1_Exemplar3_B_Stage04_DispatchBrief=I saw gas escaping from the Roses lab. The idiots must have dropped their new tear gas. Schadenfreude in action! <Col: StageText>Defend </Col>this <Col: StageText>pharmaceutical building </Col>and <Col: StageText>kill </Col>anyone who goes for the antidote.";
            contentsSimple[1489] = "TaskObjectives_JG_BCS1_Exemplar3_B_Stage05_OwnerBrief=You're a life saver. Now <Col: StageText>bring </Col>me the <Col: StageText>antidote </Col>via the <Col: StageText>drop-off </Col>before I go blind. I'm never drinking XS again!";
            contentsSimple[1490] = "TaskObjectives_JG_BCS1_Exemplar3_B_Stage05_DispatchBrief=If the Roses have the tear gas and the antidote they will be a severe threat. So stop them now! <Col: StageText>Intercept </Col>them before they reach their <Col: StageText>drop-off</Col> and confiscate the <Col: StageText>chemicals</Col>.";
            contentsSimple[1491] = "TaskObjectives_JG_BCS1_Exemplar3_B_Stage05_Opp_OwnerBrief=";
            contentsSimple[1492] = "TaskObjectives_JG_BCS1_Exemplar3_B_Stage05_Opp_DispatchBrief=If the Roses have the tear gas and the antidote they will be a severe threat. So stop them now! <Col: StageText>Intercept </Col>them before they reach their <Col: StageText>drop-off</Col> and confiscate the <Col: StageText>chemicals</Col>.";
            contentsSimple[1493] = "TaskObjectives_JG_BCS1_Exemplar3_G_Stage01_OwnerBrief=PNB bank have installed a new fingerprint scan security system. <Col: StageText>Break into </Col>this morgue <Col: StageText>building </Col>and steal the finger of a security guard we killed last week. It is in their medical waste.";
            contentsSimple[1494] = "TaskObjectives_JG_BCS1_Exemplar3_G_Stage01_DispatchBrief=The fingerprint scanners in banks should have stopped the G-Kings. Instead they plan to steal a dead security guard's finger and use it. <Col: StageText>Defend </Col>the <Col: StageText>morgue </Col>from the Gs, or <Col: StageText>kill </Col>them.";
            contentsSimple[1495] = "TaskObjectives_JG_BCS1_Exemplar3_G_Stage02_OwnerBrief=We need the finger of a higher ranking employee to activate the vault fingerprint machine. Manager Lester Leffin is laid out at the forensics lab. <Col: StageText>Burgle </Col>the <Col: StageText>building </Col>and get that digit.";
            contentsSimple[1496] = "TaskObjectives_JG_BCS1_Exemplar3_G_Stage02_DispatchBrief=The G-Kings need a second finger to fool the fingerprint machine at the bank vault. <Col: StageText>Defend </Col>the <Col: StageText>funeral parlor </Col>and the body of the bank's VP. If the Gs come, <Col: StageText>kill them</Col>, they are in the right place for it.";
            contentsSimple[1497] = "TaskObjectives_JG_BCS1_Exemplar3_G_Stage03_OwnerBrief=Alright, you can give the 'Forcers the finger - but only metaphorically. In reality you should <Col: StageText>deliver </Col>the <Col: StageText>fingers </Col>to the <Col: StageText>drop-off</Col>. Cheers.";
            contentsSimple[1498] = "TaskObjectives_JG_BCS1_Exemplar3_G_Stage03_DispatchBrief=The G-Kings are making off with two dead <Col: StageText>fingers </Col>in those biohazard containers. They could get deep inside the bank vault with those. <Col: StageText>Intercept </Col>them before they reach the <Col: StageText>drop-off</Col>.";
            contentsSimple[1499] = "TaskObjectives_JG_BCS1_Exemplar3_G_Stage04_OwnerBrief=The banks have ramped up their biometric security by introducing a retina scan. Luckily the late VP left her body to science. <Col: StageText>Burgle </Col>the <Col: StageText>office </Col>of the science lab.";
            contentsSimple[1500] = "TaskObjectives_JG_BCS1_Exemplar3_G_Stage04_DispatchBrief=The G-Kings just realized the banks have installed a retina scanner. Now they need a bank manager's eyeball. <Col: StageText>Defend </Col>the medical <Col: StageText>lab</Col>. If the Gs show, <Col: StageText>kill them</Col>.";
            contentsSimple[1501] = "TaskObjectives_JG_BCS1_Exemplar3_G_Stage05_OwnerBrief=Good work. Now <Col: StageText>deliver </Col>that <Col: StageText>eyeball </Col>to the G-King <Col: StageText>drop-off</Col>. Retinas are delicate, so keep an eye on it. Ha ha.";
            contentsSimple[1502] = "TaskObjectives_JG_BCS1_Exemplar3_G_Stage05_DispatchBrief=We can't let the criminals get away with all these body parts. Have they no respect? <Col: StageText>Intercept </Col>them before they reach their <Col: StageText>drop-off</Col>. Collect their body parts and see how they like it.";
            contentsSimple[1503] = "TaskObjectives_JG_BCS1_Exemplar3_G_Stage05_Opp_OwnerBrief=";
            contentsSimple[1504] = "TaskObjectives_JG_BCS1_Exemplar3_G_Stage05_Opp_DispatchBrief=We can't let the criminals get away with all these body parts. Have they no respect? <Col: StageText>Intercept </Col>them before they reach their <Col: StageText>drop-off</Col> and return it to ours.";
            contentsSimple[1505] = "TaskObjectives_JG_BCS2_Ars1_Stage01_OwnerBrief=Shock-jock Marta Riviera has been mouthing off about us again on her WTF San Paro show. <Col: StageText>Burn the sports cars </Col>belonging to her and her agent. That'll make for a great 'comment of the week'.";
            contentsSimple[1506] = "TaskObjectives_JG_BCS2_Ars1_Stage01_DispatchBrief=Shock-jock Marta Riviera made some decent pro-Enforcer comments on air, and the Criminals don't like it. <Col: StageText>Protect </Col>the <Col: StageText>sports cars </Col>that belong to her and her agent, and we may get another mention.";
            contentsSimple[1507] = "TaskObjectives_JG_BCS2_Ars1_Stage02_OwnerBrief=You know, it's wrong to blame Marta for calling us 'thugs' on that radio show, she is just part of the system. So <Col: StageText>set fire </Col>to the radio station owner's <Col: StageText>office </Col>instead.";
            contentsSimple[1508] = "TaskObjectives_JG_BCS2_Ars1_Stage02_DispatchBrief=<Col: StageText>Defend </Col>this <Col: StageText>building </Col>from arsonists. It belongs to the radio station that has been giving us some top publicity recently. A good job here will help our recruiting process.";
            contentsSimple[1509] = "TaskObjectives_JG_BCS2_Ars1_Stage03_OwnerBrief=Looks like the radio station has hired some help. Here's the headline - <Col: StageText>kill them all</Col>. And tune in next week, folks, for more extreme violence!";
            contentsSimple[1510] = "TaskObjectives_JG_BCS2_Ars1_Stage03_DispatchBrief=Marta Riviera's radio station has hired us to take care of their Criminal 'critics'. <Col: StageText>Kill everyone </Col>who doesn't understand that you phone in your comments - you don't take it out on the staff.";
            contentsSimple[1511] = "TaskObjectives_JG_BCS2_Bom1_Stage01_OwnerBrief=Food guru Akira Watanabe borrowed a large sum of money from us to launch a new restaurant. Repayment has not been forthcoming. <Col: StageText>Blow up </Col>his <Col: StageText>sports cars </Col>as a gentle reminder.";
            contentsSimple[1512] = "TaskObjectives_JG_BCS2_Bom1_Stage01_DispatchBrief=Food guru Akira Watanabe needs some personal protection. <Col: StageText>Defend </Col>his <Col: StageText>cars </Col>from being bombed by a gang of Criminals. They want to use his restaurant as a front.";
            contentsSimple[1513] = "TaskObjectives_JG_BCS2_Bom1_Stage02_OwnerBrief=Still no repayments from Watanabe-san. <Col: StageText>Blow up </Col>these two <Col: StageText>buildings</Col>; his storerooms. Hope he likes BBQ.";
            contentsSimple[1514] = "TaskObjectives_JG_BCS2_Bom1_Stage02_DispatchBrief=Heads up! Akira Watanabe's properties are now also at risk. <Col: StageText>Protect the buildings </Col>from the Criminal bombers. Maybe Akira's food was too rich for their delicate sensibilities.";
            contentsSimple[1515] = "TaskObjectives_JG_BCS2_Bom1_Stage03_OwnerBrief=Akira Wantanbe has hired in some extra protection. <Col: StageText>Eliminate </Col>any <Col: StageText>opponents</Col>, and I'll go visit him in person to get our money back. Revenge is a dish best served cold.";
            contentsSimple[1516] = "TaskObjectives_JG_BCS2_Bom1_Stage03_DispatchBrief=This gang really want to get to Akira Watanabe. He stood up to the Criminals and we have to support him. <Col: StageText>Kill his enemies</Col>, and Watanabe could become a powerful pro-Enforcer advocate in San Paro.";
            contentsSimple[1517] = "TaskObjectives_JG_BCS2_Exemplar1_Stage01_OwnerBrief=Two reporters have been sniffing around, looking for a scoop on San Paro's seedy underbelly. Well, go give them an exclusive. <Col: StageText>Burgle </Col>their undercover <Col: StageText>offices</Col>, and take anything that may incriminate us.";
            contentsSimple[1518] = "TaskObjectives_JG_BCS2_Exemplar1_Stage01_DispatchBrief=A couple of local reporters were sent a photo that links a local gang with the recent ram-raid of the Karnak Casino. <Col: StageText>Defend </Col>their undercover <Col: StageText>offices </Col>until we can recover that evidence.";
            contentsSimple[1519] = "TaskObjectives_JG_BCS2_Exemplar1_Stage02_OwnerBrief=Shit, those are guns we used on a recent job. I guess the reporters dug them out of a dumpster somewhere. <Col: StageText>Deliver </Col>those <Col: StageText>guns </Col>to our <Col: StageText>drop-off </Col>and we'll get rid of them - properly, this time.";
            contentsSimple[1520] = "TaskObjectives_JG_BCS2_Exemplar1_Stage02_DispatchBrief=The Criminals have recovered the photographic evidence linking them to the casino robbery. <Col: StageText>Stop </Col>that <Col: StageText>evidence </Col>before the Criminals get it to their <Col: StageText>drop-off</Col>.";
            contentsSimple[1521] = "TaskObjectives_JG_BCS2_Exemplar1_Stage03_OwnerBrief=I'm concerned that those reporters might have more intel on the job we pulled. <Col: StageText>Burgle </Col>their editor's <Col: StageText>office </Col>and see if there's anything else that could be used against us.";
            contentsSimple[1522] = "TaskObjectives_JG_BCS2_Exemplar1_Stage03_DispatchBrief=These reporters could teach our investigators a thing or two. They have footage of the Criminals committing the robbery. <Col: StageText>Protect </Col>the editor's <Col: StageText>office </Col>from any attempt to steal that film.";
            contentsSimple[1523] = "TaskObjectives_JG_BCS2_Exemplar1_Stage04_OwnerBrief=Shit, that camcorder footage shows our whole job. At least they got my good side. Still, <Col: StageText>deliver </Col>the <Col: StageText>footage </Col>to the <Col: StageText>drop-off </Col>and I'll keep it with my personal collection.";
            contentsSimple[1524] = "TaskObjectives_JG_BCS2_Exemplar1_Stage04_DispatchBrief=That <Col: StageText>camcorder </Col>carries footage that will nail our case against those Criminals. <Col: StageText>Stop </Col>them <Col: StageText>delivering </Col>it to their <Col: StageText>drop-off </Col>or it'll be gone for good.";
            contentsSimple[1525] = "TaskObjectives_JG_BCS2_Exemplar1_Stage05_OwnerBrief=Damn, the reporters must have sent their article to the Enforcers. <Col: StageText>Capture </Col>and <Col: StageText>hold </Col>this <Col: StageText>location </Col>.";
            contentsSimple[1526] = "TaskObjectives_JG_BCS2_Exemplar1_Stage05_DispatchBrief=Without that camcorder, our case is shaky. We need the Crims out of this area so we can search for new evidence. <Col: StageText>Capture </Col>and <Col: StageText>hold </Col>this <Col: StageText>location </Col>.";
            contentsSimple[1527] = "TaskObjectives_JG_BCS2_VTh1_Stage01_OwnerBrief=We need a big-ass ride to transport some merchandise. <Col: StageText>Steal </Col>this <Col: StageText>vehicle </Col>and I'll suggest that we only acquire plasma screens smaller than 70 inches from now on.";
            contentsSimple[1528] = "TaskObjectives_JG_BCS2_VTh1_Stage01_DispatchBrief=A local gang are going to rob a big electronics shipping company, but they need something to transport the humongous plasma TVs. <Col: StageText>Protect this vehicle</Col>, they want to use it to transport them.";
            contentsSimple[1529] = "TaskObjectives_JG_BCS2_VTh1_Stage02_OwnerBrief=<Col: StageText>Deliver that vehicle</Col>. We'll change the plates, then it can be sent out on our TV removal job. We can fit a lot of screens in the back of that beast.";
            contentsSimple[1530] = "TaskObjectives_JG_BCS2_VTh1_Stage02_DispatchBrief=<Col: StageText>Stop </Col>that <Col: StageText>vehicle </Col>before it reaches the gang's <Col: StageText>drop-off </Col>point. Without it, they can't pull off their planned robbery.";
            contentsSimple[1531] = "TaskObjectives_JG_BCS2_VTh1_Stage03_OwnerBrief=Even that vehicle won't be big enough on its own. <Col: StageText>Steal </Col>this <Col: StageText>van</Col> - it should have the capacity to take a few stolen 120 inch jumbo plasmas.";
            contentsSimple[1532] = "TaskObjectives_JG_BCS2_VTh1_Stage03_DispatchBrief=This gang means serious business. They are going to steal a van so they can take the largest plasma TVs. Must be planning a helluva Superbowl party this year! <Col: StageText>Don't let them steal the van</Col>.";
            contentsSimple[1533] = "TaskObjectives_JG_BCS2_VTh1_Stage04_OwnerBrief=<Col: StageText>Deliver the van </Col>so we can get it ready. I may need to up the suspension on it if these plasmas are as heavy as I've heard.";
            contentsSimple[1534] = "TaskObjectives_JG_BCS2_VTh1_Stage04_DispatchBrief=<Col: StageText>Stop </Col>that <Col: StageText>van </Col>from reaching the gang's <Col: StageText>drop-off </Col>point. That will put an end to their whole plasma TV heist.";
            contentsSimple[1535] = "TaskObjectives_JG_BCS2_VTh1_Stage05_OwnerBrief=Somebody leaked our plans to shift the plasma TVs. <Col: StageText>Hold </Col>our drop-off<Col: StageText>area </Col>.";
            contentsSimple[1536] = "TaskObjectives_JG_BCS2_VTh1_Stage05_DispatchBrief=If that's how they want to play it, then it's time to deliver some big screen justice, HD style. Take <Col: StageText>hold </Col> of the <Col: StageText>area </Col>.";
            contentsSimple[1537] = "TaskObjectives_JG_BCS3_Ars1_Stage01_OwnerBrief=We just killed Asenka Leonov, a member of the Russian Mafia. Not good - now we need cash to buy off witnesses. <Col: StageText>Set fire </Col>to these target <Col: StageText>vehicles </Col>to scare up some protection money.";
            contentsSimple[1538] = "TaskObjectives_JG_BCS3_Ars1_Stage01_DispatchBrief=An ex-KGB officer - Asenka Leonov - and her gang of Russian Mafiosos are running a protection racket. They're planning an <Col: StageText>arson </Col>attack on these civilian <Col: StageText>vehicles</Col>. <Col: StageText>Defend </Col>the vehicles.";
            contentsSimple[1539] = "TaskObjectives_JG_BCS3_Ars1_Stage02_OwnerBrief=A couple of property owners won't pay protection 'cause their buildings are safeguarded by a sprinkler system. Cut the power to their block by <Col: StageText>sabotaging </Col>these <Col: StageText>electrical boxes</Col>.";
            contentsSimple[1540] = "TaskObjectives_JG_BCS3_Ars1_Stage02_DispatchBrief=Leonov has now ordered her gang to <Col: StageText>sabotage </Col>these <Col: StageText>electrical boxes</Col>. This may be an attempt to disable the fire alarms and sprinklers in nearby buildings.<Col: StageText>Prevent </Col>the sabotage.";
            contentsSimple[1541] = "TaskObjectives_JG_BCS3_Ars1_Stage03_OwnerBrief=With the power down, the sprinkler systems are inactive. Now's the time to <Col: StageText>burn </Col>these <Col: StageText>properties</Col>. We'll start hitting up the neighbors, suggesting the same thing could happen to them...";
            contentsSimple[1542] = "TaskObjectives_JG_BCS3_Ars1_Stage03_DispatchBrief=The gang escalated their <Col: StageText>arson </Col>attack, moving onto these <Col: StageText>buildings</Col>. <Col: StageText>Defend </Col>them, and show the citizens that we can protect them.";
            contentsSimple[1543] = "TaskObjectives_JG_BCS3_Ars1_Stage04_OwnerBrief=The neigboring property owners are paying up. You've done well, but you've drawn unwanted attention. <Col: StageText>Eliminate </Col>all <Col: StageText>opponents </Col>- we need to pay off the witnesses to the Leonov killing ASAP.";
            contentsSimple[1544] = "TaskObjectives_JG_BCS3_Ars1_Stage04_DispatchBrief=The arson attempts by Asenka Leonov and her gang of Russian Mafia have been successful, but we can still salvage something - <Col: StageText>eliminate </Col>the <Col: StageText>arsonists </Col>as a deterrent against future attacks.";
            contentsSimple[1545] = "TaskObjectives_JG_BCS3_Bur1_Stage01_OwnerBrief=A gang smuggling fire opals stashed some gems nearby, but we don't know where. <Col: StageText>Steal </Col>the <Col: StageText>information </Col>from this<Col: StageText> building </Col>- Enforcers are using it as a base to investigate the smugglers.";
            contentsSimple[1546] = "TaskObjectives_JG_BCS3_Bur1_Stage01_DispatchBrief=Our investigation into a gang smuggling Mexican fire opals is based in this <Col: StageText>building</Col>. <Col: StageText>Protect </Col>the building from burglary - the gang is trying to steal our intel.";
            contentsSimple[1547] = "TaskObjectives_JG_BCS3_Bur1_Stage02_OwnerBrief=Bring that Enforcer <Col: StageText>paperwork </Col>to this <Col: StageText>drop-off </Col>and we'll try and figure out what we need to know. Those fire opals will be worth a fortune on the open market.";
            contentsSimple[1548] = "TaskObjectives_JG_BCS3_Bur1_Stage02_DispatchBrief=Our <Col: StageText>intel </Col>on that opal smuggling gang is on its way to this <Col: StageText>drop-off </Col>- be sure to <Col: StageText>stop </Col>it before it arrives. We were just about to bust the whole international operation...";
            contentsSimple[1549] = "TaskObjectives_JG_BCS3_Bur1_Stage03_OwnerBrief=Excellent. The Enforcers had located the stash of stolen opals but needed a CSA order for recovery. Move in fast and <Col: StageText>steal </Col>what you can from these <Col: StageText>buildings</Col>, before the Enforcers raid them.";
            contentsSimple[1550] = "TaskObjectives_JG_BCS3_Bur1_Stage03_DispatchBrief=The gang know we're on to them - they're trying to retrieve an opal shipment from these <Col: StageText>buildings</Col>. Be sure to <Col: StageText>defend </Col>the buildings from attempted burglary. We need to recover those gems!";
            contentsSimple[1551] = "TaskObjectives_JG_BCS3_Bur1_Stage04_OwnerBrief=Bring those <Col: StageText>fire opals </Col>to this <Col: StageText>drop-off </Col>while we arrange to sell them on to the highest bidder. Several collectors, sculptors and jewelers have already expressed an interest.";
            contentsSimple[1552] = "TaskObjectives_JG_BCS3_Bur1_Stage04_DispatchBrief=The gang of smugglers raided those storage buildings. Make sure you <Col: StageText>stop </Col>the <Col: StageText>fire opals </Col>they've taken from being delivered to this <Col: StageText>drop-off</Col>.";
            contentsSimple[1553] = "TaskObjectives_JG_BCS3_Bur1_Stage05_OwnerBrief=Excellent. One phone call, and we've got a bundle of cash in exchange for those fire opals. <Col: StageText>Grab </Col>the <Col: StageText>cash </Col>and <Col: StageText>keep it </Col>away from any opponents.";
            contentsSimple[1554] = "TaskObjectives_JG_BCS3_Bur1_Stage05_DispatchBrief=<Col: StageText>Grab </Col>the <Col: StageText>cash </Col>paid by international buyers for those opals. <Col: StageText>Keep it </Col>out of the gang's hands. We can still make a case against the gang if we can trace the money.";
            contentsSimple[1555] = "TaskObjectives_JG_BCS3_Exemplar_Stage01_OwnerBrief=We've been paid to steal national security information. It's held in a case with a digital combination lock. <Col: StageText>Burgle </Col>these <Col: StageText>buildings </Col>and recover the case and the PDA holding the combination.";
            contentsSimple[1556] = "TaskObjectives_JG_BCS3_Exemplar_Stage01_DispatchBrief=Burglars are targeting these <Col: StageText>offices</Col>. They're trying to steal classified information about off-shore naval operations. <Col: StageText>Protect </Col>the offices, and keep the data out of Criminal hands.";
            contentsSimple[1557] = "TaskObjectives_JG_BCS3_Exemplar_Stage02_OwnerBrief=We've been paid to steal national security information. It's held in a case with a digital combination lock. <Col: StageText>Burgle </Col>these <Col: StageText>buildings </Col>and recover the case and the PDA holding the combination.";
            contentsSimple[1558] = "TaskObjectives_JG_BCS3_Exemplar_Stage02_DispatchBrief=Burglars are targeting these <Col: StageText>offices</Col>. They're trying to steal classified information about off-shore naval operations. <Col: StageText>Protect </Col>the offices and keep the data out of Criminal hands.";
            contentsSimple[1559] = "TaskObjectives_JG_BCS3_Exemplar_Stage03_OwnerBrief=Great! You have the <Col: StageText>briefcase </Col>and the <Col: StageText>combination</Col>. <Col: StageText>Deliver </Col>both items to our <Col: StageText>drop-off </Col>and we'll get the case open. Our buyers will be most financially grateful.";
            contentsSimple[1560] = "TaskObjectives_JG_BCS3_Exemplar_Stage03_DispatchBrief=Criminals have stolen a <Col: StageText>briefcase </Col>and a <Col: StageText>PDA </Col>holding the keycode to get the briefcase open. <Col: StageText>Stop </Col>the Criminals before they get everything to their <Col: StageText>drop-off</Col>.";
            contentsSimple[1561] = "TaskObjectives_JG_BCS3_Exemplar_Stage04_OwnerBrief=Damn! We only got half of that classified information. The rest is in a different case with a different PDA code combination. <Col: StageText>Burgle </Col>these <Col: StageText>buildings </Col>and take the second PDA and case.";
            contentsSimple[1562] = "TaskObjectives_JG_BCS3_Exemplar_Stage04_DispatchBrief=Naval Intelligence says the second half of that classified information is in these <Col: StageText>offices</Col>. <Col: StageText>Protect </Col>the offices from the burglars and keep the data out of their hands.";
            contentsSimple[1563] = "TaskObjectives_JG_BCS3_Exemplar_Stage05_OwnerBrief=Damn! We only got half of that classified information. The rest is in a different case with a different PDA code combination. <Col: StageText>Burgle </Col>these <Col: StageText>buildings </Col>and take the second PDA and case.";
            contentsSimple[1564] = "TaskObjectives_JG_BCS3_Exemplar_Stage05_DispatchBrief=Naval Intelligence says the second half of that classified information is in these <Col: StageText>offices</Col>. <Col: StageText>Protect </Col>the offices from the burglars and keep the data out of their hands.";
            contentsSimple[1565] = "TaskObjectives_JG_BCS3_Exemplar_Stage06_OwnerBrief=Our espionage is about to be very successful: <Col: StageText>deliver </Col>the second <Col: StageText>briefcase </Col>and the <Col: StageText>combination </Col>to our <Col: StageText>drop-off </Col>and we'll complete our analysis.";
            contentsSimple[1566] = "TaskObjectives_JG_BCS3_Exemplar_Stage06_DispatchBrief=Criminals have the second <Col: StageText>briefcase </Col>and the <Col: StageText>PDA </Col>with the code to open it. <Col: StageText>Stop </Col>the Criminals before they get everything to their <Col: StageText>drop-off</Col>.";
            contentsSimple[1567] = "TaskObjectives_JG_BCS3_Exemplar_Stage07_OwnerBrief=Good work. We've compiled the national security information and uploaded it directly to our foreign clients. <Col: StageText>Recover </Col>our <Col: StageText>payment </Col>and <Col: StageText>hold </Col>it away from our opponents.";
            contentsSimple[1568] = "TaskObjectives_JG_BCS3_Exemplar_Stage07_DispatchBrief=The Criminals have uploaded the data they've stolen, but have yet to collect payment from their foreign buyers. <Col: StageText>Recover </Col>the <Col: StageText>payment </Col>and <Col: StageText>hold </Col>on to it - we may be able to trace its source.";
            contentsSimple[1569] = "TaskObjectives_JG_BCS4_Bom1_Stage01_OwnerBrief=Enforcers have shut down our usual sources of explosives, so we need to kick it home-brew style. <Col: StageText>Break into </Col>this shipping <Col: StageText>crate </Col>and steal a canister of nitric acid.";
            contentsSimple[1570] = "TaskObjectives_JG_BCS4_Bom1_Stage01_DispatchBrief=We've confiscated large quantities of explosives from local gangs. They'll probably now try to make their own. <Col: StageText>Defend </Col>this <Col: StageText>crate </Col>of nitric acid: it's a key ingredient.";
            contentsSimple[1571] = "TaskObjectives_JG_BCS4_Bom1_Stage02_OwnerBrief=Methyl-B is a little harder to come by, but we've found a small stash belonging to another gang. <Col: StageText>Burgle </Col>that <Col: StageText>building </Col>and take the chemical.";
            contentsSimple[1572] = "TaskObjectives_JG_BCS4_Bom1_Stage02_DispatchBrief=The Criminals will now be looking to acquire a solvent for their home-made explosives, and we've located a vulnerable source in this <Col: StageText>building</Col>. <Col: StageText>Defend </Col>the target from the burglars.";
            contentsSimple[1573] = "TaskObjectives_JG_BCS4_Bom1_Stage03_OwnerBrief=Lastly, we need some detonators. An explosives expert, Azrael, ram-raided an electronics store. He locked stolen <Col: StageText>detonators </Col>in the back of the <Col: StageText>van </Col>he used. <Col: StageText>Break in </Col>and take them.";
            contentsSimple[1574] = "TaskObjectives_JG_BCS4_Bom1_Stage03_DispatchBrief=The Criminals now need detonators for their home-made bombs. A shipment of electronics is in transit via this van. <Col: StageText>Protect the van </Col>from these modern day Alfred Nobels.";
            contentsSimple[1575] = "TaskObjectives_JG_BCS4_Bom1_Stage04_OwnerBrief=Now <Col: StageText>deliver everything </Col>back to our <Col: StageText>drop-off </Col>and we'll mix it up. I got a few 'expendable' guys doing the actual chemistry, since I guess you enjoy having all your fingers as much as I do.";
            contentsSimple[1576] = "TaskObjectives_JG_BCS4_Bom1_Stage04_DispatchBrief=The Criminals are now taking all of the bomb <Col: StageText>components </Col>to their <Col: StageText>drop-off</Col>. <Col: StageText>Stop </Col>that delivery, or we'll be on the other end of those explosives.";
            contentsSimple[1577] = "TaskObjectives_JG_BCS4_Bom1_Stage05_OwnerBrief=We need to test the mix, and you probably to see the results of all your hard work. <Col: StageText>Bomb </Col>these <Col: StageText>garages </Col>and show the Enforcers we're coming for them.";
            contentsSimple[1578] = "TaskObjectives_JG_BCS4_Bom1_Stage05_DispatchBrief=The Criminals have built a couple of small bombs and are going to test their home-made explosive mix. <Col: StageText>Defend </Col>these <Col: StageText>garages</Col>and prevent the terror attacks on the populace.";
            contentsSimple[1579] = "TaskObjectives_JG_BCS4_Bom1_Stage06_OwnerBrief=The mix is good, and we're ready to make a statement: everyone has a right to high explosives, here in San Paro. Plant a <Col: StageText>bomb </Col>in this public <Col: StageText>area </Col>and then <Col: StageText>protect it</Col> until it explodes.";
            contentsSimple[1580] = "TaskObjectives_JG_BCS4_Bom1_Stage06_DispatchBrief=A third, more complex, device has been built, and the Criminals want to use it in a <Col: StageText>public area</Col>. Needier for attention than kindergarteners. <Col: StageText>Protect the area </Col>and <Col: StageText>defuse the bomb </Col>if they arm it.";
            contentsSimple[1581] = "TaskObjectives_JG_BCS4_Exemplar_Stage01_OwnerBrief=I've got some low-profile crews out in the city, keeping watch on 'Forcers, scoping out future jobs, keeping me in the loop. They're due to be paid. <Col: StageText>Burgle </Col>this <Col: StageText>building </Col>and grab some cash.";
            contentsSimple[1582] = "TaskObjectives_JG_BCS4_Exemplar_Stage01_DispatchBrief=An employee at a client's company embezzled funds to pay for her wedding, and hired thugs to get rid of her paper trail. <Col: StageText>Defend this building </Col>before they take a magnet to the PCs in there.";
            contentsSimple[1583] = "TaskObjectives_JG_BCS4_Exemplar_Stage02_OwnerBrief=That should be enough cash to pay for the information from my first crew of intel-gatherers, but we'll need more. <Col: StageText>Burgle </Col>another <Col: StageText>building </Col>and get hold of some more cash.";
            contentsSimple[1584] = "TaskObjectives_JG_BCS4_Exemplar_Stage02_DispatchBrief=That bride would have gotten away with her embezzling - but she screwed up and made a transaction in the wrong currency. <Col: StageText>Defend this building </Col>from her thugs before they shred her files.";
            contentsSimple[1585] = "TaskObjectives_JG_BCS4_Exemplar_Stage03_OwnerBrief=The third crew gets a bonus: they gave me good intel for a couple of your jobs. What, you never wondered how I always know what's up? <Col: StageText>Burgle </Col>this gang's <Col: StageText>stash </Col>and steal all of it.";
            contentsSimple[1586] = "TaskObjectives_JG_BCS4_Exemplar_Stage03_DispatchBrief=The embezzling bride has been laundering money for months, and now has cash stashes in several places. <Col: StageText>Defend this building </Col>from the Crims she hired so they can't retrieve that dough.";
            contentsSimple[1587] = "TaskObjectives_JG_BCS4_Exemplar_Stage04_OwnerBrief=All right, that's enough cash to pay everyone. <Col: StageText>Deliver </Col>some of the <Col: StageText>cash </Col>to this <Col: StageText>location </Col>and the first crew will collect it from there.";
            contentsSimple[1588] = "TaskObjectives_JG_BCS4_Exemplar_Stage04_DispatchBrief=Thanks to all the embezzlement, the bride could order imported orchids for every centerpiece. Her florist was willing to be paid under the table. <Col: StageText>Stop her payment reaching this drop-off. </Col>";
            contentsSimple[1589] = "TaskObjectives_JG_BCS4_Exemplar_Stage05_OwnerBrief=Two crews' paydays left. <Col: StageText>Deliver </Col>some more of the <Col: StageText>cash </Col>to this <Col: StageText>location</Col>, and that'll be enough for the second crew. They'll dole it out.";
            contentsSimple[1590] = "TaskObjectives_JG_BCS4_Exemplar_Stage05_DispatchBrief=Our client's embezzling employee is getting some local pyro to set off a huge fireworks display during the reception - not cheap... <Col: StageText>Stop </Col>her guys reaching their <Col: StageText>drop-off </Col>with the pyro's payment.";
            contentsSimple[1591] = "TaskObjectives_JG_BCS4_Exemplar_Stage06_OwnerBrief=Just the third crew needs to be paid now. <Col: StageText>Deliver </Col>the <Col: StageText>briefcase </Col>to this <Col: StageText>location </Col>and they'll work out the split amongst themselves.";
            contentsSimple[1592] = "TaskObjectives_JG_BCS4_Exemplar_Stage06_DispatchBrief=More <Col: StageText>cash </Col>is on its way to a <Col: StageText>drop-off </Col>to bribe a hotel desk jockey. He's going to book rooms for the bride's wedding guests. <Col: StageText>Stop </Col>the bride's hired thugs before they make that delivery.";
            contentsSimple[1593] = "TaskObjectives_JG_BCS4_Exemplar_Stage07_OwnerBrief=My boys have been busy again! This <Col: StageText>case </Col>contains intelligence which will help me plan future jobs. <Col: StageText>Deliver </Col>it to our <Col: StageText>drop-off </Col>before the Enforcers get it to theirs.";
            contentsSimple[1594] = "TaskObjectives_JG_BCS4_Exemplar_Stage07_DispatchBrief=";
            contentsSimple[1595] = "TaskObjectives_JG_BCS4_Exemplar_Stage07_Opp_OwnerBrief=";
            contentsSimple[1596] = "TaskObjectives_JG_BCS4_Exemplar_Stage07_Opp_DispatchBrief=The bride's thugs collected a <Col: StageText>briefcase </Col>full of Strigoi sunglasses for wedding favors. <Col: StageText>Deliver </Col>it to our <Col: StageText>drop-off </Col>and we'll use it as evidence against this crazy broad.";
            contentsSimple[1597] = "TaskObjectives_JG_BEL0_Inv1_P_Stage01_OwnerBrief=G-Kings have been selling pirated video games; games published by companies under Praetorian protection. <Col: StageText>Raid </Col>these <Col: StageText>buildings </Col>where the copies are being made.";
            contentsSimple[1598] = "TaskObjectives_JG_BEL0_Inv1_P_Stage01_DispatchBrief=We've made quite a bit of cash selling pirated video games and now the Praetorians are on to us. <Col: StageText>Defend </Col>our <Col: StageText>buildings </Col>and ensure that the 'Torians don't <Col: StageText>raid </Col>them.";
            contentsSimple[1599] = "TaskObjectives_JG_BEL0_Inv1_P_Stage02_OwnerBrief=Good work. Now <Col: StageText>deliver </Col>the <Col: StageText>games </Col>to our <Col: StageText>drop-off </Col>and we'll take them in as evidence.";
            contentsSimple[1600] = "TaskObjectives_JG_BEL0_Inv1_P_Stage02_DispatchBrief=The Praetorians have recovered several of our pirated video games to use as evidence against us. <Col: StageText>Intercept </Col>the <Col: StageText>games </Col>before they reach the <Col: StageText>drop-off</Col>.";
            contentsSimple[1601] = "TaskObjectives_JG_BEL0_Inv1_P_Stage03_OwnerBrief=It seems that pirated copies of the latest Super Highway Patrol game are being sold from the back of vehicles around the city. <Col: StageText>Search </Col>this <Col: StageText>van </Col>for any evidence.";
            contentsSimple[1602] = "TaskObjectives_JG_BEL0_Inv1_P_Stage03_DispatchBrief=We were selling pirated copies of the latest Super Highway Patrol game from the back of this<Col: StageText> van</Col>: now it's a Praetorian target. <Col: StageText>Defend </Col>the van from Praetorian investigation.";
            contentsSimple[1603] = "TaskObjectives_JG_BEL0_Inv1_P_Stage04_OwnerBrief=That <Col: StageText>bag </Col>contains cash earned through the sale of pirated goods. <Col: StageText>Deliver </Col>the cash to the <Col: StageText>drop-off</Col>.";
            contentsSimple[1604] = "TaskObjectives_JG_BEL0_Inv1_P_Stage04_DispatchBrief=The Praetorians took the cash from our pirated game dealers! We need it to fund our new stock of diamond-studded grenades. <Col: StageText>Intercept </Col>the <Col: StageText>cash </Col>before it reaches the <Col: StageText>drop-off</Col>.";
            contentsSimple[1605] = "TaskObjectives_JG_BEL0_Inv1_P_Stage05_OwnerBrief=The G-Kings are trying to prevent seizure of 100,000 copies of Super Higway Patrol, by torching the evidence! <Col: StageText>Defend </Col>the storage <Col: StageText>buildings</Col>. Protect any evidence that survives the fires.";
            contentsSimple[1606] = "TaskObjectives_JG_BEL0_Inv1_P_Stage05_DispatchBrief=The Praetorians are about to shut this piracy operation down, so we'll need to cover our tracks. <Col: StageText>Burn </Col>these <Col: StageText>buildings </Col>containing the last pieces of evidence. <Col:StageText>Recover anything not burned.</Col>";
            contentsSimple[1607] = "TaskObjectives_JG_BEL0_Inv1_P_Stage05_Opp_OwnerBrief=";
            contentsSimple[1608] = "TaskObjectives_JG_BEL0_Inv1_P_Stage05_Opp_DispatchBrief=The Praetorians are about to shut this piracy operation down, so we'll need to cover our tracks.  <Col: StageText>Burn </Col>these <Col: StageText>buildings </Col>containing the last pieces of evidence. Keep anything that's not burned.";
            contentsSimple[1609] = "TaskObjectives_JG_BEL0_Inv1_P_Stage06_OwnerBrief=Looks like a few cases of DVDs escaped the flames.<Col:StageText> Recover the <Col:StageText>cases</Col> before the Criminals do.";
            contentsSimple[1610] = "TaskObjectives_JG_BEL0_Inv1_P_Stage06_DispatchBrief=Our fires didn't quite get everything.<Col:StageText> Recover the <Col:StageText>cases</Col> that survived the blaze and we'll get rid of them.";
            contentsSimple[1611] = "TaskObjectives_JG_BEL0_Inv1_P_Stage06_Opp_OwnerBrief=";
            contentsSimple[1612] = "TaskObjectives_JG_BEL0_Inv1_P_Stage06_Opp_DispatchBrief=Our fires didn't quite get everything.<Col:StageText> Recover the <Col:StageText>cases</Col> that survived the blaze and we'll get rid of them.";
            contentsSimple[1613] = "TaskObjectives_JG_BEL0_Inv1_T_Stage01_OwnerBrief=Chiza uncovered a group of G-Kings selling pirated video games. We've been told to follow it up and now we're ready to move. <Col: StageText>Raid </Col>these <Col: StageText>buildings </Col>where the games are being copied.";
            contentsSimple[1614] = "TaskObjectives_JG_BEL0_Inv1_T_Stage01_DispatchBrief=We've been making a nice pile of cash selling pirated copies of video games. <Col: StageText>Defend </Col>our copying <Col: StageText>facilities </Col>and ensure that Enforcers don't <Col: StageText>raid </Col>them.";
            contentsSimple[1615] = "TaskObjectives_JG_BEL0_Inv1_T_Stage02_OwnerBrief=Good work. Now <Col: StageText>deliver </Col>the pirated <Col: StageText>games </Col>to our <Col: StageText>drop-off </Col>and we'll take them into evidence.";
            contentsSimple[1616] = "TaskObjectives_JG_BEL0_Inv1_T_Stage02_DispatchBrief=The Prentiss Tigers have recovered several of our pirated video games to use as evidence against us. <Col: StageText>Intercept </Col>the <Col: StageText>games </Col>before they reach the <Col: StageText>drop-off</Col>.";
            contentsSimple[1617] = "TaskObjectives_JG_BEL0_Inv1_T_Stage03_OwnerBrief=It seems that pirated copies of the latest Super Highway Patrol game were being sold from the back of vehicles around the city. <Col: StageText>Search </Col>this <Col: StageText>van </Col>for any evidence.";
            contentsSimple[1618] = "TaskObjectives_JG_BEL0_Inv1_T_Stage03_DispatchBrief=We were selling pirated copies of the latest Super Highway Patrol game from the back of this <Col: StageText>van </Col>but it's now a Tiger target. <Col: StageText>Defend </Col>the van from those nosy Prentiss snobs.";
            contentsSimple[1619] = "TaskObjectives_JG_BEL0_Inv1_T_Stage04_OwnerBrief=That <Col: StageText>bag </Col>contains cash earned through the sale of pirated games. <Col: StageText>Deliver </Col>the cash to the <Col: StageText>drop-off</Col>. Remember, kids: piracy funds terrorism... and poseurs like the Blood Roses.";
            contentsSimple[1620] = "TaskObjectives_JG_BEL0_Inv1_T_Stage04_DispatchBrief=The Tigers took the cash we got from selling pirated games! We need it to fund some of our more ambitious plans, like the petting zoo heist. <Col: StageText>Prevent </Col>the <Col: StageText>cash </Col>from reaching their <Col: StageText>drop-off</Col>.";
            contentsSimple[1621] = "TaskObjectives_JG_BEL0_Inv1_T_Stage05_OwnerBrief=The software pirates are seeking to prevent our investigation of their properties. <Col: StageText>Defend </Col>the <Col: StageText>buildings </Col>from the <Col: StageText>arsonists</Col>.";
            contentsSimple[1622] = "TaskObjectives_JG_BEL0_Inv1_T_Stage05_DispatchBrief=The Tigers are about to shut this piracy operation down; we need to cover our tracks. <Col: StageText>Burn </Col>these <Col: StageText>buildings </Col>and destroy the evidence.";
            contentsSimple[1623] = "TaskObjectives_JG_BEL0_Inv1_T_Stage05_Opp_OwnerBrief=";
            contentsSimple[1624] = "TaskObjectives_JG_BEL0_Inv1_T_Stage05_Opp_DispatchBrief=The Tigers are about to shut this piracy operation down; we need to cover our tracks. <Col: StageText>Burn </Col>these <Col: StageText>buildings </Col>and destroy the evidence.";
            contentsSimple[1625] = "TaskObjectives_JG_BEL0_Inv1_T_Stage06_OwnerBrief=The PCs used to copy the disks are still around. <Col:StageText> Capture the majority</Col> of the <Col:StageText>machines</Col> and return them to our techs.";
            contentsSimple[1626] = "TaskObjectives_JG_BEL0_Inv1_T_Stage06_DispatchBrief=The only evidence left are the PCs used to copy the items. <Col:StageText>Recover the <Col:StageText>PCs</Col> and return them to base.";
            contentsSimple[1627] = "TaskObjectives_JG_BEL0_Inv1_T_Stage07_OwnerBrief=The PCs used to copy the disks are still around. <Col:StageText> Capture the <Col:StageText>machines</Col> and return them to our techs.";
            contentsSimple[1628] = "TaskObjectives_JG_BEL0_Inv1_T_Stage07_DispatchBrief=The only evidence left are the PCs used to copy the items. <Col:StageText>Recover the <Col:StageText>PCs</Col> and return them to base.";
            contentsSimple[1629] = "TaskObjectives_JG_BEL0_Inv1_T_Stage07_Opp_OwnerBrief=The PCs used to copy the disks are still around. <Col:StageText> Capture the <Col:StageText>machines</Col> and return them to our techs.";
            contentsSimple[1630] = "TaskObjectives_JG_BEL0_Inv1_T_Stage07_Opp_DispatchBrief=The only evidence left are the PCs used to copy the items. <Col:StageText>Recover the <Col:StageText>PCs</Col> and return them to base.";
            contentsSimple[1631] = "TaskObjectives_JG_BEL0_Inv2_Stage01_OwnerBrief=A valuable painting owned by Choi Ji-Sung was stolen from a lockup whilst awaiting restoration. <Col: StageText>Investigate </Col>the <Col: StageText>lockup </Col>for anything that might provide a suspect.";
            contentsSimple[1632] = "TaskObjectives_JG_BEL0_Inv2_Stage01_DispatchBrief=We stole a painting from a lockup where it was being restored. The owner paid some Enforcers to investigate. <Col: StageText>Defend </Col>the <Col: StageText>storehouse </Col>from their investigators.";
            contentsSimple[1633] = "TaskObjectives_JG_BEL0_Inv2_Stage02_OwnerBrief=Fingerprints from the scene match those of a local art thief. We have him registered as retired. <Col: StageText>Raid </Col>his <Col: StageText>home </Col>and <Col: StageText>workplace </Col>for anything that appears suspicious.";
            contentsSimple[1634] = "TaskObjectives_JG_BEL0_Inv2_Stage02_DispatchBrief=Damn, it looks like our guys must've got sloppy. The Enforcers are heading to a couple of our <Col: StageText>hideouts</Col>. <Col: StageText>Protect </Col>those buildings from the Enforcer raids.";
            contentsSimple[1635] = "TaskObjectives_JG_BEL0_Inv2_Stage03_OwnerBrief=Fingerprints from the scene match those of a local art thief. We have him registered as retired. <Col: StageText>Raid </Col>his <Col: StageText>home </Col>and <Col: StageText>workplace </Col>for anything that appears suspicious.";
            contentsSimple[1636] = "TaskObjectives_JG_BEL0_Inv2_Stage03_DispatchBrief=Damn, it looks like our guys must've got sloppy. The Enforcers are heading to a couple of our <Col: StageText>hideouts</Col>. <Col: StageText>Protect </Col>those buildings from the Enforcer raids.";
            contentsSimple[1637] = "TaskObjectives_JG_BEL0_Inv2_Stage04_OwnerBrief=We found breaching <Col: StageText>explosives </Col>and <Col: StageText>boltcutters</Col>. Looks like our art thief is back in business. <Col: StageText>Deliver </Col>the evidence to our <Col: StageText>lockup </Col>while we track him down.";
            contentsSimple[1638] = "TaskObjectives_JG_BEL0_Inv2_Stage04_DispatchBrief=Shit, the Enforcers found the <Col: StageText>explosives </Col>and <Col: StageText>boltcutters </Col>used during the theft. Someone's gonna get a verbal lashing! You need to <Col: StageText>stop </Col>that evidence from reaching the Enforcer <Col: StageText>drop-off</Col>.";
            contentsSimple[1639] = "TaskObjectives_JG_BEL0_Inv2_Stage05_OwnerBrief=We've picked up the thief and a street patrol has located his <Col: StageText>vehicle</Col>. <Col: StageText>Search </Col>the car. The painting was probably stashed there, ready for a quick getaway.";
            contentsSimple[1640] = "TaskObjectives_JG_BEL0_Inv2_Stage05_DispatchBrief=The jackass who stole the painting was picked up by the Enforcers. Thankfully, he left the loot stashed in his car for exactly this situation. <Col: StageText>Protect </Col>that <Col: StageText>vehicle </Col>from the Enforcers.";
            contentsSimple[1641] = "TaskObjectives_JG_BEL0_Inv2_Stage06_OwnerBrief=Great. <Col: StageText>Deliver </Col>the <Col: StageText>painting </Col>back to the <Col: StageText>drop-off</Col>. I'm no art lover, but Miss Ji-Sung was adamant that it be kept in one piece. Try not to get shot at.";
            contentsSimple[1642] = "TaskObjectives_JG_BEL0_Inv2_Stage06_DispatchBrief=Shit, the Enforcers have the <Col: StageText>painting </Col>we stole and they're heading back to their <Col: StageText>drop-off</Col>. <Col: StageText>Stop </Col>them before they get the painting there.";
            contentsSimple[1643] = "TaskObjectives_JG_BEL0_Inv2_Stage07_OwnerBrief=Ah, hell. That drop-off is too hot and Criminals are startin' to swarm. <Col: StageText>Deliver </Col>the <Col: StageText>painting </Col>to our secondary <Col: StageText>drop-off </Col>- and don't let the Criminals take it to theirs.";
            contentsSimple[1644] = "TaskObjectives_JG_BEL0_Inv2_Stage07_DispatchBrief=";
            contentsSimple[1645] = "TaskObjectives_JG_BEL0_Inv2_Stage07_Opp_OwnerBrief=";
            contentsSimple[1646] = "TaskObjectives_JG_BEL0_Inv2_Stage07_Opp_DispatchBrief=All right! You've pressured the Enforcers so much they're headed for a secondary location. Keep it up! <Col: StageText>Take </Col>the <Col: StageText>painting </Col>and <Col: StageText>deliver </Col>it back to our <Col: StageText>drop-off </Col>before they can get it to theirs.";
            contentsSimple[1647] = "TaskObjectives_JG_BEL1_Inv1_P_Stage01_OwnerBrief=A shipment of lethal nerve gas was hijacked and we need to recover it. <Col: StageText>Pick up </Col>this <Col: StageText>device</Col>. It can detect down to 1 mg / m3 of the chemical.";
            contentsSimple[1648] = "TaskObjectives_JG_BEL1_Inv1_P_Stage01_DispatchBrief=We stole nerve gas from a company trying to sneak it into San Paro. Now the Praetorians have been called in to find it. <Col: StageText>Stop them </Col>from <Col: StageText>picking up </Col>the chemical <Col: StageText>detector </Col>they'll need.";
            contentsSimple[1649] = "TaskObjectives_JG_BEL1_Inv1_P_Stage02_OwnerBrief=You need to triangulate the nerve gas by taking a range of readings. <Col: StageText>Take </Col>the <Col: StageText>detector </Col>to the first of three <Col: StageText>locations </Col>and scan for chemical traces.";
            contentsSimple[1650] = "TaskObjectives_JG_BEL1_Inv1_P_Stage02_DispatchBrief=The Praetorians are trying to triangulate the position of our nerve gas. It's safer in our hands - which is saying something! <Col: StageText>Stop them getting</Col> the chemical <Col: StageText>detector </Col>to this <Col: StageText>location</Col>.";
            contentsSimple[1651] = "TaskObjectives_JG_BEL1_Inv1_P_Stage03_OwnerBrief=That's one scan complete. Now <Col: StageText>take </Col>the <Col: StageText>detector </Col>to the second <Col: StageText>location </Col>and scan from there. This is what the Praetorians do best: rise to the challenge of city-wide chaos.";
            contentsSimple[1652] = "TaskObjectives_JG_BEL1_Inv1_P_Stage03_DispatchBrief=<Col: StageText>Stop </Col>the Praetorians <Col: StageText>taking </Col>the chemical detector to this second <Col: StageText>triangulation point</Col>. They not looking for the gas to protect the public - they just want to sell it to the highest bidder.";
            contentsSimple[1653] = "TaskObjectives_JG_BEL1_Inv1_P_Stage04_OwnerBrief=One more scan will allow us to triangulate the location of the nerve gas accurately. <Col: StageText>Go to </Col>the third <Col: StageText>location </Col>and use the <Col: StageText>detector </Col>there. Hurry. We need to recapture this deadly compound.";
            contentsSimple[1654] = "TaskObjectives_JG_BEL1_Inv1_P_Stage04_DispatchBrief=God knows where this nerve gas was originally headed - to be used by a dictator against his people, maybe. <Col: StageText>Stop </Col>the corporate lapdogs from <Col: StageText>reaching </Col>their final detection <Col: StageText>location</Col>.";
            contentsSimple[1655] = "TaskObjectives_JG_BEL1_Inv1_P_Stage05_OwnerBrief=Praetorian Tech HQ has a reading - faint, but it's there. <Col: StageText>Recover </Col>the <Col: StageText>nerve gas </Col>before anyone can put it to use.";
            contentsSimple[1656] = "TaskObjectives_JG_BEL1_Inv1_P_Stage05_DispatchBrief=The Praetorians have triangulated the location of the <Col: StageText>nerve gas</Col>. <Col: StageText>Stop </Col>them before they can <Col: StageText>pick it up</Col>. They can't be trusted with it.";
            contentsSimple[1657] = "TaskObjectives_JG_BEL1_Inv1_P_Stage06_OwnerBrief=Good work. San Paro can rest easier with those <Col: StageText>chemicals </Col>safe in Praetorian hands. <Col: StageText>Deliver </Col>them to the <Col: StageText>drop-off </Col>and we'll deal with them properly.";
            contentsSimple[1658] = "TaskObjectives_JG_BEL1_Inv1_P_Stage06_DispatchBrief=<Col: StageText>Intercept </Col>that <Col: StageText>nerve gas </Col>before the Praetorians <Col: StageText>take it back </Col>to their corporate masters. They'll sell it to the first dictator with a big enough wallet - and they call us Criminals!";
            contentsSimple[1659] = "TaskObjectives_JG_BEL1_Inv1_P_Stage07_OwnerBrief=Shit - Praetorian Tech HQ has discovered a second container, shielded from detection. It's enough gas to wipe out a city block. <Col: StageText>Pick up </Col>the <Col: StageText>barrel </Col>and don't let anyone get in your way.";
            contentsSimple[1660] = "TaskObjectives_JG_BEL1_Inv1_P_Stage07_DispatchBrief=Damn - the 'Forcers took the canister, but the bulk of the nerve gas is in a barrel nearby. Get there fast and <Col: StageText>pick up the barrel </Col>before the Praetorians do.";
            contentsSimple[1661] = "TaskObjectives_JG_BEL1_Inv1_P_Stage08_OwnerBrief=This is a priority one task. <Col: StageText>Take </Col>the <Col: StageText>nerve gas </Col>and <Col: StageText>deliver </Col>it to our HazMat <Col: StageText>drop-off</Col>. The gangs are after you - they could hold San Paro to ransom with that much Sarin.";
            contentsSimple[1662] = "TaskObjectives_JG_BEL1_Inv1_P_Stage08_DispatchBrief=";
            contentsSimple[1663] = "TaskObjectives_JG_BEL1_Inv1_P_Stage08_Opp_OwnerBrief=";
            contentsSimple[1664] = "TaskObjectives_JG_BEL1_Inv1_P_Stage08_Opp_DispatchBrief=<Col: StageText>Take </Col>the <Col: StageText>nerve gas barrel </Col>and <Col: StageText>deliver </Col>it to our <Col: StageText>drop-off.</Col> Don't let the Praetorians hand it over to their corporate bosses. Who knows what they'll do with it?";
            contentsSimple[1665] = "TaskObjectives_JG_BEL1_Inv1_T_Stage01_OwnerBrief=A shipment of ultra expensive Botox has been stolen. The office is getting a barrage of phone calls from concerned Prentiss ladies. <Col: StageText>Pick up </Col>this <Col: StageText>detector </Col>which can sniff out the protein.";
            contentsSimple[1666] = "TaskObjectives_JG_BEL1_Inv1_T_Stage01_DispatchBrief=We stole Botox; way easy to move, and worth more per ounce than heroin. The Tigers found out. <Col: StageText>Stop </Col>them from <Col: StageText>picking up </Col>the chemical detector they'll need to find it.";
            contentsSimple[1667] = "TaskObjectives_JG_BEL1_Inv1_T_Stage02_OwnerBrief=We need to triangulate the readings to track down the stolen Botox. My mom is freaking out that there'll be a shortage. <Col: StageText>Take </Col>the <Col: StageText>detector </Col>to the first of three <Col: StageText>locations </Col>and scan for traces.";
            contentsSimple[1668] = "TaskObjectives_JG_BEL1_Inv1_T_Stage02_DispatchBrief=The Prentiss Tigers are trying to triangulate the position of our stolen Botox. <Col: StageText>Stop them </Col>from <Col: StageText>getting to </Col>this <Col: StageText>location </Col>with their detector.";
            contentsSimple[1669] = "TaskObjectives_JG_BEL1_Inv1_T_Stage03_OwnerBrief=That's one scan complete. Now <Col: StageText>take </Col>the <Col: StageText>detector </Col>to the second of the three <Col: StageText>locations </Col>and scan again for the Botox. Be quick - they're rioting down at the nip/tuck clinic.";
            contentsSimple[1670] = "TaskObjectives_JG_BEL1_Inv1_T_Stage03_DispatchBrief=The Prentiss Tigers are using the detector to scan for the Botox we took. <Col: StageText>Stop </Col>the Tigers from <Col: StageText>taking </Col>the detector to the next <Col: StageText>location</Col>.";
            contentsSimple[1671] = "TaskObjectives_JG_BEL1_Inv1_T_Stage04_OwnerBrief=One more scan with the detector will allow us to triangulate the position. <Col: StageText>Get over </Col>to the third <Col: StageText>location </Col>while we're young. Or we'll need the Botox ourselves!";
            contentsSimple[1672] = "TaskObjectives_JG_BEL1_Inv1_T_Stage04_DispatchBrief=The Tigers seem more intent on recovering this Botox than that mustard gas we took last week. Guess Mommy doesn't need mustard gas... <Col: StageText>Stop them </Col>from <Col: StageText>reaching </Col>the final <Col: StageText>triangulation point</Col>.";
            contentsSimple[1673] = "TaskObjectives_JG_BEL1_Inv1_T_Stage05_OwnerBrief=We have a reading - but it's as faint as the wrinkles on my granny's Botoxed forehead. Get over there and <Col: StageText>recover </Col>the <Col: StageText>chemical </Col>before the place is swarming with washed-up celebs.";
            contentsSimple[1674] = "TaskObjectives_JG_BEL1_Inv1_T_Stage05_DispatchBrief=The Tigers know where our Botox is stashed and are eager to get their hands on it. Maybe they're all actually in their 50s! <Col: StageText>Stop </Col>them before they make the <Col: StageText>pickup</Col>.";
            contentsSimple[1675] = "TaskObjectives_JG_BEL1_Inv1_T_Stage06_OwnerBrief=Great work. I always said you were more than a good-looking face. <Col: StageText>Deliver </Col>the Botox to the <Col: StageText>drop-off</Col>. Sofia has been showing a lot of interest... for *some* reason.";
            contentsSimple[1676] = "TaskObjectives_JG_BEL1_Inv1_T_Stage06_DispatchBrief=The Tigers have the Botox. <Col: StageText>Intercept </Col>them before they reach their <Col: StageText>drop-off</Col>. Remember, one side effect can be twitching - so they may have loose trigger fingers.";
            contentsSimple[1677] = "TaskObjectives_JG_BEL1_Inv1_T_Stage07_OwnerBrief=Damn, we're picking up a second reading. I think that first container was just to throw us off the scent. <Col: StageText>Pick up </Col>the larger Botox <Col: StageText>container</Col>. It must be worth a fortune.";
            contentsSimple[1678] = "TaskObjectives_JG_BEL1_Inv1_T_Stage07_DispatchBrief=The Tigers almost fell for our plan. We let them take a small container of Botox so we could keep the main container. Get to it fast and <Col: StageText>pick up </Col>the <Col: StageText>barrel </Col>before the Tigers find it.";
            contentsSimple[1679] = "TaskObjectives_JG_BEL1_Inv1_T_Stage08_OwnerBrief=This is getting a lot of TV coverage, so don't fuck it up now. <Col: StageText>Take </Col>the <Col: StageText>barrel </Col>and <Col: StageText>deliver </Col>it to our <Col: StageText>drop-off</Col>. You could spill a bit on the sidewalk; cover up those cracks.";
            contentsSimple[1680] = "TaskObjectives_JG_BEL1_Inv1_T_Stage08_DispatchBrief=";
            contentsSimple[1681] = "TaskObjectives_JG_BEL1_Inv1_T_Stage08_Opp_OwnerBrief=";
            contentsSimple[1682] = "TaskObjectives_JG_BEL1_Inv1_T_Stage08_Opp_DispatchBrief=Are the Tigers frowning because we got their Botox? ...I can't tell! Heh heh. <Col: StageText>Take </Col>the <Col: StageText>barrel </Col>and <Col: StageText>deliver it </Col>to our <Col: StageText>drop-off</Col>. We'll sell it back to those Prentiss biddies at a markup.";
            contentsSimple[1683] = "TaskObjectives_JG_BEL2_AGr1_Stage01_OwnerBrief=The CSA licensing inspector is touring the city, and we need to convince him of our effectiveness or he'll slash our funding. <Col: StageText>Cover </Col>up the gang <Col: StageText>graffiti </Col>at this location.";
            contentsSimple[1684] = "TaskObjectives_JG_BEL2_AGr1_Stage01_DispatchBrief=Enforcers are moving through our turf. <Col: StageText>Stop </Col>them from covering up our <Col: StageText>tags</Col>. I'll try to find out what those losers are up to.";
            contentsSimple[1685] = "TaskObjectives_JG_BEL2_AGr1_Stage02_OwnerBrief=Let's get the neighborhood looking tidy. <Col: StageText>Cover </Col>the gang <Col: StageText>graffiti </Col>at these locations and then <Col: StageText>protect </Col>them from further attacks. If you don't, the writing's on the wall - for us.";
            contentsSimple[1686] = "TaskObjectives_JG_BEL2_AGr1_Stage02_DispatchBrief=More of our territory is at risk. <Col: StageText>Defend </Col>this <Col: StageText>graffiti </Col>from Enforcers and <Col: StageText>re-spray </Col>them if necessary. 'Forcers are just posing to look good for a municipal CSA inspector.";
            contentsSimple[1687] = "TaskObjectives_JG_BEL2_AGr1_Stage03_OwnerBrief=If the CSA inspector sees this, he'll cut our budget. <Col: StageText>Cover </Col>the last two locations and then <Col: StageText>protect </Col>them. The gangs will be trying to embarrass us on purpose.";
            contentsSimple[1688] = "TaskObjectives_JG_BEL2_AGr1_Stage03_DispatchBrief=I bet the city officials will love our graffiti... <Col: StageText>Defend </Col>our <Col: StageText>spots </Col>from Enforcers who want to cover up our tags. <Col: StageText>Re-spray</Col> them if necessary.";
            contentsSimple[1689] = "TaskObjectives_JG_BEL2_AGr1_Stage04_OwnerBrief=Now we need to hold this territory, ready for the CSA inspection. <Col: StageText>Capture </Col>these locations and then <Col: StageText>defend </Col>them.";
            contentsSimple[1690] = "TaskObjectives_JG_BEL2_AGr1_Stage04_DispatchBrief=It's time to evict the Enforcers. <Col: StageText>Capture </Col>these <Col: StageText>locations </Col>and <Col: StageText>hold </Col>them to defend our turf. The city CSA official should be made aware: this is OUR patch of San Paro.";
            contentsSimple[1691] = "TaskObjectives_JG_BEL2_AGr2_Stage01_OwnerBrief=We need to recover the deeds to some prime development land. A gang's got them stashed in a safe house, well-defended by members. <Col: StageText>Cover </Col>some gang <Col: StageText>tags </Col>to draw those guys away from their posts.";
            contentsSimple[1692] = "TaskObjectives_JG_BEL2_AGr2_Stage01_DispatchBrief=We've just bought the deeds to some properties, expanding our territory. Now Enforcers are covering our graffiti and moving onto our new turf. <Col: StageText>Defend </Col>our <Col: StageText>tags</Col> from those toy soldiers.";
            contentsSimple[1693] = "TaskObjectives_JG_BEL2_AGr2_Stage02_OwnerBrief=There's more graffiti on this nearby block. <Col: StageText>Clean up</Col> the gang tags while we draw their attention. You can always rely on Criminals to be vain and try to re-tag those spots eventually.";
            contentsSimple[1694] = "TaskObjectives_JG_BEL2_AGr2_Stage02_DispatchBrief=More of our <Col: StageText>tags </Col>are under threat. <Col: StageText>Protect </Col>them from the Enforcers. They're still pissed at us for expanding our territory, and they want revenge.";
            contentsSimple[1695] = "TaskObjectives_JG_BEL2_AGr2_Stage03_OwnerBrief=We have four potential targets, but we don't know exactly where the stolen deeds are. <Col: StageText>Raid </Col>these <Col: StageText>buildings </Col>and <Col: StageText>recover </Col>both property <Col: StageText>deeds</Col>.";
            contentsSimple[1696] = "TaskObjectives_JG_BEL2_AGr2_Stage03_DispatchBrief=Now our safehouses are at risk - <Col: StageText>defend </Col>each of these <Col: StageText>buildings </Col>from Enforcer raids. They're after the property deeds we just acquired.";
            contentsSimple[1697] = "TaskObjectives_JG_BEL2_AGr2_Stage04_OwnerBrief=We have four potential targets, but we don't know exactly where the stolen deeds are. <Col: StageText>Raid </Col>these <Col: StageText>buildings </Col>and <Col: StageText>recover </Col>both property <Col: StageText>deeds</Col>.";
            contentsSimple[1698] = "TaskObjectives_JG_BEL2_AGr2_Stage04_DispatchBrief=Now our safehouses are at risk - <Col: StageText>defend </Col>each of these <Col: StageText>buildings </Col>from Enforcer raids. They're after the property deeds we just acquired.";
            contentsSimple[1699] = "TaskObjectives_JG_BEL2_AGr2_Stage05_OwnerBrief=Excellent - we have what we came for. <Col: StageText>Deliver </Col>those <Col: StageText>deeds </Col>to our <Col: StageText>drop-off</Col>. The gang will have to find another way to expand its empire.";
            contentsSimple[1700] = "TaskObjectives_JG_BEL2_AGr2_Stage05_DispatchBrief=The Enforcers have taken our property deeds. They can't stand to see us developing and expanding. <Col: StageText>Don't </Col>let the 'Forcers <Col: StageText>deliver </Col>the <Col: StageText>deeds </Col>to their <Col: StageText>drop-off</Col>.";
            contentsSimple[1701] = "TaskObjectives_JG_BEL2_AGr2_Stage06_OwnerBrief=Okay, that gang's pissed. We rattled their cage, so we expected this: <Col: StageText>eliminate </Col>those <Col: StageText>Criminals </Col>before they take you down.";
            contentsSimple[1702] = "TaskObjectives_JG_BEL2_AGr2_Stage06_DispatchBrief=We tried expanding our empire officially, but it seems the Enforcers don't play by the rules they claim to uphold. <Col: StageText>Wipe out </Col>the <Col: StageText>Enforcers</Col>. We tried to play it their way; now they can play it ours.";
            contentsSimple[1703] = "TaskObjectives_JG_BEL2_Dis2_Stage01_OwnerBrief=A Montebank gun-smuggling cartel is about to attack a rival gang. We're worried about civilian casualties. <Col: StageText>Raid </Col>their <Col: StageText>properties </Col>and <Col: StageText>take </Col>any <Col: StageText>information </Col>on the attack to our <Col: StageText>drop-off</Col>.";
            contentsSimple[1704] = "TaskObjectives_JG_BEL2_Dis2_Stage01_DispatchBrief=We've been smuggling SMGs into the Yard, but the Enforcers are on to us. <Col: StageText>Stop </Col>them <Col: StageText>raiding </Col>our <Col: StageText>properties </Col>and taking any evidence to their drop-off.";
            contentsSimple[1705] = "TaskObjectives_JG_BEL2_Dis2_Stage02_OwnerBrief=A Montebank gun-smuggling cartel is about to attack a rival gang. We're worried about civilian casualties. <Col: StageText>Raid </Col>their <Col: StageText>properties </Col>and <Col: StageText>take </Col>any <Col: StageText>information </Col>on the attack to our <Col: StageText>drop-off</Col>.";
            contentsSimple[1706] = "TaskObjectives_JG_BEL2_Dis2_Stage02_DispatchBrief=We've been smuggling SMGs into the Yard, but the Enforcers are on to us. <Col: StageText>Stop </Col>them <Col: StageText>raiding </Col>our <Col: StageText>properties </Col>and taking any evidence to their drop-off.";
            contentsSimple[1707] = "TaskObjectives_JG_BEL2_Dis2_Stage03_OwnerBrief=The Montebank cartel is trying to destroy its rival's shipment of SMGs, but we need it as evidence. They've planted a <Col: StageText>bomb </Col>in a van parked nearby - get there and <Col: StageText>defuse </Col>it, fast!";
            contentsSimple[1708] = "TaskObjectives_JG_BEL2_Dis2_Stage03_DispatchBrief=We've gotta burn that last shipment of SMGs, or we'll be put away for years. Enforcers are headed towards the van and the incendiary we planted. <Col: StageText>Don't let them defuse it</Col>.";
            contentsSimple[1709] = "TaskObjectives_JG_BEL2_Dis2_Stage04_OwnerBrief=The bomb was stashed inside this <Col: StageText>case</Col>. Bring it to our <Col: StageText>drop-off </Col>and we'll analyze it. We may be able to pin other crimes on that Montebank cartel, if they've used similar devices in the past.";
            contentsSimple[1710] = "TaskObjectives_JG_BEL2_Dis2_Stage04_DispatchBrief=Now the Enforcers are taking the <Col: StageText>bomb </Col>to be analyzed; <Col: StageText>stop </Col>them <Col: StageText>taking </Col>it to their <Col: StageText>drop-off</Col>. We've made others like it, so it's bound to implicate us in other crimes.";
            contentsSimple[1711] = "TaskObjectives_JG_BEL2_Dis2_Stage05_OwnerBrief=A member of the Montebank cartel was seen fleeing the crime scene. They dropped a <Col: StageText>briefcase </Col>which may contain valuable evidence. <Col: StageText>Take </Col>it to our <Col: StageText>drop-off </Col>and prevent the cartel taking it to theirs.";
            contentsSimple[1712] = "TaskObjectives_JG_BEL2_Dis2_Stage05_DispatchBrief=";
            contentsSimple[1713] = "TaskObjectives_JG_BEL2_Dis2_Stage05_Opp_OwnerBrief=";
            contentsSimple[1714] = "TaskObjectives_JG_BEL2_Dis2_Stage05_Opp_DispatchBrief=This <Col: StageText>briefcase </Col>contains information about our entire gun smuggling operation, our Yard contacts, the works. <Col: StageText>Take it </Col>to our <Col: StageText>drop-off </Col>and stop the Enforcers taking it to theirs.";
            contentsSimple[1715] = "TaskObjectives_JG_BEL2_FoE2_Stage01_OwnerBrief=A gang has been using payphones to take illegal bets; they must have discovered that we can trace their cell phones. <Col: StageText>Bug </Col>this <Col: StageText>payphone </Col>that they regularly use.";
            contentsSimple[1716] = "TaskObjectives_JG_BEL2_FoE2_Stage01_DispatchBrief=The Enforcers are onto our numbers-running sideline. I bet they'll try to bug the payphone we use for our business - not that I'm a gambler, of course! Anyway, <Col: StageText>protect </Col>this <Col: StageText>payphone</Col>.";
            contentsSimple[1717] = "TaskObjectives_JG_BEL2_FoE2_Stage02_OwnerBrief=We need more proof of illegal bookmaking activity before we can move against the gang. <Col: StageText>Bug two </Col>more <Col: StageText>payphones </Col>they regularly use.";
            contentsSimple[1718] = "TaskObjectives_JG_BEL2_FoE2_Stage02_DispatchBrief=We've switched payphones, as the 'Forcers bugged the usual one we use to take our bets. <Col: StageText>Prevent </Col>the Enforcers from <Col: StageText>planting bugs </Col>on these <Col: StageText>payphones </Col>too.";
            contentsSimple[1719] = "TaskObjectives_JG_BEL2_FoE2_Stage03_OwnerBrief=Our bugged phones just paid off! A gang member let slip the locations of their illegal betting offices. <Col: StageText>Raid </Col>these <Col: StageText>properties </Col>and take the <Col: StageText>betting slips </Col>you find to the <Col: StageText>drop-off</Col>.";
            contentsSimple[1720] = "TaskObjectives_JG_BEL2_FoE2_Stage03_DispatchBrief=The Enforcers are onto our whole bookmaking operation. <Col: StageText>Defend </Col>our <Col: StageText>offices </Col>against raids and <Col: StageText>stop </Col>any <Col: StageText>evidence </Col>from reaching the 'Forcer <Col: StageText>drop-off</Col>.";
            contentsSimple[1721] = "TaskObjectives_JG_BEL2_FoE2_Stage04_OwnerBrief=Our bugged phones just paid off! A gang member let slip the locations of their illegal betting offices. <Col: StageText>Raid </Col>these <Col: StageText>properties </Col>and take the <Col: StageText>betting slips </Col>you find to the <Col: StageText>drop-off</Col>.";
            contentsSimple[1722] = "TaskObjectives_JG_BEL2_FoE2_Stage04_DispatchBrief=The Enforcers are onto our whole bookmaking operation. <Col: StageText>Defend </Col>our <Col: StageText>offices </Col>against raids and <Col: StageText>stop </Col>any <Col: StageText>evidence </Col>from reaching the 'Forcer <Col: StageText>drop-off</Col>.";
            contentsSimple[1723] = "TaskObjectives_JG_BEL2_FoE2_Stage05_OwnerBrief=That illegal gambling info you got us shows the gang must use a central database to track all the bets. <Col: StageText>Take </Col>their <Col: StageText>laptop </Col>and then <Col: StageText>hold on to it </Col>while we download the data via 3G.";
            contentsSimple[1724] = "TaskObjectives_JG_BEL2_FoE2_Stage05_DispatchBrief=Damn. Our numbers runner was wounded and dropped his <Col: StageText>laptop</Col> - all our betting accounts are stored on there! <Col: StageText>Get it </Col>and <Col: StageText>keep it </Col>away from the Enforcers.";
            contentsSimple[1725] = "TaskObjectives_JG_BEL2_FoE3_Stage01_OwnerBrief=Identity theft is rife in this area. 'Skimming' devices are often planted on ATMs to record a citizen's card details. <Col: StageText>Examine </Col>the first <Col: StageText>ATM </Col>for such a device.";
            contentsSimple[1726] = "TaskObjectives_JG_BEL2_FoE3_Stage01_DispatchBrief=We've called in some experitise to help attach skimming devices to ATMs. It's the best way to get a lot of bank details, fast! Enforcers are <Col: StageText>searching this ATM </Col>for one of our devices. <Col: StageText>Stop them</Col>!";
            contentsSimple[1727] = "TaskObjectives_JG_BEL2_FoE3_Stage02_OwnerBrief=No luck. However, the second <Col: StageText>ATM </Col>is located here. <Col: StageText>Examine </Col>it thoroughly. You wouldn't want the Criminals tampering with *your* slot, would you?";
            contentsSimple[1728] = "TaskObjectives_JG_BEL2_FoE3_Stage02_DispatchBrief=Enforcers are getting closer to finding one of the devices we're using to steal bank account details. <Col: StageText>Stop </Col>them <Col: StageText>searching </Col>any more <Col: StageText>ATMs</Col>.";
            contentsSimple[1729] = "TaskObjectives_JG_BEL2_FoE3_Stage03_OwnerBrief=Still nothing! But I know ID theft is going on out there. I doubt poor old Mrs. Mills really spent ten grand at the Karnak Casino. There's another <Col: StageText>ATM </Col>here; <Col: StageText>check it out </Col>too.";
            contentsSimple[1730] = "TaskObjectives_JG_BEL2_FoE3_Stage03_DispatchBrief=This <Col: StageText>ATM </Col>is fitted with one of our skimming devices for stealing bank card details. <Col: StageText>Don't let the Enforcers examine it</Col>! We borrowed the device from some techie friends.";
            contentsSimple[1731] = "TaskObjectives_JG_BEL2_FoE3_Stage04_OwnerBrief=Got one! <Col: StageText>Bring </Col>the skimming <Col: StageText>device </Col>to this <Col: StageText>location </Col>for analysis. There are only a few people in San Paro who can actually make such a device.";
            contentsSimple[1732] = "TaskObjectives_JG_BEL2_FoE3_Stage04_DispatchBrief=The 'Forcers have one of our ATM skimming <Col: StageText>devices</Col>. <Col: StageText>Stop </Col>them taking it to their <Col: StageText>drop-off </Col>for analysis. We need to return it to our high-tech experts.";
            contentsSimple[1733] = "TaskObjectives_JG_BEL2_FoE3_Stage05_OwnerBrief=The device is similar to one we recovered last week. Forensics  linked it to a local gang operating out of this <Col: StageText>building</Col>. Get there and <Col: StageText>raid </Col>the place.";
            contentsSimple[1734] = "TaskObjectives_JG_BEL2_FoE3_Stage05_DispatchBrief=The Enforcers have traced the ATM skimmer back to us. <Col: StageText>Protect our offices </Col>from being raided. Don't let them recover anything that could incriminate us.";
            contentsSimple[1735] = "TaskObjectives_JG_BEL2_FoE3_Stage06_OwnerBrief=The identity thieves already booked it, but there're enough stolen credit card details here to buy the WP tower! <Col: StageText>Bring </Col>the key <Col: StageText>evidence </Col>you find <Col: StageText>here</Col>.";
            contentsSimple[1736] = "TaskObjectives_JG_BEL2_FoE3_Stage06_DispatchBrief=The Enforcers found all our stolen credit card details. They packaged it up as <Col: StageText>evidence</Col>. <Col: StageText>Don't let them </Col>get it to their <Col: StageText>drop-off</Col>.";
            contentsSimple[1737] = "TaskObjectives_JG_BEL2_FoE3_Stage07_OwnerBrief=We believe the identity thieves are trying to flee the area. <Col: StageText>Capture </Col>and then <Col: StageText>hold </Col>these <Col: StageText>locations </Col>to prevent their escape, while we move in on them.";
            contentsSimple[1738] = "TaskObjectives_JG_BEL2_FoE3_Stage07_DispatchBrief=We need to get our techie guys out of here so they can continue the ATM scam elsewhere in San Paro. <Col: StageText>Capture </Col>and then <Col: StageText>hold </Col>these <Col: StageText>locations </Col>on their escape route.";
            contentsSimple[1739] = "TaskObjectives_JG_BEL2_VPi1_Stage01_OwnerBrief=One of our key political supporters had his car stolen and wants it back. First, <Col: StageText>track </Col>the vehicle's GPS locator <Col: StageText>through </Col>this <Col: StageText>satellite</Col>.";
            contentsSimple[1740] = "TaskObjectives_JG_BEL2_VPi1_Stage01_DispatchBrief=I stole a sweeeet ride, and I want to keep it for myself. The Enforcers sent a whole team out after it - that's how cool it is. <Col: StageText>Defend this satellite </Col>dish, so they can't trace the car's GPS.";
            contentsSimple[1741] = "TaskObjectives_JG_BEL2_VPi1_Stage02_OwnerBrief=The car is being driven by a local gang leader, who's keeping it as a trophy. Lure him out by <Col: StageText>covering </Col>the gang <Col: StageText>tags </Col>on his turf. See how he likes us messing with his stuff!";
            contentsSimple[1742] = "TaskObjectives_JG_BEL2_VPi1_Stage02_DispatchBrief=<Col: StageText>Don't </Col>let the Enforcers <Col: StageText>cover our tags </Col>or the whole gang will be drawn into this. I stole that car just for me, and they'd all want a cruise in it.";
            contentsSimple[1743] = "TaskObjectives_JG_BEL2_VPi1_Stage03_OwnerBrief=We know the gang leader hates anyone touching his 'art' - had three graffiti removal teams killed nearby last month alone. Continue to <Col: StageText>covering </Col>the <Col: StageText>tags </Col>in this area until he's lured out of hiding.";
            contentsSimple[1744] = "TaskObjectives_JG_BEL2_VPi1_Stage03_DispatchBrief=<Col: StageText>Don't </Col>let the Enforcers <Col: StageText>cover </Col>up more of our <Col: StageText>tags</Col>. They just want to draw us out into the open. Jeez, you steal one car and suddenly everyone is up in your grill.";
            contentsSimple[1745] = "TaskObjectives_JG_BEL2_VPi1_Stage04_OwnerBrief=There - the vehicle has been moved and he's meeting with his lieutenants. <Col: StageText>Recover </Col>the <Col: StageText>vehicle </Col>while it's unattended.";
            contentsSimple[1746] = "TaskObjectives_JG_BEL2_VPi1_Stage04_DispatchBrief=Damn, they found my sweet ride. <Col: StageText>Protect </Col>my <Col: StageText>car</Col>! I just fitted new speakers. They were so solid I had to up the suspension. That's how heavy my sound is, baby!";
            contentsSimple[1747] = "TaskObjectives_JG_BEL2_VPi1_Stage05_OwnerBrief=<Col: StageText>Bring the vehicle </Col>to this <Col: StageText>location</Col>. We'll arrange to return it to its original owner. He may want to de-pimp his ride though.";
            contentsSimple[1748] = "TaskObjectives_JG_BEL2_VPi1_Stage05_DispatchBrief=The Enforcers are moving my <Col: StageText>ride </Col>to a safe <Col: StageText>location</Col>. <Col: StageText>Don't let them </Col>get it there. Those dudes won't appreciate the solid marble dashboard I had installed.";
            contentsSimple[1749] = "TaskObjectives_JG_BEL2_VPi1_Stage06_OwnerBrief=Our benefactor has provided us with some additional funding, thanks to your good work. It's in this case. <Col: StageText>Take </Col>the <Col: StageText>case </Col>and <Col: StageText>keep it </Col>away from any gang members - who have to walk home now! Ha ha.";
            contentsSimple[1750] = "TaskObjectives_JG_BEL2_VPi1_Stage06_DispatchBrief=The Enforcers took my ride - and I know why. There was a hefty reward for it's return to the original owner. <Col: StageText>Get to </Col>the <Col: StageText>case </Col>full of reward money first, and <Col: StageText>keep it </Col>away from them.";
            contentsSimple[1751] = "TaskObjectives_JG_BEL3_Dis1_Stage01_OwnerBrief=We picked up Azrael bragging about a bomb he'd planted, but the prick won't tell us where it is. <Col: StageText>Raid </Col>his <Col: StageText>base </Col>of operations.";
            contentsSimple[1752] = "TaskObjectives_JG_BEL3_Dis1_Stage01_DispatchBrief=Itchy - one of our bombing crew - was careless and got picked up. Now he's squealing like a bitch to any Enforcer who'll listen. <Col: StageText>Defend </Col>our old <Col: StageText>hideout </Col>from the Enforcer bomb squad.";
            contentsSimple[1753] = "TaskObjectives_JG_BEL3_Dis1_Stage02_OwnerBrief=Nothing solid on location, but it looks like Azrael's using a cellphone trigger for his device. <Col: StageText>Hack </Col>this telecom satellite <Col: StageText>uplink </Col>- we're going to track the phone signal from the bomb.";
            contentsSimple[1754] = "TaskObjectives_JG_BEL3_Dis1_Stage02_DispatchBrief=Itchy blabbed about the bombs we planted, but didn't know where the last one was. <Col: StageText>Protect </Col>this telecom satellite <Col: StageText>uplink </Col>- the Enforcers may use it to locate the bomb's cellphone detonator.";
            contentsSimple[1755] = "TaskObjectives_JG_BEL3_Dis1_Stage03_OwnerBrief=There's another telecom satellite <Col: StageText>uplink </Col>in this area. <Col: StageText>Hack </Col>it quickly, and we'll be one step closer to narrowing down the location of Azrael's bomb.";
            contentsSimple[1756] = "TaskObjectives_JG_BEL3_Dis1_Stage03_DispatchBrief=Now the Enforcers know about our bomb, they're trying to trace its cellphone detonator. <Col: StageText>Protect </Col>this telecom satellite <Col: StageText>uplink </Col>until the bomb explodes.";
            contentsSimple[1757] = "TaskObjectives_JG_BEL3_Dis1_Stage04_OwnerBrief=All right, this last satellite <Col: StageText>uplink </Col>will let us triangulate the position of the bomb's cell-phone detonator. <Col: StageText>Hack </Col>it - and hope we can get to the bomb in time!";
            contentsSimple[1758] = "TaskObjectives_JG_BEL3_Dis1_Stage04_DispatchBrief=If the Enforcers complete their hack into this last telecom satellite <Col: StageText>uplink </Col>they'll be able to track the detonator on our bomb. <Col: StageText>Protect </Col>it until the bomb explodes.";
            contentsSimple[1759] = "TaskObjectives_JG_BEL3_Dis1_Stage05_OwnerBrief=We've found the <Col: StageText>bomb</Col>. Get to it quickly, and <Col: StageText>disarm </Col>it before it blows!";
            contentsSimple[1760] = "TaskObjectives_JG_BEL3_Dis1_Stage05_DispatchBrief=It looks like the Enforcers have found our bomb and are gearing up to defuse it. <Col: StageText>Protect </Col>the <Col: StageText>bomb </Col>until it explodes.";
            contentsSimple[1761] = "TaskObjectives_JG_BEL3_Dis1_Stage06_OwnerBrief=Shit, that was a chemical bomb. Real nasty. Azrael's going down for this! Recover the <Col: StageText>chemicals </Col>for analysis. <Col: StageText>Deliver </Col>them to our <Col: StageText>drop-off </Col>before the Criminals can take them to theirs.";
            contentsSimple[1762] = "TaskObjectives_JG_BEL3_Dis1_Stage06_DispatchBrief=";
            contentsSimple[1763] = "TaskObjectives_JG_BEL3_Dis1_Stage06_Opp_OwnerBrief=";
            contentsSimple[1764] = "TaskObjectives_JG_BEL3_Dis1_Stage06_Opp_DispatchBrief=The Enforcers have defused our bomb and are taking components as evidence. <Col: StageText>Deliver </Col>those volatile <Col: StageText>chemicals </Col>to our <Col: StageText>drop-off </Col>before the Enforcers get them to theirs.";
            contentsSimple[1765] = "TaskObjectives_JG_BEL4_FoE1_Stage01_OwnerBrief=Things have been quiet in this area - maybe too quiet. Head out into the neighborhood, <Col: StageText>cover-up </Col>any <Col: StageText>graffiti </Col>you find, and keep an eye open for anything suspicious.";
            contentsSimple[1766] = "TaskObjectives_JG_BEL4_FoE1_Stage01_DispatchBrief=A few Enforcers are hanging around, pretending to care about graffiti - but they're probably staking us out in case we get a weapons delivery. <Col: StageText>Defend </Col>our <Col: StageText>graffiti </Col>and keep the Enfornicators busy.";
            contentsSimple[1767] = "TaskObjectives_JG_BEL4_FoE1_Stage02_OwnerBrief=One of the locals saw a Criminal stashing something in this <Col: StageText>electrical box </Col>as you approached. Your patrol must have spooked him. <Col: StageText>Smash open </Col>that Criminal's stash and take anything you find.";
            contentsSimple[1768] = "TaskObjectives_JG_BEL4_FoE1_Stage02_DispatchBrief=We've been sneaking weapons into the city under the Enforcers' noses, but now they're onto us. <Col: StageText>Protect </Col>this <Col: StageText>electrical box </Col>that we use as a disguised cache.";
            contentsSimple[1769] = "TaskObjectives_JG_BEL4_FoE1_Stage03_OwnerBrief=A weapons cache? Things just got a little more interesting. <Col: StageText>Deliver </Col>those <Col: StageText>weapons </Col>back to our <Col: StageText>lock-up </Col>and we'll examine them.";
            contentsSimple[1770] = "TaskObjectives_JG_BEL4_FoE1_Stage03_DispatchBrief=The Enforcers raided our weapons cache and took a couple of our guns. <Col: StageText>Stop </Col>those <Col: StageText>guns </Col>from <Col: StageText>reaching </Col>the Enforcer's <Col: StageText>lock-up</Col>.";
            contentsSimple[1771] = "TaskObjectives_JG_BEL4_FoE1_Stage04_OwnerBrief=Those guns are military-grade, a big cut above the usual weapons we find. We need to locate the source. <Col: StageText>Plant bugs </Col>on <Col: StageText>targets </Col>throughout the block and we'll listen for any useful chatter.";
            contentsSimple[1772] = "TaskObjectives_JG_BEL4_FoE1_Stage04_DispatchBrief=The guns that the Enforcers found were only part of our supply. Now they're looking for evidence to lead them to the main stash. <Col: StageText>Defend </Col>these <Col: StageText>targets </Col>and stop the Enforcers investigating.";
            contentsSimple[1773] = "TaskObjectives_JG_BEL4_FoE1_Stage05_OwnerBrief=Those guns are military-grade, a big cut above the usual weapons we find. We need to locate the source. <Col: StageText>Plant bugs </Col>on <Col: StageText>targets </Col>throughout the block and we'll listen for any useful chatter.";
            contentsSimple[1774] = "TaskObjectives_JG_BEL4_FoE1_Stage05_DispatchBrief=The guns that the Enforcers found were only part of our supply. Now they're looking for evidence to lead them to the main stash. <Col: StageText>Defend </Col>these <Col: StageText>targets </Col>and stop the Enforcers investigating.";
            contentsSimple[1775] = "TaskObjectives_JG_BEL4_FoE1_Stage06_OwnerBrief=Those Criminals love to talk! We already have information suggesting that guns are smuggled in via unmarked crates. <Col: StageText>Break into </Col>the first <Col: StageText>crate </Col>and confiscate the weapons.";
            contentsSimple[1776] = "TaskObjectives_JG_BEL4_FoE1_Stage06_DispatchBrief=<Col: StageText>Protect </Col>this <Col: StageText>crate </Col>and don't let the Enforcers get hold of it and see what's inside. Hint: it isn't cat food. That's our main weapons shipment for the month.";
            contentsSimple[1777] = "TaskObjectives_JG_BEL4_FoE1_Stage07_OwnerBrief=Whoa; that's a LOT of guns. <Col: StageText>Break into </Col>the second <Col: StageText>crate </Col>and <Col: StageText>deliver </Col>all of those guns back to the <Col: StageText>drop-off</Col>. We need to get those guns out of circulation ASAP.";
            contentsSimple[1778] = "TaskObjectives_JG_BEL4_FoE1_Stage07_DispatchBrief=Shit, the Enforcers have half of our weapons shipment. You need to <Col: StageText>protect </Col>our second <Col: StageText>crate </Col>and <Col: StageText>stop </Col>our <Col: StageText>guns </Col>from <Col: StageText>reaching </Col>the Enforcer's <Col: StageText>drop-off</Col>.";
            contentsSimple[1779] = "TaskObjectives_JG_BEL4_FoE1_Stage08_OwnerBrief=Whoa; that's a LOT of guns. <Col: StageText>Break into </Col>the second <Col: StageText>crate </Col>and <Col: StageText>deliver </Col>all of those guns back to the <Col: StageText>drop-off</Col>. We need to get those guns out of circulation ASAP.";
            contentsSimple[1780] = "TaskObjectives_JG_BEL4_FoE1_Stage08_DispatchBrief=Shit, the Enforcers have half of our weapons shipment. You need to <Col: StageText>protect </Col>our second <Col: StageText>crate </Col>and <Col: StageText>stop </Col>our <Col: StageText>guns </Col>from <Col: StageText>reaching </Col>the Enforcer's <Col: StageText>drop-off</Col>.";
            contentsSimple[1781] = "TaskObjectives_JG_BEL4_FoE1_Stage09_OwnerBrief=With the weapons gone, we have a great opportunity to try and push the Criminals out of this neighborhood. <Col: StageText>Capture </Col>and <Col: StageText>hold </Col>this <Col: StageText>location </Col>.";
            contentsSimple[1782] = "TaskObjectives_JG_BEL4_FoE1_Stage09_DispatchBrief=With our weapons gone, we're vulnerable and the Enforcers can move into our turf. <Col: StageText>Capture </Col>and <Col: StageText>hold </Col>this <Col: StageText>location</Col>.";
            contentsSimple[1783] = "TaskObjectives_JG_BEL4_Inv1_Stage01_OwnerBrief=We got a call from Paloma Rosario, a CUP city councilor, saying that her baby was kidnapped. <Col: StageText>Pick up </Col>the <Col: StageText>ransom</Col>, while we work on tracking down the fucker who did this.";
            contentsSimple[1784] = "TaskObjectives_JG_BEL4_Inv1_Stage01_DispatchBrief=We've got a lucrative sideline going on: smuggling exotic pets for the San Paro glitterati. But the Enforcers are on to us - <Col: StageText>prevent </Col>the Enforcers <Col: StageText>picking up </Col>our 'pet passport' <Col: StageText>documentation</Col>.";
            contentsSimple[1785] = "TaskObjectives_JG_BEL4_Inv1_Stage02_OwnerBrief=All right, the sick bastard who kidnapped that kid is insisting that we <Col: StageText>deliver </Col>the <Col: StageText>briefcase </Col>to this <Col: StageText>location</Col>. Get there fast before this gets out of hand.";
            contentsSimple[1786] = "TaskObjectives_JG_BEL4_Inv1_Stage02_DispatchBrief=The 'Forcers got hold of our 'pet passports' - which is gonna make smuggling those twenty-foot albino gators out of Silverfield tricky. <Col: StageText>Stop </Col>the <Col: StageText>Enforcers collecting </Col>our <Col: StageText>cargo invoices</Col>.";
            contentsSimple[1787] = "TaskObjectives_JG_BEL4_Inv1_Stage03_OwnerBrief=Shit, it looks like the kidnapper got spooked and ran off. Luckily, he dropped his cell phone. <Col: StageText>Pick up </Col>his <Col: StageText>phone </Col>and <Col: StageText>deliver everything </Col>to our <Col: StageText>lockup </Col>- I think we might have a lead.";
            contentsSimple[1788] = "TaskObjectives_JG_BEL4_Inv1_Stage03_DispatchBrief=<Col: StageText>Stop </Col>the Enforcers <Col: StageText>delivering </Col>those <Col: StageText>pet passports</Col>. We make too much cash smuggling pets into San Paro to stop now. You know, tiger sharks have a really sweet disposition, once you get to know them.";
            contentsSimple[1789] = "TaskObjectives_JG_BEL4_Inv1_Stage04_OwnerBrief=We got prints from the kidnapper's phone - seems they belong to an ex-con, Vernon Kaig, now living in the area. <Col: StageText>Raid </Col>this <Col: StageText>property</Col>; he's been known to hang out there.";
            contentsSimple[1790] = "TaskObjectives_JG_BEL4_Inv1_Stage04_DispatchBrief=Shit, the Enforcers are going to raid a couple of our buildings. <Col: StageText>Protect </Col>the first <Col: StageText>building </Col>and stop the investigation. We have fifty Madagascan fruit bats in there, all wearing diamond collars...";
            contentsSimple[1791] = "TaskObjectives_JG_BEL4_Inv1_Stage05_OwnerBrief=Nothing. Shit, we need to find this kid soon; the first 48 hours are the most important. <Col: StageText>Raid </Col>this <Col: StageText>building </Col>where a couple of Kaig's known associates are based.";
            contentsSimple[1792] = "TaskObjectives_JG_BEL4_Inv1_Stage05_DispatchBrief=The Enforcers raided our first property, and now they're heading towards the second. <Col: StageText>Protect </Col>the <Col: StageText>building </Col>- don't let them impound our consignment of snow leopard cubs.";
            contentsSimple[1793] = "TaskObjectives_JG_BEL4_Inv1_Stage06_OwnerBrief=That sick fuck! Turns out the kid's been stashed in the kidnapper's vehicle. <Col: StageText>Rescue </Col>the <Col: StageText>child from </Col>the back of this <Col: StageText>van</Col>.";
            contentsSimple[1794] = "TaskObjectives_JG_BEL4_Inv1_Stage06_DispatchBrief=We've been using this <Col: StageText>van </Col>to smuggle in these exotic pets for our buyers. <Col: StageText>Protect </Col>the van from the Enforcers - we've got a blue-throated Gila in there.";
            contentsSimple[1795] = "TaskObjectives_JG_BEL4_Inv1_Stage07_OwnerBrief=Oh, for fuck's sake. A dog? Her baby is a dog? San Paro is swarming with crime and Rosario's wasting our time with this? <Col: StageText>Deliver </Col>the <Col: StageText>mutt </Col>to our <Col: StageText>drop-off</Col>. The reward better be worth this bullshit.";
            contentsSimple[1796] = "TaskObjectives_JG_BEL4_Inv1_Stage07_DispatchBrief=Damn, that fucking lizard is worth a lot more than you'd think. <Col: StageText>Stop </Col>the Enforcers before they <Col: StageText>deliver </Col>the box to their <Col: StageText>drop-off</Col>.";
            contentsSimple[1797] = "TaskObjectives_JG_BEL4_Inv1_Stage08_OwnerBrief=Maybe I can forgive the mis-use of our time after all: that crazy councilor paid us well enough. <Col: StageText>Take </Col>the <Col: StageText>cash </Col>to our <Col: StageText>drop-off </Col>before it gets stolen.";
            contentsSimple[1798] = "TaskObjectives_JG_BEL4_Inv1_Stage08_DispatchBrief=";
            contentsSimple[1799] = "TaskObjectives_JG_BEL4_Inv1_Stage08_Opp_OwnerBrief=";
            contentsSimple[1800] = "TaskObjectives_JG_BEL4_Inv1_Stage08_Opp_DispatchBrief=Meh. That damn reptile was shitting all over my ride anyway. Enforcers found our sale invoices, though. Either <Col: StageText>deliver </Col>the <Col: StageText>briefcase </Col>to our <Col: StageText>drop-off </Col>before the <Col: StageText>Enforcers</Col>.";
            contentsSimple[1801] = "TaskObjectives_JG_BEL4_Inv2_Stage01_OwnerBrief=A website offering Filipino brides has been traced to San Paro, but we're worried it's a cover for something even seedier. <Col: StageText>Raid </Col>this <Col: StageText>building </Col>and shut down the website.";
            contentsSimple[1802] = "TaskObjectives_JG_BEL4_Inv2_Stage01_DispatchBrief=We've been running a scam website - San Paro Poker - from a building in the area, but Enforcers are looking for our machines. <Col: StageText>Protect </Col>the <Col: StageText>building </Col>housing our dummy networking system.";
            contentsSimple[1803] = "TaskObjectives_JG_BEL4_Inv2_Stage02_OwnerBrief=Damn, no sign of that Filipino brides website. They must be re-routing through a local network. <Col: StageText>Place a packet sniffer on </Col>this <Col: StageText>satellite dish </Col>and we'll be able to trace the wireless signal.";
            contentsSimple[1804] = "TaskObjectives_JG_BEL4_Inv2_Stage02_DispatchBrief=We've been re-routing our system wirelessly, but Enforcers are trying to hack our <Col: StageText>telecom dish</Col>. <Col: StageText>Protect </Col>it or they'll trace the actual network that runs our scam poker website.";
            contentsSimple[1805] = "TaskObjectives_JG_BEL4_Inv2_Stage03_OwnerBrief=All right - we're close. <Col: StageText>Investigate </Col>the next <Col: StageText>satellite dish </Col>and we should be able to pinpoint the location of that Filipino brides site and shut it down for good.";
            contentsSimple[1806] = "TaskObjectives_JG_BEL4_Inv2_Stage03_DispatchBrief=Damn, the Enforcers are getting close. <Col: StageText>Protect </Col>this <Col: StageText>telecom dish </Col>or they'll find our computers and shut down our poker website for good.";
            contentsSimple[1807] = "TaskObjectives_JG_BEL4_Inv2_Stage04_OwnerBrief=Good job. It's time to <Col: StageText>raid </Col>this <Col: StageText>building </Col>belonging to SP Web International. Shut down the website and recover any hardware that you can. Hopefully this will help protect those girls.";
            contentsSimple[1808] = "TaskObjectives_JG_BEL4_Inv2_Stage04_DispatchBrief=The Enforcers are on their way to our server farm <Col: StageText>building </Col>- <Col: StageText>protect </Col>it from the Enforcer raids or they'll take all of our expensive hardware. If that happens, San Paro Poker is gonna fold.";
            contentsSimple[1809] = "TaskObjectives_JG_BEL4_Inv2_Stage05_OwnerBrief=Whoa. Jackpot. You definitely wouldn't want your mom seeing the pics in there. All right, we need to get those <Col: StageText>computers </Col>back to our <Col: StageText>drop-off</Col>. <Col: StageText>Deliver </Col>all of them as quickly as you can.";
            contentsSimple[1810] = "TaskObjectives_JG_BEL4_Inv2_Stage05_DispatchBrief=Those <Col: StageText>computers </Col>contain a lot of incriminating data - on San Paro Poker, see, the dealer wins 75% of the time. <Col: StageText>Intercept </Col>the computers before the Enforcers get them to their <Col: StageText>lock-up</Col>.";
            contentsSimple[1811] = "TaskObjectives_JG_BEL4_Inv2_Stage06_OwnerBrief=There's a <Col: StageText>laptop </Col>nearby that contains a lot of the transaction data from that Filipino brides website. <Col: StageText>Recover </Col>it and <Col: StageText>hold </Col>it away from any opposition.";
            contentsSimple[1812] = "TaskObjectives_JG_BEL4_Inv2_Stage06_DispatchBrief=Last chance - we've got wireless incendiaries rigged up inside those machine cases. <Col: StageText>Recover </Col>the <Col: StageText>laptop </Col>used to trigger the devices and <Col: StageText>hold </Col>it away from any opposition.";
            contentsSimple[1813] = "TaskObjectives_JG_BEM0_FoE1_P_Stage01_OwnerBrief=We've discovered that conflict diamonds are being transported through the docks and then into the rest of the city. <Col: StageText>Search </Col>these <Col: StageText>crates </Col>and confiscate any diamonds that you can find.";
            contentsSimple[1814] = "TaskObjectives_JG_BEM0_FoE1_P_Stage01_DispatchBrief=We make good money sourcing cheap diamonds for the jewelers of San Paro. <Col: StageText>Protect </Col>these shipping <Col: StageText>crates</Col>. The Praetorians will be looking to raid them and take our merchandise.";
            contentsSimple[1815] = "TaskObjectives_JG_BEM0_FoE1_P_Stage02_OwnerBrief=We've discovered that conflict diamonds are being transported through the docks and then into the rest of the city. <Col: StageText>Search </Col>these <Col: StageText>crates </Col>and confiscate any diamonds that you can find.";
            contentsSimple[1816] = "TaskObjectives_JG_BEM0_FoE1_P_Stage02_DispatchBrief=We make good money sourcing cheap diamonds for the jewelers of San Paro. <Col: StageText>Protect </Col>these shipping <Col: StageText>crates</Col>. The Praetorians will be looking to raid them and take our merchandise.";
            contentsSimple[1817] = "TaskObjectives_JG_BEM0_FoE1_P_Stage03_OwnerBrief=The trade in these conflict diamonds funds war in places that make San Paro look like Candyland. <Col: StageText>Deliver </Col>those <Col: StageText>diamonds </Col>to the Praetorian <Col: StageText>drop-off </Col>and we'll shut down these traders for good.";
            contentsSimple[1818] = "TaskObjectives_JG_BEM0_FoE1_P_Stage03_DispatchBrief=The Praetorians have taken some of our diamonds - and we'd already arranged a buyer. If you <Col: StageText>intercept </Col>the <Col: StageText>diamonds </Col>before the 'Forcers reach their <Col: StageText>drop-off </Col>then we can recover this situation.";
            contentsSimple[1819] = "TaskObjectives_JG_BEM0_FoE1_P_Stage04_OwnerBrief=Those diamonds were on their way to a jeweler in the city, one who doesn't ask questions. <Col: StageText>Raid </Col>the jeweler's <Col: StageText>storeroom </Col>and take anything without the proper import paperwork.";
            contentsSimple[1820] = "TaskObjectives_JG_BEM0_FoE1_P_Stage04_DispatchBrief=Our main jewelry client fears that the Praetorians will be raiding his property next. <Col: StageText>Defend </Col>the jeweler's <Col: StageText>storeroom </Col>from any raids.";
            contentsSimple[1821] = "TaskObjectives_JG_BEM0_FoE1_P_Stage05_OwnerBrief=Other jewelers will think twice before buying these conflict diamonds. They don't seem so cheap once you count the cost of us kicking your door in. <Col: StageText>Deliver </Col>that <Col: StageText>necklace </Col>to our <Col: StageText>evidence store</Col>.";
            contentsSimple[1822] = "TaskObjectives_JG_BEM0_FoE1_P_Stage05_DispatchBrief=Damn, that jeweler is pissed at us. The Praetorians raided his building and took the jewelry we sold him. <Col: StageText>Intercept </Col>the <Col: StageText>necklace </Col>before it reaches the Enforcer's <Col: StageText>drop-off</Col>.";
            contentsSimple[1823] = "TaskObjectives_JG_BEM0_FoE1_P_Stage06_OwnerBrief=We found another crate from the same original shipment. The Criminals are after it already. <Col: StageText>Protect </Col>the <Col: StageText>crate </Col>and its contents.";
            contentsSimple[1824] = "TaskObjectives_JG_BEM0_FoE1_P_Stage06_DispatchBrief=";
            contentsSimple[1825] = "TaskObjectives_JG_BEM0_FoE1_P_Stage06_Opp_OwnerBrief=";
            contentsSimple[1826] = "TaskObjectives_JG_BEM0_FoE1_P_Stage06_Opp_DispatchBrief=Our reputation is at stake now. You need to break open this <Col: StageText>crate </Col>and <Col: StageText>take the diamonds</Col>. We can use them to replace the goods our client lost.";
            contentsSimple[1827] = "TaskObjectives_JG_BEM0_FoE1_P_Stage07_OwnerBrief=You have to <Col: StageText>retrieve </Col>those <Col: StageText>diamonds </Col>before they reach the Criminal <Col: StageText>drop-off</Col>. If they get away with this, more people will needlessly die digging those rocks out of the ground.";
            contentsSimple[1828] = "TaskObjectives_JG_BEM0_FoE1_P_Stage07_DispatchBrief=";
            contentsSimple[1829] = "TaskObjectives_JG_BEM0_FoE1_P_Stage07_Opp_OwnerBrief=";
            contentsSimple[1830] = "TaskObjectives_JG_BEM0_FoE1_P_Stage07_Opp_DispatchBrief=All right, now you need to <Col: StageText>deliver </Col>the <Col: StageText>diamonds </Col>to our <Col: StageText>drop-off</Col>. The Praetorians are gonna be all over you, so try to stay alive.";
            contentsSimple[1831] = "TaskObjectives_JG_BEM0_FoE1_T_Stage01_OwnerBrief=Judge Mya Singh has hired the Tigers as private security for her daughter's wedding. We can't go empty-handed. <Col: StageText>Search </Col>these <Col: StageText>crates </Col>and take some champagne. It's on its way to the Blood Roses.";
            contentsSimple[1832] = "TaskObjectives_JG_BEM0_FoE1_T_Stage01_DispatchBrief=Our luxury goods import business is going great. Now the Petty Tigers want to steal our latest shipment, probably for themselves. <Col: StageText>Protect </Col>our shipping <Col: StageText>crates</Col>.";
            contentsSimple[1833] = "TaskObjectives_JG_BEM0_FoE1_T_Stage02_OwnerBrief=Judge Mya Singh has hired the Tigers as private security for her daughter's wedding. We can't go empty-handed. <Col: StageText>Search </Col>these <Col: StageText>crates </Col>and take some champagne. It's on its way to the Blood Roses.";
            contentsSimple[1834] = "TaskObjectives_JG_BEM0_FoE1_T_Stage02_DispatchBrief=Our luxury goods import business is going great. Now the Petty Tigers want to steal our latest shipment, probably for themselves. <Col: StageText>Protect </Col>our shipping <Col: StageText>crates</Col>.";
            contentsSimple[1835] = "TaskObjectives_JG_BEM0_FoE1_T_Stage03_OwnerBrief=<Col: StageText>Deliver </Col>that <Col: StageText>champagne </Col>back to our <Col: StageText>drop-off </Col>and I'll wrap it up. Seems like the Roses are specializing in importing stolen luxury goods these days.";
            contentsSimple[1836] = "TaskObjectives_JG_BEM0_FoE1_T_Stage03_DispatchBrief=The Tigers are making off with some of our top quality imported wines. There's a long line of buyers for 'em. <Col: StageText>Intercept </Col>the Tigers and get our <Col: StageText>merchandise </Col>back before they reach their <Col: StageText>drop-off</Col>.";
            contentsSimple[1837] = "TaskObjectives_JG_BEM0_FoE1_T_Stage04_OwnerBrief=The Roses are importing stolen luxury goods for sure. They run it through a legitimate-looking business. <Col: StageText>Raid </Col>their <Col: StageText>storeroom </Col>and take a high-value item as evidence.";
            contentsSimple[1838] = "TaskObjectives_JG_BEM0_FoE1_T_Stage04_DispatchBrief=The Tigers are following the paperwork of our import business and it leads to our storeroom. <Col: StageText>Defend </Col>our <Col: StageText>building </Col>from any Prentiss Tiger raids.";
            contentsSimple[1839] = "TaskObjectives_JG_BEM0_FoE1_T_Stage05_OwnerBrief=This is great. We can take this to the Judge. That will be the best wedding present of all - definitive proof against the Roses. <Col: StageText>Deliver </Col>that <Col: StageText>stolen necklace </Col>to the nearest Tiger <Col: StageText>drop-off</Col>.";
            contentsSimple[1840] = "TaskObjectives_JG_BEM0_FoE1_T_Stage05_DispatchBrief=The Tigers already took our imported fine wines, and now some jewelry. <Col: StageText>Intercept </Col>them and get the <Col: StageText>jewelry </Col>back before it reaches the <Col: StageText>drop-off</Col>.";
            contentsSimple[1841] = "TaskObjectives_JG_BEM0_FoE1_T_Stage06_OwnerBrief=Shit, Judge Singh got it all wrong. She gave the necklace to her daughter and sent the champagne off to be tested. <Col: StageText>Protect </Col>this <Col: StageText>crate </Col>from being taken. It's the only piece of potential evidence left.";
            contentsSimple[1842] = "TaskObjectives_JG_BEM0_FoE1_T_Stage06_DispatchBrief=";
            contentsSimple[1843] = "TaskObjectives_JG_BEM0_FoE1_T_Stage06_Opp_OwnerBrief=";
            contentsSimple[1844] = "TaskObjectives_JG_BEM0_FoE1_T_Stage06_Opp_DispatchBrief=If the Tigers can find one more illegally imported item then a judge will grant search warrants against us. Get to this <Col: StageText>crate </Col>and <Col: StageText>break into</Col> it. We can't be caught with those diamonds.↵";
            contentsSimple[1845] = "TaskObjectives_JG_BEM0_FoE1_T_Stage07_OwnerBrief=We have to recover that <Col: StageText>evidence</Col>! <Col: StageText>Intercept </Col>it before the Criminals reach their <Col: StageText>drop-off</Col>. Then take it to Judge Singh, provided she doesn't give it away again!";
            contentsSimple[1846] = "TaskObjectives_JG_BEM0_FoE1_T_Stage07_DispatchBrief=";
            contentsSimple[1847] = "TaskObjectives_JG_BEM0_FoE1_T_Stage07_Opp_OwnerBrief=";
            contentsSimple[1848] = "TaskObjectives_JG_BEM0_FoE1_T_Stage07_Opp_DispatchBrief=Great, now <Col: StageText>deliver </Col>the <Col: StageText>bag </Col>to our <Col: StageText>drop-off</Col>. The Tigers will be on your tail - they'd need to catch you in possession of that bag to get their search warrant.";
            contentsSimple[1849] = "TaskObjectives_JG_BEM1_AGr1_P_Stage01_OwnerBrief=G-King terror attacks have lowered our recruitment rates, and now Teng wants us to win back the hearts and minds of San Paro. <Col: StageText>Paint over graffiti </Col>to help kick-start our publicity drive.";
            contentsSimple[1850] = "TaskObjectives_JG_BEM1_AGr1_P_Stage01_DispatchBrief=Justin Teng's got his Praetorians on an anti-graffiti drive after someone put up a piece advertizing his phone number as a sex hotline. <Col: StageText>Stop the Praetorians </Col>from covering our graffiti.";
            contentsSimple[1851] = "TaskObjectives_JG_BEM1_AGr1_P_Stage02_OwnerBrief=There's more <Col: StageText>graffiti </Col>nearby - keep the <Col: StageText>cover-up </Col>operation going in this neighborhood; we need to show the people we're still alive and kicking. It's all good PR at the end of the day.";
            contentsSimple[1852] = "TaskObjectives_JG_BEM1_AGr1_P_Stage02_DispatchBrief=We've got <Col: StageText>graffiti </Col>nearby - <Col: StageText>stop </Col>the Praetorians <Col: StageText>covering it up</Col>. I guess Teng's truly pissed about that graffiti with his phone number on it. Wonder if he got any calls askin' for hot lovin'?";
            contentsSimple[1853] = "TaskObjectives_JG_BEM1_AGr1_P_Stage03_OwnerBrief=We've got <Col: StageText>graffiti </Col>left over in this sector. <Col: StageText>Conceal </Col>this last tag and you're good for now. Hell, you did such a good job I might even recommend you for our next promotional video.";
            contentsSimple[1854] = "TaskObjectives_JG_BEM1_AGr1_P_Stage03_DispatchBrief=Our last <Col: StageText>tag </Col>is nearby. <Col: StageText>Protect </Col>the tag from Praetorian clean-up squads. Heh, I've done the old 'for a good time' phone number prank before, but the one that riled Teng was three stories tall!";
            contentsSimple[1855] = "TaskObjectives_JG_BEM1_AGr1_P_Stage04_OwnerBrief=We've got hostiles! Taggers are in the area to reclaim their lost work - <Col: StageText>capture </Col>and <Col: StageText>hold </Col>these locations to keep them out. Show them the Praetorians haven't gone soft.";
            contentsSimple[1856] = "TaskObjectives_JG_BEM1_AGr1_P_Stage04_DispatchBrief=Sucks that the 'Torians managed to cover all our tags; I'll get some taggers to put our colors back up. <Col: StageText>Capture </Col>and <Col: StageText>hold </Col>these locations so our taggers can spray up replacement 'fiti safely.";
            contentsSimple[1857] = "TaskObjectives_JG_BEM1_AGr1_T_Stage01_OwnerBrief=2iP are doing a piece on the Tigers, but it'll look bad if they come round and find our chapter houses are all graffed up. Do us a favor and <Col: StageText>cover </Col>the crappy <Col: StageText>graffiti </Col>up.";
            contentsSimple[1858] = "TaskObjectives_JG_BEM1_AGr1_T_Stage01_DispatchBrief=Heh, the Tigers don't appreciate our tags. Who'd have thought? They've sent some lackeys to clean them up. <Col: StageText>Stop the Tigers </Col>from covering our graffiti.";
            contentsSimple[1859] = "TaskObjectives_JG_BEM1_AGr1_T_Stage02_OwnerBrief=Awesome. There's <Col: StageText>graffiti </Col>nearby - keep up the good work and <Col: StageText>cover over </Col>that one too.";
            contentsSimple[1860] = "TaskObjectives_JG_BEM1_AGr1_T_Stage02_DispatchBrief=We've got <Col: StageText>graffiti </Col>nearby - <Col: StageText>stop </Col>the Tigers from <Col: StageText>covering up </Col>our work. Graffiti is art: letting them spray over it just ain't right.";
            contentsSimple[1861] = "TaskObjectives_JG_BEM1_AGr1_T_Stage03_OwnerBrief=One last piece of <Col: StageText>graffiti </Col>we missed over here. <Col: StageText>Spray over </Col>the tag and we're good to go. You fuckin' rock; I owe you a beer for this one.";
            contentsSimple[1862] = "TaskObjectives_JG_BEM1_AGr1_T_Stage03_DispatchBrief=Our last <Col: StageText>tag </Col>claiming the area is close by. <Col: StageText>Protect </Col>the tag from those Prentiss freaks.";
            contentsSimple[1863] = "TaskObjectives_JG_BEM1_AGr1_T_Stage04_OwnerBrief=Looks like those tags were new: the dickwads who put them up are back to try and refresh them. <Col: StageText>Capture </Col>and <Col: StageText>hold </Col>these locations to keep the taggers out.";
            contentsSimple[1864] = "TaskObjectives_JG_BEM1_AGr1_T_Stage04_DispatchBrief=They got all our tags? Well fuck them then! <Col: StageText>capture </Col>and <Col: StageText>hold </Col>these locations so we can put our graffiti back up.";
            contentsSimple[1865] = "TaskObjectives_JG_BEM1_FoE1_Stage01_OwnerBrief=The SPPD are worried that one of their number is on a Criminal payroll. Their internal affairs department asked us to look into it. <Col: StageText>Bug </Col>this <Col: StageText>building </Col>where the cop hangs out.";
            contentsSimple[1866] = "TaskObjectives_JG_BEM1_FoE1_Stage01_DispatchBrief=SPPD Internal Affairs is searching for one of our our mole by bugging places where we meet<Col:StageText>Stop them</Col> from planting the bugs.";
            contentsSimple[1867] = "TaskObjectives_JG_BEM1_FoE1_Stage02_OwnerBrief=The cop's working with car thieves - so you're going to set him up. <Col: StageText>Pick up </Col>this <Col: StageText>vehicle </Col>and then <Col: StageText>leave it at </Col>this <Col: StageText>location</Col>. An expensive car like that will be an irresistible target.";
            contentsSimple[1868] = "TaskObjectives_JG_BEM1_FoE1_Stage02_DispatchBrief=That bug has revealed the location of a cut and shut that's passed through our Chop Shop. <Col:Stagetext>Protect the vehicle</Col> and make sure they don't get it to their labs.";
            contentsSimple[1869] = "TaskObjectives_JG_BEM1_FoE1_Stage03_OwnerBrief=The cop's working with car thieves - so you're going to set him up. <Col: StageText>Pick up </Col>this <Col: StageText>vehicle </Col>and then <Col: StageText>leave it at </Col>this <Col: StageText>location</Col>. An expensive car like that will be an irresistible target.";
            contentsSimple[1870] = "TaskObjectives_JG_BEM1_FoE1_Stage03_DispatchBrief=That bug has revealed the location of a cut and shut that's passed through our Chop Shop. <Col:Stagetext>Protect the vehicle</Col> and make sure they don't get it to their labs.";
            contentsSimple[1871] = "TaskObjectives_JG_BEM1_FoE1_Stage04_OwnerBrief=The trap's set. We need you to use this <Col: StageText>satellite </Col>to listen in on Criminal chatter in the area. <Col: StageText>Hack </Col>the signal and see if they mention anything about the bent cop.";
            contentsSimple[1872] = "TaskObjectives_JG_BEM1_FoE1_Stage04_DispatchBrief=Our chop shop operation is under threat - an Enforcer tech team is trying to hack the shop's encrypted communications. <Col: StageText>Defend </Col>this communications <Col: StageText>antenna</Col>.";
            contentsSimple[1873] = "TaskObjectives_JG_BEM1_FoE1_Stage05_OwnerBrief=The crooked cop picked up the car and is taking it to a chop shop. We have everything on tape. Now <Col: StageText>raid </Col>the cop's <Col: StageText>home </Col>and take anything linking him to the car thieves.";
            contentsSimple[1874] = "TaskObjectives_JG_BEM1_FoE1_Stage05_DispatchBrief=Enforcers got a couple of addresses after analyzing that chop shop's comms - they're moving in on a <Col: StageText>building </Col>we use to stash parts. <Col: StageText>Protect </Col>the building from an Enforcer raid.";
            contentsSimple[1875] = "TaskObjectives_JG_BEM1_FoE1_Stage06_OwnerBrief=That <Col: StageText>car stereo </Col>was marked as missing from an SPPD evidence locker. <Col: StageText>Deliver </Col>it to our <Col: StageText>lockup </Col>and we'll return it after our lab's run some tests.";
            contentsSimple[1876] = "TaskObjectives_JG_BEM1_FoE1_Stage06_DispatchBrief=Enforcers have taken a <Col: StageText>car stereo </Col>which will link us to an assload of recent car thefts. <Col: StageText>Stop </Col>the <Col: StageText>recovery </Col>of the stereo.";
            contentsSimple[1877] = "TaskObjectives_JG_BEM1_FoE1_Stage07_OwnerBrief=All right, we just picked up that rogue cop. With him out of the picture it's time to shut down the car thieves. Get to the <Col: StageText>chop shop </Col>and <Col: StageText>investigate </Col>the scene for further evidence.";
            contentsSimple[1878] = "TaskObjectives_JG_BEM1_FoE1_Stage07_DispatchBrief=That stereo gave the 'Forcers probable cause for a full-blown raid - <Col: StageText>protect </Col>the <Col: StageText>chop shop </Col>. We've only lost parts so far - we better not lose the whole operation.";
            contentsSimple[1879] = "TaskObjectives_JG_BEM1_FoE1_Stage08_OwnerBrief=<Col: StageText>Deliver </Col>those <Col: StageText>stolen goods </Col>back to the <Col: StageText>lockup</Col>. More work like this, and our relationship with the SPPD will only get sweeter.";
            contentsSimple[1880] = "TaskObjectives_JG_BEM1_FoE1_Stage08_DispatchBrief=Enforcers have taken <Col: StageText>files </Col>and business documents that could damage more of our chop shop operations elsewhere in the city. <Col: StageText>Retrieve </Col>the Evidence before the enforcers deliver it!";
            contentsSimple[1881] = "TaskObjectives_JG_BEM1_FoE1_Stage08_Opp_OwnerBrief=";
            contentsSimple[1882] = "TaskObjectives_JG_BEM1_FoE1_Stage08_Opp_DispatchBrief=Enforcers have taken <Col: StageText>files </Col>and business documents that could damage more of our chop shop operations elsewhere in the city. <Col: StageText>Retrieve </Col>the Evidence before the enforcers deliver it!";
            contentsSimple[1883] = "TaskObjectives_JG_BEM2_Dis1_Stage01_OwnerBrief=Whiny anti-blood-diamond-traffic crybaby activists are on the warpath. We've heard that a <Col: StageText>bomb</Col> has been planted at a nearby jewelry store. Get there quickly and <Col: StageText>defuse</Col> it.";
            contentsSimple[1884] = "TaskObjectives_JG_BEM2_Dis1_Stage01_DispatchBrief=We've planted a <Col: StageText>bomb</Col> at a nearby jewelry store that buys black market blood diamonds. <Col: StageText>Prevent</Col> the Enforcers from <Col: StageText>defusing</Col> it.";
            contentsSimple[1885] = "TaskObjectives_JG_BEM2_Dis1_Stage02_OwnerBrief=More <Col: StageText>bombs</Col>, planted by those bleeding-heart activists, have been reported not far from your location. Find them and <Col: StageText>disarm</Col> them before they explode.";
            contentsSimple[1886] = "TaskObjectives_JG_BEM2_Dis1_Stage02_DispatchBrief=We planted a few more <Col: StageText>bombs</Col> not far from where you are. <Col: StageText>Stop</Col> the Enforcers before they <Col: StageText>disarm</Col> them. Think of those poor kids mining blood diamonds!";
            contentsSimple[1887] = "TaskObjectives_JG_BEM2_Dis1_Stage03_OwnerBrief=The activist <Col: StageText>bombers</Col> have been identified, and they're still in the area - <Col: StageText>eliminate</Col> them. Can't have ideologues forcing their agendas on us.";
            contentsSimple[1888] = "TaskObjectives_JG_BEM2_Dis1_Stage03_DispatchBrief=Enforcers are targeting you now - <Col: StageText>Take them out</Col> They call us Criminals, but they're the ones protecting blood diamond black marketeers...";
            contentsSimple[1889] = "TaskObjectives_JG_BEM2_FoE1_Stage01_OwnerBrief=<Col: StageText>Raid</Col> these targeted <Col: StageText>buildings</Col> - we've had reports of criminals using them to stash counterfeit Rakshasa brand jeans.";
            contentsSimple[1890] = "TaskObjectives_JG_BEM2_FoE1_Stage01_DispatchBrief=<Col: StageText>Stop Enforcers</Col> heading over to <Col: StageText>raid these buildings</Col> - we have caches of counterfeit Jean Zasou branded shirts hidden there.";
            contentsSimple[1891] = "TaskObjectives_JG_BEM2_FoE1_Stage02_OwnerBrief=<Col: StageText>Return</Col> the counterfeit Rakshasa <Col: StageText>jeans</Col> to a safe location <Col: StageText>nearby</Col>.";
            contentsSimple[1892] = "TaskObjectives_JG_BEM2_FoE1_Stage02_DispatchBrief=<Col: StageText>Prevent</Col> Enforcers from taking our stash of<Col: StageText> fake Zasou shirts </Col>to their <Col: StageText>drop-off</Col>.";
            contentsSimple[1893] = "TaskObjectives_JG_BEM2_FoE1_Stage03_OwnerBrief=Some other counterfeit Rakshasa haute couture was recently sold - <Col: StageText>raid</Col> this <Col: StageText>building</Col> to confiscate the cash.";
            contentsSimple[1894] = "TaskObjectives_JG_BEM2_FoE1_Stage03_DispatchBrief=<Col: StageText>Prevent</Col> the Enforcers' attempt to raid another <Col: StageText>building</Col> - we've been using it as a distribution center for our faux Zasou shirts.";
            contentsSimple[1895] = "TaskObjectives_JG_BEM2_FoE1_Stage04_OwnerBrief=<Col: StageText>Return</Col> the <Col: StageText>cash</Col> those Crims earned from selling Rakshasa knockoffs to a safe location <Col: StageText>nearby</Col>.";
            contentsSimple[1896] = "TaskObjectives_JG_BEM2_FoE1_Stage04_DispatchBrief=<Col: StageText>Prevent</Col> Enforcers from taking another of our stashes of<Col: StageText> fake Zasou shirts </Col>to their <Col: StageText>drop-off</Col>. We're gonna need to sell that stock for bail money!";
            contentsSimple[1897] = "TaskObjectives_JG_BEM2_FoE1_Stage05_OwnerBrief=We stored evidence about this counterfeit Rakshasa clothing case on a <Col: StageText>PDA</Col> that a rookie dropped. <Col: StageText>Take</Col> it and <Col: StageText>hold</Col> it out of enemy hands.";
            contentsSimple[1898] = "TaskObjectives_JG_BEM2_FoE1_Stage05_DispatchBrief=Enforcers stored evidence against us regarding our faux Zasou shirt scam on a <Col: StageText>PDA</Col> - and one of their rookies dropped it! <Col: StageText>Get</Col> it first, and <Col: StageText>keep it </Col>out of enemy hands.";
            contentsSimple[1899] = "TaskObjectives_JG_BEM2_FoE2_Stage01_OwnerBrief=A jewelry store was robbed by some Blood Rose rookies out for bling. We've found their getaway vehicle. <Col: StageText>Investigate</Col> the <Col: StageText>vehicle</Col> and recover what you can.";
            contentsSimple[1900] = "TaskObjectives_JG_BEM2_FoE2_Stage01_DispatchBrief=We robbed a jewelry store for bling to bribe Hanging Judge Jefferson not to send Big Joey to the chair, but Enforcers found our getaway wheels. <Col: StageText>Prevent</Col> them investigating the <Col: StageText>vehicle</Col>.";
            contentsSimple[1901] = "TaskObjectives_JG_BEM2_FoE2_Stage02_OwnerBrief=Hmm, there's only this <Col: StageText>necklace</Col> left. They've fenced or taken the rest already. <Col: StageText>Raid</Col> these three <Col: StageText>buildings</Col> and find anything the Blood Rose rookies left.";
            contentsSimple[1902] = "TaskObjectives_JG_BEM2_FoE2_Stage02_DispatchBrief=Damn, they found a <Col: StageText>necklace</Col> from our haul; now they're searching for the rest of the bling we plan on using to bribe Judge Jefferson. <Col: StageText>Protect</Col> our <Col: StageText>building</Col> from their raids.";
            contentsSimple[1903] = "TaskObjectives_JG_BEM2_FoE2_Stage03_OwnerBrief=Hmm, there's only this <Col: StageText>necklace</Col> left. They've fenced or taken the rest already. <Col: StageText>Raid</Col> these three <Col: StageText>buildings</Col> and find anything the Blood Rose rookies left.";
            contentsSimple[1904] = "TaskObjectives_JG_BEM2_FoE2_Stage03_DispatchBrief=Damn, they found a <Col: StageText>necklace</Col> from our haul; now they're searching for the rest of the bling we plan on using to bribe Judge Jefferson. <Col: StageText>Protect</Col> our <Col: StageText>building</Col> from their raids.";
            contentsSimple[1905] = "TaskObjectives_JG_BEM2_FoE2_Stage04_OwnerBrief=Take the <Col: StageText>necklace</Col> and the <Col: StageText>briefcase</Col> you found to our <Col: StageText>drop-off</Col>. We'll return them to the stores - minus our cut, of course. Want some emerald studs?";
            contentsSimple[1906] = "TaskObjectives_JG_BEM2_FoE2_Stage04_DispatchBrief=We need to <Col: StageText>intercept</Col> the <Col: StageText>necklace</Col> and the <Col: StageText>briefcase</Col> containing the rest of the judge's bribe before those Enforcers reach their <Col: StageText>drop-off</Col>.";
            contentsSimple[1907] = "TaskObjectives_JG_BEM2_FoE2_Stage05_OwnerBrief=Those newbie Blood Roses don't look too happy about us taking back their haul. <Col: StageText>Eliminate</Col> them and their allied muscle, before they do the same to us.";
            contentsSimple[1908] = "TaskObjectives_JG_BEM2_FoE2_Stage05_DispatchBrief=Damn those Enforcers! <Col: StageText>Wipe out</Col> all <Col: StageText>opponents</Col> and we can start to plan for another heist - we'll need one soon if we're gonna be able to pay that crooked judge off.";
            contentsSimple[1909] = "TaskObjectives_JG_BEM2_Inv1_Stage01_OwnerBrief=There's been a bomb threat against an electronics franchise owned by a well-known pro-CSA CEO; <Col: StageText>bug</Col> these <Col: StageText>payphones</Col> the bombers use, and we'll listen in, in case they use one again.";
            contentsSimple[1910] = "TaskObjectives_JG_BEM2_Inv1_Stage01_DispatchBrief=We're targeting the CEO of an electronics franchise, as he's firmly in Mayor Derren's pocket. <Col: StageText>Stop</Col> Enforcers from investigating these <Col: StageText>payphones</Col> - we leave coded messages at them.";
            contentsSimple[1911] = "TaskObjectives_JG_BEM2_Inv1_Stage02_OwnerBrief=<Col: StageText>Scan</Col> these <Col: StageText>antennae</Col> to pick up any chatter about the bomb threat. The CEO that owns the threatened company has a lot of pull with Mayor Derren; let's keep him happy.";
            contentsSimple[1912] = "TaskObjectives_JG_BEM2_Inv1_Stage02_DispatchBrief=Enforcers got wind of our plan to bomb that pro-CSA CEO's electronics franchise branch. <Col: StageText>Stop</Col> them hacking these radio <Col: StageText>antennae</Col> and listening in on us some more.";
            contentsSimple[1913] = "TaskObjectives_JG_BEM2_Inv1_Stage03_OwnerBrief=We may have something; there was mention of a nearby electronics <Col: StageText>store</Col> - a probable target. Get there quickly and <Col: StageText>disarm</Col> any devices.";
            contentsSimple[1914] = "TaskObjectives_JG_BEM2_Inv1_Stage03_DispatchBrief=The bomb is at this electronics<Col: StageText> store</Col>, and the Enforcers are on their way. <Col: StageText>Defend </Col>the target and make the Enforcers pay for keeping this CEO in business.";
            contentsSimple[1915] = "TaskObjectives_JG_BEM2_Inv1_Stage04_OwnerBrief=The <Col: StageText>bombers</Col> remain at large, but now we're after them - <Col: StageText>eliminate</Col> any resistance in the area. Let's show Derren's electronics magnate buddy that we're on the ball.";
            contentsSimple[1916] = "TaskObjectives_JG_BEM2_Inv1_Stage04_DispatchBrief=That pro-CSA electronics magnate sent a bunch of thugs... <Col: StageText>Eliminate the Enforcers</Col>and show them not to mess with us in future.";
            contentsSimple[1917] = "TaskObjectives_JG_BEM2_VPi1_Stage01_OwnerBrief=We've located two pickup trucks that may have been used in a recent pirated movie smuggling op. <Col: StageText>Recover</Col> the first <Col: StageText>pickup</Col> so we can examine it for evidence.";
            contentsSimple[1918] = "TaskObjectives_JG_BEM2_VPi1_Stage01_DispatchBrief=Enforcers are trying to <Col: StageText>recover</Col> a <Col: StageText>vehicle</Col> we use to smuggle pirated movies. <Col: StageText>Stop</Col> them!";
            contentsSimple[1919] = "TaskObjectives_JG_BEM2_VPi1_Stage02_OwnerBrief=<Col: StageText>Take</Col> the first <Col: StageText>pickup</Col> to be investigated at this <Col: StageText>drop-off</Col>. We'll crack this pirated movie smuggling ring yet.";
            contentsSimple[1920] = "TaskObjectives_JG_BEM2_VPi1_Stage02_DispatchBrief=Our <Col: StageText>vehicle</Col> is being taken to the Enforcer <Col: StageText>drop-off</Col>. <Col: StageText>Stop</Col> it before it arrives. We need those wheels! We have a shipment of pirated copies of Avahontas: Return 2 Pendara to move quickly.";
            contentsSimple[1921] = "TaskObjectives_JG_BEM2_VPi1_Stage03_OwnerBrief=We got a few partial prints, but it's not enough. <Col: StageText>Collect</Col> the second <Col: StageText>pickup</Col>, and hopefully we'll get something more useful. Movie piracy hurts several of our protected companies.";
            contentsSimple[1922] = "TaskObjectives_JG_BEM2_VPi1_Stage03_DispatchBrief=Enforcers are now trying to <Col: StageText>recover</Col> our backup <Col: StageText>vehicle</Col>. <Col: StageText>Stop</Col> them! We've got a bunch more pirated movies to smuggle.";
            contentsSimple[1923] = "TaskObjectives_JG_BEM2_VPi1_Stage04_OwnerBrief=<Col: StageText>Take</Col> the second <Col: StageText>pickup</Col> to be investigated at this <Col: StageText>drop-off</Col> location. The fewer vehicles this pirated movie ring has available to move their product, the better.";
            contentsSimple[1924] = "TaskObjectives_JG_BEM2_VPi1_Stage04_DispatchBrief=Enforcers are taking the <Col: StageText>vehicle </Col>to their <Col: StageText>drop-off</Col> as well. Make sure that you <Col: StageText>stop</Col> them before it arrives. Our pirated movie smuggling ring funds all sorts of ops.";
            contentsSimple[1925] = "TaskObjectives_JG_BEM2_VPi1_Stage05_OwnerBrief=The gang that instigated this pirated movie smuggling ring has been identified, but it's fighting back. <Col: StageText>Eliminate</Col> all of the <Col: StageText>gang members</Col> before they eliminate you!";
            contentsSimple[1926] = "TaskObjectives_JG_BEM2_VPi1_Stage05_DispatchBrief=Rentacops are on their way - probably hired by some entertainment superstore magnate. <Col: StageText>Eliminate</Col> all of the <Col: StageText>Enforcers</Col> before they take us down.";
            contentsSimple[1927] = "TaskObjectives_JG_BEM3_Dis1_Stage01_OwnerBrief=A fringe sect of the Church of Unbroken Light have decided that Armageddon can't come soon enough - they're tagging up targets for a bombing campaign. <Col: StageText>Clear </Col>the <Col: StageText>graffiti </Col>to prevent the attacks.";
            contentsSimple[1928] = "TaskObjectives_JG_BEM3_Dis1_Stage01_DispatchBrief=Want a new football stadium? We're working with property developers to, uh, 'redevelop' this area. We've tagged bombing locations, but Enforcers are removing the tags - <Col: StageText>defend </Col>these <Col: StageText>markers</Col>.";
            contentsSimple[1929] = "TaskObjectives_JG_BEM3_Dis1_Stage02_OwnerBrief=More bombing targets have been tagged by the sect. <Col: StageText>Cover the tags </Col>to prevent the bombings. CUL-TV evangelists are warning the faithful that those 'marked as evil' shall soon be struck down…";
            contentsSimple[1930] = "TaskObjectives_JG_BEM3_Dis1_Stage02_DispatchBrief=More bombing targets have been tagged to make way for the stadium. <Col: StageText>Prevent </Col>Enforcers <Col: StageText>clearing </Col>the markers. Sure, they lose their homes - but they can say, 'I used to live on that end zone'. Cool.";
            contentsSimple[1931] = "TaskObjectives_JG_BEM3_Dis1_Stage03_OwnerBrief=We didn't find some targets in time, and bombs have been set. Get to these <Col: StageText>devices </Col>and <Col: StageText>disarm </Col>them before they explode. Watch out for sect leaders - their idea of salvation is quick and fatal...";
            contentsSimple[1932] = "TaskObjectives_JG_BEM3_Dis1_Stage03_DispatchBrief=The developers are accelerating things; they need us to clear the site for the stadium ASAP. We've got<Col: StageText> bombs here </Col>but the Enforcers have found them. <Col: StageText>Stop </Col>the Enforcers <Col: StageText>defusing </Col>the devices.";
            contentsSimple[1933] = "TaskObjectives_JG_BEM3_Dis1_Stage04_OwnerBrief=The neighborhood is safe for now, despite lurid tales of bearded comets and The Beast swallowing City Hall (Derren will be amused). <Col: StageText>Wipe out </Col>this weirdo <Col: StageText>sect </Col>for good.";
            contentsSimple[1934] = "TaskObjectives_JG_BEM3_Dis1_Stage04_DispatchBrief=The Enforcers are gonna hunt you down and put a permanent halt to our stadium development plans. Screw that, go <Col:StageText>Kill them all</Col>";
            contentsSimple[1935] = "TaskObjectives_JG_BEM4_Dis1_Stage01_OwnerBrief=We've received word of a potential bomb attack on two <Col: StageText>vehicles</Col>. They're a few blocks apart - you might need to split up to <Col: StageText>defuse </Col>the <Col: StageText>bombs </Col>in time. Clearly, this has been expertly planned.";
            contentsSimple[1936] = "TaskObjectives_JG_BEM4_Dis1_Stage01_DispatchBrief=Our good friend and explosives guru, Azrael, is out on parole. <Col: StageText>Defend </Col>the bombs he planted on these <Col: StageText>vehicles</Col>. The cars belong to the killjoys who locked him away.";
            contentsSimple[1937] = "TaskObjectives_JG_BEM4_Dis1_Stage02_OwnerBrief=We've received word of a potential bomb attack on two <Col: StageText>vehicles</Col>. They're a few blocks apart - you might need to split up to <Col: StageText>defuse </Col>the <Col: StageText>bombs </Col>in time. Clearly, this has been expertly planned.";
            contentsSimple[1938] = "TaskObjectives_JG_BEM4_Dis1_Stage02_DispatchBrief=Our good friend and explosives guru, Azrael, is out on parole. <Col: StageText>Defend </Col>the bombs he planted on these <Col: StageText>vehicles</Col>. The cars belong to the killjoys who locked him away.";
            contentsSimple[1939] = "TaskObjectives_JG_BEM4_Dis1_Stage03_OwnerBrief=I think we've identified our bomber - a local bomb nut called Azrael. He is out on parole and seeking revenge. <Col: StageText>Raid </Col>these <Col: StageText>buildings</Col>; they are his known bomb factories.";
            contentsSimple[1940] = "TaskObjectives_JG_BEM4_Dis1_Stage03_DispatchBrief=Damn, the Enforcers defused the bombs and now they're raiding our buildings - they contain key bomb components that we must hold on to. <Col: StageText>Defend </Col>our <Col: StageText>buildings</Col>!";
            contentsSimple[1941] = "TaskObjectives_JG_BEM4_Dis1_Stage04_OwnerBrief=I think we've identified our bomber - a local bomb nut called Azrael. He is out on parole and seeking revenge. <Col: StageText>Raid </Col>these <Col: StageText>buildings</Col>; they are his known bomb factories.";
            contentsSimple[1942] = "TaskObjectives_JG_BEM4_Dis1_Stage04_DispatchBrief=Damn, the Enforcers defused the bombs and now they're raiding our buildings - they contain key bomb components that we must hold on to. <Col: StageText>Defend </Col>our <Col: StageText>buildings</Col>!";
            contentsSimple[1943] = "TaskObjectives_JG_BEM4_Dis1_Stage05_OwnerBrief=A <Col: StageText>bag </Col>full of fertilizer and a <Col: StageText>canister </Col>of chemicals - looks like Azrael is up to his old tricks again. <Col: StageText>Deliver </Col>everything to this specialist <Col: StageText>lockup</Col>.";
            contentsSimple[1944] = "TaskObjectives_JG_BEM4_Dis1_Stage05_DispatchBrief=We have to recover those <Col: StageText>supplies </Col>- they're key to our friend Azrael's bombing campaign. <Col: StageText>Stop </Col>the Enforcers <Col: StageText>delivering </Col>them back to their <Col: StageText>lock-up</Col>.";
            contentsSimple[1945] = "TaskObjectives_JG_BEM4_Dis1_Stage06_OwnerBrief=Azrael wasn't working alone - he was working with a gang. <Col: StageText>Defend </Col>this <Col: StageText>vehicle</Col>; it belongs to our bomb squad and are a likely target.";
            contentsSimple[1946] = "TaskObjectives_JG_BEM4_Dis1_Stage06_DispatchBrief=";
            contentsSimple[1947] = "TaskObjectives_JG_BEM4_Dis1_Stage06_Opp_OwnerBrief=";
            contentsSimple[1948] = "TaskObjectives_JG_BEM4_Dis1_Stage06_Opp_DispatchBrief=Damn it, those Enforcers took most of our supplies. But Azrael has one more trick up his vengeful sleeve... <Col: StageText>Bomb </Col>this <Col: StageText>vehicle </Col>belonging to the bomb squad.";
            contentsSimple[1949] = "TaskObjectives_JG_BEM4_Dis1_Stage07_OwnerBrief=Some of the bomb components survived the blast.  <Col:StageText>Recover the parts</Col> and deliver them to our facilities.";
            contentsSimple[1950] = "TaskObjectives_JG_BEM4_Dis1_Stage07_DispatchBrief=Some of the bomb fragments survived the blast.  <Col:StageText>Collect and Return them</Col> to our safehouse and we'll make sure they are disposed of.";
            contentsSimple[1951] = "TaskObjectives_JG_BEM4_Dis1_Stage07_Opp_OwnerBrief=";
            contentsSimple[1952] = "TaskObjectives_JG_BEM4_Dis1_Stage07_Opp_DispatchBrief=Some of the bomb fragments survived the blast.  <Col:StageText>Collect and Return them</Col> to our safehouse and we'll make sure they are disposed of.";
            contentsSimple[1953] = "TaskObjectives_JG_BES2_Dis1_Stage01_OwnerBrief=We've got word of a planned bombing campaign against the security company that protects the ParKunst. <Col: StageText>Defuse</Col> the bombs on these nearby security <Col: StageText>vehicles</Col>.";
            contentsSimple[1954] = "TaskObjectives_JG_BES2_Dis1_Stage01_DispatchBrief=We've planted bombs on trucks belonging to the security company that protects the ParKunst. <Col: StageText>Protect</Col> the bombs on these <Col: StageText>vehicles</Col> - we're planning an art heist.";
            contentsSimple[1955] = "TaskObjectives_JG_BES2_Dis1_Stage02_OwnerBrief=These secure lockups belong to the security company that protects the ParKunst. <Col: StageText>Disarm</Col> any devices at these <Col: StageText>buildings</Col>. There may be an art theft in the planning stages.";
            contentsSimple[1956] = "TaskObjectives_JG_BES2_Dis1_Stage02_DispatchBrief=<Col: StageText>Defend</Col> our devices on these <Col: StageText>buildings</Col> so the ParKunst's rentacops can't get their gear from their lockups. We've got a fence eager to move some high-class art.";
            contentsSimple[1957] = "TaskObjectives_JG_BES2_Dis1_Stage03_OwnerBrief=The <Col: StageText>bombers</Col> have been identified - as we suspected, they were trying to cripple the ParKunst's security and make off with part of their contemporary miniatures collection. <Col: StageText>Eliminate</Col> them.";
            contentsSimple[1958] = "TaskObjectives_JG_BES2_Dis1_Stage03_DispatchBrief=<Col: StageText>Enforcers</Col> are onto us! <Col: StageText>Eliminate them</Col>, or we won't be able to raid the ParKunst's recent collection of contemporary miniatures. That shit sells like hotcakes.";
            contentsSimple[1959] = "TaskObjectives_JG_BES2_FoE1_Stage01_OwnerBrief=A local gang is operating out of a nearby <Col: StageText>building</Col>. Get there and <Col: StageText>raid</Col> the place - they're planning to blackmail a client company's CFO with sensational photos of her husband.";
            contentsSimple[1960] = "TaskObjectives_JG_BES2_FoE1_Stage01_DispatchBrief=We're blackmailing a pro-Enforcement tech company. We've got compromising photos of the CFO's husband, but she decided to call for CSA help. <Col: StageText>Protect</Col> the <Col: StageText>building</Col> where we stashed the pics.";
            contentsSimple[1961] = "TaskObjectives_JG_BES2_FoE1_Stage02_OwnerBrief=The blackmailers has backups of the photos at a secondary hideout, not far from the first. <Col: StageText>Raid</Col> the second <Col: StageText>building</Col> for the rest of the compromising pics of that CFO's husband.";
            contentsSimple[1962] = "TaskObjectives_JG_BES2_FoE1_Stage02_DispatchBrief=We have a second safehouse nearby where we stashed the rest of our blackmail material, and Enforcers are moving toward it. <Col: StageText>Defend</Col> the <Col: StageText>building</Col> so they can't recover those photos.";
            contentsSimple[1963] = "TaskObjectives_JG_BES2_FoE1_Stage03_OwnerBrief=<Col: StageText>Deliver</Col> all of the <Col: StageText>photos</Col> to our <Col: StageText>drop-off</Col>, and we'll hand them back to that CFO being blackmailed. Check out that third one - who knew people could bend like that?";
            contentsSimple[1964] = "TaskObjectives_JG_BES2_FoE1_Stage03_DispatchBrief=Enforcers are taking the <Col: StageText>photos</Col> we were gonna use for blackmail to their <Col: StageText>drop-off</Col>. <Col: StageText>Intercept</Col> them before they make it there. Damn, that CFO's husband sure was ...inventive.";
            contentsSimple[1965] = "TaskObjectives_JG_BES2_FoE1_Stage04_OwnerBrief=We uploaded scanned versions of the blackmail to a laptop in a <Col: StageText>briefcase</Col> on the CFO's request - she may need them in the divorce - but it was misplaced. <Col: StageText>Recover</Col> and <Col: StageText>hold</Col> it away from all opponents.";
            contentsSimple[1966] = "TaskObjectives_JG_BES2_FoE1_Stage04_DispatchBrief=The Enforcers destroyed the physical photos, but we'd uploaded lo-res versions onto a laptop in a <Col: StageText>briefcase</Col>. <Col: StageText>Get to it first</Col> and <Col: StageText>keep</Col> it away from them. We may still be able to blackmail that CFO.";
            contentsSimple[1967] = "TaskObjectives_JG_BES2_Inv1_Stage01_OwnerBrief=A gang has been selling soil from Chernobyl as weapons-grade uranium. We've been tracking this 'uranium' since it was smuggled into the city. <Col: StageText>Search </Col>this <Col: StageText>car </Col>and <Col: StageText>take </Col>anything suspicious.";
            contentsSimple[1968] = "TaskObjectives_JG_BES2_Inv1_Stage01_DispatchBrief=We've been smuggling radioactive soil from Chernobyl into the city, passing it off as weapons-grade uranium. Now the Enforcers are on to our scam. <Col: StageText>Defend </Col>our <Col: StageText>car </Col>from Enforcer investigation.";
            contentsSimple[1969] = "TaskObjectives_JG_BES2_Inv1_Stage02_OwnerBrief=More vehicles are being used to smuggle that fake uranium. <Col: StageText>Investigate </Col>these <Col: StageText>vans </Col>and <Col: StageText>take </Col>anything that appears suspicious.";
            contentsSimple[1970] = "TaskObjectives_JG_BES2_Inv1_Stage02_DispatchBrief=More vehicles used for smuggling our radioactive soil are located nearby. <Col: StageText>Protect </Col>these <Col: StageText>vans </Col>and ensure that Enforcers find nothing of value - particularly our fake weapons-grade 'uranium'.";
            contentsSimple[1971] = "TaskObjectives_JG_BES2_Inv1_Stage03_OwnerBrief=More vehicles are being used to smuggle that fake uranium. <Col: StageText>Investigate </Col>these <Col: StageText>vans </Col>and <Col: StageText>take </Col>anything that appears suspicious.";
            contentsSimple[1972] = "TaskObjectives_JG_BES2_Inv1_Stage03_DispatchBrief=More vehicles used for smuggling our radioactive soil are located nearby. <Col: StageText>Protect </Col>these <Col: StageText>vans </Col>and ensure that Enforcers find nothing of value - particularly our fake weapons-grade 'uranium'.";
            contentsSimple[1973] = "TaskObjectives_JG_BES2_Inv1_Stage04_OwnerBrief=We need to analyze those soil samples and verify that the uranium is fake. It may constitute a health hazard. <Col: StageText>Deliver </Col>all the <Col: StageText>packages </Col>to this <Col: StageText>drop-off</Col>.";
            contentsSimple[1974] = "TaskObjectives_JG_BES2_Inv1_Stage04_DispatchBrief=Enforcers are delivering our radioactive soil for analysis - a Geiger counter will show it's dangerous, if not actually 'uranium'. <Col: StageText>Prevent </Col>all of the <Col: StageText>packages </Col>reaching the Enforcers' <Col: StageText>drop-off</Col>.";
            contentsSimple[1975] = "TaskObjectives_JG_BES2_Inv1_Stage05_OwnerBrief=The gang of smugglers was close to closing a major deal and need their 'uranium' back. They're coming for you. <Col: StageText>Eliminate </Col>all <Col: StageText>opponents </Col>before they take you down.";
            contentsSimple[1976] = "TaskObjectives_JG_BES2_Inv1_Stage05_DispatchBrief=The Enforcers have decided to act with caution and eliminate the presumed nuclear terrorists. That means you. <Col: StageText>Kill </Col>all <Col: StageText>opponents </Col>before they kill you and bury you in a lead box.";
            contentsSimple[1977] = "TaskObjectives_JG_BES2_Inv2_Stage01_OwnerBrief=There's been a bomb threat - the San Paro Liberation Front claims to have planted a bomb on a nearby property. <Col: StageText>Defuse </Col>the bomb before the <Col: StageText>building </Col>is destroyed!";
            contentsSimple[1978] = "TaskObjectives_JG_BES2_Inv2_Stage01_DispatchBrief=The San Paro Liberation Front has spent years trying to get Derren out of power. They planted a bomb, but the Enforcers are on to it. <Col: StageText>Defend </Col>the bomb and ensure the <Col: StageText>building </Col>is destroyed.";
            contentsSimple[1979] = "TaskObjectives_JG_BES2_Inv2_Stage02_OwnerBrief=The bomb threat was traced to one of two payphones. <Col: StageText>Investigate </Col>the first <Col: StageText>payphone </Col>and recover any evidence. The SPLF are determined to run Mayor Derren out of office. Yeah, good luck!";
            contentsSimple[1980] = "TaskObjectives_JG_BES2_Inv2_Stage02_DispatchBrief=The SPLF made one of their latest bomb threats from a payphone in this area. <Col: StageText>Protect </Col>the <Col: StageText>payphone </Col>to prevent the Enforcers investigating it. We'll support the SPLF while our goals align.";
            contentsSimple[1981] = "TaskObjectives_JG_BES2_Inv2_Stage03_OwnerBrief=That payphone showed no evidence of Criminal activity. <Col: StageText>Investigate </Col>the second <Col: StageText>payphone </Col>- it could also have been used to call in the SPLF bomb threat.";
            contentsSimple[1982] = "TaskObjectives_JG_BES2_Inv2_Stage03_DispatchBrief=The SPLF used a different payphone to call in their final bomb threat. <Col: StageText>Protect </Col>the <Col: StageText>payphone </Col>from Enforcer investigation.";
            contentsSimple[1983] = "TaskObjectives_JG_BES2_Inv2_Stage04_OwnerBrief=Explosives traces confirm this phone was used by the bombers - the on-site investigation is complete and the findings are on this <Col: StageText>PDA</Col>. <Col: StageText>Deliver </Col>it to the nearby <Col: StageText>drop-off </Col>for further analysis.";
            contentsSimple[1984] = "TaskObjectives_JG_BES2_Inv2_Stage04_DispatchBrief=The SPLF member on that last call must've dropped their PDA - the Enforcers have it. <Col: StageText>Stop </Col>the <Col: StageText>PDA </Col>being delivered to the Enforcer <Col: StageText>drop-off</Col>. The SPLF will owe us a favor.";
            contentsSimple[1985] = "TaskObjectives_JG_BES2_Inv2_Stage05_OwnerBrief=Interviewing locals about that payphone, we've positively identified several SPLF sympathizers active in the <Col: StageText>area</Col>. Take <Col: StageText>control </Col> of that location!";
            contentsSimple[1986] = "TaskObjectives_JG_BES2_Inv2_Stage05_DispatchBrief=The data on that PDA has blown the Enforcer's SPLF investigation wide open. Keep <Col: StageText>control </Col> of the <Col: StageText>area </Col> we're staging in.";
            contentsSimple[1987] = "TaskObjectives_JG_BES3_Dis1_Stage01_OwnerBrief=A gang specializing in illegal immigration into San Paro are going to hit a competing people-smuggling organization. They've planted car bombs. <Col: StageText>Defuse </Col>the device attached to the first <Col: StageText>vehicle</Col>.";
            contentsSimple[1988] = "TaskObjectives_JG_BES3_Dis1_Stage01_DispatchBrief=We've been organizing illegal immigration into San Paro, but the Enforcers are onto us. You better destroy our vehicles: they're lousy with prints and DNA. <Col: StageText>Defend </Col>the first <Col: StageText>vehicle </Col>until the bomb explodes.";
            contentsSimple[1989] = "TaskObjectives_JG_BES3_Dis1_Stage02_OwnerBrief=Two more <Col: StageText>bombs </Col>have been planted on these <Col: StageText>vehicles</Col>. <Col: StageText>Defuse </Col>the devices. We want to recover the forged visa applications stashed in both vehicles.";
            contentsSimple[1990] = "TaskObjectives_JG_BES3_Dis1_Stage02_DispatchBrief=<Col: StageText>Defend </Col>these <Col: StageText>vehicles </Col>from any Enforcer attempts to defuse the bombs. We have hundreds of forged visa applications packed in the vehicles' trunks - they had better burn!";
            contentsSimple[1991] = "TaskObjectives_JG_BES3_Dis1_Stage03_OwnerBrief=We've already disabled two more devices, but need you to <Col: StageText>pick up </Col>the <Col: StageText>vehicles </Col>and <Col: StageText>deliver </Col>them to our <Col: StageText>drop-off </Col>for analysis.";
            contentsSimple[1992] = "TaskObjectives_JG_BES3_Dis1_Stage03_DispatchBrief=Two of our <Col: StageText>vehicles </Col>are nearby. The Enforcers are taking them to this <Col: StageText>drop-off </Col>for investigation - <Col: StageText>intercept </Col>those vehicles.";
            contentsSimple[1993] = "TaskObjectives_JG_BES3_Dis1_Stage04_OwnerBrief=We've already disabled two more devices, but need you to <Col: StageText>pick up </Col>the <Col: StageText>vehicles </Col>and <Col: StageText>deliver </Col>them to our <Col: StageText>drop-off </Col>for analysis.";
            contentsSimple[1994] = "TaskObjectives_JG_BES3_Dis1_Stage04_DispatchBrief=Two of our <Col: StageText>vehicles </Col>are nearby. The Enforcers are taking them to this <Col: StageText>drop-off </Col>for investigation - <Col: StageText>intercept </Col>those vehicles.";
            contentsSimple[1995] = "TaskObjectives_JG_BES3_Dis1_Stage05_OwnerBrief=That illegal immigration ring needs to be brought to justice. Stamp their passports for permanent deportation - take <Col: StageText>control </Col> of the <Col: StageText>area </Col>they conduct their operations in.";
            contentsSimple[1996] = "TaskObjectives_JG_BES3_Dis1_Stage05_DispatchBrief=Dammit, the Enforcers recovered several vehicles used in our illegal immigration operation and traced them back to us. Keep <Col: StageText>control </Col>of the <Col: StageText>area </Col>around our safehouse.";
            contentsSimple[1997] = "TaskObjectives_JG_BES4_FoE2_Stage01_OwnerBrief=Criminals hit the National Progress Bank a few days ago and stole a lot of gold bullion. We've located the cut of one of the getaway drivers involved. <Col: StageText>Loot </Col>this <Col: StageText>vehicle </Col>and <Col: StageText>take the gold</Col>.";
            contentsSimple[1998] = "TaskObjectives_JG_BES4_FoE2_Stage01_DispatchBrief=We hit the National Progress Bank last week and stole a lot of gold. We've avoided capture so far, but the Enforcers have finally located us. <Col: StageText>Defend </Col>our <Col: StageText>vehicle </Col>from an Enforcer search.";
            contentsSimple[1999] = "TaskObjectives_JG_BES4_FoE2_Stage02_OwnerBrief=The Criminals stashed some of the gold they stole in this <Col: StageText>shipping crate </Col>ready for transfer to their foreign partners. <Col: StageText>Raid </Col>the crate and recover the bullion.";
            contentsSimple[2000] = "TaskObjectives_JG_BES4_FoE2_Stage02_DispatchBrief=We put some of the gold into this <Col: StageText>shipping crate </Col>ready for be moved to an offshore bank in Kazakhstan. Now you'll need to <Col: StageText>defend </Col>the crate from the Enforcers - stop them recovering the bullion.";
            contentsSimple[2001] = "TaskObjectives_JG_BES4_FoE2_Stage03_OwnerBrief=All right, the last of the gold is still in the Criminal hideout. What do you figure they do with it in there? Play with it? <Col: StageText>Raid </Col>that <Col: StageText>building </Col>and deprive the Criminals of their expensive toys.";
            contentsSimple[2002] = "TaskObjectives_JG_BES4_FoE2_Stage03_DispatchBrief=The last of the gold is in our hideout - we were planning to buy a couple of UH-5R helicopter gunships. Why not fly to work? <Col: StageText>Defend </Col>our <Col: StageText>hideout </Col>and ensure the Enforcers don't recover the gold.";
            contentsSimple[2003] = "TaskObjectives_JG_BES4_FoE2_Stage04_OwnerBrief=Good job, that's all of it. <Col: StageText>Deliver </Col>all of the <Col: StageText>gold bullion </Col>back to our <Col: StageText>drop-off </Col>and we'll return it to the National Progress Bank. Hopefully they've modificationd their security...";
            contentsSimple[2004] = "TaskObjectives_JG_BES4_FoE2_Stage04_DispatchBrief=Shit, stealing that gold put us on the city's most wanted, and now it's being recovered. What a waste! <Col: StageText>Stop </Col>the <Col: StageText>gold </Col>before the Enforcers can take it to their <Col: StageText>drop-off</Col>.";
            contentsSimple[2005] = "TaskObjectives_JG_BES4_FoE2_Stage05_OwnerBrief=We loaded the recovered gold into a <Col: StageText>truck </Col>for transport, but the Criminals won't let it go. <Col: StageText>Defend </Col>the truck from bombing attacks - don't let the Criminals get their hands on the gold again.";
            contentsSimple[2006] = "TaskObjectives_JG_BES4_FoE2_Stage05_DispatchBrief=";
            contentsSimple[2007] = "TaskObjectives_JG_BES4_FoE2_Stage05_Opp_OwnerBrief=";
            contentsSimple[2008] = "TaskObjectives_JG_BES4_FoE2_Stage05_Opp_DispatchBrief=The Enforcers decided that the gold wasn't safe at their drop-off. Well, they were right! <Col: StageText>Bomb </Col>the <Col: StageText>truck </Col>they loaded the gold into, and stop its transfer to the National Progress Bank.";
            contentsSimple[2009] = "TaskObjectives_JG_BES4_FoE2_Stage06_OwnerBrief=Shit, the Criminals have blown the van and now the <Col: StageText>gold </Col>is an easy target. <Col: StageText>Recover the bars </Col>and <Col: StageText>deliver </Col>them to our <Col: StageText>lockup</Col>. Don't let the Criminals get any gold to theirs!";
            contentsSimple[2010] = "TaskObjectives_JG_BES4_FoE2_Stage06_DispatchBrief=";
            contentsSimple[2011] = "TaskObjectives_JG_BES4_FoE2_Stage06_Opp_OwnerBrief=";
            contentsSimple[2012] = "TaskObjectives_JG_BES4_FoE2_Stage06_Opp_DispatchBrief=Perfect, you've blown the van, and now the <Col: StageText>gold </Col>is an easy target. <Col: StageText>Recover the bars </Col>and <Col: StageText>deliver </Col>it to our <Col: StageText>lockup</Col>. Don't let the Enforcers get any gold to theirs!";
            contentsSimple[2013] = "TaskObjectives_JG_FEL4_FoE1_Stage01_OwnerBrief=We're eroding Criminal influence over the construction union and they don't like it. <Col: StageText>Recover </Col>this construction <Col: StageText>truck </Col>before the Criminals can attack it out of revenge.";
            contentsSimple[2014] = "TaskObjectives_JG_FEL4_FoE1_Stage01_DispatchBrief=The leader of the construction workers' union is a vocal supporter of the Enforcers. <Col: StageText>Defend </Col>this construction <Col: StageText>truck </Col>we've sabotaged. We want to use it in a revenge attack.";
            contentsSimple[2015] = "TaskObjectives_JG_FEL4_FoE1_Stage02_OwnerBrief=The Criminals are planning attacks on various construction companies to regain their influence over the union. <Col: StageText>Secure </Col>the second <Col: StageText>truck </Col>they've targeted and <Col: StageText>deliver both </Col>to this <Col: StageText>drop-off</Col>.";
            contentsSimple[2016] = "TaskObjectives_JG_FEL4_FoE1_Stage02_DispatchBrief=We've sabotaged another truck as revenge against the construction union for supporting the Enforcers. <Col: StageText>Defend </Col>the construction <Col: StageText>truck </Col>and stop the Enforcers picking it up.";
            contentsSimple[2017] = "TaskObjectives_JG_FEL4_FoE1_Stage03_OwnerBrief=The Criminals are planning attacks on various construction companies to regain their influence over the union. <Col: StageText>Secure </Col>the second <Col: StageText>truck </Col>they've targeted and <Col: StageText>deliver both </Col>to this <Col: StageText>drop-off</Col>.";
            contentsSimple[2018] = "TaskObjectives_JG_FEL4_FoE1_Stage03_DispatchBrief=The Enforcers are taking our sabotaged construction trucks back to their drop-off. <Col: StageText>Destroy </Col>the <Col: StageText>trucks </Col>and the evidence of our tampering. We'll find another way to attack that pro-'Forcer union.";
            contentsSimple[2019] = "TaskObjectives_JG_FEL4_FoE1_Stage04_OwnerBrief=The Criminals have raised the intensity of their attacks and planted three bombs on <Col: StageText>portacabins </Col>throughout the area. <Col: StageText>Defuse the bombs </Col>to protect the construction union.";
            contentsSimple[2020] = "TaskObjectives_JG_FEL4_FoE1_Stage04_DispatchBrief=If we can show the construction workers that their union leader can't protect them, he'll lose support and so will the Enforcers. <Col: StageText>Defend </Col>our <Col: StageText>bombs </Col>on these <Col: StageText>portacabins </Col>from Enforcers.";
            contentsSimple[2021] = "TaskObjectives_JG_FEL4_FoE1_Stage05_OwnerBrief=More attacks on the construction union are inevitable so we need to move against the Criminals. <Col: StageText>Raid </Col>their <Col: StageText>hideouts </Col>and <Col: StageText>take </Col>any <Col: StageText>explosives </Col>you find so they can't launch further attacks.";
            contentsSimple[2022] = "TaskObjectives_JG_FEL4_FoE1_Stage05_DispatchBrief=We have a cache of explosives ready for further attacks on the Enforcer-loving construction workers. <Col: StageText>Protect </Col>our <Col: StageText>storehouses </Col>from the Enforcer raids.";
            contentsSimple[2023] = "TaskObjectives_JG_FEL4_FoE1_Stage06_OwnerBrief=Good work. <Col: StageText>Deliver </Col>the seized <Col: StageText>explosives </Col>back to our <Col: StageText>drop-off </Col>to keep them out of Criminal hands. Together, we can construct a better San Paro.";
            contentsSimple[2024] = "TaskObjectives_JG_FEL4_FoE1_Stage06_DispatchBrief=The Enforcers have taken our <Col: StageText>explosives</Col>. Without them we can't launch further attacks against the 'Forcer-supporting construction workers' union. <Col: StageText>Stop </Col>that delivery reaching their <Col: StageText>drop-off</Col>.";
            contentsSimple[2025] = "TaskObjectives_JG_FEL4_FoE1_Stage07_OwnerBrief=With those explosives recovered, the Criminals are going low-tech and plan to burn the portacabins you saved. <Col: StageText>Defend </Col>the <Col: StageText>portacabins.</Col>";
