            contentsSimple[2166] = "TaskObjectives_JG_TutEnf_Part5E_Stage02_DispatchBrief=";
            contentsSimple[2167] = "TaskObjectives_JG_WEL4_FoE1_Stage01_OwnerBrief=We received a tip-off: Jericho, the weapons dealer, has a shipment of weapons awaiting pickup at the docks. We're going to seize those guns. First, <Col: StageText>get </Col>the <Col: StageText>trucks </Col>you'll need to carry them.";
            contentsSimple[2168] = "TaskObjectives_JG_WEL4_FoE1_Stage01_DispatchBrief=Port Authority plans to seize our weapons shipment as it arrives at the docks. Buy us time to move the guns. First, <Col: StageText>stop </Col>the authorities <Col: StageText>picking up </Col>the <Col: StageText>vehicles </Col>they'll use for the raid.";
            contentsSimple[2169] = "TaskObjectives_JG_WEL4_FoE1_Stage02_OwnerBrief=We received a tip-off: Jericho, the weapons dealer, has a shipment of weapons awaiting pickup at the docks. We're going to seize those guns. First, <Col: StageText>get </Col>the <Col: StageText>trucks </Col>you'll need to carry them.";
            contentsSimple[2170] = "TaskObjectives_JG_WEL4_FoE1_Stage02_DispatchBrief=Port Authority plans to seize our weapons shipment as it arrives at the docks. Buy us time to move the guns. First, <Col: StageText>stop </Col>the authorities <Col: StageText>picking up </Col>the <Col: StageText>vehicles </Col>they'll use for the raid.";
            contentsSimple[2171] = "TaskObjectives_JG_WEL4_FoE1_Stage03_OwnerBrief=All right. There's a crate of guns in the marked container. <Col: StageText>Break in </Col>to the <Col: StageText>container</Col> and <Col: StageText>recover the guns</Col>. Knowing Jericho, he probably planned to sell them to the Blood Roses.";
            contentsSimple[2172] = "TaskObjectives_JG_WEL4_FoE1_Stage03_DispatchBrief=We managed to get some of the weapons but the rest are being held in this container. <Col: StageText>Protect </Col>that <Col: StageText>container</Col>, and don't let those Port Authority idiots get them open.";
            contentsSimple[2173] = "TaskObjectives_JG_WEL4_FoE1_Stage04_OwnerBrief=There's some serious hardware here - Port Authority will want in on this. <Col: StageText>Deliver </Col>either <Col: StageText>vehicle </Col>and <Col: StageText>the crate </Col>to the SPPA <Col: StageText>drop-off </Col>and we'll get hold of any intelligence they may have.";
            contentsSimple[2174] = "TaskObjectives_JG_WEL4_FoE1_Stage04_DispatchBrief=The SPPA are taking those <Col: StageText>weapons </Col>and <Col: StageText>vehicles </Col>to their <Col: StageText>drop-off </Col>nearby. You need to <Col: StageText>stop </Col>those deliveries and take back our arsenal.";
            contentsSimple[2175] = "TaskObjectives_JG_WEL4_FoE1_Stage05_OwnerBrief=Good. Now <Col: StageText>deliver </Col>our second <Col: StageText>vehicle </Col>to our own <Col: StageText>drop-off</Col>. We'll get them out of circulation before Jericho even knows they're missing.";
            contentsSimple[2176] = "TaskObjectives_JG_WEL4_FoE1_Stage05_DispatchBrief=The <Col: StageText>weapons </Col>and one of the <Col: StageText>vehicles </Col>have been delivered. Port Authority is taking the rest to a second <Col: StageText>drop-off</Col>, not far from the first. <Col: StageText>Stop </Col>that delivery.";
            contentsSimple[2177] = "TaskObjectives_JG_WEL4_FoE1_Stage06_OwnerBrief=We're working with the Port Authority to trace the smuggling route Jericho used. Go back and <Col: StageText>capture </Col>this <Col: StageText>crime scene</Col>. If any of Jericho's men turn up, don't let them take back the scene.";
            contentsSimple[2178] = "TaskObjectives_JG_WEL4_FoE1_Stage06_DispatchBrief=The SPPA may have taken those weapons, but if we control the docks, we can bring in more. Get back and <Col: StageText>capture </Col>this <Col: StageText>location</Col>.";
            contentsSimple[2179] = "TaskObjectives_JP_BCS0_TagBag_B_Stage01_OwnerBrief=Get a feel for how strong the Enforcers are around here. They hate <Col: StageText>graffiti</Col>, so go <Col: StageText>spray</Col> some up and get their attention, Blood Rose style!";
            contentsSimple[2180] = "TaskObjectives_JP_BCS0_TagBag_B_Stage01_DispatchBrief=Street intel says the Blood Roses are out <Col: StageText>tagging</Col> up the area. <Col: StageText>Stop</Col> their mindless vandalism.";
            contentsSimple[2181] = "TaskObjectives_JP_BCS0_TagBag_B_Stage02_OwnerBrief=Sweet! Look at all that paint. Bet they don't want to see the Roses ruling this area. <Col: StageText>Spray</Col> one more <Col: StageText>wall</Col> for good measure.";
            contentsSimple[2182] = "TaskObjectives_JP_BCS0_TagBag_B_Stage02_DispatchBrief=<Col: StageText>Stop</Col> those Blood Rose vandals. Take a gun and explain, in real small, bullet-sized words, that we don't consider <Col: StageText>graffiti</Col> to be an art form.";
            contentsSimple[2183] = "TaskObjectives_JP_BCS0_TagBag_B_Stage03_OwnerBrief=Stupid 'Forcers fell for it, and you can easily take out those few losers. <Col: StageText>Capture this area</Col>. Send a message that this is Blood Rose turf.";
            contentsSimple[2184] = "TaskObjectives_JP_BCS0_TagBag_B_Stage03_DispatchBrief=It's an ambush! <Col: StageText>Capture the area</Col> and take out these wannabe paint jockeys.";
            contentsSimple[2185] = "TaskObjectives_JP_BCS0_TagBag_G_Stage01_OwnerBrief=The 'Forcers sneak around this neighborhood like a bunch of schoolgirls playing hookey. Draw them out into the open by <Col: StageText>spraying</Col> some <Col: StageText>graffiti</Col>.";
            contentsSimple[2186] = "TaskObjectives_JP_BCS0_TagBag_G_Stage01_DispatchBrief=Got a street patrol saying those skater-punk G-Kings are spraying <Col: StageText>graffiti</Col> all over our fair city. <Col: StageText>Prevent</Col> this vandalism.";
            contentsSimple[2187] = "TaskObjectives_JP_BCS0_TagBag_G_Stage02_OwnerBrief=They ain't gonna like the the G-Kings being so bold! <Col: StageText>Spray</Col> one more <Col: StageText>wall</Col> for good measure.";
            contentsSimple[2188] = "TaskObjectives_JP_BCS0_TagBag_G_Stage02_DispatchBrief=The G-Kings are like tomcats pissing around to mark their territory. <Col: StageText>Stop</Col> them spraying their stinking <Col: StageText>graffiti</Col> on the walls.";
            contentsSimple[2189] = "TaskObjectives_JP_BCS0_TagBag_G_Stage03_OwnerBrief=Dumb Enforcers fell for it. <Col: StageText>Capture the area</Col>and show no mercy. See how they like going toe to toe with the G-Kings!";
            contentsSimple[2190] = "TaskObjectives_JP_BCS0_TagBag_G_Stage03_DispatchBrief=Looks like they want to fight. Rub their noses in it! <Col: StageText>Capture the area</Col>.";
            contentsSimple[2191] = "TaskObjectives_PGA_MovingTargetTaskItem_OwnerBrief=<col:HeatLevel5_Crim>Item Hold</col> - Grab the <col:purple>Organs</col> and keep them away from the enemy team!";
